INSERT INTO `zbp_config` VALUES (12, 'Timeline', 'a:7:{s:7:\"Version\";s:3:\"1.0\";s:7:\"logopic\";s:68:\"{#ZC_BLOG_HOST#}zb_users/upload/2019/10/201910141570987985819435.png\";s:6:\"mcolor\";s:6:\"000000\";s:8:\"homepcid\";s:1:\"1\";s:7:\"setsave\";s:1:\"0\";s:11:\"description\";s:246:\"这是一个关于植物的网站,很简约,只会介绍植物,每日一花是本站的特色，站主亲自搜集信息图片，给你展示一幅赏心悦目的每日一花图。很简约但是不简单。让你每天了解一种植物的属性\";s:8:\"keywords\";s:133:\"植物之家,植物,植物网,植物图片,植物大全,植物养殖,多肉植物,每日一花,各种花朵,植物介绍,植物属性\";}');
