-- ----------------------------
-- Table structure for zbp_post
-- ----------------------------
DROP TABLE IF EXISTS `zbp_post`;
CREATE TABLE `zbp_post` (
  `log_ID` int(11) NOT NULL AUTO_INCREMENT,
  `log_CateID` int(11) NOT NULL DEFAULT '0',
  `log_AuthorID` int(11) NOT NULL DEFAULT '0',
  `log_Tag` varchar(255) NOT NULL DEFAULT '',
  `log_Status` tinyint(4) NOT NULL DEFAULT '0',
  `log_Type` tinyint(4) NOT NULL DEFAULT '0',
  `log_Alias` varchar(255) NOT NULL DEFAULT '',
  `log_IsTop` int(11) NOT NULL DEFAULT '0',
  `log_IsLock` tinyint(1) NOT NULL DEFAULT '0',
  `log_Title` varchar(255) NOT NULL DEFAULT '',
  `log_Intro` text NOT NULL,
  `log_Content` longtext NOT NULL,
  `log_PostTime` int(11) NOT NULL DEFAULT '0',
  `log_CommNums` int(11) NOT NULL DEFAULT '0',
  `log_ViewNums` int(11) NOT NULL DEFAULT '0',
  `log_Template` varchar(50) NOT NULL DEFAULT '',
  `log_Meta` longtext NOT NULL,
  PRIMARY KEY (`log_ID`),
  KEY `zbp_log_TPISC` (`log_Type`,`log_PostTime`,`log_IsTop`,`log_Status`,`log_CateID`),
  KEY `zbp_log_VTSC` (`log_ViewNums`,`log_Type`,`log_Status`,`log_CateID`)
) ENGINE=MyISAM AUTO_INCREMENT=132 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of zbp_post
-- ----------------------------
BEGIN;
INSERT INTO `zbp_post` VALUES (45, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】21-《栀子花》', '栀子花（学名：Gardenia jasminoides），又名栀子、黄栀子。属双子叶植物纲、茜草科、栀子属常绿灌木，枝叶繁茂，叶色四季常绿，花芳香，是重要的庭院观赏植物。单叶对生或三叶轮生，叶片倒卵形，革质，翠绿有光泽。', '<div style=\'text-align: center;overflow: auto;\'><img title=栀子花 alt=栀子花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcs83PjGHFficGClEWuXWVZxIZgByWytEGugvRjCylnaxox23ZKr2A4ddIRqt5DOhh0aVtyBh2VM58g/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>（学名：Gardenia jasminoides），又名栀子、黄栀子。属双子叶植物纲、茜草科、栀子属常绿灌木，枝叶繁茂，叶色四季常绿，花芳香，是重要的庭院观赏植物。单叶对生或三叶轮生，叶片倒卵形，革质，翠绿有光泽。浆果卵形，黄色或橙色。除观赏外，其花、果实、叶和根可入药，有泻火除烦，清热利尿，凉血解毒之功效。花可做茶之香料，果实可消炎祛热。是优良的芳香花卉。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>喜光照充足且通风良好的环境，但忌强光曝晒。宜用疏松肥沃、排水良好的酸性土壤种植。可用扦插、压条、分株或播种繁殖。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>又名栀子，原产中国。喜温暖湿润和阳光充足环境，较耐寒，耐半阴，怕积水，要求疏松、肥沃和酸性的沙壤土。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>叶色四季常绿，花芳香素雅，绿叶白花，格外清丽可爱。它适用于阶前、池畔和路旁配置，也可有作篱和盆栽观赏，花还可做插花和佩带装饰。     灌木，高0.3-3米；嫩枝常被短毛，枝圆柱形，灰色。叶对生，革质，稀为纸质，少为3枚轮生，叶形多样，通常为长圆状披针形、倒卵状长圆形、倒卵形或椭圆形，长3-25厘米，宽1.5-8厘米，顶端渐尖、骤然长渐尖或短尖而钝，基部楔形或短尖，两面常无毛，上面亮绿，下面色较暗；侧脉8-15对，在下面凸起，在上面平。叶柄长0.2-1厘米，托叶膜质。     花芳香，通常单朵生于枝顶，花梗长3-5毫米；萼管倒圆锥形或卵形，长8-25毫米，有纵棱，萼檐管形，膨大，顶部5-8裂，通常6裂，裂片披针形或线状披针形，长10-30毫米，宽1-4毫米，结果时增长，宿存；花冠白色或乳黄色，高脚碟状，喉部有疏柔毛，冠管狭圆筒形，长3-5厘米，宽4-6毫米，顶部5至8裂，通常6裂，裂片广展，倒卵形或倒卵状长圆形，长1.5-4厘米，宽0.6-2.8厘米。     花丝极短，花药线形，长1.5-2.2厘米，伸出；花柱粗厚，长约4.5厘米，柱头纺锤形，伸出，长1-1.5厘米，宽3-7毫米，子房直径约3毫米，黄色，平滑。果卵形、近球形、椭圆形或长圆形，黄色或橙红色，长1.5-7厘米，直径1.2-2厘米，有翅状纵棱5-9条，顶部的宿存萼片长达4厘米，宽达6毫米；种子多数，扁，近圆形而稍有棱角，长约3.5毫米，宽约3毫米。花期5-7月，果期5月至翌年2月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>繁殖方式播种繁殖:一般选择饱满、色深红的成熟果实，连壳晒或晾干作种，播种前，用剪刀将种子果皮剪开，挖出种子，在热水中搓散，去掉浮种或杂质，然后将下沉的充实种子捞出，摊于竹席上，置通风处晾去过多的水分。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>可春播或秋播，一般多在春季进行，春播在雨水前后下种，秋播在秋分前后、播时将种子拌上火灰均匀的播在播种沟内，然后用细土或火土覆盖平播种沟，盖草淋水，经常保持土壤湿润，以利出苗，每亩用种子4～6斤。出苗后要注意及时去掉盖草，在幼苗期应经常除草，注意不要伤幼苗的根，除草后均需要追淡人粪尿水，育苗一年后即可移栽。如运输较远，宜用黄泥浆浆根，盖草保湿，每穴种一株，盖土、压实、淋水即可。  扦插繁殖:栀子的枝条很容易生根，南方暖地常于3-10月，北方则常5-6月间扦插，剪取健壮成熟枝条，插于沙床上，只要经常保持湿润，极易生根成活。水插远胜于土插，成活率接近100%，4-7月进行，剪下插穗仅保留顶端的2个叶片和顶芽插在盛有清水的容器中，经常换水，以免切口腐烂，3周后即开始生根。     压条繁殖:一般在4月清明前后或梅雨季节进行，4月份从3年生母株上选取1年生健壮枝条，长25～30厘米进行压条，将其拉到地面，刻伤枝条上的入土部位，如能在刻伤部位蘸上200ppm粉剂案乙酸，再盖上土压实，则更容易生根。如有三叉枝，则可在叉口处，一次可得三苗。一般经20～30天即可生根，在6月生根后可与母株分离，至次春可带土分栽或单株上盆。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>生长习性喜光也能耐荫，在庇荫条件下叶色浓绿，但开花稍差；喜温暖湿润气候，耐热也稍耐寒（-3℃），喜肥沃、排水良好、酸性的轻粘壤土，也耐干旱瘠薄，但植株易衰老。抗二氧化硫能力较强。萌蘖力、萌芽力均强，耐修剪更新。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>主要价值观赏价值：栀子叶色亮绿，四季常青，花大洁白，芳香馥郁，又有一定耐荫和抗有毒气体的能力，故为良好的绿化、美化、香化的材料，可成片丛植或配置于林缘、庭前、庭隅、路旁，植作花篱也极适宜，作阳台绿化、盆花、切花或盆景都十分适宜，也可用于街道和厂矿绿化。药用价值：中医上以果实入药，称之为山栀子，或称为栀子、焦山栀、生栀子、支子、木丹、越桃、㮁桃，性寒、味苦，功能清热泻火，主治热病心烦、目赤、黄疸、吐血、衄血、热毒疮疡等症。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>花 语坚强、永恒的爱、一生的守候。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>自我经验</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd style=\'color:yellow;text-decoration: underline\'>栀子花</a>，这花香谁不迷离，香味很浓，有人喜有人厌。纯白的花朵给人以高洁之趣。以前不知道</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000313&idx=1&sn=cfe17bfbe1cc571485f9fccea6a63d5d&chksm=4ffc143f788b9d295a207b7b941ec38e95b7eea342f6938790b6788bfaa9a8dd190c26499c7f#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564673653, 0, 0, 'GvnDcC2QjGEN4AUqdM7DySwIm_cd_rYExdqTA0-Db5w', '0');
INSERT INTO `zbp_post` VALUES (44, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】05-《猪笼草》', '快点关注我们吧大千世界，植物王国，每日一花，让我们一起来揭开其神奇的面纱！猪笼草猪笼草猪笼草是猪笼草属全体物', '<div style=\'text-align: center;overflow: auto;\'><img title=猪笼草 alt=猪笼草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibct2ENicOpI6Tr3ZficnRWDDr9iaJrbYUkel40dMCCAmdMeBhfwDG3yZoyiaUJ70yC0iascqlbpHxm10NnA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>属全体物种的总称。属于热带食虫植物，原产地主要为旧大陆热带地区。其拥有一个独特的吸取营养的器官——捕虫笼，捕虫笼呈圆筒形，下半部稍膨大，笼口上具有盖子，因其形状像猪笼而得名。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>为多年生藤本植物，茎木质或半木质，差不多3米多高，攀援于树木或者沿地面而生。叶一般为长椭圆形，末端有笼蔓，以便于攀援。在笼蔓的末端会形成一个瓶状或漏斗状的捕虫笼，并带有笼盖。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>生长多年后才会开花，花一般为总状花序，少数为圆锥花序 ，雌雄异株，花小而平淡，白天味道淡，略香；晚上味道浓烈，转臭。其观赏性无法与捕虫笼相比。果为蒴果，成熟时开裂散出种子。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>属植物在自然界常常平卧生长。叶的构造复杂，分叶柄，叶身和卷须。卷须尾部扩大并反卷形成瓶状，可捕食昆虫。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>具有总状花序，开绿色或紫色小花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>叶顶的瓶状体是捕食昆虫的工具。瓶状体的瓶盖复面能分秘香味，引诱昆虫。瓶口光滑，昆虫会被滑落瓶内，被瓶底分泌的液体淹死，并分解虫体营养物质，逐渐消化吸收。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>属植物的统称。是一种能够捕食昆虫的多年生草本植物，主产地是热带亚洲地区。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>为地生植物，是攀援状的亚灌木。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>拥有一幅独特的吸取营养的器官——捕虫囊，捕虫囊呈圆筒形，下半部稍膨大，因为形状像猪笼，故称</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>。在中国的产地海南又被称作雷公壶，意指它像酒壶。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>依靠捕捉昆虫等小动物来补充营养的植物被称为食虫植物。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>的繁殖方法包括有性繁殖和无性繁殖两种类型。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>常用的繁殖方法有扦插、压条和播种。   无性繁殖：包括扦插法、空中压条法、组织培养             有性繁殖:用种子来繁殖</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>结种子，必须要拥有两种性别的植株。然而，雌性的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>数量较少，因此少量种植的栽培者不易使</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>结出种子。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>易得叶斑病、根腐病、日灼病等。以叶斑病、根腐病危害较重。叶斑病:症状特征：叶片上发生水渍状和小点，逐渐扩大，形成椭圆形斑点，褐色。病部产生黑色霉层，后期破裂。严重影响植株生长和观赏效果。防治方法：加强管理，促使植株健壮，增强抗病力。喷施10%抗菌剂1000倍液。要求喷洒均匀、全株周到。根腐病:症状特征：在生长衰弱，对病虫抵抗力下降时，易诱发病害的发生，使</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>生长无生机，根部变褐无新根、腐烂，地上部萎蔫。由镰刀菌侵入所致，有的致病力强，产生毒素侵害植株；有的致病力弱，只在有伤口或植物衰弱时才侵染。低温、浇水过多是主要诱因。防治方法：于发病初期，喷施50%立枯净可湿性粉剂900倍液、50%根腐灵可湿性粉剂800倍液或80%多福锌可湿性粉剂800倍液。根粉蚧:防治方法：用2.5%凯素灵胶悬剂100～200倍液、40%速扑杀乳油1000～1500倍液或25%爱卡士乳油700～800倍液喷雾或根。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd style=\'color:yellow;text-decoration: underline\'>猪笼草</a>主要价值观赏价值：其形态奇特，如一个个吊着的猪笼，非常吸引人，与常见植物形成鲜明的对比。能吃掉昆虫也是一大特色。药用价值：清肺润燥，行水，解毒。治肺燥咳嗽，百日咳，黄疸，胃痛，痢疾，水肿，痈肿，虫咬伤。</p>自我经验3月份的时候，偶然发现花店有卖，便买了一株，挂养在我香樟树枝上，新叶长了不少，笼里的昆虫的尸体也是不少，但是没有继续长出捕虫笼，惊喜的是长出来一株新芽，很期待它的茁壮成长。end来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除<div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000074&idx=1&sn=cf312e55f5fe6fdfbe154750835c2549&chksm=4ffc174c788b9e5a5d54b4cf899f97a0ab20694450575e2fb3ff857377efdb2c8a956e7a2719#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563195989, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyaFImsLbDa26HaMl8y88l18', '0');
INSERT INTO `zbp_post` VALUES (42, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】06-《马齿苋》', '马齿苋（学名：Portulaca oleracea L.）：为石竹目、马齿苋科一年生草本，全株无毛。茎平卧，伏地铺散，枝淡绿色或带暗红色。叶互生，叶片扁平，肥厚，似马齿状，上面暗绿色，下面淡绿色或带暗红色；叶柄粗短。', '<div style=\'text-align: center;overflow: auto;\'><img title=马齿苋 alt=马齿苋 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibct2ENicOpI6Tr3ZficnRWDDr9ycYxcR8jsW79RytiaZibMhL30ibtPX4DF5S69iaPJOFVthHicue6OnVhjrA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000091&idx=1&sn=66197c838e2a4a28784e5c64fa64c964&chksm=4ffc175d788b9e4b974f9f8cf2edc335cffff11ff2a273ce3519a1a186c2841a5fc1bc7306eb#rd style=\'color:yellow;text-decoration: underline\'>马齿苋</a>科一年生草本，全株无毛。茎平卧，伏地铺散，枝淡绿色或带暗红色。叶互生，叶片扁平，肥厚，似马齿状，上面暗绿色，下面淡绿色或带暗红色；叶柄粗短。花无梗，午时盛开；苞片叶状；萼片绿色，盔形；花瓣黄色，倒卵形；雄蕊花药黄色；子房无毛。蒴果卵球形；种子细小，偏斜球形，黑褐色，有光泽。花期5-8月，果期6-9月。形态特征一年生草本，全株无毛。茎平卧或斜倚，伏地铺散，多分枝，圆柱形，长10-15厘米淡绿色或带暗红色。茎紫红色，叶互生，有时近对生，叶片扁平，肥厚，倒卵形，似马齿状，长1-3厘米，宽0.6-1.5厘米，顶端圆钝或平截，有时微凹，基部楔形，全缘，上面暗绿色，下面淡绿色或带暗红色，中脉微隆起；叶柄粗短。花无梗，直径4-5毫米，常3-5朵簇生枝端，午时盛开；苞片2-6，叶状，膜质，近轮生；萼片2，对生，绿色，盔形，左右压扁，长约4毫米，顶端急尖，背部具龙骨状凸起，基部合生；花瓣5，稀4，黄色，倒卵形，长3-5毫米，顶端微凹，基部合生；雄蕊通常8，或更多，长约12毫米，花药黄色；子房无毛，花柱比雄蕊稍长，柱头4-6裂，线形。蒴果卵球形，长约5毫米，盖裂；种子细小，多数偏斜球形，黑褐色，有光泽，直径不及1毫米，具小疣状凸起。花期5-8月，果期6-9月。     繁殖方式种子繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000091&idx=1&sn=66197c838e2a4a28784e5c64fa64c964&chksm=4ffc175d788b9e4b974f9f8cf2edc335cffff11ff2a273ce3519a1a186c2841a5fc1bc7306eb#rd style=\'color:yellow;text-decoration: underline\'>马齿苋</a>进行种子繁殖所用种子都是头年从野外采集或栽培时留的种。其种子籽粒极小，整地一定要精细，播后保持土壤湿润，7-10天即可出。  扦插繁殖:扦插枝条从当年播种苗或野生苗上采集，从发枝多、长势旺的强壮植株上采集为好，每段要留有3-5个节。扦插前精细整土；结合整地施足充分腐熟的农家肥。扦插密度（株行距）3厘米×5厘米，插穗入士深度3厘米左右，插后保持一定的湿度和适当的荫蔽，一周后即可成活。                 栽培技术及病虫防治播种或扦插后15-20天即可移入大田栽培，栽培面积较 小时也可直接扦插到大田。移栽前将田土翻耕，结合整地每亩施入1500公斤充分腐熟的人粪或15-20公斤三元复合肥，然后按1.2米宽开厢，按株行距12厘米×20厘米定植，栽后浇透定根水。为保证成活率，移栽最好选阴天进行，如在晴天移栽，栽后2天内应采取遮荫措施，并于每天傍晚浇水一次，移栽时按要求施足底肥后，前期可不追肥，以后每采收1-2次追一次稀薄入畜粪水，形成的花蕾要及时摘除，以促进营养枝的抽生。干旱时适当浇水抗旱。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000091&idx=1&sn=66197c838e2a4a28784e5c64fa64c964&chksm=4ffc175d788b9e4b974f9f8cf2edc335cffff11ff2a273ce3519a1a186c2841a5fc1bc7306eb#rd style=\'color:yellow;text-decoration: underline\'>马齿苋</a>整个生育期间病虫危害极少，一般不需喷药。主要价值食用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000091&idx=1&sn=66197c838e2a4a28784e5c64fa64c964&chksm=4ffc175d788b9e4b974f9f8cf2edc335cffff11ff2a273ce3519a1a186c2841a5fc1bc7306eb#rd style=\'color:yellow;text-decoration: underline\'>马齿苋</a>生食、烹食均可，柔软的茎可像菠菜一样烹制。不过如果对它强烈的味道不太习惯的话，就不要用太多。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000091&idx=1&sn=66197c838e2a4a28784e5c64fa64c964&chksm=4ffc175d788b9e4b974f9f8cf2edc335cffff11ff2a273ce3519a1a186c2841a5fc1bc7306eb#rd style=\'color:yellow;text-decoration: underline\'>马齿苋</a>茎顶部的叶子很柔软，可以像豆瓣菜一样烹食，可用来做汤或用于做沙司、蛋黄酱和炖菜。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000091&idx=1&sn=66197c838e2a4a28784e5c64fa64c964&chksm=4ffc175d788b9e4b974f9f8cf2edc335cffff11ff2a273ce3519a1a186c2841a5fc1bc7306eb#rd style=\'color:yellow;text-decoration: underline\'>马齿苋</a>和碎萝卜或马铃薯泥一起做，也可以和洋葱或番茄一起烹饪，其茎和叶可用醋腌泡食用。药用价值：全草治痢疾，肠炎腹泻                     自我经验随处可见的杂草，却是依然有着自己的价值。今天才知道幼时小河沟的杂草，原来有</p>，也算是迟到的自我介绍。end来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除<div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000091&idx=1&sn=66197c838e2a4a28784e5c64fa64c964&chksm=4ffc175d788b9e4b974f9f8cf2edc335cffff11ff2a273ce3519a1a186c2841a5fc1bc7306eb#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563286963, 0, 0, 'GvnDcC2QjGEN4AUqdM7Dyd3y2FmI5mH52MJpZGbC3mk', '0');
INSERT INTO `zbp_post` VALUES (43, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】07-《蒲公英》', '蒲公英（拉丁学名：Taraxacum mongolicum Hand.-Mazz.）菊科，蒲公英属多年生草本植物。根圆锥状，表面棕褐色，皱缩，叶边缘有时具波状齿或羽状深裂，基部渐狭成叶柄，叶柄及主脉常带红紫色，花葶上部紫红色...', '<div style=\'text-align: center;overflow: auto;\'><img title=蒲公英 alt=蒲公英 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcu3AhicOqrwIToYT5pj2NU5ibYeEaPwYws6Ux32zUAkJUejznpyaFKKojicVUAKTdNqAGZoXr0PGtp5w/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>（拉丁学名：Taraxacum mongolicum Hand.-Mazz.）菊科，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>属多年生草本植物。根圆锥状，表面棕褐色，皱缩，叶边缘有时具波状齿或羽状深裂，基部渐狭成叶柄，叶柄及主脉常带红紫色，花葶上部紫红色，密被蛛丝状白色长柔毛；头状花序，总苞钟状，瘦果暗褐色，长冠毛白色，花果期4～10月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>别名黄花地丁、婆婆丁、华花郎等。菊科多年生草本植物。头状花序，种子上有白色冠毛结成的绒球，花开后随风飘到新的地方孕育新生命。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>别名黄花地丁、婆婆丁、华花郎等。菊科多年生草本植物。头状花序，种子上有白色冠毛结成的绒球，花开后随风飘到新的地方孕育新生命。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>形态特征多年生草本。根略呈圆锥状，弯曲，长4～10厘米，表面棕褐色，皱缩，根头部有棕色或黄白色的毛茸。叶成倒卵状披针形、倒披针形或长圆状披针形，长4～20厘米，宽1-5厘米，先端钝或急尖，边缘有时具波状齿或羽状深裂，有时倒向羽状深裂或大头羽状深裂，顶端裂片较大，三角形或三角状戟形，全缘或具齿，每侧裂片3～5片，裂片三角形或三角状披针形，通常具齿，平展或倒向，裂片间常夹生小齿，基部渐狭成叶柄，叶柄及主脉常带红紫色，疏被蛛丝状白色柔毛或几无毛。     花葶1至数个，与叶等长或稍长，高10～25厘米，上部紫红色，密被蛛丝状白色长柔毛；头状花序直径约30-40毫米；总苞钟状，长12～14毫米，淡绿色；总苞片2～3层，外层总苞片卵状披针形或披针形，长8～10毫米，宽1-2毫米，边缘宽膜质，基部淡绿色，上部紫红色，先端增厚或具小到中等的角状突起；内层总苞片线状披针形，长10-16毫米，宽2-3毫米，先端紫红色，具小角状突起；舌状花黄色，舌片长约8毫米，宽约1.5毫米，边缘花舌片背面具紫红色条纹，花药和柱头暗绿色。瘦果倒卵状披针形，暗褐色，长约4～5毫米，宽约1～1.5毫米，上部具小刺，下部具成行排列的小瘤，顶端逐渐收缩为长约1毫米的圆锥至圆柱形喙基，喙长6～10毫米，纤细；冠毛白色，长约6毫米。花期4～9月，果期5-10月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>繁殖采用种子繁殖。种子无休眠期，成熟采收后的种子，从春到秋可随时播种。根据场需求，冬季也可在温室内播种，露地直播采用条播，在畦面上按行距25～30厘米开浅横沟，播幅约10厘米，种子播下后覆土1 厘米，然后稍加镇压。播种量每亩0.5～0.75千克。平畦撒播，亩用种1.5～2.0千克。品质优良的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>良种每亩的播种量仅为25～50克。播种后盖草保温，出苗时揭去盖草，约6天可以出苗。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>是多年生宿根性植物，野生条件下二年生植株就能开花结籽，初夏为开花结数随生长年限而增多，有的单株开花数达20个以上，优良品种单株开花数量可高达20个以上，开花后经13～15天种子即成熟。采种时可将</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>的花盘摘下，放在室内存放后熟一天，待花盘全部散开，再阴干1～2天至种子半干时，用手搓掉种子尖端的绒毛，然后晒干种子备用。叶斑病:叶面初生针尖大小褪绿色至浅褐色小斑点，后扩展成圆形至椭圆形或不规则状，中心暗灰色至褐色，边缘有褐色线隆起，直径3～8毫米，个别病斑20毫米。斑枯病：初于下部叶片上出现褐色小斑点，后扩展成黑褐色圆形或近圆形至不规则形斑，大小5～10毫米，外部有一不明显黄色晕圈。后期病斑边缘呈黑褐色。锈病：主要危害叶片和茎。初在叶片上现浅黄色小斑点，叶背对应处也生出小褪绿斑。后产生稍隆起的疱状物，疱状物破裂后，散出大量黄褐色粉状物，叶片上病斑多时，叶缘上卷。上述三种病害的防治方法是：注意田间卫生，结合采摘收集病残体携出田外烧毁；清沟排水，避免偏施氮肥，适时喷施植宝素等，使植株健壮生长，增强抵抗力；发病初期开始喷洒42%福星乳油8000倍液，或20.67%万兴乳油2000～30000倍液，或40%多?硫悬浮剂500倍液，或50%扑海因可湿性粉剂1500倍液，每10～15天1次，连续防治2-3次。采收前7天停止用药。枯萎病：初发病时叶色变浅发黄，萎蔫下垂，茎基部也变成浅褐色。横剖茎基部可见维管束变为褐色，向上扩展枝条的维管束也逐渐变成淡褐色，向下扩展致根部外皮坏死或变黑腐烂。有的茎基部裂开，湿度大时产生白霉。防治方法：提倡施用酵素菌沤制的堆肥或腐熟有机肥;加强田间管理，与其他作物轮作;选种适宜本地的抗病品种;选择宜排水的沙性土壤栽种；合理灌溉，尽量避免田间过湿或雨后积水；发病初期选用50%多菌灵可湿性粉剂500倍液、或40%多?硫悬浮剂600倍液、或50%琥胶肥酸铜可湿性粉剂400倍液、或30%碱式硫酸铜悬浮剂400倍液灌根，每株用药液0.4～0.5升，视病情连续灌2-3次。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>富含维生素A、维生素C及钾，也含有铁、钙、维生素B2、维生素B1、镁、维生素B6、叶酸及铜。具体的元素含量主要是水分，每60克生</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>叶含水分86%，蛋白质1.6克，碳水化合物5.3克，热量约有108.8千焦。药用价值：清热解毒，利尿散结。治急性乳腺炎，淋巴腺炎，瘰疬，疔毒疮肿，急性结膜炎，感冒发热，急性扁桃体炎，急性支气管炎，胃炎，肝炎，胆囊炎，尿路感染。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd style=\'color:yellow;text-decoration: underline\'>蒲公英</a>的最深印象是出现在课本上的，而每次见到</p>都忍不住去将其吹飞，那飘零种子总是让人心生向往。end来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除<div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000104&idx=1&sn=7a93a6c6f066badcc0d3a1ee21fe1132&chksm=4ffc176e788b9e787229df3107db9ef60414274698f835dda9b28fc280c32679f07d2d5071eb#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563370546, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyW7wFNHH0HBr_qMzvcx4OeY', '0');
INSERT INTO `zbp_post` VALUES (41, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】10-《木棉》', '木棉又名红棉、英雄树、攀枝花、斑芝棉、斑芝树、攀枝，属木棉科，落叶大乔木，原产印度。木棉是一种在热带及亚热带地区生长的落叶大乔木，高10－25米。树干基部密生瘤刺，以防止动物的侵入。', '<div style=\'text-align: center;overflow: auto;\'><img title=木棉 alt=木棉 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctofH9OvmyHlFSmEOm01dH2LydUAawzSGYknPZBcvtDfxuML2K2qhnPbUWlRnxFVHVUxYxcy8P99w/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>又名红棉、英雄树、攀枝花、斑芝棉、斑芝树、攀枝，属</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>是一种在热带及亚热带地区生长的落叶大乔木，高10－25米。树干基部密生瘤刺，以防止动物的侵入。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>外观多变化：春天时，一树橙红；夏天绿叶成荫；秋天枝叶萧瑟；冬天秃枝寒树，四季展现不同的景象。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>花桔红色，3～4月开花，先开花后长叶，树形具阳刚之美。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>的花大而美，树姿巍峨，可植为园庭观赏树，行道树。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>形态特征落叶大乔木，高可达25米，树皮灰白色，幼树的树干通常有圆锥状的粗刺；分枝平展。掌状复叶，小叶5-7片，长圆形至长圆状披针形，长10-16厘米，宽3.5-5.5厘米，顶端渐尖，基部阔或渐狭，全缘，两面均无毛，羽状侧脉15-17对，上举，其间有1条较细的2级侧脉，网脉极细密，二面微凸起；叶柄长10-20厘米；小叶柄长1.5-4厘米；托叶小。     花单生枝顶叶腋，通常红色，有时橙红色，直径约10厘米；萼杯状，长2-3厘米，外面无毛，内面密被淡黄色短绢毛，萼齿3-5，半圆形，高1.5厘米，宽2.3厘米，花瓣肉质，倒卵状长圆形，长8-10厘米，宽3-4厘米，二面被星状柔毛，但内面较疏；雄蕊管短，花丝较粗，基部粗，向上渐细，内轮部分花丝上部分2叉，中间10枚雄蕊较短，不分叉，外轮雄蕊多数，集成5束，每束花丝10枚以上，较长；花柱长于雄蕊。     蒴果长圆形，钝，长10-15厘米，粗4.5-5厘米，密被灰白色长柔毛和星状柔毛；种子多数，倒卵形，光滑。花期3-4月，果夏季成熟。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>外观多变化：春天时，一树橙红；夏天绿叶成荫；秋天枝叶萧瑟；冬天秃枝寒树，四季展现不同的风情。花桔红色，每年2~3月树叶落光后进入花期，然后长叶，树形具阳刚之美。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>繁殖方式嫁接繁殖 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>种子4月～5月成熟即可采收。由于种子含油量高，易变坏丧失萌发力，一般要求采后当年及时播种。苗床育苗采用条播和撒播，条播的行距20厘米，深5厘米，沟内点播种子。覆土2厘米，表土盖草，播后6～7 天开始发芽，13～15天基本出齐。幼苗出土后及时松土除草，并适当间苗除草，注意病虫害防治。幼苗高4～5厘米，有初生叶2片以上时，可移入营养袋，苗高40厘米左右可移植培育大苗。当苗胸径有1～115厘米时即可嫁接。      播种繁殖采种：蒴果成熟易爆裂，种子随棉絮飞散，故要在果实开裂前采收。播种：春季3月份左右种入土壤即可。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>栽培技术及病虫防治病虫害有炭疽病、斑点病、介壳虫、粉虱等，可对症防治。播种繁殖采种：蒴果成熟易爆裂，种子随棉絮飞散，故要在果实开裂前采收。播种：春季3月份左右种入土壤即可。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>主要价值使用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>树形高大雄伟，春季红花盛开，是优良的行道树、庭荫树和风景树。可园林栽培观赏。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>生长迅速，材质轻软，可供蒸笼、包装箱之用。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>纤维短而细软，无拈曲，中空度高达86%以上，远超人工纤维（25%－40%）和其他任何天然材料，不易被水浸湿，且耐压性强，保暖性强，天然抗菌，不蛀不霉，可填充枕头、救生衣。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>纤维被誉为“植物软黄金”，是目前天然纤维中较细、较轻、中空度较高、较保暖的纤维材料。药用价值：药材性状：花朵全长5～8厘米，干时皱缩，呈不规则的块状，多不具子房和花柄。花萼杯状，长2.5～4厘米，直径2～3厘米，顶端3～5裂，厚革质，甚脆，外表棕黑色，有纵皱纹，内面被灰黄色短绒毛。花瓣5片，皱缩或破碎，面棕黄色或深棕色，被星状毛，内面红棕色，星状毛较少；雄蕊多数，排成多列，基部合生，花药肾形，卷曲。气微，味淡微甘。以朵大完整，色棕黄者为佳。清热，利湿，解毒。用于泄泻，痢疾，血崩，疮毒。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>花 语珍惜身边的人，珍惜身边的幸福</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>自我经验</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd style=\'color:yellow;text-decoration: underline\'>木棉</a>没有真实见过，倒是孩童时代随着父母看一部剧《</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000140&idx=1&sn=a449034abb25dbaeb612f621295d204f&chksm=4ffc178a788b9e9c93331e211dd763a56171eaad36d118357f0713a617d2d53f67dcbff27e1c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563633355, 0, 0, 'GvnDcC2QjGEN4AUqdM7DydZhQlEYieC-Su5JLxu4NNU', '0');
INSERT INTO `zbp_post` VALUES (40, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】11-《风信子》', '风信子（学名：Hyacinthus orientalis L.）：是多年草本球根类植物，鳞茎卵形，有膜质外皮，皮膜颜色与花色成正相关，未开花时形如大蒜，原产地中海沿岸及小亚细亚一带，是研究发现的会开花的植物中最香的一个品种。', '<div style=\'text-align: center;overflow: auto;\'><img title=风信子 alt=风信子 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcs6NciaF76q0aH5UoWJx7jAYgVmzBr67dUJPlOKgxPJ5has8XlDaYXJh7Tv3ibkFjwyuR3B9cm6ibJTA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>（学名：Hyacinthus orientalis L.）：是多年草本球根类植物，鳞茎卵形，有膜质外皮，皮膜颜色与花色成正相关，未开花时形如大蒜，原产地中海沿岸及小亚细亚一带，是研究发现的会开花的植物中最香的一个品种。喜阳光充足和比较湿润的生长环境，要求排水良好和肥沃的沙壤土等。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>是多年草本生球根类植物，鳞茎球形或扁球形，有膜质外皮，外被皮膜呈紫蓝色或白色等，皮膜颜色与花色成正相关。未开花时形如大蒜。叶4-9枚，狭披针形，肉质，基生，肥厚，带状披针形，具浅纵沟，绿色有光。花茎肉质，花葶高15-45厘米，中空，端着生总状花序；小花10-20朵密生上部，多横向生长，少有下垂，漏斗形，花被筒形，上部四裂，花冠漏斗状，基部花筒较长，裂片5枚。向外侧下方反卷。根据其花色，大致分为蓝色、粉红色、白色、鹅黄、紫色、黄色、绯红色、红色等八个品系。原种为浅紫色，具芳香。蒴果。花期早春，自然花期3-4月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>繁殖方式分球繁殖:6月份把鳞茎挖回后，将大球和子球分开，“大球秋植后来年早春可开花，子球需培养3年才能开花。由于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>自然分球率低，一般母株栽植一年以后只能分生1-2个子球，为提高繁殖系数，可在夏季休眠期对大球采用阉割手术，刺激它长出子球。            播种繁殖:多在培育新品种时使用，于秋季播入冷床中的培养土内，覆土1厘米，翌年1月底2月初萌发。实生苗培养的小鳞茎，4-5年后开花。一般条件贮藏下种子发芽力可保持3年。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>栽培技术及病虫防治</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>开花所需养分，主要靠鳞茎叶中储存的养分供给，只要选择表皮无损伤、肉质鳞片不过分皱缩、较坚硬而沉重、饱满的种球，就能开出丰硕美丽的花。在选购种头时，要注意挑选皮色鲜明、质地结实，没有病斑和虫口的为好，通常从种皮的颜色可以基本判断它所开的是什么颜色的花。比如外皮为紫红色的它就会开紫红色的花，若是白色的将会开白色的花，但有些经过杂交育成的品种其颜色较为复杂，有时会分辨不清，需要向经营者询问清楚才好购买。当种头购回后，用多菌灵泡好，晾干。为了使它打破休眠期，要先放进冰箱的最下格冷藏一个月左右，以便于日后顺利开花。但从冰箱取出时，最好移放在阴凉的地方七八天才可播种。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>常见的病害有生芽腐烂，软腐病、菌核病和病毒病。种植前基质严格消毒，种球清选并作消毒处理，生长期间每7天喷一次1000倍退菌特或百菌清，交替使用，可以在一定程度上抑制病菌的传播。严格控制浇水量，加强通风管理，控制环境中空气相对湿度，出现中心病株及时拔除，可以大幅度降低发病率。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>植株低矮整齐，花序端庄，花色丰富，花姿美丽，是早春开花的著名球根花卉之一，也是重要的盆花种类。适于布置花坛、花境和花槽，也可作切花、盆栽或水养观赏。有滤尘作用，花香能稳定情绪，消除疲劳作用。花除供观赏外，还可提取芳香油。球茎有毒性，如果误食，会引起头晕、胃痉挛、拉肚子等症状，所以要严防小孩子或者动物的误食。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>花 语胜利、竞技、喜悦、爱意、幸福、浓情、倾慕、顽固、生命、得意、永远的怀念。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd style=\'color:yellow;text-decoration: underline\'>风信子</a>自我经验养过几次</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000150&idx=1&sn=f778e5489bd90ebd1b3c076d56fd6c08&chksm=4ffc1790788b9e86004a9beee08b55948d3aedf591a9abc637e451c9a247d6df803e6f8a765c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563719852, 0, 0, 'GvnDcC2QjGEN4AUqdM7Dya3qPj4AapXPuSgGZo4SJRg', '0');
INSERT INTO `zbp_post` VALUES (39, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】09-《悬铃花》', '悬铃花，拉丁学名（Malvaviscus arboreus Cav.），属常绿小灌木。外型略似朱槿，高30-60cm，鲜红色花朵，较为奇特，花期终年、花量多。悬铃花性强健，喜高温多湿和阳光充足环境。', '<div style=\'text-align: center;overflow: auto;\'><img title=悬铃花 alt=悬铃花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcunIFQIB0FtZIEIZysGK7fDXu8XMjmMrve5qRLKxo6paxctC0BTrKTAukM7KquLLWSZqEuCGhtvJA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>，拉丁学名（Malvaviscus arboreus Cav.），属常绿小灌木。外型略似朱槿，高30-60cm，鲜红色花朵，较为奇特，花期终年、花量多。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>性强健，喜高温多湿和阳光充足环境。原产墨西哥至秘鲁及巴西，现分布于世界各地热带及亚热带地区，包括中国南部，多为野生，华南地区多植于庭院。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>形似风铃，美丽可爱，为不可多得的盆栽佳品。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>形态特征常绿灌木，外型略似朱槿，但叶片较为狭昌浓绿；花朵不若其他朱槿类会完全展开，鲜红的花瓣螺旋卷屈，呈吊钟状，雌雄蕊细长突出瓣外苞，花瓣略左旋，不开含苞状，鱼红色，叶阔心形，浅二裂或角状。花朵向下悬垂。叶有柄，互生，集株端，长椭圆形状，先端渐尖，粗钝锯齿缘，主叶脉掌状，有五至七条。绿色。全年出叶，尤以3-8月为盛。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>繁殖方式主要用扦插繁殖，也可嫁接或高压法。可在夏、秋季节剪取健壮、充实的半木质化嫩枝10-15厘米长，插于沙床，保持湿润，一般插后20-25天可生根。生产中主要用扦插育苗繁殖。扦插的季节，除冬季低温不宜外，春夏秋三季均可进行，但以3~5月份最为适宜，此时扦插的幼苗，翌年即可定植。老枝和嫩枝均可用作繁殖材料，但以1~2年生枝条的成苗率最高。插穗剪成10厘米左右长，可不经任何处理，剪后即插入沙床或蛭石床内，经常保湿，约1个月即可发叶发根，一般成活率可达80%以上。当插穗发根3~5条，根长3厘米左右时，即可移入圃地定植，注意浇水保湿，按常规育苗方法管理，翌年即可出圃。             </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>栽培技术及病虫防治土壤:每年春季换盆，增添腐叶土，盆土宜用含腐殖质丰富的肥沃、疏松和排水良好的微酸性沙质土壤。光照:日照充足，植株生长较快，茎叶较繁密，开花亦较多；荫蔽处开花不良，结蕾易脱落，或花朵变小，花色暗淡。浇水:浇水避免浇到花瓣，以免产生白斑。肥料:生长期每半月施肥1次。盛夏土壤保持湿润，多见阳光，但要防烈日曝晒，早晚在叶面喷水。秋季天气转凉时，应搬入室内，停止施肥，控制浇水，保持通风。修剪:栽培中当分枝稀少时，要对枝条进行修剪，使其多发侧枝，剪去过长、过乱的枝条及弱枝、病枝，以促发新枝，从而达到多萌发分枝多开花的目的。成株每年春季定期修剪整枝一次，植株老化需强剪，促使枝叶繁密。剪枝直接扦插于篱地，即能发根成长。病害:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>常发生叶斑病和白粉病，这是一种真菌病害，是由真菌引起的。真菌是一类没有叶绿素的低等生物，个体大小不一，多数要在显微镜下才能看清。防治方法：一是深秋或早春清除枯枝落叶并及时剪除病枝、叶烧毁；二是发病前喷洒65%代森锌可湿性粉剂600倍液保护；三是合理施肥与浇水，注意通风透光；四是发病初期喷洒50%多菌灵，或50%托布津500--600倍液，或75%百菌清600--800倍液。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>主要价值园林价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>美丽而永不开展，极易栽培，为有价值的观赏植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>为常绿小灌木，适应性强，鲜红色花朵,较为奇特，在热带地区全年开花不断。不但适合于庭园、绿地、行道树的配植，也可以列植为花境、花篱或自然式种植，还可剪扎造型和盆栽观赏。在南京地区常以盆栽室内观赏。环保价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>具有吸附烟尘和净化有害气体的作用。它可供厂矿污染区绿化作用。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd style=\'color:yellow;text-decoration: underline\'>悬铃花</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000127&idx=1&sn=3501e61897c53b6c78def734bbca06b5&chksm=4ffc1779788b9e6fe9afc31fa4fa2a14ae58024d7e07e1ac5d61b44f81340efc03f963fa9003#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563548026, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQA17VFgvD83X8VtYIfCAPU', '0');
INSERT INTO `zbp_post` VALUES (38, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】08-《含笑》', '含笑花 拉丁文名：Michelia figo （Lour.）Spreng。常绿灌木，高2-3米，树皮灰褐色，分枝繁密；叶革质，狭椭圆形或倒卵状椭圆形，花期3-5月，果期7-8月。。', '<div style=\'text-align: center;overflow: auto;\'><img title=含笑 alt=含笑 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuCkHQDdITOvVmN3aWnxXfrdVOu7D0gmdwEIazyZs2d61O7UpdALWh2SDpgwPLOk35DTTAzrvfpmg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>花拉丁文名：Michelia figo （Lour.）Spreng。常绿灌木，高2-3米，树皮灰褐色，分枝繁密；叶革质，狭椭圆形或倒卵状椭圆形，花期3-5月，果期7-8月。原产中国华南南部各省区，广东鼎湖山有野生，生于阴坡杂木林中。芳香花木，苞润如玉，香幽若兰。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>形态特征常绿灌木，高2-3米，树皮灰褐色，分枝繁密；芽、嫩枝,叶柄，花梗均密被黄褐色绒毛。叶革质，狭椭圆形或倒卵状椭圆形，长4-10厘米，宽1.8-4.5厘米，先端钝短尖，基部楔形或阔楔形，上面有光泽，无毛，下面中脉上留有褐色平伏毛，余脱落无毛，叶柄长2-4毫米，托叶痕长达叶柄顶端。     花直立，花瓣长12-20毫米，宽6-11毫米，淡黄色而边缘有时红色或紫色，具甜浓的芳香，花被片6，肉质，较肥厚，长椭圆形，长12－20毫米，宽6-11毫米；雄蕊长7-8毫米，药隔伸出成急尖头，雌蕊群无毛，长约7毫米，超出于雄蕊群；雌蕊群柄长约6毫米，被淡黄色绒毛。聚合果长2-3.5厘米；蓇葖卵圆形或球形，顶端有短尖的喙。花期3-5月，果期7-8月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>花可用扦插、圈枝繁殖和嫁接法等方式繁殖。扦插法宜于7月下旬至9月上旬为之，可取犹未发出新芽、但留有3～8片叶子之木质化枝条或顶芽约15公分，于插穗基部沾附发根素插置于沙质土壤上，另予适当遮荫及保持环境湿润，约2～3个月即可生根，再于翌春移植。             圈枝繁殖4月份选取发育良好、组织充实健壮的2年生枝条。在枝条的适当部位做宽0.5cm的环剥，深达木质部，用湿润苔藓植物敷于环剥部位，用塑料膜包在外面，上下扎紧，约2个月生根。待新根充分发育后，剪下上盆栽培，栽培后要浇透水，以后每天浇水一次或不浇。当新梢长有7cm左右时开始施肥。 嫁接法，宜在5～6月间实施，常是以木兰作为砧木，成活之后可快速生长。根部肥厚多肉的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>花，不耐移植，若实在必须进行移植时宜多带土球，而植株的修剪、整型则是以越冬之前为宜。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>栽培技术及病虫防治平时要保持盆土湿润，但决不宜过湿。因其根部多位肉质，如浇水太多或雨后盆涝会照成烂根，故阴雨季节要注意控制湿度。生长期和开花前需较多水分，每天浇水一次，夏季高温天气须往叶面浇水，以保持一定空气湿度。秋季冬季因日照偏短每周浇水一至两次即可。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>的常见病害有叶枯病、炭疽病、藻斑病、煤污病等，这些病均危害叶片，不利于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>生长。发生病害时，都应立即摘除病叶并烧毁，然后喷洒相应的药剂防止蔓延。预防叶枯病，可在初春每隔15天左右喷洒一次0.3%的石硫合剂；发病后可用65%代森锌可湿性粉剂500－600倍液进行喷雾防治。防治时，先要适当疏剪过密枝，使树体通风透光。在虫害较轻时可人工用刷子轻轻除之；在若虫孵化期，可使用40%氧化乐果乳油2000倍液喷雾予以杀灭。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>主要价值观赏价值：以盆栽为主，庭园造景次之。在园艺用途上主要是栽植2～3公尺之小型</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>花灌木，作为庭园中备供观赏暨散发香气之植物，当花苞膨大而外苞行将裂解脱落时，所采摘下的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>花气味最为香浓。药用价值：据《本草纲目》等医书记载和科学考证，天然的植物花草不但以其艳色、美形于香味使人赏心悦目，而且含有丰富的营养成分、生物活性成分以及天然植物精华。根据花草所含的不同营养成分和药用功效，结合专家们多年的实践经验，将具有相同功效的天然花草有机组合，并根据花色、花型、花香与品种调配成具有不同美容养颜和保健功效的花草茶系列。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd style=\'color:yellow;text-decoration: underline\'>含笑</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000115&idx=1&sn=cd0df5d6769b6501134dda78ce6797f4&chksm=4ffc1775788b9e636edb1658749289bc870c48c8c16ade71f0c23054a837d529124f1e472ad1#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563460073, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyUc9Vh8SFsr-2NUkHUo83JY', '0');
INSERT INTO `zbp_post` VALUES (36, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】13-《鸭跖草》', '鸭跖草，拉丁学名：（Commelina communis），别名碧竹子、翠蝴蝶、淡竹叶等。属粉状胚乳目、鸭跖草科、鸭跖草属一年生披散草本。鸭跖草叶形为披针形至卵状披针形，叶序为互生，茎为匍匐茎，花朵为聚花序，顶生或腋生，雌雄同株，', '<div style=\'text-align: center;overflow: auto;\'><img title=鸭跖草 alt=鸭跖草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvC7s86uP5YkKBcd7hibbKyeibfaTq7giaJwk1icQdW6l4bACYHcGW4LOPiahTra3Y3TzWkBPeTzMb0SMg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>，拉丁学名：（Commelina communis），别名碧竹子、翠蝴蝶、淡竹叶等。属粉状胚乳目、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>叶形为披针形至卵状披针形，叶序为互生，茎为匍匐茎，花朵为聚花序，顶生或腋生，雌雄同株，花瓣上面两瓣为蓝色，下面一瓣为白色，花苞呈佛焰苞状，绿色，雄蕊有6枚。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>形态特征一年生披散草本。茎匍匐生根，多分枝，长可达1米，下部无毛，上部被短毛。叶披针形至卵状披针形，长3-9厘米，宽1.5-2厘米。总苞片佛焰苞状，有1.5-4厘米的柄，与叶对生，折叠状，展开后为心形，顶端短急尖，基部心形，长1.2-2.5厘米，边缘常有硬毛；聚伞花序，下面一枝仅有花1朵，具长8毫米的梗，不孕；上面一枝具花3-4朵，具短梗，几乎不伸出佛焰苞。花梗长仅3毫米，果期弯曲，长不过6毫米；萼片膜质，长约5毫米，内面2枚常靠近或合生；花瓣深蓝色；内面2枚具爪，长近1厘米。蒴果椭圆形，长5-7毫米，2室，2爿裂，有种子4颗。种子长2-3毫米，棕黄色，一端平截、腹面平，有不规则窝孔。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>繁殖方式播种繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>用种子繁殖可在2月下旬至3月上旬在温室育苗。播种前用25~27℃温水浸种8~10小时后捞出，在25~27℃下催芽3~5天，种子露白后即可播种。育苗可采用条播或撒播的方式，在整好的畦内按10~15cm行距开沟，将催好芽的种子均匀撒入沟内，覆土，稍加镇压后保持土壤湿润。在适宜的温、湿度条件下，1周左右即可出苗，出苗后降低温、湿度管理，以免幼苗徒长。       扦插繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>的每个节都可以产生新根。将植株的茎剪下，在整好的田内按5×10cm的株、行距扦插定植。扦插后保持土壤湿润，光照较强时，应搭阴棚遮阳，避免失水过多而使扦插苗死亡。15天左右即可生根。分株繁殖:春季在地上部分萌发前，将根挖出，分根定植。一般每块根可分为10株左右，按10×10cm的株、行距定植于大田。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>，一般每年春季换一次盆，盆土用园土加少量河沙即可。幼苗期放在具有散射光处养护，成株期放半阴处培养。虽然在室内光线明亮处也能生长，但长期置于室内光照较少处，则枝叶易徒长，叶片变薄而缺乏光泽，叶色变浅，因此，春、秋季节宜将花盆悬挂在中午受不到阳光直射的阳台或廊檐下，经常喷水以增加空气湿度，同时保持盆土湿润。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>主要价值药用价值：味甘、微苦，性寒。能清热，解毒，利尿。为消肿利尿、清热解毒之良药，此外对麦粒肿、咽炎、扁桃腺炎、宫颈糜烂、腹蛇咬伤有良好疗效。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd style=\'color:yellow;text-decoration: underline\'>鸭跖草</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000169&idx=1&sn=c405fd4da00dbed4f2b6a41a49292634&chksm=4ffc17af788b9eb9628ccb4ecc44aaf5172e45b3a146c22593202fc01515fbd264ec6468a61b#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563885726, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyTwX7yfj0ueHMj7f_uCAy44', '0');
INSERT INTO `zbp_post` VALUES (37, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】12-《向日葵》', '向日葵（拉丁文：Helianthus annuusL.），为木兰纲、菊目、菊科、向日葵属的一年生草本植物。高1～3.5米。茎直立，圆形多棱角，质硬被白色粗硬毛。广卵形的叶片通常互生，先端锐突或渐尖，有基出3脉，边缘具粗锯齿，两面粗糙....', '<div style=\'text-align: center;overflow: auto;\'><img title=向日葵 alt=向日葵 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvC7s86uP5YkKBcd7hibbKyeiayHgA6KzGFW2zoKialiavibgQCVS770FvIhYKj0OiaHZmUcOzZXZpncHGw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>（拉丁文：Helianthus annuusL.），为木兰纲、菊目、菊科、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>属的一年生草本植物。高1～3.5米。茎直立，圆形多棱角，质硬被白色粗硬毛。广卵形的叶片通常互生，先端锐突或渐尖，有基出3脉，边缘具粗锯齿，两面粗糙，被毛，有长柄。头状花序，直径10～30厘米，单生于茎顶或枝端。总苞片多层，叶质，覆瓦状排列，被长硬毛，夏季开花，花序边缘生中性的黄色舌状花，不结实。花序中部为两性管状花，棕色或紫色，能结实。矩卵形瘦果，果皮木质化，灰色或黑色，称葵花籽。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>形态特征1年生草本，高1.0～3.5米，杂交品种有半米高植株。茎直立，粗壮，圆形多棱角，为白色粗硬毛。叶通常互生，心状 卵形或卵圆形，先端锐突或渐尖，有基出3脉，边缘具粗锯齿，两面粗糙，被毛，有长柄。头状花序，极大，直径10~30厘米，单生于茎顶或枝端，常下倾。总苞片多层，叶质，覆瓦状排列，被长硬毛，夏季开花，花序边缘生黄色的舌状花，不结实。花序中部为两性的管状花，棕色或紫色，结实。瘦果，倒卵形或卵状长圆形，稍扁压，果皮木质化，灰色或黑色，俗称葵花籽。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>繁殖方式播种繁殖:培育种子先由温水泡过，再以种子尖端朝下种植在土中，约4～5天即可发芽。当125px土层温度5℃即可播种。播期选择的基本原则是：第一，根据盐碱发生规律，适当早播或晚播，使幼苗避开盐碱为害；第二，根据品种生育期进行春播或夏播；第三，根据发病规律调整播期，使其避开病害多发季节；第四，根据生长特性调整播期，使开花灌浆期避开28℃以上高温天气.葵的发芽率和出苗率低于油葵，顶土能力也不如油葵。为保证出苗株数，实际播量比计算播量应加大10—20%。食葵的自我调节能力很强，种植密度大则花盘小籽粒小；密度小则花盘大籽粒也大。在既保证产量、又保证商品性的前提下，667平方米保苗株数应保持在3000株左右为宜，行距60—1750px，株距30—875px。播量根据品种特性和栽培要求确定播量。一般人工点播每667平方米需种600克，机播需种700克。带种肥 食葵生育期较长，而且植株高大。种肥用量每667平方米穴施或条施磷酸二铵5—8公斤，或三料磷肥10公斤。播种方式 播种时墒情要好，深度3—100px为宜。        </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>栽培应选择土地平整，肥力中等，灌排方便，土壤粘性相对较小的地块。不宜重茬。以种子方式繁衍后代，播种时以泥炭土为宜。播种前用新高脂膜拌种，能驱避地下害虫，隔离病毒感染，提高种子发芽率</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>主要价值食用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>的种子含油量极高，味香可口，可炒食，亦可榨油，为重要的油料作物。有食用型、油用型和兼用型3类。花托、茎秆、果壳等可作工业原料。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>一身是药，其种子、花盘、茎叶、茎髓、根、花等均可入药。种子油可作软膏的基础药；茎髓可作利尿消炎剂；叶与花瓣可作苦味健胃剂；果盘（花托）有降血压作用。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>向着太阳转的原因在阳光的照射下，生长素在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>背光一面含量升高，刺激背光面细胞拉长向太阳转动。在太阳落山后，生长素重新分布，使</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>转回东方。花盘盛开后，就不再向日转动，而是固定朝向东方。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>的花粉怕高温，如果温度高于30℃，就会被灼伤，因此固定朝向东方，可以避免正午阳光的直射，减少辐射量。但是，花盘在早上受阳光照射，有助于烘干在夜晚时凝聚的露水，减少受霉菌侵袭的可能性；此外早上温度较低，阳光的照射使</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>的花盘成了温暖的小窝，能吸引昆虫帮助传粉。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>花 语太阳(The Sun)、沉默的爱、爱慕</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>自我经验</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd style=\'color:yellow;text-decoration: underline\'>向日葵</a>，没种过，葵花籽倒是经常吃。记得上一次近距离的接触</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000163&idx=1&sn=bcf6543923cdf66a487a118293e84202&chksm=4ffc17a5788b9eb39bff1cc84e7f52ca3ec42384f39b28d0f04a3b938fb37793a27936b48a48#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563808616, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyVJ3l2PCnthX2DL7aWvuz-E', '0');
INSERT INTO `zbp_post` VALUES (35, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】14-《曼陀罗》', '曼陀罗（学名：Datura stramonium Linn.）是茄科曼陀罗属植物，草本或半灌木状，高0.5-1.5米茎粗壮，圆柱状，淡绿色或带紫色，下部木质化。', '<div style=\'text-align: center;overflow: auto;\'><img title=曼陀罗 alt=曼陀罗 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctjp3ng8NwicHLmNbegibibQ1VCswyPJsBboItVMP8J0K9ibvFFFedp3icfdzBAGoTYiaey7iaqicgu7Y7ibIg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>（学名：Datura stramonium Linn.）是茄科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>属植物，草本或半灌木状，高0.5-1.5米茎粗壮，圆柱状，淡绿色或带紫色，下部木质化。叶广卵形，顶端渐尖，基部不对称楔形，边缘有不规则波状浅裂，裂片顶端急尖，花单生于枝叉间或叶腋，直立，有短梗；花萼筒状，长4-5厘米，筒部有5棱角，蒴果直立生，卵状，长3-4.5厘米，直径2-4厘米。花期6-10月，果期7-11月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>是茄科野生直立木质一年生草本植物，草本或半灌木状，高0.5-1.5米，全体近于平滑或在幼嫩部分被短柔毛。茎粗壮，圆柱状，淡绿色或带紫色，下部木质化。叶广卵形，顶端渐尖，基部不对称楔形，边缘有不规则波状浅裂，裂片顶端急尖，有时亦有波状牙齿，侧脉每边3-5条，直达裂片顶端，长8-17厘米，宽4-12厘米；叶柄长3-5厘米。     花单生于枝叉间或叶腋，直立，有短梗；花萼筒状，长4-5厘米，筒部有5棱角，两棱间稍向内陷，基部稍膨大，顶端紧围花冠筒，5浅裂，裂片三角形，花后自近基部断裂，宿存部分随果实而增大并向外反折；花冠漏斗状，下半部带绿色，上部白色或淡紫色，檐部5浅裂，裂片有短尖头，长6-10厘米，檐部直径3-5厘米；雄蕊不伸出花冠，花丝长约3厘米，花药长约4毫米；子房密生柔针毛，花柱长约6厘米。蒴果直立生，卵状，长3-4.5厘米，直径2-4厘米，表面生有坚硬针刺或有时无刺而近平滑，成熟后淡黄色，规则4瓣裂。种子卵圆形，稍扁，长约4毫米，黑色。花期6-10月，果期7-11月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>繁殖方式种子繁殖，在4月中、下旬播种。按株、行距0.66米×0.66米开穴，穴深12厘米。每穴4-5粒，每亩用种150克，播后轻覆土，在气温达15℃时，经2-3周，即可发芽出苗。在4月中、下旬选择温暖、向阳的地块，施入腐熟肥料、耕翻平整土地后做床，撒播、条播均可，条播的行距在12厘米左右，播后撒入细土，以盖严种子为宜，再盖一薄层稻草保湿。用喷壶浇透水，可在半月内出苗，出苗后除去稻草。在幼苗长至4-6片真叶时利用早、晚进行带土移栽。         </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>宜生长在阳光充足处，适应性强，不择土壤，但以富含有机质和石灰质的土壤为好。在茄科作物后不宜种植</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>，以防加重病虫害。以休闲地、豆科和禾本科作物为前作为好。选好地后，施入腐熟的农家肥2500千克，把地耕好，起垄待播。田间管理由于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>适生于较开阔的地块里，田间杂草多或者幼苗过密均会抑制植株生长，分叉高，植株瘦小影响产量。所以在幼苗阶段应特别注意田间管理。在幼苗出土后，要松土、平穴，3-4叶可间苗1次，5-6叶可定苗，间苗、定苗后需锄杂草3-4次。每穴保留1健壮植株。及时培土，以防倒伏。         </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>因花朵大而美丽，具有观赏价值，可种植于花园、庭院中，美化环境。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>花不仅可用于麻醉，而且还可用于治疗疾病。其叶、花、籽均可入药，味辛性温，有大毒。花能去风湿，止喘定痛，可治惊痫和寒哮，煎汤洗治诸风顽痹及寒湿脚气。花瓣的镇痛作用尤佳，可治神经痛等。叶和籽可用于镇咳镇痛。经济价值：种子油可制肥皂和掺合油漆[3]。可用作杀虫、杀菌剂：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>1千克加水8千克，煮成4千克原液，每公斤原液加水4千克。喷洒使用，对蚜虫、玉米螺防治效果达90%。经生产实践，效果佳。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>全草有毒，以果实特别是种子毒性最大，嫩叶次之，干叶的毒性比鲜叶小。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>中毒，一般在食后半小时，最快20分钟出现症状，最迟不超过3小时，症状多在24小时内消失或基本消失，严重者在24小时后进入晕睡、痉挛、紫绀，最后晕迷死亡。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>中毒的解救方法多种，《动物毒物学》记载</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>中毒后，应立刻用0.1%的高锰酸钾溶液或1%-6%的鞣酸洗胃，然后内服氧化镁、木炭末或通用解毒剂（活性炭2份、氧化镁1份、鞣酸1份），也可用盐类泻剂灌服，同时静脉注射葡萄糖溶液，以促进毒物的排出。呼吸抑制者应及时给予兴奋剂（如尼可刹米、洛贝林等）；《中药大辞典》记载：中药解毒可用甘草30克、绿豆60克煎汤频服或用绿豆120克、金银花60克、连翘30克、甘草15克煎水服；亦可用防风、桂枝煎汤服。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>自我经验</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd style=\'color:yellow;text-decoration: underline\'>曼陀罗</a>，最初是在《天龙八部》中的王语嫣她家好像叫曼陀山庄吧，在哪里开始了解到的。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000187&idx=1&sn=9926d3969a28666d4251510e9d476278&chksm=4ffc17bd788b9eab4ea27fd2bf7207701929e7744e0c5eac8c0170cc7b0fb20baa01cce42a37#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563976773, 0, 0, 'GvnDcC2QjGEN4AUqdM7DybbadhThe-i5wX6m2lgtka0', '0');
INSERT INTO `zbp_post` VALUES (34, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】01-《凹叶景天》', '【每日一花】01-《凹叶景天》  多年生草本。茎细弱，高10-15厘米。叶对生，匙状倒卵形至宽卵形，长1-2厘米，宽5-10毫米，先端圆，有微缺，基部渐狭，有短距。花序聚伞状，顶生，宽3-6毫米，有多花，常有3个分枝；花无梗....', '<div style=\'text-align: center;overflow: auto;\'><img title=凹叶景天 alt=凹叶景天 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibct5DMnuFp5cv4jC69jibzhiacTewO98hsTRRoNLusR06qncoIt3IYlh3Rrezrnt7Cp8licqOrZVKJ0Ig/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000015&idx=1&sn=e85046bcfd9e7bf1d8b9998fb087ba4a&chksm=4ffc1709788b9e1fd1bbf61e8313a6bbf8f0837cc4b2eccd1e2c433207d547e42328ace63c0d#rd style=\'color:yellow;text-decoration: underline\'>凹叶景天</a>叶的气孔器单个分散或多个集结成群，表皮细胞长方形或不规则形状，细胞结合紧密，无间隙。气孔呈椭圆形、卵圆形，气孔外拱盖1-2层、光滑，角质膜多皱或波状，蜡纸纹饰为颗粒状。茎由表皮、皮层、 维管柱三部分构成，韧皮部位于木质部外方，组织内有较多的异细胞。其茎中细胞内均含有棕黄色物质。自外向内由木栓层、皮层、维管束组成，无髓。根细胞内含有大量淀粉粒及棕黄色物质。立体形状为近球形或长球形。花粉的各个发育阶段其细胞形态及内含物质都会发生有规律的变化，其韧皮部的原蜜汁以淀粉粒的形式存贮于产蜜组织中，泌蜜期水解为蜜汁由气孔和表皮细胞渗出。为多年生匍甸状肉质草本。耐寒，喜半荫环境，4-5月开花，6-7月结果。室外越冬时部分叶片呈紫红色。生于海拔600-1800米处山坡阴湿处。主产自云南、四川、湖北、湖南、江西、安徽、浙江、江苏、甘肃、陕西、福建等。栽培技术及病虫防治栽培技术：主要采用茎段插和分株的方式。首先，挑选长势良好的母株，剪取10厘米左右的茎段，除去茎下部叶片，在阴凉处晾1-2天。选用河砂或炭泥作为生根基质，容器和选用花盆或穴盘。接下来，插条入土4厘米左右。此时要注意使用遮阴网遮挡阳光，切忌暴晒。同时注意经常喷水，直至根茎有明显的生长迹象。最后，除去遮阴网，使基质偏干。生长3-4周后可移入土壤中生长。扦插苗繁殖要点：选择排水良好的基质，适宜的EC值为0.75-0.80,PH值为5.6-5.9.土壤温度保持在20度至23度，直到插条生根。扦插后注意喷洒杀菌剂防止细菌感染。一旦生根要注意不可水分过多，否则根茎极易腐烂。根长出后喷出75ppm至10fa0ppm的氮肥，长好后逐渐增加至200ppm。若根茎生长过快需使用植物生长调节剂来抑制生长，过快生长会导致植株变形甚至过早死亡。扦插3-4周基质营养已经无法继续供应植株生长，务必及时移入土壤。病虫防治：   清洁园圃：在冬季将园圃内的枯枝落叶和杂草彻底清除销毁，改善卫生状况，可以大大地减少各种病虫源。　　冬耕深翻：冬耕深翻可以使潜伏在土壤中的地下害虫如蛴螬、地老虎、蝼蛄、金针虫等的幼虫、蛹、卵等遭受机械损伤，暴露在地表后又可以被鸟类等天敌啄食，必要时还可以进行人工捕杀。另外，深翻将土壤表层的病原物翻埋入深土层，深土层中的病原物被翻到地面，破坏了病虫的适生环境，有效地控制了病虫的发生。　　药物防治：由于冬季各种病虫多处于休眠状态，对于那些在发生季节不易防治的病虫可以采用冬季药物防治的措施。如用50-100倍液的机油乳剂喷雾可防治介壳虫。主要价值</p>植株低矮，叶片翠绿密集，聚伞花序大而平展，小花繁密，盛开时一片金黄，群体观赏效果极佳，是优良的地被植物和岩石园植物。绿色期长，是园林中较好的耐阴地被植物，但因它的叶子肥厚多汁，不耐践踏，故适宜在封闭式绿地上种植或作观赏草坪。全草药用，可清热解毒，散瘀消肿，治跌打损伤、热疖、疮毒等。缘起&nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp;幼时，某次去河滩，遇见之，见其在堤岸的斜坡上，及其干旱的地方长得非常茂盛，被其坚韧顽强所感，故摘之养之，开启我种花养草的缘份。乐在其中，便一发不可拾...end来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除<div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000015&idx=1&sn=e85046bcfd9e7bf1d8b9998fb087ba4a&chksm=4ffc1709788b9e1fd1bbf61e8313a6bbf8f0837cc4b2eccd1e2c433207d547e42328ace63c0d#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1562435626, 0, 1, 'GvnDcC2QjGEN4AUqdM7DyRaSA3g0H53gV5E-qiv6K0Y', '0');
INSERT INTO `zbp_post` VALUES (33, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】03-《含羞草》', '含羞草（学名：Mimosa pudica Linn. ）：为豆科多年生草本或亚灌木，由于叶子会对热和光产生反应，受到外力触碰会立即闭合，所以得名含羞草。形状似绒球。开花后结荚果，果实呈扁圆形。叶为羽毛状复叶互生，呈掌状排列。', '<div style=\'text-align: center;overflow: auto;\'><img title=含羞草 alt=含羞草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuI8iaouay2tdeKn4wBgkM8ng8U8KAccOPnWHicw0OGz368oZWHBicKULSMkrZVBFzFEiclX1mNBs2E9Q/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>。形状似绒球。开花后结荚果，果实呈扁圆形。叶为羽毛状复叶互生，呈掌状排列。大约在盛夏以后开花，头状花序长圆形，2-3个生于叶腋。花为白色、粉红色，花萼钟状，有8个微小萼齿，花瓣四裂，雄蕊四枚，子房无毛。荚果扁平，每荚节有1颗种子，成熟时节间脱落。花期9月。形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>是披散、亚灌木状草本，高可达1米；茎圆柱状，具分枝，有散生、下弯的钩刺及倒生刺毛。托叶披针形，长5-10毫米，有刚毛。羽片和小叶触之即闭合而下垂；羽片通常2对，指状排列于总叶柄之顶端，长3-8厘米；小叶10-20对，线状长圆形，长8-13毫米，宽1.5-2.5毫米，先端急尖，边缘具刚毛。头状花序圆球形，直径约1厘米，具长总花梗，单生或2-3个生于叶腋；花小，淡红色，多数；苞片线形；花萼极小；花冠钟状，裂片4，外面被短柔毛；雄蕊4枚，伸出于花冠之外；子房有短柄，无毛；胚珠3-4颗，花柱丝状，柱头小。荚果长圆形，长1-2厘米，宽约5毫米，扁平，稍弯曲，荚缘波状，具刺毛，成熟时英节脱落，荚缘宿存；种子卵形，长3.5毫米。花期3-10月；果期5-11月。     繁殖方式  </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>为直根性植物，须根很少，适宜播种繁殖，而且最好采取直播的方法，以免移栽伤根；若必须移栽者，应在幼苗期移栽，否则不易成活，作为一年生栽培的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>，一般于早春在室内播种。可以用小盆直播．也可用浅盆养苗分栽。播种土用2成腐叶土、3成园土、5成细黄砂过筛后混合配制。    播种时，先在盆中盛上适量的培养土，铺平压实，用喷壶洒透水，待水全部渗透后立即进行播种。小盆直播，每盆播种子1-2粒。如用浅盆育苗，则以2厘米 ×2厘米的距离进行点播。播后覆土3-5厘米，以盖住种子为宜。盆上盖玻璃或塑料薄膜保湿，将盆放到20℃左右的散光处。以后见盆土干燥时用浸盆法灌水。约经7-10天，种子发芽出苗。                        出苗后，应将盆上覆盖物拿掉，并使逐渐接受阳光。待幼苗长到3厘米高时，播在浅盆中的即可分苗移栽。分苗应带土移栽，否则不易成活。刚上盆的幼苗浇透水后，先放到半阴处，待缓苗后再移到阳光充足的地方。而直播在小盆中的可直接放在阳光充足处生长。南方4月中，北方5月初，可将苗盆移出室外培养，并及时浇水经常保持盆土湿润。幼苗长到4片叶时开始追施液肥，一般7-10天追肥一次腐熟淡液肥即可。苗长大后可再换一次盆，但用盆不宜过大，一般定植到15-20厘米的中号花盆中即可。   栽培技术及病虫防治</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>习性强健、生长迅速。生长季节可放在阳台上或院子里，要求土壤深厚、肥沃、湿润。冬季应移到室内窗台上，室内温度在10℃左右即可安全过冬。在阳光充足的条件下，根系生长很快，需要每天浇水。夏季炎热干旱时应该早、晚各浇一次水，缺水则叶片会下垂以至发黄，受触动也不再闭合。苗期每半月施追肥1次。如不想让株形过大，则要减少施肥量。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>小叶细小，羽状排列，甩手触及小叶受刺激后，即行合拢，如震动大可使刺激传至全叶，总叶柄也会下垂，甚至可能传递到邻叶使其叶柄下垂。主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>株形散落，羽叶纤细秀丽，其叶片一碰即闭合；</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>花多而清秀，楚楚动人，给人以文弱清秀的印象。白天她是展开的，夜晚她是闭合的，神奇的在于其生物钟。常见的植物上静态的，大家都习惯了静态的植物，而害羞草能如动物一般给人以及时的反馈，自然给人以感官上的冲击、心灵上的及时反馈，妙哉。药用价值：全草（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>）：甘、涩，凉。宁心安神，清热解毒。用于吐泻，失眠，小儿疳积，目赤肿痛，深部脓肿，带状泡疹。根（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd style=\'color:yellow;text-decoration: underline\'>含羞草</a>根）：涩、微苦。有毒。止咳化痰，利湿通络，和胃，消积。用于咳嗽痰喘，风湿关节痛，小儿消化不良。花 语害羞，豆蔻少女一般，渴望外面的世界，又怕被伤害，遇到风吹草动就会很快的保护自己。自我经验</p>这种植物，特别害羞娇嫩，一碰就闭合，晚上也闭合睡觉，给人即时的反馈，我深爱之，每年必养，总因照顾不周，被太阳给晒焉了，少有从种子被我繁育到开花结果的！end来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除<div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000044&idx=1&sn=0c6dac72e0dd9d03f64d9a23be60bf3c&chksm=4ffc172a788b9e3c1ca8b422191cd24441eefd560a4b9e5cdf58f2233197cdf9e6e63264d5bd#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1562980336, 0, 1, 'GvnDcC2QjGEN4AUqdM7DyU9uPxP0ixdP5BcGALVfY3g', '0');
INSERT INTO `zbp_post` VALUES (28, 0, 1, '', 0, 1, '', 0, 0, '关于本站', '', '<p style=\"text-indent: 0em;\"><strong><span style=\"font-size: 16px;\">Hello <span style=\"font-size: 16px; color: #FFFF00;\">everyone</span>,&nbsp; &nbsp; &nbsp; &nbsp;</span></strong></p><p style=\"text-indent: 0em;\"><strong><span style=\"font-size: 16px;\">欢迎来到<span style=\"font-size: 16px; color: #FFFF00;\">每日一花(dailyflower)</span>,<br/></span></strong></p><p style=\"text-indent: 0em;\"><strong><span style=\"font-size: 16px;\">欢迎来到这个充满鲜花、绿植的世界，</span></strong></p><p style=\"text-indent: 0em;\"><strong><span style=\"font-size: 16px;\">在这里你可以收获什么？</span></strong></p><p style=\"text-indent: 0em;\"><strong><span style=\"font-size: 16px;\">你可以收获无数的植物的知识、无数的植物的美图、奇异的植物、一些植物种植习惯。</span></strong></p><p style=\"text-indent: 0em;\"><strong><span style=\"font-size: 16px;\">本站与微信公众号每日一花 Daily Flower（dailyflower_）同步更新，在公众号的阅读体验比较好，为了更友好的体验，请大家在阅读本站的有关植物类型的文章的时候，请点击<span style=\"font-size: 16px; color: #00B050;\">【阅读原文】</span></span></strong></p><p style=\"text-indent: 0em;\"><strong><span style=\"font-size: 16px;\">以下是本站的微信公众号二维码，欢迎您的到来</span></strong></p><p style=\"text-align: center;\"><img src=\"{#ZC_BLOG_HOST#}zb_users/upload/2019/10/201910141570991475666095.jpg\" title=\"qrcode_for_gh_c40c0306845e_344.jpg\" alt=\"qrcode_for_gh_c40c0306845e_344.jpg\"/></p><p style=\"text-align: center;\"><span style=\"color: #FF0000;\"><strong><span style=\"font-size: 16px;\">【打开微信 扫一扫 无限植物等你】</span></strong></span></p><p><br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/></p>', 1570963050, 0, 32, '', '');
INSERT INTO `zbp_post` VALUES (29, 4, 1, '{3}{4}{5}', 0, 0, '种树郭橐驼传', 0, 0, '从古人哪里得到栽种植物的秘诀（种树郭橐驼传 唐代：柳宗元）', '橐驼非能使木寿且孳也，能顺木之天，以致其性焉尔。凡植木之性，其本欲舒，其培欲平，其土欲故，其筑欲密。既然已，勿动勿虑，去不复顾。其莳也若子，其置也若弃，则其天者全而其性得矣。故吾不害其长而已，非有能硕茂之也；不抑耗其实而已，非有能早而蕃之也。他植者则不然，根拳而土易，其培之也，若不过焉则不及。苟有能反是者，则又爱之太恩，忧之太勤，旦视而暮抚，已去而复顾，甚者爪其肤以验其生枯，摇其本以观其疏密，而木之性日以离矣。虽曰爱之，其实害之；虽曰忧之，其实仇之，故不我若也。吾又何能为哉！译文：我郭橐驼不是能<!--autointro-->', '<p style=\"text-indent: 2em; text-align: center;\"><img src=\"{#ZC_BLOG_HOST#}zb_users/upload/2019/10/201910141570988117449123.png\" title=\"title.png\" alt=\"title.png\"/></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: #FFFF00;\">橐驼非能使木寿且孳也，能顺木之天，以致其性焉尔<strong>。凡植木之性，其本欲舒，其培欲平，其土欲故，其筑欲密。</strong>既然已，勿动勿虑，去不复顾。其莳也若子，其置也若弃，则其天者全而其性得矣。故吾不害其长而已，非有能硕茂之也；不抑耗其实而已，非有能早而蕃之也。他植者则不然，根拳而土易，其培之也，若不过焉则不及。苟有能反是者，则又爱之太恩，忧之太勤，旦视而暮抚，已去而复顾，甚者爪其肤以验其生枯，摇其本以观其疏密，而木之性日以离矣。虽曰爱之，其实害之；虽曰忧之，其实仇之，故不我若也。吾又何能为哉！</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: #FFFF00;\">译文：我郭橐驼不是能够使树木活得长久而且长得很快，只不过能够顺应树木的天性，来实现其自身的习性罢了。但凡种树的方法，它的树根要舒展，它的培土要平均，它根下的土要用原来培育树苗的土，它捣土要结实。已经这样做了，就不要再动，不要再忧虑它，离开它不再回顾。栽种时要像对待子女一样细心，栽好后要像丢弃它一样放在一边，那么树木的天性就得以保全，它的习性就得以实现。所以我只不过不妨碍它的生长罢了，并不是有能使它长得高大茂盛的办法；只不过不抑制、减少它的结果罢了，也并不是有能使它果实结得早又多的办法。别的种树人却不是这样，树根拳曲又换了生土；他培土的时候，不是过紧就是太松。如果有能够和这种做法相反的人，就又太过于吝惜它们了，担心它太过分了，在早晨去看了，在晚上又去摸摸，已经离开了，又回头去看看。更严重的，甚至掐破树皮来观察它是死是活着，摇晃树根来看它是否栽结实了，这样树木的天性就一天天远去了。虽然说是喜爱它，这实际上是害了它，虽说是担心它，这实际上是仇视它。所以他们都不如我。我又能做什么呢？</span></p>', 1570963123, 0, 12, '', '');
INSERT INTO `zbp_post` VALUES (30, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】04-《黄蜀葵》', '黄蜀葵，别名：秋葵、棉花葵、假阳桃、野芙蓉、黄芙蓉、黄花莲、鸡爪莲、疽疮药、追风药、豹子眼睛花、荞面花，拉丁文名：Abelmoschus manihot (Linn.) Medicus ，锦葵科、秋葵属一年生或多年生粗壮直立草本植物。', '<div style=\'text-align: center;overflow: auto;\'><img title=黄蜀葵 alt=黄蜀葵 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcumeBwWR4B3bmOcvWGJzgf57QM6MQNq8kOM7lXUqqnHKBBRId0PTlzHmNBHxGXPaBxs2eLiaE1VQHw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000063&idx=1&sn=b9e4f06942438e17c6112e5b6c650458&chksm=4ffc1739788b9e2fe08acc5549de6b8e203bf576056af50b0f7fa1ebd089085a82a5c56e9c8e#rd style=\'color:yellow;text-decoration: underline\'>黄蜀葵</a>栽培粗放，无重大病虫害发生，基本上不需要打药。6月下旬、7月上旬，刚孕蕾时为了控制植株陡长，可喷2次矮壮素，间隔7~10天使用。特别是梅雨季节应做好清沟挖墒工作，力争做到雨止田干，植株不可太密，应保持良好的通风透光。为了防止田间残留病菌，可使用多菌灵或托布津等杀菌剂进行防治。 主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000063&idx=1&sn=b9e4f06942438e17c6112e5b6c650458&chksm=4ffc1739788b9e2fe08acc5549de6b8e203bf576056af50b0f7fa1ebd089085a82a5c56e9c8e#rd style=\'color:yellow;text-decoration: underline\'>黄蜀葵</a>的花大色美，栽培供园林观赏用。          药用价值：【僳僳药】质腊西：种子治大便秘结，尿路结石；根、叶用于疔疮，腮腺炎，骨折《怒江药》。nifsair yot：功用同前《滇药录》。经济价值：根含粘质，可作造纸糊料；</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000063&idx=1&sn=b9e4f06942438e17c6112e5b6c650458&chksm=4ffc1739788b9e2fe08acc5549de6b8e203bf576056af50b0f7fa1ebd089085a82a5c56e9c8e#rd style=\'color:yellow;text-decoration: underline\'>黄蜀葵</a>作为曾经的国家濒危植物品种，后来被科研人员在深山中发现并培育，研究出了规模化种植的方法以及帮助农民增收致富的途径。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000063&idx=1&sn=b9e4f06942438e17c6112e5b6c650458&chksm=4ffc1739788b9e2fe08acc5549de6b8e203bf576056af50b0f7fa1ebd089085a82a5c56e9c8e#rd style=\'color:yellow;text-decoration: underline\'>黄蜀葵</a>不仅可以入药，还可以从茎秆中提炼植物胶作为食品添加剂，在食品工业中可用做增稠剂、稳定剂和乳化剂，它可用于冰淇淋、雪糕、冰棍和面包、饼干、糕点、果酱等食品的制作中。自我经验</p>，在我本地称作漏笼花，爷爷便秘时候，就放在粥里一起吃。黄色的花，很靓很吸引人，花很大，像个多页的风车，枝干上有点小毛刺，总体感觉很不错的一种植物，集美与价值于一身。end来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除<div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000063&idx=1&sn=b9e4f06942438e17c6112e5b6c650458&chksm=4ffc1739788b9e2fe08acc5549de6b8e203bf576056af50b0f7fa1ebd089085a82a5c56e9c8e#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1563081599, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQrwyocxfVhUtclDPgPWW9M', '0');
INSERT INTO `zbp_post` VALUES (31, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '如何来照顾一棵植物？', '凡植木之性，其本欲舒，其培欲平，其土欲故，其筑欲密。既然已，勿动勿虑，去不复顾。其莳也若子，其置也若弃，则其天者全而其性得矣。故吾不害其长而已，非有能硕茂之也；不抑耗其实而已，非有能早而蕃之也。', '', 1562777486, 0, 1, 'GvnDcC2QjGEN4AUqdM7DydHnQc6V2QMfwBY5GD1xBGI', '0');
INSERT INTO `zbp_post` VALUES (32, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】02-《石莲》', '石莲（学名：Sinocrassula indica A.Berger），别名因地卡，为二年生草本植物，全株无毛，具须根。花茎高15-60厘米，直立，常被微乳头状突起。茎生叶互生，宽倒披针状线形至近倒卵形。', '<div style=\'text-align: center;overflow: auto;\'><img title=石莲 alt=石莲 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctamFIZzURicZmbcof52dKTbdsY3PsXfrMjJw2M9eetCAqz83UAlib8GbOMDHfCiabX4YrI8KiaFRQzBg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd style=\'color:yellow;text-decoration: underline\'>石莲</a>（学名：Sinocrassula indica A.Berger），别名因地卡，为二年生草本植物，全株无毛，具须根。花茎高15-60厘米，直立，常被微乳头状突起。茎生叶互生，宽倒披针状线形至近倒卵形。分布于中国陕，甘，湘，鄂，川，黔，桂，滇，藏等地区。形态特征基生叶莲座状，匙状长圆形，长3.5-6厘米，宽1-1.5厘米；茎生叶互生，宽倒披针状线形至近倒卵形，上部的渐缩小，长2.5-3厘米，宽4-10毫米，渐尖。花序圆锥状或近伞房状，总梗长5-6厘米；苞片似叶而小；萼片5，宽三角形，长2毫米，宽1毫米，先端稍急尖，花瓣5，红色，披针形至卵形，长4-5毫米，宽2毫米，先端常反折；雄蕊5，长3-4毫米；鳞片5，正方形，长0.5毫米，先端有微缺；心皮5，基部0.5-1毫米合生，卵形，长2.5-3毫米，先端急狭，花柱长不及1毫米。蓇葖的喙反曲；种子平滑。花期7-10月。栽培技术及病虫防治繁殖方法：常用扦插，于春、夏进行。茎插、叶插均可。叶插时将完整的成熟叶片平铺在潮润的沙土上，叶面朝上，叶背朝下，不必覆土，放置阴凉处，10天左右从叶片基部可长出小叶丛及新根。室内扦插，四季均可进行，以8-10月为更好，生根快，成活率高。插穗可用单叶、蘖枝或顶枝，剪取的插穗长短不限，但剪口要干燥后，再插入沙床。插后一般20天左右生根。插壤不能太湿，否则剪口易发黄腐烂，根长2-3厘米时上盆。或用分株法，即把根茎处萌发的小苗掰下，直接栽于盆中。  养护管理：在北方宜温室盆栽。每年三、四月间换盆一次，加些磷肥。平时不施肥也可以，它虽然是耐阴植物，但时间过长叶片会稀瘦而失去原来风采。应隔几天拿到室外照晒一段时间，以保其丰满的神态。浇水除了夏季多一些外，一般不宜过多，尤其是冬季应控制浇水，使盆土经常保持干燥，以免腐烂而死亡。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd style=\'color:yellow;text-decoration: underline\'>石莲</a>花管理简单，每年早春换盆，清理萎缩的枯叶和过多的子株。盆栽土以排水好的泥炭土或腐叶土加粗沙。生长期以干燥环境为好，不需多浇水。盆土过湿，茎叶易徒长，反耐观赏期缩短。特别冬季在低温条件下，水分过多根部易腐烂，变成无根植株。盛夏高温时，也不宜多浇水，可少些喷水，切忌阵雨冲淋。生长期每月施肥1次，以保持叶片青翠碧绿。但施肥过多，也会引支茎叶徒长，2-3年生以上的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd style=\'color:yellow;text-decoration: underline\'>石莲</a>花，植株趋向老化，应培育新苗及时更新。春、秋是</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd style=\'color:yellow;text-decoration: underline\'>石莲</a>花属植物的主要生长期，需要充足的光照，否则会造成植株徒长，株型松散，叶片变薄，叶色黯淡，叶面白粉减少。浇水掌握“不干不浇，浇则浇透”的原则，避免盆土积水，空气干燥时可向植株周围洒水，但叶面，特别是叶丛中心不宜积水，否则会造成烂心。尤其要注意避免长期雨淋。生长季节每20天左右施一次腐熟的稀薄液肥或低氮高磷钾的复合肥，施肥时不要将肥水溅到叶片上。施肥一般在天气晴朗的早上或傍晚进行，当天的傍晚或第二天早上浇一次透水，以冲淡土壤中残留的肥液。冬季放在室内阳光充足的地方，倘若夜间最低温度在10℃左右，并有一定的昼夜温差，可适当浇水，酌情施肥，使植株继续生长。如果保持不了这么高的温度，应控制浇水，维持盆土干燥，停止施肥，使植株休眠，也能耐5℃的低温，某些品种甚至能耐0℃的低温。 病虫防治：常有锈病、叶斑病和根结线虫危害，可用75%百菌清可湿性粉剂800倍液喷洒防治，根结线虫用3%呋喃丹颗粒剂防治。虫害有黑象甲危害，用25%西维因可湿性粉剂500倍液喷杀。常见的虫害有蚧壳虫，多发生在叶片上。高温、通风不良时容易发生。介壳虫吸食汁液影响生长，严重时叶片脱落.并诱发煤烟病。防治方法：①受害植株不多时，可用竹片刮掉或毛刷刷掉成虫，或用镊子将虫体镊出杀死，但勿损伤叶面。也可结合修剪将虫多的部分剪掉烧毁。②在夏季高温高湿季节，加强通风，控制浇水，降低空气湿度，保持适当干燥。③在初孵若虫期用清水或中性洗衣粉70-100倍液进行冲洗灭虫。④初孵若虫期喷洒40%氧化乐果乳油剂1000倍液，或蚧螨灵80~100倍液，每隔7天喷1次，连续喷洒2-3次。⑤在为害期，可在盆土中埋施15%铁灭克颗粒刺，每盆（15厘米直径）埋施1-2克，也可埋施3%呋嘀丹，每盆（15厘米直径）埋施3克，也可用40%氧化乐果乳油剂2000倍液浇灌根际部位，每盆（15厘米直径）浇灌50-100毫升。主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd style=\'color:yellow;text-decoration: underline\'>石莲</a>是常见的多浆植物，叶片莲座状排列，肥厚如翠玉，姿态秀丽，形状池中莲花，观赏价值较高。盆栽是室内绿色装饰的佳品，也可地栽用于花境点缀。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd style=\'color:yellow;text-decoration: underline\'>石莲</a>花株形奇特，宛如玉石雕刻成的莲花座，华丽雅致，四季碧翠，深受大家的喜爱。在热带、亚热带地区可露地配植或点缀在花坛边缘、岩石孔隙间，北方则盆栽观赏，是布置客厅、书房等几桌的上好植物材料。                     药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd style=\'color:yellow;text-decoration: underline\'>石莲</a>全草入药，味甘、淡，性凉。有清热解毒、提伤止痛、止血止痢之效，可治咽喉肿痛、痢疾、崩漏、便血，外用治疮疡久不收口及烧、烫伤。为滋阴益气药、散瘀消肿药。用法用量：煎汤，30-50g，外用时取适量，捣敷。治跌打损伤及外伤肿痛（湖北西部），或作清热、消炎用（广西、云南）。《中华本草》自我经验</p>是非常好养活的多肉植物，分芽繁殖也是特别的好养，是我养的时间最久的植物之一，完全是懒养，所以长得形状就特别奇怪，弯弯曲曲的，向着光生长！end来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除<div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000022&idx=1&sn=fff9fdd2e43dd6bbbeb364a125083a86&chksm=4ffc1710788b9e06feb67757e71d8631024f6963f79429b1819d61b5f9cd62b6f7bb09e69fe5#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1562775383, 0, 0, 'GvnDcC2QjGEN4AUqdM7DychI9HG5KsTqjijfPzoY2SI', '0');
INSERT INTO `zbp_post` VALUES (46, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】19-《文殊兰》', '文殊兰（一般指亚洲文殊兰），拉丁学名：Crinum asiaticum ，别称文珠兰、十八学士、翠堤花等，为石蒜科文殊兰属植物。文殊兰的名称可能使人误会此品种为一种兰科植物，但实际上此品种为石蒜科多年生粗壮草本植物。', '<div style=\'text-align: center;overflow: auto;\'><img title=文殊兰 alt=文殊兰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvXNm3duguVpW5sibibXulOzhCNfYC7cm9U7EE3byOOzaE1GVcmxH5IeWu6JHcj5Sy28qL14icA0ZG4Q/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>），拉丁学名：Crinum asiaticum ，别称文珠兰、十八学士、翠堤花等，为石蒜科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>的名称可能使人误会此品种为一种兰科植物，但实际上此品种为石蒜科多年生粗壮草本植物。花期6-8月，傍晚时发出芳香。花被裂片白色，线形，雄蕊淡红色，花药线形，顶端渐尖，子房纺锤形。果近球形，通常有1颗种子。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>形态特征多年生粗壮草本。鳞茎长柱形。叶20-30枚，多列，带状披针形，长可达1米，宽7-12厘米或更宽，顶端渐尖，具1急尖的尖头，边缘波状，暗绿色。     花茎直立，几与叶等长，伞形花序有花10-24朵，佛焰苞状总苞片披针形，长6-10厘米，膜质，小苞片狭线形，长3-7厘米；花梗长0.5-2.5厘米；花高脚碟状，芳香；花被管纤细，伸直，长10厘米，直径1.5-2毫米，绿白色，花被裂片线形，长4.5-9厘米，宽6-9毫米，向顶端渐狭，白色；雄蕊淡红色，花丝长4-5厘米，花药线形，顶端渐尖，长1.5厘米或更长；子房纺锤形，长不及2厘米。蒴果近球形，直径3-5厘米；通常种子1枚。花期夏季。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>繁殖方式分株繁殖:分株可在春、秋季进行，以春季结合换盆时进行。将母株从盆内倒出，将其周围的鳞茎剥下，分别栽种即可。  播种繁殖:以3～4月为宜。在北方需人工辅助授粉，否则不易结实。种子含水量大，宜采后即播。可用浅盆点播，覆土约2厘米厚，浇透水，在16～22℃温度下，保持适度湿润，不可过湿，约2周后可发芽。待幼苗长出2～3片真叶时，即可移栽于小盆中。栽培3～4年可以开花。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>性喜温暖、湿润、光照充足、肥沃沙质壤环境，不耐寒，耐盐碱土，但在幼苗期忌强直射光照，生长适宜温度15~20℃，冬季鳞茎休眠期，适宜贮藏温度为8℃左右.盆栽以腐殖质含量高、疏松肥沃、通透性能强的沙质培养土为宜。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>花叶并美，具有较高的观赏价值，既可作园林景区、校园、机关的绿地、住宅小区的草坪的点缀品，又可作庭院装饰花卉，还可作房舍周边的绿篱；如用盆栽，则可置于庄重的会议厅、富丽的宾馆、宴会厅门口等，雅丽大方，满堂生香，令人感到赏心悦目。药用价值：功能主治 叶与鳞茎药用，有活血散瘀、消肿止痛之效，治跌打损伤、风热头痛、热毒疮肿等症.用法用量 5～15克；外用适量，鲜品捣烂敷患处。误食</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>警 告全株有毒，以鳞茎最毒，内服宜慎。故作为药用时候必须严格遵照医嘱，且严防小孩和动物的误食.中毒症状：腹部疼痛，先便秘，后剧烈下泻，脉搏增速，呼吸不整，体温上升。解救：早期可洗胃，服浓茶或鞣酸，应特别注意发生休克。亦可用白米醋4两，生姜汁2两，轻者含淑，重者内服。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>自我经验</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd style=\'color:yellow;text-decoration: underline\'>文殊兰</a>，第一次接触是是幼时，一长辈说被鱼刺卡住了，吃这个</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000270&idx=1&sn=10f6f192292c28713b9aa66cd37aa7c3&chksm=4ffc1408788b9d1e2ad13f9ea810cddad7b8c9a4c9c401ca3cdea74955c6d81ee297cec60c3c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564408586, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyW8XgTI82R4RDVCruEZpFRU', '0');
INSERT INTO `zbp_post` VALUES (47, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】25-《石竹》', '石竹，拉丁文名：Dianthus chinensis L. 为双子叶植物纲、石竹科、石竹属多年生草本，高30-50厘米，全株无毛，带粉绿色。茎由根颈生出，疏丛生，直立，上部分枝。叶片线状披针形，顶端渐尖，基部稍狭，全缘或有细小齿，中脉较显。', '<div style=\'text-align: center;overflow: auto;\'><img title=石竹 alt=石竹 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuqxRhsxR37OYeTfTLC1K3PdFVbOPGzfP89GSyvWWtkAGfiafvQaTNZAIX5LqAlXA55cfKa3DqecAQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>，拉丁文名：Dianthus chinensis L. 是双子叶、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>属多年生草本植物。它高30-50厘米，无毛，粉绿色。茎生于根颈，疏生丛生，直立，上枝。叶片线状披针形，先端渐尖，基部稍狭窄，全缘或具小齿，中脉更突出。花单生分枝端部或数朵花整合聚伞花序；紫红色，粉红色，鲜红色或白色，顶端边缘不规则牙齿浅裂，喉条纹，疏生有毛；雄蕊暴露在喉部，花药蓝色；子房长圆形，花柱线形。蒴果圆柱状，包围在宿存花萼内，具黑色，扁球形种子。花期为5~6月，果期为7~9月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>形态特征多年生草本植物，30-50厘米高，无毛，粉绿色。茎生于根颈，疏生丛生，直立，上枝。叶片线状披针形，长3-5厘米，宽2-4毫米，先端渐尖，基部稍狭窄，全缘或具小齿，中脉更突出。     花单分枝末端或花整合聚合花序；花梗长13cm；苞片4，卵形，先端长渐细，可达花萼1/2长，边缘膜质，边缘毛；花萼圆柱形，长15至25mm，直径45mm，纵向条纹，花萼披针形，长约5mm，直，先端，边缘毛；花瓣长15~18mm，卵形三角形，长13~15mm，紫色，粉红色，鲜红色或白色；顶部边缘不规则，喉部杂色稀疏；雄蕊暴露在喉部外面，另一个蓝色；子房长圆形，圆柱形。蒴果圆柱状，包围在宿存花萼中，先端4浅裂；种子黑色，扁球形。花期为5~6月，果期为7~9月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>繁殖方式播种繁殖:其性耐寒、耐干旱，不耐酷暑，夏季多生长不良或枯萎，栽培时应注意遮荫降温。喜阳光充足、干燥，通风及凉爽湿润气候。要求肥沃、疏松、排水良好及含石灰质的壤土或沙质壤土，忌水涝，好肥。生于草原和山坡草地。   扦插繁殖:在10月至翌年2月下旬到3月进行，枝叶茂盛期剪取嫩枝5-6厘米长作插条；插后15-20天主根。       分株繁殖:多在花后利用老株分株，可在秋季或早春进行。例如可于4月分株，夏季注意排水，9月份以后加强肥水管理，于10月初再次开花。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>生长习性其性耐寒、耐干旱，不耐酷暑，夏季多生长不良或枯萎，栽培时应注意遮荫降温。喜阳光充足、干燥，通风及凉爽湿润气候。要求肥沃、疏松、排水良好及含石灰质的壤土或沙质壤土，忌水涝，好肥。生于草原和山坡草地。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>已广泛栽培。育出许多品种，是很好的观赏花卉。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd style=\'color:yellow;text-decoration: underline\'>石竹</a>株型低矮，茎秆似竹，叶丛青翠，自然花期5~9月，从暮春季节可开至仲秋，温室盆栽可以花开四季。花顶生枝端，单生或成对，也有呈圆锥状聚伞花序；花径不大，仅2至3厘米，但花朵繁茂，此起彼伏，观赏期较长。花色有白、粉、红、粉红、大红、紫、淡紫、黄、蓝等，五彩缤纷，变化万端。药用价值：根和全草入药，清热利尿，破血通经，散瘀消肿。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000280&idx=1&sn=fa9afaf42ef231bbadc8f373302652d1&chksm=4ffc141e788b9d08b8eabbce4fce24b81a702bda1df585588835868dec2e2ca93ef84abb0c82#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565012351, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyVkFUlNNSVxSz99sQVLszAo', '0');
INSERT INTO `zbp_post` VALUES (48, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】20-《旋花》', '旋花Calystegia sepium (Linn.) R. Br.，植物名、旋花科、多年生蔓草，茎细长，缠络他物之上，叶互生、戟形、有长柄，夏天开漏斗状合瓣花，色有淡红和白色等，又名鼓子草，俗称野牵牛。', '<div style=\'text-align: center;overflow: auto;\'><img title=旋花 alt=旋花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuKgbXbZuf9JPZjic7zPZKSkqmXHDhQqO9n8vG8NOShDe7AHhOJybprpmLfNNMfShuwYXNYYMqqNgQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000292&idx=1&sn=625becf1ef75db9eb1dc8d0e1aa74d84&chksm=4ffc1422788b9d349d59cd1e8b481c7203b819d4d0166e73980540b1ae57b32b2979da6a0d6d#rd style=\'color:yellow;text-decoration: underline\'>旋花</a>Calystegia sepium (Linn.) R. Br.，植物名、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000292&idx=1&sn=625becf1ef75db9eb1dc8d0e1aa74d84&chksm=4ffc1422788b9d349d59cd1e8b481c7203b819d4d0166e73980540b1ae57b32b2979da6a0d6d#rd style=\'color:yellow;text-decoration: underline\'>旋花</a>科、多年生蔓草，茎细长，缠络他物之上，叶互生、戟形、有长柄，夏天开漏斗状合瓣花，色有淡红和白色等，又名鼓子草，俗称野牵牛。单叶互生，又名打碗花，在湖北一带被称为喇叭花，为多年生草本。茎缠绕，稍被毛，具棱。生于山坡、平原、荒地等，属于常见科目。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000292&idx=1&sn=625becf1ef75db9eb1dc8d0e1aa74d84&chksm=4ffc1422788b9d349d59cd1e8b481c7203b819d4d0166e73980540b1ae57b32b2979da6a0d6d#rd style=\'color:yellow;text-decoration: underline\'>旋花</a>形态特征多年生草本，全株无毛。茎缠绕，有棱，多分枝。叶柄较叶片略短，长3～5厘米，叶片3裂，呈椭圆状箭形或戟形，长5～10cm，宽1～4cm，中裂片卵状披针形或狭卵状三角形，侧裂片开展，略呈三角形，基部深心形或戟形，先端钝或稍锐尖，全缘；通常茎基部叶较宽，上部叶较狭细；叶柄长1.5～4cm。花生叶腋，单一，花梗长约5cm；苞卵形，长1.5～2.5cm；萼片5；花冠大，长约5cm，漏斗形，淡红色；雄蕊5，花丝基部膨大；雌蕊比雄蕊长，子房2室，每室2个胚珠，柱头2裂。蒴果球形，无毛。种子卵状圆形，无毛。花梗长，有棱．小苞片宽卵状心形，长2～2.5厘米，有浅裂片或全缘呈先端钝尖．萼片卵圆状披针形，先端尖．花冠漏斗形，粉红色或白色，长4～6厘米，有不明显的5浅裂：雄蕊花丝基部有小鳞毛，子房2室，柱头2裂。蒴果球形，无毛．种子卵状三棱形，无毛。花期5-7月，果期7～8月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000292&idx=1&sn=625becf1ef75db9eb1dc8d0e1aa74d84&chksm=4ffc1422788b9d349d59cd1e8b481c7203b819d4d0166e73980540b1ae57b32b2979da6a0d6d#rd style=\'color:yellow;text-decoration: underline\'>旋花</a>生长环境生于海拔140～2600m的路旁、溪边草丛，农田边及山坡林缘。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000292&idx=1&sn=625becf1ef75db9eb1dc8d0e1aa74d84&chksm=4ffc1422788b9d349d59cd1e8b481c7203b819d4d0166e73980540b1ae57b32b2979da6a0d6d#rd style=\'color:yellow;text-decoration: underline\'>旋花</a>主要价值药用价值：益气；养颜；涩精。主面皯；遗精；遗尿</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000292&idx=1&sn=625becf1ef75db9eb1dc8d0e1aa74d84&chksm=4ffc1422788b9d349d59cd1e8b481c7203b819d4d0166e73980540b1ae57b32b2979da6a0d6d#rd style=\'color:yellow;text-decoration: underline\'>旋花</a>自我经验平时感觉看起来像喇叭花一样，不是分得很清楚。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000292&idx=1&sn=625becf1ef75db9eb1dc8d0e1aa74d84&chksm=4ffc1422788b9d349d59cd1e8b481c7203b819d4d0166e73980540b1ae57b32b2979da6a0d6d#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564500312, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyfsHUe00QhxEtRzqSzsOSXg', '0');
INSERT INTO `zbp_post` VALUES (49, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】21-《叶子花》', '叶子花（学名：Bougainvillea spectabilis Willd.），紫茉莉科，叶子花属木质藤本状灌木，茎有弯刺，并密生绒毛。单叶互生，卵形全缘，被厚绒毛，顶端圆钝。', '<div style=\'text-align: center;overflow: auto;\'><img title=叶子花 alt=叶子花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctC2vnVPleZRhotR8fENHt60rEJgGqa0ySRfVkGmTEaDxYY9cbeDMtib6MDmQdiakF9KYiapYmDibOyNg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>（学名：Bougainvillea spectabilis Willd.），紫茉莉科，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>属木质藤本状灌木，茎有弯刺，并密生绒毛。单叶互生，卵形全缘，被厚绒毛，顶端圆钝。花很细小，黄绿色，三朵聚生于三片红苞中，外围的红苞片大而美丽，有鲜红色、橙黄色、紫红色、乳白色等，被误认为是花瓣，因其形状似叶，故称其为</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>。花期可从11月起至第二年6月。冬春之际，姹紫嫣红的苞片展现，给人以奔放、热烈的感受，因此又得名贺春红。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>形态特征藤状灌木。枝、叶密生柔毛；刺腋生、下弯。叶片椭圆形或卵形，基部圆形，有柄。花序腋生或顶生；苞片椭圆状卵形，基部圆形至心形，长2.5-6.5厘米，宽1.5-4厘米，暗红色或淡紫红色；花被管狭筒形，长1.6-2.4厘米，绿色，密被柔毛，顶端5-6裂，裂片开展，黄色，长3.5-5毫米；雄蕊通常8；子房具柄。果实长1-1.5厘米，密生毛。花期冬春间。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>繁殖方式压条繁殖:在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>母株上选择筷子头以上粗细的健壮枝条。选枝条时要注意两点：一是成活后便于从母株上剪断或锯断；二是枝条上部要有2条至3条细枝，着生一定数量的叶片，以便进行光合作用，制造有机养料往下输送，利于生根。  </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>生长习性性喜温暖、湿润的气候和阳光充足的环境。不耐寒，耐瘠薄，耐干早，耐盐碱，耐修剪，生长势强，喜水但忌积水。要求充足的光照，长江流域及以北地区均盆栽养护。对土壤要求不严，但在肥沃、疏松、排水好的沙质壤土能旺盛生长。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>光照不足会影响其开花；适宜生长湿度为20～30℃。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>主要价值环保价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>具有一定的抗二氧化硫功能，是一种很好的一环保绿化植物。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>的花可入药，具有解毒清热、调和气血的功效。对治疗妇女月经不调，疽毒有一定的效果。观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>的观赏部位是苞片，其苞片似叶，花于苞片中间，故称之“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>树势强健，花形奇特，色彩艳丽，缤纷多彩，花开时节格外鲜艳夺目。特别是冬季室内当嫣红姹紫的苞片开放时，大放异彩，热烈奔放。深受人们喜爱。赞比亚将其定为国花。中国南方常用于庭院绿化，做花篱、棚架植物，花坛、花带的配置，均有其独特的风姿。切花造型有其独特的魅力</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd style=\'color:yellow;text-decoration: underline\'>叶子花</a>自我经验更喜欢叫她三角梅，听起来格调要高点，哈哈。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000302&idx=1&sn=54f0ac58722fb6538f37bb04f65d7732&chksm=4ffc1428788b9d3e8b42bff6bc5a8419f3efa0e7c5815fba9936d62f1c45ec58fef8b3a87f08#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564586393, 0, 0, 'GvnDcC2QjGEN4AUqdM7DybwlGgH5-Te9FTcCIVx6vHc', '0');
INSERT INTO `zbp_post` VALUES (50, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】24-《郁金香》', '郁金香（学名：Tulipa gesneriana L.）是百合科郁金香属的多年生草本植物，具球茎。英文名为“Garden tulip”或“Didier\'s tulip”。', '<div style=\'text-align: center;overflow: auto;\'><img title=郁金香 alt=郁金香 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuzDNbhLeD1iau2liaXu5tpKwywibjOJjgDcbzxHdcCKp0ZWcNpsDoSXWxiaa1lSQ1QtKSFuKbdzHDjXg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>（学名：Tulipa gesneriana L.）是一种多年生草本植物，属植物属百合科植物，具球茎。人们普遍认为</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>起源于土耳其,是土耳其、荷兰、匈牙利等国的国花。叶3-5枚，条状披针形至卵状披针状，单顶生花，大而艳丽，花被红色或与白色和黄色混合，有时白色或黄色，长5-7厘米，宽2-4厘米，6枚雄蕊等长，花丝无毛，无花柱，柱头增大呈鸡冠状，花期：4月至5月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>形态特征多年生的草本。鳞茎偏圆锥形，直径大概2-3厘米，外被是淡黄至棕褐色皮膜，内有肉质鳞片2-5片。茎叶光滑，被白粉覆盖。叶3-5，带状披针形至卵状披针形，全缘并成波形，通常有毛，其中的2-3宽广且基生。花单生至茎顶部，大型且直立杯状，洋红色，鲜黄至紫红色，基部带有墨紫斑，花被片6枚，离生，倒卵状长圆形，花期3月至5月。蒴果室背开裂，种子扁平。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>繁殖方式分球繁殖:经过一个生长季后，当年种下的雌鳞茎可同时生产1个大鳞茎和3个小鳞茎。可根据球茎大小分别种植。大鳞茎种植后一年开花，小鳞茎种植后一至两年开花。  播种繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>的播种和繁殖，大部分用于培育新品种。种子在蒴果成熟开裂前收获，沙藏到10月在室内盆播。保持湿润，并在第二年春天发芽。大约3-4年开花。       </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>生长习性由于地中海的气候，形成</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>适应冬季湿冷和夏季干热的特点，具有夏季休眠、秋冬生根并萌发新芽但不出土，需经冬季低温后第二年2月上旬左右（温度在5℃以上）开始伸展生长形成茎叶，3～4月开花的特性。生长开花适温为15～20℃。花芽分化是在茎叶变黄时将鳞茎从盆内掘起放阴冷的室外内度夏的贮藏期间进行的。分化适温为20～25℃，最高不得超过28℃。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>属长日照花卉，性喜向阳、避风，冬季温暖湿润，夏季凉爽干燥的气候。8℃以上即可正常生长，一般可耐-14℃低温。耐寒性很强，在严寒地区如有厚雪覆盖，鳞茎就可在露地越冬，但怕酷暑，如果夏天来的早，盛夏又很炎热，则鳞茎休眠后难于度夏。要求腐殖质丰富、疏松肥沃、排水良好的微酸性沙质壤土。忌碱土和连作。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>是世界著名的球根花卉，还是优良的切花品种，花卉刚劲挺拔，叶色素雅秀丽，荷花似的花朵端庄动人，惹人喜爱。在欧美视为胜利和美好的象征，荷兰、伊朗、土耳其等许多国家珍为国花。药用价值：化湿辟秽。主脾胃湿浊；胸脘满闷；呕逆腹痛；口臭苔腻。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>：博爱.体贴.高雅.富贵.能干.聪颖，善良。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>（红）:爱的告白、喜悦、热烈的爱意。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>（黑）:神秘，高贵，独特领袖权力、荣誉的皇冠。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>自我经验</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>，一个光看名字都让人觉得美好的花朵，实际上也没让人失望，成片的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>让人觉得很美丽、很漂亮。种植方面，在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd style=\'color:yellow;text-decoration: underline\'>郁金香</a>从花苞到绽放的那段时间真的很让人期待、花放时节让人觉得很漂亮，短暂的花季结束后，就是</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000240&idx=1&sn=7dd0a100ca0d00ef384d69372731e6dd&chksm=4ffc17f6788b9ee04a0032483f2cf8064892ecf77b21a850f23f28b5a0ecfde1c55908db6d62#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564927015, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyW6MR13SiYcRQbClcYqrHkw', '0');
INSERT INTO `zbp_post` VALUES (51, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】18-《旱金莲》', '旱金莲，是双子叶植物纲、旱金莲科、旱金莲属植物，为多年生的半蔓生或倾卧植物。株高30cm－70cm。基生叶具长柄，叶片五角形，三全裂，二回裂片有少数小裂片和锐齿。', '<div style=\'text-align: center;overflow: auto;\'><img title=旱金莲 alt=旱金莲 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvXNm3duguVpW5sibibXulOzhSIGrKRro1ctF6PIicq6Fs7yq3zBd5KlAnmXUUy9eAjklxvBJcHMqNQg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>属植物，为多年生的半蔓生或倾卧植物。株高30cm－70cm。基生叶具长柄，叶片五角形，三全裂，二回裂片有少数小裂片和锐齿。花单生或2朵－3朵成聚伞花序，花瓣五，萼片8枚－19枚，黄色，椭圆状倒卵形或倒卵形，花瓣与萼片等长，狭条形。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>形态特征多年生做一年生栽培，茎叶稍肉质，草本，半蔓生，无毛或被疏毛。叶互生；叶柄长6-31厘米，向上扭曲，盾状，着生于叶片的近中心处；叶片圆形，直径3-10厘米，有主脉9条．由叶柄着生处向四面放射，边缘为波浪形的浅缺刻，背面通常被疏毛或有乳凸点。     单花腋生，花柄长6-13厘米；花黄色、紫色、桔红色或杂色，直径2.5-6厘米；花托杯状；萼片5，长椭圆状披针形，长1.5-2厘米，宽5-7毫米，基部合生，边缘膜质，其中一片延长成一长距，距长2.5-3.5厘米，渐尖；花瓣5，通常圆形，边缘有缺刻，上部2片通常全缘，长2.5-5厘米，宽1-1.8厘米，着生在距的开口处，下部3片基部狭窄成爪，近爪处边缘具睫毛；雄蕊8，长短互间，分离；子房3室，花柱1枚，柱头3裂，线形。果扁球形，成熟时分裂成3个具一粒种子的瘦果。花期6-10月，果期7-11月。染色体x=6，7。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>繁殖方式播种繁殖:一般情况下播种期：3-6月，叫春播。花期8-12月。在秋季8月下旬或9月上旬播种也可以，这叫秋播。这样</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>会在元旦、春节期间开花。播种及育苗：发芽适温18-20度。点播，覆土1厘米左右，浇透水并保持湿润，7天发芽。2-3片真叶时摘心上盆，适当整理叶片及分枝利于通风，使花朵外露。   扦插繁殖:扦插于4-6月进行，选取嫩茎作插穗，去除下部叶片，插后遮荫，保持湿度，2-3周可生根。随茎蔓的生长，应及时用竹片设置立架，绑缚枝蔓。每10-15天追肥1次，整个生育期均应保持充足的水分和较高的空气湿度。每一朵花具有8枚雄蕊和一枚雌蕊，其中的8枚雄蕊并不是同时成熟的。且要等到所有的花粉都散播完毕时候，那枚雌蕊才成熟并伸到花的喇叭口处。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>生长习性性喜温和气候，不耐严寒酷暑。适生温度为18至24摄氏度。能忍受短期0℃喜温暖湿润，越冬温度10℃以上。夏季高温时不易开花，35℃以上生长受抑制。冬、春、秋需充足光照，夏季盆栽忌烈日曝晒。盆栽需疏松、肥沃、通透性强的培养土，喜湿润怕渍涝。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>叶肥花美，叶形如碗莲，呈圆盾形互生具长柄。花朵形态奇特，腋生呈喇叭状，茎蔓柔软娉婷多姿，叶、花都具有极高的观赏价值。药用价值：清热解毒。用于眼结膜炎，痈疖肿毒</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd style=\'color:yellow;text-decoration: underline\'>旱金莲</a>自我经验第一次见</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000254&idx=1&sn=b0ffd3412e23528637575515bd426155&chksm=4ffc17f8788b9eeec9e0a13773e4e8ed2a97864cf492612e05da3d1a0862d3d8cbc75c0f46d4#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564320035, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyW4h7s4qXEs1fGr1jO9jm9Q', '0');
INSERT INTO `zbp_post` VALUES (52, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】17-《文竹》', '文竹（学名：Asparagus setaceus (Kunth) Jessop）又称云片松、刺天冬、云竹、山草、鸡绒芝。攀援植物，高可达3-6米。根稍肉质，细长。茎的分枝极多，分枝近平滑。', '<div style=\'text-align: center;overflow: auto;\'><img title=文竹 alt=文竹 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsIibiaIxo45hDpA38HwFIopuZwnecEWwPeSInFvPP5rOibL04nicYPISbDaJicyGmNwhrxJSKMkeKNw3g/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>（学名：Asparagus setaceus (Kunth) Jessop）又称云片松、刺天冬、云竹、山草、鸡绒芝。攀援植物，高可达3-6米。根稍肉质，细长。茎的分枝极多，分枝近平滑。叶状枝通常每10-13枚成簇，刚毛状，略具三稜，长4-5毫米；鳞片状叶基部稍具刺状距或距不明显。花通常每1-3（-4）朵腋生，白色，有短梗；花被片长约7毫米。浆果直径约6-7毫米，熟时紫黑色，有1-3颗种子。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>成株高度可达3-6米。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>根部稍肉质，茎柔软丛生，细长。茎的分枝极多，近平滑。叶状枝通常每10-13枚成簇，刚毛状，略具三棱，长4-5毫米；鳞片状叶基部稍具刺状距或距不明显。花通常每1-3（-4）朵腋生，白色，有短梗；花被片长约7毫米。花期9-10月。浆果直径约6-7毫米，熟时紫黑色，有1-3颗种子。果期为冬季至翌年春季。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>繁殖方式播种繁殖:播种繁殖是</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>繁殖的主要方法，最适宜的播种时间是春天3-4月份。播种方法：搓去成熟</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>种子的果皮，淘净晾干，然后播种。播种后 30-40天就会发芽。   分株繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>便会在根际处萌发出新的蘖苗，当小植株壮大后，就可以进行分株繁殖，分株繁殖最好的季节是春季，将</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>从花盆中取出，利刀顺势将丛生的茎和根分成2-3丛，使每丛含有3-5枝芽，然后分别种植上盆，便可获得新的植株。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>性喜温暖湿润和半阴通风的环境，冬季不耐严寒，不耐干旱，不能浇太多水,根会腐烂，夏季忌阳光直射。以疏松肥沃排水良好的富含腐殖质的砂质壤土栽培为好。室温保持在12-18℃之间为宜，超过20℃时要通风散热，生长适温为15-25℃，越冬温度为5℃。生于山野，亦栽培于庭园。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>是观赏价值极高的小植物，以盆栽观叶为主，体态轻盈，姿态潇洒，文雅娴静，是深受人们喜爱的观叶花卉，尤其在书房、案头、卧室、客厅置一盆朱砂盆栽的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>，配以古铜色花架，显得文雅、别致、大方</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>四季常绿，经冬不凋虽无花之艳丽，但胜花之飘逸。纤细如羽毛之叶，若遇上开花，虽花小色白，但星星点点，在翠绿丛中，显得清雅可爱，而其香却很浓郁。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>根能润肺止咳，可用于治疗肺痨咳嗽、咳嗽痰喘、痢疾。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>全草能凉血解毒、利尿通淋，可用于治疗郁热咳血、小便淋漓等</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>花 语象征永恒，朋友纯洁的心，水远不变。婚礼用花中，它是婚姻幸福甜蜜，爱情地久天长的象征。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd style=\'color:yellow;text-decoration: underline\'>文竹</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000228&idx=1&sn=1148eab30bcc41b268efebe88df4f115&chksm=4ffc17e2788b9ef4b403bf6f39cf084df1cdeb9c3e154e0423714ebe511f306e3d44fb8c35d9#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564235225, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyUq8CLE9IM8DQ42TiqFKYDI', '0');
INSERT INTO `zbp_post` VALUES (53, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】16-《杜鹃》', '杜鹃（学名：Rhododendron simsii Planch.）：又名映山红、山石榴，为常绿或平常绿灌木。相传，古有杜鹃鸟，日夜哀鸣而咯血，染红遍山的花朵，因而得名。杜鹃花一般春季开花，每簇花2-6朵，花冠漏斗形，有红、淡红、杏红、雪青', '<div style=\'text-align: center;overflow: auto;\'><img title=杜鹃 alt=杜鹃 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuXAzvU3ibd03sTWx6TFia1bOxRlpBZGb8QTno7R7tCFF74PYYnuDS610lS9sYdY4aaicFXTNa1icgePw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>（学名：Rhododendron simsii Planch.）：又名映山红、山石榴，为常绿或平常绿灌木。相传，古有</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>鸟，日夜哀鸣而咯血，染红遍山的花朵，因而得名。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>花一般春季开花，每簇花2-6朵，花冠漏斗形，有红、淡红、杏红、雪青、白色等，花色繁茂艳丽。生于海拔500-1200（-2500）米的山地疏灌丛或松林下，为中国中南及西南典型的酸性土指示植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>形态特征落叶灌木，高2（-5）米；分枝多而纤细，密被亮棕褐色扁平糙伏毛。叶革质，常集生枝端，卵形、椭圆状卵形或倒卵形或倒卵形至倒披针形，长1.5-5厘米，宽0.5-3厘米，先端短渐尖，基部楔形或宽楔形，边缘微反卷，具细齿，上面深绿色，疏被糙伏毛，下面淡白色，密被褐色糙伏毛，中脉在上面凹陷，下面凸出；叶柄长2-6毫米，密被亮棕褐色扁平糙伏毛。     花芽卵球形，鳞片外面中部以上被糙伏毛，边缘具睫毛。花2-3（-6）朵簇生枝顶；花梗长8毫来，密被亮棕褐色糙伏毛；花萼5深裂，裂片三角状长卵形，长5毫米，被糙伏毛，边缘具睫毛；花冠阔漏斗形，玫瑰色、鲜红色或暗红色，长3.5-4厘米，宽1.5-2厘米，裂片5，倒卵形，长2.5-3厘米，上部裂片具深红色斑点；雄蕊10，长约与花冠相等，花丝线状，中部以下被微柔毛；子房卵球形，10室，密被亮棕褐色糙伏毛，花柱伸出花冠外，无毛。蒴果卵球形，长达1厘米，密被糙伏毛；花萼宿存。花期4-5月，果期6-8月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>的繁殖，可以用扦插、嫁接、压条、分株、播种五种方法，其中以采用扦插法最为普遍，繁殖量最大；压条成苗最快，嫁接繁殖最复杂，只有扦插不易成活的品种才用嫁接，播种主要用培育品种。   扦插繁殖:此法应用最广，优点是操作简便、成活率高、生长迅速、性状稳定。①时间，西鹃在5月下旬至6月上旬，毛鹃在6月上、中旬，春鹃、夏鹃在6月中下旬，此时枝条老嫩适中，气候温暖湿润。②插穗，取当年生刚木质化的枝条，带踵掰下，修平毛头，剪去下部叶片，保留顶部3片至5片叶，保湿待插；③扦插管理，扦插基质可用兰花土、高山腐殖土、黄心土、蛭石等，扦插深度以穗长的1/3至1/2为宜，扦插完成后要喷透水，加盖薄膜保湿，给予适当遮荫，一个月内始终保持扦插基质湿润，毛鹃、春鹃、夏鹃约一个月即可生根，西鹃约需60天至70天。    压条繁殖:采用高技压条。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>压条常在4-5月间进行。具体操作法是：先在盆栽的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>的母株上取2-3年生的健壮枝条，离枝条顶端10-12厘米处用锋利的小刀割开约1厘米宽的一圈环形枝皮，将韧皮部的筛管轻轻剥离干净，切断叶子制造有机物向下输送的渠道，使之聚集，以加速细胞分裂而形成瘤状突起，萌发根芽。然后用一块长方形塑料薄膜松松地包卷两圈，在环形切口下端2-3厘米处用细绳扎紧，留塑料薄膜上端张开成喇叭袋子状，随即将潮湿的泥土和少许苔藓填入，再把袋形的上端口扎紧，将花盆移到阳光直射不到的地方做日常管理。浇水时应向叶片喷水，让水沿着枝干下流，慢慢渗入袋中，保持袋内泥土经常湿润，以利枝条上伤口愈合，使之及早萌生新的根须。大约在3-4个月后根须长至2-3厘米长时，即可切断枝条，使其离开母株，栽入新的盆土中。  嫁接繁殖:可一砧接多穗，多品种，生长快，株形好，成活率高。①时间，5月至6月间，采用嫩梢劈接或腹接法；②砧木，选用2年生的毛鹃，要求新梢与接穗粗细得当，砧木品种以毛鹃“玉蝴蝶”、“紫蝴蝶”为好。③接穗，在西鹃母株上，剪取3厘米至4厘米长的嫩梢，去掉下部的叶片，保留端部的3片至4片小叶，基部用刀片削成楔形，削面长约0.5厘米至1.0厘米。5嫁接管理，在毛鹃当年生新梢2厘米至3厘米处截断，摘去该部位叶片，纵切1厘米，插入接穗楔形端，皮层对齐，用塑料薄膜带绑扎接合部，套正塑料袋扎口保湿；置于荫棚下，忌阳光直射和暴晒。接后7天，只要袋内有细小水珠且接穗不萎蔫，即有可能成活；2个月去袋，翌春再解去绑扎带。    播种繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>绝大多数都能结实采种，仅有重瓣不结实。一般种子的成熟期从每年的10月-1月，当果皮由青转黄至褐色时，果的顶端裂开，种子开始散落，此时要随时采收。末开裂的变褐均采下来，放在室内通风良好处摊凉，使之自然开裂，再去掉果壳等杂质，装入纸袋或布袋中，保存在阴凉通风处。如果有温室条件，随采随播发芽率高。一般播种时间3-4月份，采用盆播，因为种子小，把盆里外洗干净，放在阳光下晒干，灭菌消毒，土壤也灭菌消毒，装盆±要通透性好的、湿润肥沃含有丰富的有机质的酸性土。为了出苗均匀，种子掺些细土，撒入盆内上面盖一层薄细土，浇水用窨水法渗入盆内，把盆放在前窗台上，盖一层玻璃或塑料薄膜，目的是提高盆内温度。小苗出土后，逐渐减少覆盖时间，因苗嫩小，注意温变，突然高低变化，强光的照射。苗长得很慢，5-6月份才长出2-3片真叶，这时在室内做第一次移栽，株行距2-3厘米，苗高2-3厘米（大约11月份），大苗移栽在10厘米盆中一株，小苗栽三株。用细喷壶浇水和淡肥水。播种后第二年春季出花房，放在荫棚下养护。6月换13.3厘米盆中，第三年植株有20厘米高，已有分枝几个也有花蕾出现，换16.7厘米盆中，以后根据植株的大小，逐年换盆。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>生于海拔500-1200（-2500）米的山地疏灌丛或松林下，喜欢酸性土壤，在钙质土中生长得不好，甚至不生长。因此土壤学家常常把</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>性喜凉爽、湿润、通风的半阴环境，既怕酷热又怕严寒，生长适温为12℃至25℃，夏季气温超过35℃，则新梢、新叶生长缓慢，处于半休眠状态。夏季要防晒遮阴，冬季应注意保暖防寒。忌烈日暴晒，适宜在光照强度不大的散射光下生长，光照过强，嫩叶易被灼伤，新叶老叶焦边，严重时会导致植株死亡。冬季，露地栽培</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>要采取措施进行防寒，以保其安全越冬。观赏类的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>中，西鹃抗寒力最弱，气温降至0℃以下容易发生冻害。玫瑰主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>繁叶茂，绮丽多姿，萌发力强，耐修剪，根桩奇特，是优良的盆景材料。园林中最宜在林缘、溪边、池畔及岩石旁成丛成片栽植，也可于疏林下散植。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>也是花篱的良好材料，毛鹃还可经修剪培育成各种形态。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>专类园极具特色。在花季中绽放时即使</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>总是给人热闹而喧腾的感觉，而不是花季时，深绿色的叶片也很适合栽种在庭园中做为矮墙或屏障。药用价值：根（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>根）：酸、甘、温。活血，止痛，祛风，止痛。用于吐血、衄血，月经不调，崩漏，风湿痛，跌打损伤。叶（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>叶）：酸，平。清热解毒，止血。用于痈肿疔疮，外伤出血，瘾疹。花（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>）：酸、甘，温。活血，调经，祛风湿。用于月经不调，经闭，崩漏，跌打损伤，风湿痛，吐血，衄血。经济价值：有的叶花可入药或提取芳香油，有的花可食用，树皮和叶可提制烤胶，木材可做工艺品等。高山</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>根系发达，是很好的水土保持植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd style=\'color:yellow;text-decoration: underline\'>杜鹃</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000212&idx=1&sn=8be863934bce4ba33f6a9ee8dfeac957&chksm=4ffc17d2788b9ec436a3b9224ac6493c3f4e2099f75dd5f042161ce008de4be435da1f4fb9db#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564146858, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyfLceOQbDZXa0opTJjkmePQ', '0');
INSERT INTO `zbp_post` VALUES (54, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】15-《玫瑰》', '玫瑰（学名：Rosa rugosa Thunb.）：原产地中国。属蔷薇目，蔷薇科落叶灌木，枝杆多针刺，奇数羽状复叶，小叶5-9片，椭圆形，有边刺。花瓣倒卵形，重瓣至半重瓣，花有紫红色、白色，果期8-9月，扁球形。', '<div style=\'text-align: center;overflow: auto;\'><img title=玫瑰 alt=玫瑰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctvalibu3mictk3VOQX4UdBQsEol2U0FTKs3Nxo5rRUqRhZG0tXzH4dD8mBvicMgAMkG4a8nedEfwQUg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>（学名：Rosa rugosa Thunb.）：原产地中国。属蔷薇目，蔷薇科落叶灌木，枝杆多针刺，奇数羽状复叶，小叶5-9片，椭圆形，有边刺。花瓣倒卵形，重瓣至半重瓣，花有紫红色、白色，果期8-9月，扁球形。枝条较为柔弱软垂且多密刺，每年花期只有一次，因此较少用于育种，近来其主要被重视的特性为抗病性与耐寒性。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>形态特征直立灌木，高可达2米；茎粗壮，丛生；小枝密被绒毛，并有针刺和腺毛，有直立或弯曲、淡黄色的皮刺，皮刺外被绒毛。小叶5-9，连叶柄长5-13厘米；小叶片椭圆形或椭圆状倒卵形，长1.5-4.5厘米，宽1-2.5厘米，先端急尖或圆钝，基部圆形或宽楔形，边缘有尖锐锯齿，上面深绿色，无毛，叶脉下陷，有褶皱，下面灰绿色，中脉突起，网脉明显，密被绒毛和腺毛，有时腺毛不明显；叶柄和叶轴密被绒毛和腺毛；托叶大部贴生于叶柄，离生部分卵形，边缘有带腺锯齿，下面被绒毛。     花单生于叶腋，或数朵簇生，苞片卵形，边缘有腺毛，外被绒毛；花梗长5 -22 5毫米，密被绒毛和腺毛；花直径4-5.5厘米；萼片卵状披针形，先端尾状渐尖，常有羽状裂片而扩展成叶状，上面有稀疏柔毛，下面密被柔毛和腺毛；花瓣倒卵形，重瓣至半重瓣，芳香，紫红色至白色；花柱离生，被毛，稍伸出萼筒口外，比雄蕊短很多。果扁球形，直径2-2.5厘米，砖红色，肉质，平滑，萼片宿存。花期5-6月，果期8-9月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>繁殖方式播种繁殖:一般都是为培养新品种才采用，将秋季采收的种子装入盛有湿润砂土的塑料袋内，置于夜冻昼融的环境里，经过1个月左右后再逐渐加温至20℃左右，种子裂口发芽后即可以播种（或者砂藏到第二年春季播种），当幼苗长出3-5小叶时分栽。   扦插繁殖:带踵嫩枝插。即在早春选新萌发的枝，用利刀在其茎部带少许木质削下，用生长激素处理后插入扦插床或小盆中。半木质化枝扦插。此法是在6-9月，选择</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>花朵初谢的枝条，剪去花柄，削平其下部，以2-3节为一段，切除下面的一枚叶片，再剪去不健康及嫩叶，用生长刺激素处理后插于上法同样条件的床中。硬枝扦插。是将越冬前剪下的一年生充实枝条，2-3节为一段，每10枝成一捆，在低温温室内挖一个30厘米的坑将插条倒埋在湿润砂土中，顶部覆土5-10厘米（要保持不干），第二年早春，将插穗插入插床。硬枝水插法——选带1-2片叶的半木质化枝或硬枝，用利刀将基部削平，插入盛水容器中，枝条浸入水中1/2，放在15℃-20℃且能见到阳光的地方，使其长出根来。在促根期间，容器内的水每隔2-3天要更换一次，等枝上新根的表皮变浅黄或淡褐色时，即可取出细心栽入营养袋中培养。     压条繁殖:地面压条法。在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>枝条芽下刻伤，弯形埋入湿润的土中，枝条先端一段伸出土面，当压埋在土中的刻伤处长出新根，就可以切开分栽。空中压条法。在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>枝条上，选一个合适部位，将枝条刻伤或把表皮环剥1-1.5厘米，在剥皮处用竹筒或塑料布包一直径6-8厘米的土球，经常保持湿润，约经1个月左右，伤口长出新根，剪下，栽植于苗床或花盆中。  分株繁殖:一般可将</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>适当深栽或根部培土，促使各分枝茎部长新根。结合换盆， 可将长新根的侧枝切开，另成一新植株。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>喜阳光充足，耐寒、耐旱，喜排水良好、疏松肥沃的壤土或轻壤土，在粘壤土中生长不良，开花不佳。宜栽植在通风良好、离墙壁较远的地方，以防日光反射，灼伤花苞，影响开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>为阳性植物，日照充分则花色浓，香味亦浓。生长季节日照少于8小时则徒长而不开花。对空气湿度要求不甚严格，气温低、湿度大时发生锈病和白粉病；开花季节要求空气有一定的湿度；高温干燥时产油率则会降低。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>对土壤的酸碱度要求不严格，微酸性土壤至微碱性土壤均能正常生长。冬季有雪覆盖的地区能忍耐-38℃至-40℃的低温，无雪覆盖的地区也能耐-25℃至-30℃的低温，但不耐早春的旱风。土壤尚未解冻而地面风大的地区，枝条往往被风吹干；若土壤已解冻，根部不断向茎输送水分和养分，风不能造成严重危害。干燥度大于4的地区需要有灌溉条件才能正常发育。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>主要价值营养价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>花含有多种微量元素，维生素C含量高，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>花中含有300多种化学成份，如芳香的醇、醛、脂肪酸、酚和含香精的油和脂，常食</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>制品中以柔肝醒胃，舒气活血，美容养颜，令人神爽。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd style=\'color:yellow;text-decoration: underline\'>玫瑰</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000197&idx=1&sn=472dd60d0f70a630d4227f3465e1a959&chksm=4ffc17c3788b9ed52c86ccac490b887932e8e1a3ffdb073844d5b7edabf6623e3543c3830f31#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564061492, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyTiOwhfcMhtEvFEzDt6W3Ug', '0');
INSERT INTO `zbp_post` VALUES (55, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】33-《牡丹》', '牡丹（学名：Paeonia suffruticosa Andr.）是毛茛科、芍药属植物，为多年生落叶灌木。茎高达2米；分枝短而粗。叶通常为二回三出复叶，表面绿色，无毛，背面淡绿色，有时具白粉，叶柄长5-11厘米，和叶轴均无毛。', '<div style=\'text-align: center;overflow: auto;\'><img title=牡丹 alt=牡丹 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcv5s1G6icWRoxpZfztVdxvvSGuU3gsgXLAoVGEhqciaArz7STFsLicFqKWNQ9swy1PkMTv1vPd1dOZwA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>（学名：Paeonia suffruticosa Andr.）是毛茛科、芍药属植物，为多年生落叶灌木。茎高达2米；分枝短而粗。叶通常为二回三出复叶，表面绿色，无毛，背面淡绿色，有时具白粉，叶柄长5-11厘米，和叶轴均无毛。花单生枝顶，苞片5，长椭圆形；萼片5，绿色，宽卵形，花瓣5或为重瓣，玫瑰色、红紫色、粉红色至白色，通常变异很大，倒卵形，顶端呈不规则的波状；花药长圆形，长4毫米；花盘革质，杯状，紫红色；心皮5，密生柔毛。蓇葖长圆形，密生黄褐色硬毛。花期5月；果期6月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>是落叶灌木。茎高达2米；分枝短而粗。叶通常为二回三出复叶，偶尔近枝顶的叶为3小叶；顶生小叶宽卵形，长7-8厘米，宽5.5-7厘米，3裂至中部，裂片不裂或2-3浅裂，表面绿色，无毛，背面淡绿色，有时具白粉，沿叶脉疏生短柔毛或近无毛，小叶柄长1.2-3厘米；侧生小叶狭卵形或长圆状卵形，长4.5-6.5厘米，宽2.5-4厘米，不等2裂至3浅裂或不裂，近无柄；叶柄长5-11厘米，和叶轴均无毛。     花单生枝顶，直径10-17厘米；花梗长4-6厘米；苞片5，长椭圆形，大小不等；萼片5，绿色，宽卵形，大小不等；花瓣5，或为重瓣，玫瑰色、红紫色、粉红色至白色，通常变异很大，倒卵形，长5-8厘米，宽4.2-6厘米，顶端呈不规则的波状；雄蕊长1-1.7厘米，花丝紫红色、粉红色，上部白色，长约1.3厘米，花药长圆形，长4毫米；花盘革质，杯状，紫红色，顶端有数个锐齿或裂片，完全包住心皮，在心皮成熟时开裂；心皮5，稀更多，密生柔毛。蓇葖长圆形，密生黄褐色硬毛。花期5月；果期6月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>繁殖方式分株繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>的分株繁殖在明代已被广泛采用。具体方法为：将生长繁茂的大株</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>，整株掘起，从根系纹理交接处分开。每株所分子株多少以原株大小而定，大者多分，小者可少分。一般每3-4枝为一子株，且有较完整的根系。再以硫磺粉少许和泥。将根上的伤口涂抹、擦匀，即可另行栽植。分株繁殖的时间是在每年的秋分到霜降期间内，适时进行为好。此时，气温和地温较高，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>处于半休眠状态，但还有相当长的一段营养生长时间，进行分株栽培对根部生长影响不甚严重，分株栽植后还能生出一些新根和少量的株芽。若分株栽植过迟，当年根部生长很弱，或不发生新根，次年春，植株发育更弱，根弱则不耐旱，容易死亡。如分株过早，气温、地温较高，还能迅速生长，容易引起秋发。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>分株的母株，一般是利用健壮的株丛。进行分株繁殖的母株上应尽量保留根蘖，新苗上的根应全部保留，以备生长5年可以多分生新苗。这样的株苗栽后易成活，生长亦较旺盛。根保留的越多，生长愈旺。   嫁接繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>的嫁接繁殖，依所用砧木的不同分为两种，一种是野生</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>嫁接方法主要有嵌接法、腹接法和芽接法三种。    扦插繁殖:扦插繁殖，是利用</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>枝条易生不定根而繁殖新株的一种方法，属无性繁殖方法之一。方法是将扦插的枝条先剪下，脱离母株，再插入土壤或其他基质内使之生根，成为新株。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>整形修剪时，选择茎干充实，顶芽饱满而无病虫害的枝条作穗，长10-18厘米。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>的根为肉质根，喜高燥、忌潮湿、耐干旱。因此，育苗床应选择通风向阳处，筑成高床育苗。扦插时，插完一畦浇灌一畦，一次浇透。 播种繁殖:播种繁殖，是以种子繁衍后代或选育新品种，是一种有性繁殖方法。播种前必须对土壤进行较细致地整理消毒，土地要深耕细作，施足底肥。然后筑成70-80厘米宽的小畦，穴播、条播均可。播种不可过深，以3-4厘米为度，播种后覆土与要面平。再轻轻将土壤踏实，随即浇透水。 压条繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>压条法，是利用枝条能产生不定根的道理而进行的繁殖方法，将枝条压倒或在植株上用土压埋，不脱离母株，土壤保持湿润，技条被埋处生根，然后剪掉栽植，成为新株，同样属</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>的无性繁殖法。这种方法主要有：套盆培土压条法和双平法。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>生长习性性喜温暖、凉爽、干燥、阳光充足的环境。喜阳光，也耐半阴，耐寒，耐干旱，耐弱碱，忌积水，怕热，怕烈日直射。适宜在疏松、深厚、肥沃、地势高燥、排水良好的中性沙壤土中生长。酸性或黏重土壤中生长不良。充足的阳光对其生长较为有利，但不耐夏季烈日暴晒，温度在25℃以上则会使植株呈休眠状态。开花适温为17-20℃，但花前必须经过1-10℃的低温处理2-3个月才可。最低能耐-30℃的低温，但北方寒冷地带冬季需采取适当的防寒措施，以免受到冻害。南方的高温高湿天气对</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>需给其特定的环境条件才可观赏到奇美的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>色、姿、香、韵俱佳，花大色艳，花姿绰约，韵压群芳。栽培</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>系等品系，通常分为墨紫色、白色、黄色、粉色、红色、紫色、雪青色、绿色等八大色系，按照花期又分为早花、中花、晚花类，依花的结构分为单花、台阁两类，又有单瓣、重瓣、千叶之异。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>栽培和研究愈来愈兴旺，品种也越来越丰富，中国产有五百余种，着名品种有姚黄、魏紫、赵粉、二乔、梨花雪、金轮黄、冰凌罩红石、瑶池春、掌花案、首案红、葛巾紫、蓝田玉、乌龙卧墨池、豆绿等等。清程先贞咏“春烟笼宝墨，入夜看来难。恐奏清平调，杨妃砚滴干”，足见此时已有色与暗夜无异的黑</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>品种出现，而江苏盐城便仓产的枯枝</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>，竟然“奇在一放十二瓣，如果是闰年，就一定是十三瓣，而且枝干枯黄……一离开便仓，花即变种”。食用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>花可供食用。明代的《遵生八笺》载有“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>新落瓣也可煎食”，同是明代的《二如亭群芳谱》谓：“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>花煎法与玉兰同，可食，可蜜浸”，“花瓣择洗净拖面，麻油煎食至美”，中国不少地方有用</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>露酒口味香醇。药用价值：药用栽培者品种单调，花多为白色。以根皮入药，称</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>皮，又名丹皮、粉丹皮、刮丹皮等，系常用凉血祛瘀中药。李时珍谓：“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>以色丹者为上，虽结籽而根上生苗，故谓之</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>”，还认为野生单瓣者入药为好，人工为观赏栽培的重瓣者气味不纯，不可药用。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>花 语花型宽厚，被称为百花之王，有圆满，浓情，富贵，雍容华贵之意。生命，期待，淡淡的爱，用心付出。高洁，端庄秀雅，仪态万千，国色天香，守信的人。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>自我经验</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd style=\'color:yellow;text-decoration: underline\'>牡丹</a>，大富大贵的一种象征，所谓的花开富贵就是指的是</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000433&idx=1&sn=eec668832a5e13163fadb520a6000c1b&chksm=4ffc14b7788b9da1827b18ae33f793f98c378007286715f3e57a5ef257a26a945ed2d2000b6a#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565707178, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyUhHUd0-fCz04xLzxK-TxWo', '0');
INSERT INTO `zbp_post` VALUES (56, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】31-《百日草》', '百日菊（学名：Zinnia elegans Jacq.）：一年生草本。茎直立，高30-100厘米，被糙毛或长硬毛。叶宽卵圆形或长圆状椭圆形，两面粗糙，下面被密的短糙毛，基出三脉。头状花序单生枝端，总苞宽钟状；总苞片多层', '<div style=\'text-align: center;overflow: auto;\'><img title=百日草 alt=百日草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctcWAu4CEESGuLf2qJzswEggCGZMbNuSpXzGrcDcoB5xrpxOCHFujVzsFib9q7aPwzSW5QD4XzKjIA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>，又称百日菊花。一年生草本植物。茎直立，30-100厘米高，粗糙或多毛。叶宽卵形或长圆形卵圆形，粗糙在两边，基部3脉下面密具糙伏毛。花序单生分枝，总苞宽钟形；总苞多层，宽卵形或椭圆形。舌花深红色，玫瑰色，紫色堇型或白色，舌倒卵形，先端2~3齿浅裂或全缘，上部多毛，下面具长柔毛。管状的花黄色或橙色，顶裂片卵状披针形，顶部有黄棕色浓密的毛。雌花瘦果倒卵形，管状瘦果倒卵形楔形。六月至九月开花，七月至十月果实。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>形态特征百日菊是一年生草本植物。茎直立，30-100厘米高，粗糙或多毛。叶宽卵球形或长圆形椭圆形，长5~10厘米，宽2.5~5厘米，基部稍心形，两面粗糙，下面密具糙伏毛，基部三脉。     头状花序直径5-6.5厘米，单生枝，没有空和厚的花梗。中间苞片宽钟状；总苞片多层次，宽卵形或卵圆形，外层约5毫米，内层约10毫米，边缘黑色。托架的上端有一个延伸的附件；附件为紫红色和流苏状三角形。舌花深红色，玫瑰色，紫色堇型或白色，舌倒卵形，先端2-3齿浅裂或全缘，上部多毛，下面具长柔毛。管状花黄色或橙色，长7-8毫米，先端裂片卵状披针形，顶部有黄棕色浓密的茸毛。     雌花瘦果倒卵圆形，长6-7毫米，宽4-5毫米，扁平，腹面正中和两侧边缘各有1棱，顶端截形，基部狭窄，被密毛；管状花瘦果倒卵状楔形，长7-8毫米，宽3.5-4毫米，极扁，被疏毛，顶端有短齿。有单瓣、重瓣、卷叶、皱叶和各种不同颜色的园艺品种。花期6-9月，果期7-10月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>繁殖方式播种繁殖:播种前，土壤和种子要经过严格的消毒处理，以防生长期出现病虫害。播种在4月上旬至6月下旬均可，种子消毒用1%高锰酸钾液浸种30分钟，基质用腐叶土2份、河沙1份、泥炭2份、珍珠岩2份混合配制而成，可用0.05%高锰酸钾或1000倍甲醛消毒，土壤可采用高温熏蒸法，杀死其中的病菌、害虫及草种。播前基质湿润后点播，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>为嫌光性花种，播种后须覆盖一层蛭石。在21-23℃温度时，3-5天即可发芽，发芽期不需要光照，发芽后苗床保持50%-60%的含水量，不能太湿，以免烂根或发生猝倒病。   扦插繁殖:扦插育苗不如播种苗整齐，可选择长10厘米侧芽进行扦插，一般5-7天生根，以后栽培管理与播种一样，30-45天后即可出圃。      </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>生长习性喜温暖、不耐寒、喜阳光、怕酷暑、性强健、耐干旱、耐瘠薄、忌连作。根深茎硬不易倒伏。宜在肥沃深土层土壤中生长。生长期适温15-30°C。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>主要价值园林价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>花大色艳，开花旱，花期长，株型美观，可按高矮分别用于花坛、花境、花带。也常用于盆栽。药用价值：罗波施巴：全草治上感发热，口腔炎，风火牙痛《滇药录》。落波师粑：全株治痢疾，淋症，乳头痈，感冒发热，口腔炎，风火牙痛《滇省志》。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>花 语百日菊（洋红色）——持续的爱;百日菊（混色）——纪念一个不在的友人;百日菊（绯红色）——恒久不变;百日菊（白色）——善良;百日菊（黄色）——每日的问候</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>花期很长，从六月到九月，花朵陆续开放，长期保持鲜艳的色彩，象征友谊天长地久，更有趣的是</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>第一朵花开在顶端，然后侧枝顶端开花比第一朵开的更高，所以又得名“步步高”。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>花的颜色非常丰富，作盆栽欣赏，观其花朵，一朵更比一朵高，会激发人们的上进心。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd style=\'color:yellow;text-decoration: underline\'>百日草</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000400&idx=1&sn=ae2890259965efa972a00244c3077e3f&chksm=4ffc1496788b9d80cb6f6716ac4b3e8d664f576448717713cd34aea3033664484c6495d88de4#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565525887, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyfqekO2shjObhqtKHXR5-6Q', '0');
INSERT INTO `zbp_post` VALUES (57, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】30-《石斛》', '石斛（学名：Dendrobium nobile Lindl），又名仙斛兰韵、不死草、还魂草、紫萦仙株、吊兰、林兰、禁生、金钗花等。茎直立，肉质状肥厚，稍扁的圆柱形，长10~60厘米，粗达1.3厘米。药用植物，性味甘淡微咸，寒，归胃、肾，肺经', '<div style=\'text-align: center;overflow: auto;\'><img title=石斛 alt=石斛 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsyrBNQibktYic6cbqgTwTdv9sQGWSJCxPic8icyATWS89nU2MToia86YpVAe8f5qicR5WTtGUJGjvqufMQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>（学名：Dendrobium nobile Lindl），又名仙斛兰韵、不死草、还魂草、紫萦仙株、吊兰、林兰、禁生、金钗花等。茎直立，肉质状肥厚，稍扁的圆柱形，长10~60厘米，粗达1.3厘米。药用植物，性味甘淡微咸，寒，归胃、肾，肺经。益胃生津，滋阴清热。用于阴伤津亏，口干烦渴，食少干呕，病后虚热，目暗不明。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>花姿优雅，玲珑可爱，花色鲜艳，气味芳香，被喻为“四大观赏洋花”之一。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>形态特征茎直立，肉质状肥厚，稍扁的圆柱形，长10~60厘米，粗达1.3厘米，上部多少回折状弯曲，基部明显收狭，不分枝，具多节，节有时稍肿大；节间多少呈倒圆锥形，长2~4厘米，干后金黄色。叶革质，长圆形，长6~11厘米，宽1~3厘米，先端钝并且不等侧2裂，基部具抱茎的鞘。     总状花序从具叶或落了叶的老茎中部以上部分发出，长2~4厘米，具1~4朵花；花序柄长5~15毫米，基部被数枚筒状鞘；花苞片膜质，卵状披针形，长6~13毫米，先端渐尖；花梗和子房淡紫色，长3~6毫米；花大，白色带淡紫色先端，有时全体淡紫红色或除唇盘上具1个紫红色斑块外，其余均为白色；中萼片长圆形，长2.5~3.5厘米，宽1~1.4厘米，先端钝，具5条脉；侧萼片相似于中萼片，先端锐尖，基部歪斜，具5条脉；萼囊圆锥形，长6毫米；花瓣多少斜宽卵形，长2.5~3.5厘米，宽1.8~2.5厘米，先端钝，基部具短爪，全缘，具3条主脉和许多支脉；唇瓣宽卵形，长2.5~3.5厘米，宽2.2~3.2厘米，先端钝，基部两侧具紫红色条纹并且收狭为短爪，中部以下两侧围抱蕊柱，边缘具短的睫毛，两面密布短绒毛，唇盘中央具1个紫红色大斑块；蕊柱绿色，长5毫米，基部稍扩大，具绿色的蕊柱足；药帽紫红色，圆锥形，密布细乳突，前端边缘具不整齐的尖齿。花期4~5月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>繁殖方式分株繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>种植一般在春季进行，因春季湿度大、降雨量渐大，种植易成活。选择健壮、无病虫害的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>，剪去3年以上的老茎作药用，二年生新茎作繁殖用。繁殖时减去过长老根，留2~3厘米，将种蔸分开，每克含2~3个茎，然后栽植，可采取贴石栽植和贴树栽植法。   贴石栽植:在选好的石块上，按30厘米的株距凿出凹穴，用牛粪拌稀泥涂一薄层于种蔸处塞人石穴或石槽，力求稳固不使脱落即可，可塞小石块固定。     贴树栽植:在选好的树上，按30~40厘米在树上砍去一部分树皮将种蔸涂一薄层牛粪与泥浆混合物，然后塞入破皮处或树纵裂沟处贴紧树皮，再覆一层稻草，用竹蔑捆好。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>喜在温暖、潮湿、半阴半阳的环境中生长，以年降雨量1000毫米以上、空气湿度大于80%、 1月平均气温高于8℃的亚热带深山老林中生长为佳，对土肥要求不甚严格，野生多在疏松且厚的树皮或树干上生长，有的也生长于石缝中。产于安徽省霍山县的霍山</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>生长环境苛刻，产量稀少，市面上只有少数像福临门铁皮</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>主要价值观赏价值：从外观上看，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>兰构造独特的“斛”状花形，以及斑谰多变的色彩，都给人热烈、亮丽的感觉．对</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>兰的欣赏，国际上习惯用百分制来作衡量，具体为：花形30分，花色30分，其他特征40分。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>植物文化在我国，少数民族众多，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>民俗作为一种文化现象，它源于自然、源于生活，是先民在长期与大自然的接触中逐步形成和成长起来的。在云南的傣族地区，人们对花尤为崇拜，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>以其性能和特征，被当地人所崇敬，它寄托了傣族人民美好的愿望与情感，人们将它种植于自家的房顶上，而且都是些非常显眼的地方，如房顶中央，房檐的拐角处，这为傣家竹楼增添了一道亮丽的风景，同时也形成了</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd style=\'color:yellow;text-decoration: underline\'>石斛</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000399&idx=1&sn=9bee3ff2c738cfc676630295e35f3cdd&chksm=4ffc1489788b9d9f0bef741a928ad7c324973b1a37252cb0cf51d6bc9ef2825b380cbba09291#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565448476, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyXRIIG72yCjskrqNgwUwt3M', '0');
INSERT INTO `zbp_post` VALUES (58, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】32-《蝴蝶兰》', '蝴蝶兰（学名：Phalaenopsis aphrodite Rchb. F.）为兰科蝴蝶兰属，原产于亚热带雨林地区，为附生性兰花。蝴蝶兰白色粗大的气根露在叶片周围，除了具有吸收空气中养分的作用外，还有生长和光合作用。', '<div style=\'text-align: center;overflow: auto;\'><img title=蝴蝶兰 alt=蝴蝶兰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuibXw4mo5BCHeonVSup2sKueXuFhxg3hxB4Tt39fgpDX7P7xqYOpodEVicIxRdw2EwF2uXOYVwrcag/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>（学名：Phalaenopsis aphrodite Rchb. F.）为兰科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>属，原产于亚热带雨林地区，为附生性兰花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>白色粗大的气根露在叶片周围，除了具有吸收空气中养分的作用外，还有生长和光合作用。新春时节，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>植株从叶腋中抽出长长的花梗，并且开出形如蝴蝶飞舞般的花朵，深受花迷们的青睐，素有“洋兰王后”之称。分布在泰国、菲律宾、马来西亚、印度尼西亚，及中国台湾。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>形态特征茎很短，常被叶鞘所包。叶片稍肉质，常3-4枚或更多，上面绿色，背面紫色，椭圆形，长圆形或镰刀状长圆形，长10-20厘米，宽3-6厘米，先端锐尖或钝，基部楔形或有时歪斜，具短而宽的鞘。花序侧生于茎的基部，长达50厘米，不分枝或有时分枝；花序柄绿色，粗4-5毫米，被数枚鳞片状鞘；花序轴紫绿色，多少回折状，常具数朵由基部向顶端逐朵开放的花；花苞片卵状三角形，长3-5毫米；花梗连同子房绿色，纤细，长2.5-4.5厘米；花白色，美丽，花期长；中萼片近椭圆形，长2.5-3厘米，宽1.4-1.7厘米，先端钝，基部稍收狭，具网状脉；侧萼片歪卵形，长2.6-3.5厘米，宽1.4-2.2厘米，先端钝，基部收狭并贴生在蕊柱足上，具网状脉；花瓣菱状圆形，长2.7-3.4厘米，宽2.4-3.8厘米，先端圆形，基部收狭呈短爪，具网状脉；唇瓣3裂，基部具长约7-9毫米的爪；侧裂片直立，倒卵形，长2厘米，先端圆形或锐尖，基部收狭，具红色斑点或细条纹，在两侧裂片之间和中裂片基部相交处具1枚黄色肉突；中裂片似菱形，长1.5-2.8厘米，宽1.4-1.7厘米，先端渐狭并且具2条长8-18毫米的卷须，基部楔形；蕊柱粗壮，长约1厘米，具宽的蕊柱足；花粉团2个，近球形，每个劈裂为不等大的2爿。花期4-6月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>繁殖方式播种繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>是附生性兰花，以气生根附着于岩石或树干生长，从空气中吸收水分和养份。因此，植料以疏松透气的材料为佳。所以盆栽材料最好不选单纯表土或园土，而选用如水苔、树皮、树蕨根、碎砖瓦、椰子壳。纤维、陶粒等基质为宜。或直接把幼苗固定在木炭上，让其自行攀附生长，PH值为6.5左右。   花梗催芽繁殖:通过以花梗腋芽为外植体的无性繁殖方法，建立了4个无性繁殖系。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>无菌播种的采果适宜时期是授粉后110-130天。播种后150-180天便可培养出瓶小苗，出瓶小苗经1年以上栽培便可开花成为商品花。利用电子扫描显微镜和体视显微镜在国内首次对</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>无菌播种过程中的胚胎发育过程进行了系统观察和探讨。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>出生于热带雨林地区，本性喜暖畏寒。生长适温为15~20℃，冬季10℃以下就会停止生长，低于5℃容易死亡。原产马来西亚热带地区的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>属，是一种多年生草本植物。高温高湿河川海岸边的森林树木是</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>由于其生理结构可以保护自身在低湿状态下受到的应力影响，但是在太湿的环境下，高温高湿往往相伴着病害。最适宜的相对湿度范围为60%～80%RH。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>的学名按希腊文的原意为“好似蝴蝶般的兰花”。它能吸收空气中的养分而生存，归入气生兰范畴，可说是热带兰花中的一个大族。台湾原生种白花</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>闻名世界，南洋诸岛菲律宾、婆罗洲、印尼、马来西亚各地约有五、六十种原生种。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>色彩多种，从纯白、粉红、黄花着斑、线都有。育种家们利用各地搜取到珍贵的原种进行人工交配，改良出各种花色、花型，在花的尺寸上也有惊人的成就，当今达六寸的大白花，近五寸的粉红花，各种黄花红斑、红点、红线、纯黄、白花红心等色彩在各处兰花展都可看到，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>已是当今兰花之后（嘉德丽雅是兰花之王）</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>也被用来与近亲异属杂交，尤其与Doritis的交配（称为朵丽蝶兰）产生很悦目的深紫红花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd style=\'color:yellow;text-decoration: underline\'>蝴蝶兰</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000423&idx=1&sn=d3c07f53f61045c60f3f54947aa36cf3&chksm=4ffc14a1788b9db7a4756d1a2869dcfd2613fc321f92500a1cd78246fab0d2b7d9966b5e6c1b#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565620659, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQNq1wKZ7sIH2AfZNASNhxU', '0');
INSERT INTO `zbp_post` VALUES (59, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】29-《夹竹桃》', '夹竹桃（学名：Nerium indicum Mill.）夹竹桃族夹竹桃属常绿直立大灌木，高可达5米，枝条灰绿色，嫩枝条具棱，被微毛，老时毛脱落。叶3-4枚轮生，叶面深绿，叶背浅绿色，中脉在叶面陷入，叶柄扁平，聚伞花序顶生，花冠深红色或粉红色', '<div style=\'text-align: center;overflow: auto;\'><img title=夹竹桃 alt=夹竹桃 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctEJmvjJIgWSzzmbCIiaoF2yLywEXAlJThvvJJZytSr5AwmcUexy7QXWhwnF9H3Qs4Rl3kaOoba4tw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>（学名：Nerium indicumMill.）</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>属常绿直立大灌木，高可达5米，枝条灰绿色，嫩枝条具棱，被微毛，老时毛脱落。叶3-4枚轮生，叶面深绿，叶背浅绿色，中脉在叶面陷入，叶柄扁平，聚伞花序顶生，花冠深红色或粉红色，花冠为单瓣呈5裂时，其花冠为漏斗状，种子长圆形，花期几乎全年，夏秋为最盛；果期一般在冬春季，栽培很少结果。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>形态特征常绿直立大灌木，高达5米，枝条灰绿色，含水液；嫩枝条具棱，被微毛，老时毛脱落。叶3-4枚轮生，下枝为对生，窄披针形，顶端急尖，基部楔形，叶缘反卷，长11-15厘米，宽2-2.5厘米，叶面深绿，无毛，叶背浅绿色，有多数洼点，幼时被疏微毛，老时毛渐脱落；中脉在叶面陷入，在叶背凸起，侧脉两面扁平，纤细，密生而平行，每边达120条，直达叶缘；叶柄扁平，基部稍宽，长5-8毫米，幼时被微毛，老时毛脱落；叶柄内具腺体。     聚伞花序顶生，着花数朵；总花梗长约3厘米，被微毛；花梗长7-10毫米；苞片披针形，长7毫米，宽1.5毫米；花芳香；花萼5深裂，红色，披针形，长3-4毫米，宽1.5-2毫米，外面无毛，内面基部具腺体；花冠深红色或粉红色，栽培演变有白色或黄色，花冠为单瓣呈5裂时，其花冠为漏斗状，长和直径约3厘米，其花冠筒圆筒形，上部扩大呈钟形，长1.6-2厘米，花冠筒内面被长柔毛，花冠喉部具5片宽鳞片状副花冠，每片其顶端撕裂，并伸出花冠喉部之外，花冠裂片倒卵形，顶端圆形，长1.5厘米，宽1厘米；花冠为重瓣呈15-18枚时，裂片组成三轮，内轮为漏斗状，外面二轮为辐状，分裂至基部或每2-3片基部连合，裂片长2-3.5厘米，宽约1-2厘米，每花冠裂片基部具长圆形而顶端撕裂的鳞片；雄蕊着生在花冠筒中部以上，花丝短，被长柔毛，花药箭头状，内藏，与柱头连生，基部具耳，顶端渐尖，药隔延长呈丝状，被柔毛；无花盘；心皮2，离生，被柔毛，花柱丝状，长7-8毫米，柱头近球圆形，顶端凸尖；每心皮有胚珠多颗。          蓇葖2，离生，平行或并连，长圆形，两端较窄，长10-23厘米，直径6-10毫米，绿色，无毛，具细纵条纹；种子长圆形，基部较窄，顶端钝、褐色，种皮被锈色短柔毛，顶端具黄褐色绢质种毛；种毛长约1厘米。花期几乎全年，夏秋为最盛；果期一般在冬春季，栽培很少结果。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>繁殖方式播种繁殖:果熟后可随采随播，播后放在温室内3个月后才能出苗，露地秋播的来年清明可陆续出苗。  扦插繁殖:苗床尽量选择背风向阳、不积水、土壤病、虫、杂草少，肥力充足、便于管理的地块作为苗床。一般苗床应为东西走向，扦插育苗无论用哪种方式都必须细致整地。一般耕地的深度应达到25至30厘米，床宽1米，长度适宜，步道宽50厘米。土壤黏重时，可适当掺沙，并注意土壤消毒。     压条繁殖:于雨季进行，把近地表的枝条割伤压入土中，约经2个月后生根，即可与母体分离。 水插繁殖:生长季节都可进行，剪取30-40厘米长枝条，在下端用小刀劈开4~6cm插入盛水玻璃容器中，春、秋季温度适宜2-3周就能长根，夏季要勤换水，防水变质造成烂根。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>生长习性喜温暖湿润的气候，耐寒力不强，在中国长江流域以南地区可以露地栽植，但在南京有时枝叶冻枯，小苗甚至冻死。在北方只能盆栽观赏，室内越冬，白花品种比红花品种耐寒力稍强：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>不耐水湿，要求选择高燥和排水良好的地方栽植，喜光好肥，也能适应较阴的环境，但庇荫处栽植花少色淡。萌蘖力强，树体受害后容易恢复。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>的叶片如柳似竹，红花灼灼，胜似桃花，花冠粉红至深红或白色，有特殊香气，花期为6-10月，是有名的观赏花卉。环保价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>有抗烟雾、抗灰尘、抗毒物和净化空气、保护环境的能力。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>的叶片，对人体有毒、对二氧化硫、二氧化碳、氟化氢、氯气等有害气体有较强的抵抗作用。据测定，盆栽的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>，在距污染源40米处，仅受到轻度损害，170米处则基本无害，仍能正常开花，其叶片的含硫量比未污染的高7倍以上。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>即使全身落满了灰尘，仍能旺盛生长，被人们称为\"环保卫士\"。药用价值：叶（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>）：辛、苦、涩，温。有毒。强心利尿，祛痰杀虫。用于心力衰竭，癫痫；外用于甲沟炎，斑秃，杀蝇。全株：有毒。强心，利尿，发汗，祛痰，散瘀，止痛，解毒，透疹。用于哮喘，羊癫痫，心力衰竭；杀蝇，灭孑孓。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>是最毒的植物之一，包含了多种毒素，有些甚至是致命的。它的毒性极高，曾有小量致命或差点致命的报告。当中最大量的毒素是强心甙类的欧</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>甙及neriine。强心甙类是自然的植物或动物毒素，对心脏同时有正面或毒性的影响。在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>的各个部分都可以找到这些毒素，在树液中浓度最高，在皮肤上可以造成痳痹。科学家相信</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>内仍有很多未知的有害物质。另外，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>树皮上有rosagenin，可以造成像番木虌碱的影响。整棵植物包括其树液都带有毒性，其他的部分亦会有不良影响。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>所发生之烟雾亦有高度的毒性。些许或10-20块叶子就能对成人造成不良影响，单一叶子就可以令婴孩丧命。对于动物而言，致死量低至每公斤体重0.5毫克。大部分的动物对于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd style=\'color:yellow;text-decoration: underline\'>夹竹桃</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000378&idx=1&sn=aee51563ca5521f35561bd6c28d6ce79&chksm=4ffc147c788b9d6a36a92c928abd0d237f54e176534b4d552704cf253e1ff40cb5d75db117c5#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565358646, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyVOcjaWvJkqDTzFyy0nwaJ0', '0');
INSERT INTO `zbp_post` VALUES (60, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】28-《蔷薇》', '蔷薇（学名：Rosa sp.）：是蔷薇属部分植物的通称，主要指蔓藤蔷薇的变种及园艺品种。大多是一类藤状爬篱笆的小花，是原产于中国的落叶灌木，变异性强。茎刺较大且一般有钩，每节大致有3、4个；叶互生，奇数羽状复叶，小叶为5-9片....', '<div style=\'text-align: center;overflow: auto;\'><img title=蔷薇 alt=蔷薇 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuSkGib4cBwdpMHWV8BBSMlEQcJUzZh4zUtN7gaOQpcToFARiaJNTyzyChkJYkqwlJiagSJxEl4jno0Q/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>（学名：Rosasp.）：是</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>的变种及园艺品种。大多是一类藤状爬篱笆的小花，是原产于中国的落叶灌木，变异性强。茎刺较大且一般有钩，每节大致有3、4个；叶互生，奇数羽状复叶，小叶为5-9片，叶缘有齿，叶片平展但有柔毛。花常是6-7朵族生，为圆锥状伞房花序，生于枝条顶部，花径约3厘米，每年只开一次。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>花花盘环绕萼筒口部，有白色、黄色等多种颜色。花谢后萼片萼片会脱落；果实为圆球体。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>形态特征攀援灌木；小枝圆柱形，通常无毛，有短、粗稍弯曲皮束。小叶5-9，近花序的小叶有时3，连叶柄长5-10厘米；小叶片倒卵形、长圆形或卵形，长1.5-5厘米，宽8-28毫米，先端急尖或圆钝，基部近圆形或楔形，边缘有尖锐单锯齿，稀混有重锯齿，上面无毛，下面有柔毛；小叶柄和叶轴有柔毛或无毛，有散生腺毛；托叶篦齿状，大部贴生于叶柄，边缘有或无腺毛。花多朵，排成圆锥状花序，花梗长1.5-2.5厘米，无毛或有腺毛，有时基部有篦齿状小苞片；花直径1.5-2厘米，萼片披针形，有时中部具2个线形裂片，外面无毛，内面有柔毛；花瓣白色，宽倒卵形，先端微凹，基部楔形；花柱结合成束，无毛，比雄蕊稍长。果近球形，直径6-8毫米，红褐色或紫褐色，有光泽，无毛，萼片脱落。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>繁殖方式种子可供育苗，但生产上多用当年嫩枝扦插育苗，容易成活。名贵品种较难扦插，可用压条或嫁接法繁殖，无性繁殖的幼苗，当年即可开花。用作盆花的苗，应选择优良品种中较老的枝条，用压条法育苗，还要注意修剪主芽，进行人工矮化。用作切花的苗，应选择能形成采花母枝、花大色艳的品种育苗。  </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>喜欢阳光，亦耐半阴，较耐寒，在中国北方大部分地区都能露地越冬。对土壤要求不严，耐干旱，耐瘠薄，但栽植在土层深厚、疏松、肥沃湿润而又排水通畅的土壤中则生长更好，也可在粘重土壤上正常生长。不耐水湿，忌积水。萌蘖性强，耐修剪，抗污染。花期一般为每年的4-9月，次序开放，可达半年之久，由于温室效应而导致全球变暖，某些地方的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>提早在4月，甚至是3月份便开始开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>主要价值园林价值：色泽鲜艳，气味芳香，是香色并具的观赏花。枝干成半攀缘状，可依架攀附成各种形态，宜布置于花架、花格、辕门、花墙等处，夏日花繁叶茂，确有“密叶翠幄重，脓花红锦张”的景色，亦可控制成小灌木状，培育作盆花。有些品种可培育作切花。药用价值：倒钩刺，七姊妹：花治疗暑热胸闷，口渴，吐血；根治疗风湿关节痛，跌打损伤，月经不调，白带，遗尿，外用于烧伤，外伤出血；叶外用治疗痈疖疮疡。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd style=\'color:yellow;text-decoration: underline\'>蔷薇</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000377&idx=1&sn=01fe5972f485b6c890ab73e4afc88c1a&chksm=4ffc147f788b9d6942987d261ecca28c251dcbadcd43c8f1541ae698659346077a8537fa772d#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565271921, 0, 0, 'GvnDcC2QjGEN4AUqdM7DydeRdZ4hjuS_qr9_DO5amhE', '0');
INSERT INTO `zbp_post` VALUES (61, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】27-《木芙蓉》', '木芙蓉（Hibiscus mutabilis Linn.）又名芙蓉花、拒霜花、木莲、地芙蓉、华木，原产中国。其喜温暖、湿润环境，不耐寒，忌干旱，耐水湿。对土壤要求不高，瘠薄土地亦可生长。为锦葵科、木槿属落叶灌木或小乔木。', '<div style=\'text-align: center;overflow: auto;\'><img title=木芙蓉 alt=木芙蓉 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvjMRO7NYJrI8y7x9VU3GbwpN1V3vh312C8Ij9ZZGD6QFjg9ovoYnXtFIq3dAWuxx8L1M3ibiaXSqdw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>（Hibiscus mutabilis Linn.）又名芙蓉花、拒霜花、木莲、地芙蓉、华木，原产中国。其喜温暖、湿润环境，不耐寒，忌干旱，耐水湿。对土壤要求不高，瘠薄土地亦可生长。为锦葵科、木槿属落叶灌木或小乔木。花于枝端叶腋间单生。花、叶均可入药，有清热解毒，消肿排脓，凉血止血之效。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>（原变型）为落叶灌木或小乔木，高2-5米；小枝、叶柄、花梗和花萼均密被星状毛与直毛相混的细绵毛。叶宽卵形至圆卵形或心形，直径10-15厘米，常5-7裂，裂片三角形，先端渐尖，具钝圆锯齿，上面疏被星状细毛和点，下面密被星状细绒毛；主脉7-11条；叶柄长5-20厘米；托叶披针形，长5-8毫米，常早落。     花单生于枝端叶腋间，花梗长约5-8厘米，近端具节；小苞片8，线形，长10-16毫米，宽约2毫米，密被星状绵毛，基部合生；萼钟形，长2.5-3厘米，裂片5，卵形，渐尖头；花初开时白色或淡红色，后变深红色，直径约8厘米，花瓣近圆形，直径4-5厘米，外面被毛，基部具髯毛；雄蕊柱长2.5-3厘米，无毛；花柱枝5，疏被毛。蒴果扁球形，直径约2.5厘米，被淡黄色刚毛和绵毛，果爿5；种子肾形，背面被长柔毛。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>繁殖方式扦插繁殖:多在秋末冬初进行，等植株落叶后，将枝条离地面5厘米至10厘米处剪断，截成10厘米至15厘米的插条，每50根捆成一束，在背风向阳处挖沟（坑）贮藏，沟深40厘米，宽50厘米，捆好的枝条在坑内垂直排列整齐，上面覆盖10厘米至20厘米的干净湿砂，以后保持砂土湿润。翌年春天，其基部已经形成愈合组织，扦插很容易成活。也可在春季的3月至4月剪取枝条扦插，插穗宜选取一年生健壮而充实的枝条，每段长10厘米至15厘米，插于沙土中1/2左右，在北方地区应罩上塑料薄膜保温保湿，约1个月左右可生根。     压条繁殖:在6月至7月进行，将植株外围的枝条弯曲，压入土中，由于生根容易，不必刻伤，约1个月后生根，两个月后与母株分离，连根掘起，上盆在温室或地窖内越冬，翌年春天栽种。 分株繁殖:2月至3月进行，将植株的根挖出后分开，采用湿土干栽的方法，栽后压实，5天后浇一次透水。当年生长很快，到10月就能开花。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>喜光，稍耐阴；喜温暖湿润气候，不耐寒，在长江流域以北地区露地栽植时，冬季地上部分常冻死，但第2年春季能从根部萌发新条，秋季能正常开花。喜肥沃湿润而排水良好的砂壤土。生长较快，萌蘖性强。对SO2抗性特强，对Cl2、HCl也有一定抗性。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>主要价值经济价值：茎皮含纤维素39%，茎皮纤维柔韧而耐水、洁白柔韧、耐水湿，可供纺织、制绳、缆索、作麻类代用品和原料，也可造纸等用。古人还用</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>鲜花捣汁为浆，染丝作帐，即为有名的“芙蓉帐”。观赏价值：与其他园林植物一样，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>的枝、干、芽、叶有其自然生长规律，形成了四季中的不同形态，主要表现春季梢头嫩绿，一派生机盎然的景象；夏季绿叶成荫，浓荫覆地，消除炎热带来清凉；秋季拒霜宜霜，花团锦簇，形色兼备；冬季褪去树叶，尽显扶疏枝干，寂静中孕育新的生机；一年四季，各有风姿和妙趣。食用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>花亦可烧汤食，软滑爽口，花瓣与鸡肉一道可制成芙蓉花鸡片；与竹笋同煮可制成雪霞羹；与粳米一道可煮\"芙蓉花粥\"；还可与面粉调合，放入油锅中炸，炸后与软骨煨汤等。药用价值：清热解毒，消肿排脓，凉血止血。用于肺热咳嗽，月经过多，白带；外用治痈肿疮疖，乳腺炎，淋巴结炎，腮腺炎，烧烫伤，毒蛇咬伤，跌打损伤。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>植物文化其花或白或粉或赤，皎若芙蓉出水，艳似菡萏展瓣，故有“芙蓉花”之称，又因其生于陆地，为木本植物，故又名“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd style=\'color:yellow;text-decoration: underline\'>木芙蓉</a>开的花一日三变，故又名“三变花”，其花晚秋始开，霜侵露凌却丰姿艳丽，占尽深秋风情，因而又名“拒霜花”。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000366&idx=1&sn=74c34c3fccab7dd0ec9ca12f114578b0&chksm=4ffc1468788b9d7ebd7b06df86dafaea8fb698a69b96edc3165897a49bca7c7d4c7d44f4f29f#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565190134, 0, 0, 'GvnDcC2QjGEN4AUqdM7DySd0d_-lVek_O-5Z9CuSkCQ', '0');
INSERT INTO `zbp_post` VALUES (62, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】23-《勿忘我》', '勿忘草（学名：Myosotis silvatica Ehrh. ex Hoffm.）又名勿忘我，为管状花目、紫草科、勿忘草属植物。多年生草本，茎直立，高20-45厘米，基生叶和茎下部叶有柄，狭倒披针形、长圆状披针形或线状披针形', '<div style=\'text-align: center;overflow: auto;\'><img title=勿忘我 alt=勿忘我 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibct8WYDB4MeTl0Qtrqhia2hIT9CXfxDaORF1oy4hrxFtaUycvGyV8iasC7FibVsSxgLLIREUuqPUluDMw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>（学名：Myosotis silvatica Ehrh.ex Hoffm。）也称为</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>。多年生草本，茎直立，20-45厘米高，基生叶和无梗茎柄，具长裂果披针形的长椭圆形披针形或线状披针形，花序短的花冠蓝色，小坚果椭圆形，深棕色，光滑，有光泽。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>形态特征多年生草本。茎直立，单一或数条簇生，高20-45厘米，通常具分枝，疏生开展的糙毛，有时被卷毛。基生叶和茎下部叶有柄，狭倒披针形、长圆状披针形或线状披针形，长达8厘米，宽5-12毫米，先端圆或稍尖，基部渐狭，下延成翅，两面被糙伏毛，毛基部具小形的基盘；茎中部以上叶无柄，较短而狭。     花序短在花期，花后拉长，可达15厘米长，具苞片；花梗更厚，直立在果期，4-6毫米长，等长或稍长于花萼，密短伏特；花萼1.5-2.5毫米长，4-5毫米长，深分为2/3至3/4花萼长度，裂片披针形，顶部渐尖，密被具钩状毛；花冠蓝色，直径6-8毫米，筒部约2.5毫米，裂片5，近圆形，附属物3.5毫米，喉部附属物5，长约0.5毫米；花药椭圆形，先端具圆形附属物。小坚果卵球形，长约2毫米，宽1毫米，暗棕色，平滑，光滑，周围有狭窄的边缘，但在顶端更明显，基部无附属物。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>繁殖方式播种一般在9月 - 次年1月，种子有嫌光性，种子播种后，土壤应略微覆盖，保持湿度。在15-20℃的合适温度下发芽超过10-15天。播种时应注意温度不应超过25°C，发芽后应进行通风，幼苗有5个以上的真正叶时定植。通过组织培养技术培养组织培养幼苗，4-6个叶时定植。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>生长习性忘记高温和湿度，花芽分化需要1.5-2个月的低温期，所需温度在15℃以下，30℃以上或5℃以下不利于其生长。因此，当苗木未经低温处理时，应推迟进入温室的时间，使其完全接受低温，完成春化。勿忘草性喜干燥，喜土层深厚，疏松透气，微碱性沙质壤土，如腐熟的有机肥和缓效复合肥，施后均匀翻入土中；定植株行距为30厘米×40厘米，栽植深度以基质稍高于根颈部为宜栽植后应有2个月的时间保持15℃以下温度，以利植株通过春化阶段，有利正常生长开花。勿忘草喜阳光，必须在充足的日光照射下才能正常生长。若环境荫蔽，丛虽然繁茂，但抽生的花葶少。因此，每天植株接受日光照射要在4小时以上。如果能够保证全日照，则生长更好。勿忘草性喜干燥，喜土层深厚，疏松透气，微碱性沙质壤土，如腐熟的有机肥和缓效复合肥，施后均匀翻入土中；定植株行距为30厘米×40厘米，栽植深度以基质稍高于根颈部为宜栽植后应有2个月的时间保持15℃以下温度，以利植株通过春化阶段，有利正常生长开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>主要价值观赏价值：勿忘草株形柔美，茎枝纤细，叶片的形状很像柳树的叶子，无数朵小蓝花开满茎顶部的每个小枝，花瓣的蓝色仿佛是由天空的颜色染成的让人充满无限遐想。花朵中央有一圈黄色心蕊色彩搭配非常和谐。药用价值：勿忘草含有比较多的维生素，能够调理人体的新陈代谢，对于减肥和美容都有不错的效果。从中医上来讲主要有清肝明目和清热解毒的作用，对于大便秘结和皮肤粉刺的疗效都很不错。有护肤养颜、促进新陈代谢的作用，能有效地调节女性的生理问题，有清热解毒、护肤养颜、美白肌肤、清肝明目并有促进肌体新陈代谢、延缓衰老、提高免疫力的功效，对雀斑、粉刺有一定的消除作用，能有效调节女性的生理问题。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>花 语永恒的爱，浓情厚谊，永不变的心，永远的回忆。请不要忘记我真诚的爱；请想念我，忠贞的希望一切都还没有晚，我会再次归来给你幸福。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>植物文化勿忘草原是一种极普通的花草。人们之所以送给它“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>”这样的极富诗意的名字，是源于这么一个美丽的传说：欧洲古代有一位骑士，他带着恋人到海滨游览。恋人看到水边有一丛小花，要采来插戴。骑士为表示自己对恋人的爱情，涉水去采。不料海潮汹涌而来，把他去了。骑士忙将那丛花用力抛到岸上，大声喊道：“不要忘了我！”后来，人们就把这种花叫做“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>”花。这种花由于寄寓了一种深沉的爱，人们就把它看做花中的情种，男女相爱，往往把它扎成花束互相赠送，代表“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>”相思之情，表示双方的深恋密爱。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd style=\'color:yellow;text-decoration: underline\'>勿忘我</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000339&idx=1&sn=481d798d09f2e4742669be974038642a&chksm=4ffc1455788b9d43111bf6cd62e8359b4fe24b5d84d3a8c619bd80db13d4316e93d9b02496b6#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564845939, 0, 0, 'GvnDcC2QjGEN4AUqdM7DybDwPM94th486FXj3617Cjg', '0');
INSERT INTO `zbp_post` VALUES (63, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】26-《紫薇》', '紫薇（拉丁文名：Lagerstroemia indica L.），别名：痒痒花、痒痒树、紫金花、紫兰花、蚊子花、西洋水杨梅、百日红、无皮树。是双子叶植物纲、千屈菜科、紫薇属落叶灌木或小乔木，高可达7米；树皮平滑，灰色或灰褐色；枝干多扭曲', '<div style=\'text-align: center;overflow: auto;\'><img title=紫薇 alt=紫薇 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvWZ6EfUrFSMiaYFbfbwCOlmVKnQh2IWtj92iafxZT3veusxg9cic1hGBhibaGYkRthL7vO73s4X7I4vw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>（拉丁文名：Lagerstroemia indica L.），别名：痒痒花、痒痒树、紫金花、紫兰花、蚊子花、西洋水杨梅、百日红、无皮树。是双子叶植物纲、千屈菜科、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>属落叶灌木或小乔木，高可达7米；树皮平滑，灰色或灰褐色；枝干多扭曲，小枝纤细，叶互生或有时对生，纸质，椭圆形、阔矩圆形或倒卵形，幼时绿色至黄色，成熟时或干燥时呈紫黑色，室背开裂；种子有翅，长约8毫米。花期6-9月，果期9-12月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>形态特征落叶灌木或小乔木，高可达7米；树皮平滑，灰色或灰褐色；枝干多扭曲，小枝纤细，具4棱，略成翅状。叶互生或有时对生，纸质，椭圆形、阔矩圆形或倒卵形，长2.5-7厘米，宽1.5-4厘米，顶端短尖或钝形，有时微凹，基部阔楔形或近圆形，无毛或下面沿中脉有微柔毛，侧脉3-7对，小脉不明显；无柄或叶柄很短。     花色玫红、大红、深粉红、淡红色或紫色、白色，直径3-4厘米，常组成7-20厘米的顶生圆锥花序；花梗长3-15毫米，中轴及花梗均被柔毛；花萼长7-10毫米，外面平滑无棱，但鲜时萼筒有微突起短棱，两面无毛，裂片6，三角形，直立，无附属体；花瓣6，皱缩，长12-20毫米，具长爪；雄蕊36-42，外面6枚着生于花萼上，比其余的长得多；子房3-6室，无毛。蒴果椭圆状球形或阔椭圆形，长1-1.3厘米，幼时绿色至黄色，成熟时或干燥时呈紫黑色，室背开裂；种子有翅，长约8毫米。花期6-9月，果期9-12月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>繁殖方式播种繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>的播种繁殖方法可一次得到大量健壮整齐的苗木。播种繁殖过程包括种子采集、整地作床、种子催芽处理、播种时间和播种方法。  扦插繁殖:1、嫩枝扦插：嫩枝扦插一般在7~8月进行，此时新枝生长旺盛，最具活力，此时扦插成活率高。选择半木质化的枝条，剪成10cm左右长的插穗，枝条上端保留2-3片叶子。扦插深度约为8cm，插后灌透水，为保湿保温在苗床覆盖一层塑料薄膜，搭建遮阴网进行遮阴，一般在15-20天左右便可生根，将薄膜去掉，保留遮阴网，在生长期适当浇水，当年枝条可达到70cm，成活率高。2、硬枝扦插：硬枝扦插一般在3月下旬至4月初枝条发芽前进行。在长势良好的母株上选择粗壮的一年生枝条，剪成10~15cm长的枝条，扦插深度约为8-13cm。插后灌透水，为保湿保温在苗床覆盖一层塑料薄膜.当苗木生长15~20cm的时候可将薄膜掀开，搭建遮阴网。在生长期适当浇水，当年生枝条可长至80cm左右。    压条繁殖:压条繁殖在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>的整个生长季节都可进行，以春季3-4月较好。  分株繁殖:早春3月将</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>根际萌发的萌蘖与母株分离，另行栽植，浇足水即可成活。 嫁接繁殖:在每年春季</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>枝条萌芽前，选择粗壮的实生苗作砧木，先在砧木顶端靠外围部分纵劈一刀（深3~4cm），劈缝须从树心切下；再取长5~8em带2-3个芽的接穗，在其基部两侧削成3-4cm的楔形。接穗外侧比内侧稍厚。将接穗稍厚的一面放外面插入砧木劈口对准形成层，然后用塑料薄膜将整个穗条枝全部包扎好，露出芽头。此法可在同一砧木上分层嫁接不同颜色的枝条，形成一树多色。嫁接2~3个月后解膜，此时穗头长可达50~80cm，应及时将枝头剪短，以免遭风折断，并可培养粗壮枝，此培育法成活率达98%以上。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>其喜暖湿气候，喜光，略耐阴，喜肥，尤喜深厚肥沃的砂质壤土，好生于略有湿气之地，亦耐干旱，忌涝，忌种在地下水位高的低湿地方，性喜温暖，而能抗寒，萌蘖性强。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>还具有较强的抗污染能力，对二氧化硫、氟化氢及氯气的抗性较强。半阴生，喜生于肥沃湿润的土壤上，也能耐旱，不论钙质土或酸性土都生长良好。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>主要价值使用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>的木材坚硬、耐腐，可作农具、家具、建筑等用材药用价值：树皮、叶及花为强泻剂；根和树皮煎剂可治咯血、吐血、便血。观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>花色鲜艳美丽，花期长，寿命长，树龄有达200年的，热带地区已广泛栽培为庭园观赏树，有时亦作盆景。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>花 语沉迷的爱，好运，雄辩，女性。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd style=\'color:yellow;text-decoration: underline\'>紫薇</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000352&idx=1&sn=e05337e5590ff94aa36bccb64bec06e1&chksm=4ffc1466788b9d70167aa64bac1c5f52c1b61eea634366a54ddc4354dd4240f0d581459c6829#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565101234, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyZCKArldwAzjK9azqj9OYzc', '0');
INSERT INTO `zbp_post` VALUES (64, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】22-《菟丝子》', '菟丝子（学名：Cuscuta chinensis Lam.），别名禅真、豆寄生、豆阎王、黄丝、黄丝藤、金丝藤等。一年生寄生草本。茎缠绕，黄色，纤细，无叶。花序侧生，少花或多花簇生成小伞形或小团伞花序；苞片及小苞片小....', '<div style=\'text-align: center;overflow: auto;\'><img title=菟丝子 alt=菟丝子 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvScXvt1pvbdlb2yKszgk0E4OoYswADaXrKN9NtjvyjmTRDQsIaJWcRTeYVPkGzJRN9ibMsDtyC79A/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>（学名：Cuscuta chinensis Lam.），别名禅真、豆寄生、豆阎王、黄丝、黄丝藤、金丝藤等。一年生寄生草本。茎缠绕，黄色，纤细，无叶。花序侧生，少花或多花簇生成小伞形或小团伞花序；苞片及小苞片小，鳞片状；花梗稍粗壮；花萼杯状，中部以下连合，裂片三角状；花冠白色，壶形；雄蕊着生花冠裂片弯缺微下处；鳞片长圆形；子房近球形，花柱2。蒴果球形，几乎全为宿存的花冠所包围。种子2-49，淡褐色，卵形，长约1毫米，表面粗糙。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>形态特征一年生寄生草本。茎缠绕，黄色，纤细，直径约1毫米，无叶。花序侧生，少花或多花簇生成小伞形或小团伞花序，近于无总花序梗；苞片及小苞片小，鳞片状；花梗稍粗壮，长仅1毫米许；花萼杯状，中部以下连合，裂片三角状，长约1.5毫米，顶端钝；花冠白色，壶形，长约3毫米，裂片三角状卵形，顶端锐尖或钝，向外反折，宿存；雄蕊着生花冠裂片弯缺微下处；鳞片长圆形，边缘长流苏状；子房近球形，花柱2，等长或不等长，柱头球形。     蒴果球形，直径约3毫米，几乎全为宿存的花冠所包围，成熟时整齐的周裂。种子2-49，淡褐色，卵形，长约1毫米，表面粗糙。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>种子成熟后落入土中，休眠越冬后，竖年3~6月间温湿度适宜时萌发，幼苗胚根伸入土中，胚芽伸出土面，形成丝状的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>喜高温湿润气候，对土壤要求不严，适应性较强。野生</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>常见于平原、荒地、坟头、地边以及豆科、菊科、寥科、蘸科等植物地内。遇到适宜寄主就缠绕在上面，在接触处形成吸根伸入寄主，吸根进入寄主组织后，部分组织分化为导管和筛管，分别与寄主的导管和筛管相连，自寄主吸取养分和水分。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>一旦幼芽缠绕于寄主植物体上，生活力极强，生长旺盛，最喜寄生于豆科植物上。南方</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>：一年生寄生草本。茎缠绕，金黄色，纤细，直径1毫米左右，无叶。花序侧生，少花或多花簇生成小伞形或小团伞花序，总花序梗近无；苞片及小苞片均小，鳞片状；花梗稍粗壮，长1-2.5毫米；花萼杯状，基部连合，裂片3-4-5，长圆形或近圆形，通常不等大，长约0.8-1.8毫米，顶端圆；花冠乳白色或淡黄色，杯状，长约2毫米，裂片卵形或长圆形，顶端圆，约与花冠管近等长，直立，宿存；雄蕊着生于花冠裂片弯缺处，比花冠裂片稍短；鳞片小，边缘短流苏状；子房扁球形，花柱2，等长或稍不等长，柱头球形。蒴果扁球形，直径3-4毫米，下半部为宿存花冠所包，成熟时不规则开裂，不为周裂。通常有4种子，淡褐色，卵形，长约1.5毫米，表面粗糙。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>：一年生寄生草本。茎缠绕，黄色，纤细，直径约1毫米，无叶。花序侧生，少花或多花簇生成小伞形或小团伞花序，近于无总花序梗；苞片及小苞片小，鳞片状；花梗稍粗壮，长仅1毫米许；花萼杯状，中部以下连合，裂片三角状，长约1.5毫米，顶端钝；花冠白色，壶形，长约3毫米，裂片三角状卵形，顶端锐尖或钝，向外反折，宿存；雄蕊着生花冠裂片弯缺微下处；鳞片长圆形，边缘长流苏状；子房近球形，花柱2，等长或不等长，柱头球形。蒴果球形，直径约3毫米，几乎全为宿存的花冠所包围，成熟时整齐的周裂。种子2-49，淡褐色，卵形，长约1毫米，表面粗糙。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>主要价值药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>是一味平补肾、肝、脾之良药，临床主要应用于肾虚腰痛、阳痿遗精、尿频、宫冷不孕、目暗便溏之肾阴阳虚证。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>是一种生理构造特别的全寄生植物,其组成的细胞中没有叶绿体,只能利用爬藤状构造攀附在其他植物上,吸取养分以维生,其藤茎生长迅速,常缠绕枝条,甚至把整个树冠覆盖,严重影响叶片的光合作用,它的吸器不仅吸收寄主的养料和水分,而且给寄主的输导组织造成机械性障碍,致使受害寄主生长不良,甚至成片死亡；同时</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>也是传播某些植物病害的媒介或中间寄主,除本身有害外,还能传播植原体病害和病毒病等病害。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd style=\'color:yellow;text-decoration: underline\'>菟丝子</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000325&idx=1&sn=cf97f1cb524a7ff6021db01df4a14905&chksm=4ffc1443788b9d557e84240eaba2029a94cc3f0099bfff2e126d032a05f6fd7a2c8576c039e8#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1564758217, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyWIQfnqrxqG42A-BSNkZdb4', '0');
INSERT INTO `zbp_post` VALUES (65, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】43-《昙花》', '昙花（学名：Epiphyllum oxypetalum （DC.）Haw ）：附生肉质灌木，高2~6米，老茎圆柱状，木质化。分枝多数，叶状侧扁，披针形至长圆状披针形，边缘波状或具深圆齿，基部急尖、短渐尖或渐狭成柄状，深绿色', '<div style=\'text-align: center;overflow: auto;\'><img title=昙花 alt=昙花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcu56x8vibKpWKEscIY98rMn8unqQKo0ibwX0Q00sNTSAXcibibnZQonq9icPibmtM849UmTZW3tCtNZvaKA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>（学名：Epiphyllum oxypetalum （DC.）Haw ）：附生肉质灌木，高2~6米，老茎圆柱状，木质化。分枝多数，叶状侧扁，披针形至长圆状披针形，边缘波状或具深圆齿，基部急尖、短渐尖或渐狭成柄状，深绿色，无毛，中肋粗大，老株分枝产生气根。花单生于枝侧的小窠，漏斗状，于夜间开放，芳香，长25~30厘米，直径10~12厘米；花托绿色，略具角，被三角形短鳞片；瓣状花被片白色，倒卵状披针形至倒卵形，长7-10厘米，宽3~4.5厘米，边缘全缘或啮蚀状。浆果长球形，具纵棱脊，无毛，紫红色。种子多数，卵状肾形，亮黑色，具皱纹，无毛。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>是附生肉质灌木植物，高2~6米，老茎圆柱状，木质化。分枝多数，叶状侧扁，披针形至长圆状披针形，长15~100厘米，宽5~12厘米，先端长渐尖至急尖，或圆形，边缘波状或具深圆齿，基部急尖、短渐尖或渐狭成柄状，深绿色，无毛，中肋粗大，宽2~6毫米，于两面突起，老株分枝产生气根；小窠排列于齿间凹陷处，小形，无刺，初具少数绵毛，后裸露。花单生于枝侧的小窠，漏斗状，于夜间开放，芳香，长25~30厘米，直径10~12厘米；花托绿色，略具角，被三角形短鳞片；花托筒长13~18厘米，基部直径4~9毫米，多少弯曲，疏生长3~10毫米的披针形鳞片，鳞腋小窠通常无毛；萼状花被片绿白色、淡琥珀色色或带红晕，线形至倒披针形，长8-10厘米，宽3~4毫米，先端渐尖，边缘全缘，通常反曲；瓣状花被片白色，倒卵状披针形至倒卵形，长7~10厘米，宽3~4.5厘米，先端急尖至圆形，有时具芒尖，边缘全缘或啮蚀状；雄蕊多数，排成两列；花丝白色，长2.5~5厘米；花药淡黄色，长3~3.5毫米；花柱白色，长20~22厘米，直径3~4毫米；柱头15~20，狭线形，长16~18毫米，先端长渐尖，开展，黄白色。 浆果长球形，具纵棱脊，无毛，紫红色。种子多数，卵状肾形，亮黑色，具皱纹，无毛。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>繁殖方式播种繁殖:播种繁殖常用于杂交育种，从播种至开花需4~5年。  扦插繁殖:扦插繁殖以3~5月选取健壮、肥厚叶状茎作插穗，长20厘米~30厘米，按2~3节一段剪开，并将基部削平，待剪口稍干燥后插入干净的沙床，土中含水量保持 60%左右，室温保持 18 C~24 C，插后约3周即生根，待根长3厘米~4厘米时上盆。如用主茎扦插，当年可以见花，用侧茎则需2~3年才开花。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>生长习性生长地海拔1000~1200米。模式标本采自墨西哥。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>原产美洲墨西哥至巴西的热带沙漠中。那里的气候又干又热，但到晚上就凉快多了。晚上开花，可以避开强烈的阳光曝晒；缩短开花时间，又可以大大减少水分的损失，有利于它的生存。使它生命得到延续。于是天长日久，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>在夜间短时间开花的特性就逐渐形成，代代相传。喜温暖湿润的半阴、温暖和潮湿的环境，不耐霜冻，忌强光暴晒。特别是对温度的要求比原产地高，为15~25℃，春夏季夜温需16~18℃，白天21~24℃。越冬温度在10~12℃，土壤不宜过干。冬季可耐5℃左右低温。土壤要求宜用富含腐殖质、排水性能好、疏松肥沃的微酸性沙质土，否则易沤根。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>主要价值观赏价值：该种为著名的观赏花卉，俗称月下美人，琼花。仙人掌科，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>属。每逢夏秋节令，繁星满天、夜深人静时，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>开放，展现美姿秀色，当人们还沉睡于梦乡时，素净芬芳的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>一现之称。其奇妙的开花习性，常博得花卉爱好者的浓厚兴趣。药用价值：具有软便去毒，清热疗喘的功效。主治大肠热症、便秘便血、肿疮、肺炎、痰中有血丝、哮喘等症。花具有强健的功效，兼治高血压及血脂肪过高等。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>一现比喻美好事物不持久。类似的成语有弹指之间、电光火石、白驹过隙、稍纵即逝等。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd style=\'color:yellow;text-decoration: underline\'>昙花</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000564&idx=1&sn=9f04a0b97b53aa429cfa4815b194ae9b&chksm=4ffc1532788b9c24921ed6ad21b3cffe91e732c754cfeba1cf3b9d338d87a95575710952a281#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566660511, 0, 0, 'GvnDcC2QjGEN4AUqdM7DySwHQiKBq1_VNU9nZioJaWQ', '0');
INSERT INTO `zbp_post` VALUES (66, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】42-《水仙》', '水仙（Narcissus tazetta L. var. chinensis Roem. ）：又名中国水仙，是多花水仙的一个变种。是石蒜科多年生草本植物。水仙的叶由鳞茎顶端绿白色筒状鞘中抽出花茎（俗称箭）再由叶片中抽出。', '<div style=\'text-align: center;overflow: auto;\'><img title=水仙 alt=水仙 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctTWACRTxrxWnaCtAJvuRuvnKR4ocTULceUicYdNvDSytdVskPFfY7AwWiciaHlEx3nqSEJH6iaIibuzbA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>（Narcissus tazetta L. var. chinensis Roem. ）：又名中国</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>的一个变种。是石蒜科多年生草本植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>的叶由鳞茎顶端绿白色筒状鞘中抽出花茎（俗称箭）再由叶片中抽出。一般每个鳞茎可抽花茎1-2枝，多者可达8-11枝，伞状花序。花瓣多为6片，花瓣末处呈鹅黄色。花蕊外面有一个如碗一般的保护罩。鳞茎卵状至广卵状球形，外被棕褐色皮膜。叶狭长带状，蒴果室背开裂。花期春季。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>形态特征鳞茎卵球形。叶宽线形，扁平，长20-40厘米，宽8-15毫米，钝头，全缘，粉绿色。花茎几与叶等长；伞形花序有花4-8朵；佛焰苞状总苞膜质；花梗长短不一；花被管细，灰绿色，近三棱形，长约2厘米，花被裂片6，卵圆形至阔椭圆形，顶端具短尖头，扩展，白色，芳香；副花冠浅杯状，淡黄色，不皱缩，长不及花被的一半；雄蕊6，着生于花被管内，花药基着；子房3室，每室有胚珠多数，花柱细长，柱头3裂。蒴果室背开裂。花期春季。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>花瓣多为6片，也有8片的，酷似椭圆形，花瓣末处呈鹅黄色。花蕊外面有一个如碗一般的保护罩。花葶自叶丛中抽出，高于叶面；一般开花的多为4-5片叶的叶丛，每球抽花1-7支，多者可达10支以上；伞房花序（伞形花序）着花4-6朵，多者达10余朵；花白色，芳香；花期1-2月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>繁殖方式侧球繁殖:这是最普通常用的一种繁殖方法。储球着生在鳞茎球外的两侧，仅基部与母球相连，很容易自行脱离母体，秋季将其与母球分离，单独种植，次年产生新球。  侧芽繁殖:侧芽是包在鳞茎球内部的芽。只在进行球根阉割时，才随挖出的碎鳞片一起脱离母体，拣出白芽，秋季撒播在苗床上，翌年产生新球。    双鳞片繁殖:一个鳞茎球内包含着很多侧芽，有明显可见的，有隐而不见的。但其基本规律是闲两张鳞片1个芽。用带有两个鳞片的鳞茎盘作繁殖材料就叫双鳞片繁殖。其方法是把鳞茎先放在低温4-10℃处4-8周，然后在常温中把鳞茎盘切小，使每块带有两个鳞片，并将鳞片上端切除留下2厘米作繁殖材料，然后用塑料袋盛含水50%的蛭石或含水6%的砂，把繁殖材料放入袋中，封闭袋口，置20-28℃温度中黑暗的地方。经2-3月可长出小鳞茎，成球率80-90%。这是近年开始发展的新方法，四季可以进行，但以4-9月为好。生成的小鳞茎移栽后的成活率高，可达80-100%。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>为秋植球根类温室花卉，喜阳光充足，生命力顽强，能耐半阴，不耐寒。7-8月份落叶休眠，在休眠期鳞茎的生长点部分进行花芽分化，具秋冬生长，早春开花，夏季休眠的生理特性。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>喜光、喜水、喜肥，适于温暖、湿润的气候条件，喜肥沃的砂质土壤。生长前期喜凉爽、中期稍耐寒、后期喜温暖。因此要求冬季无严寒夏季无酷暑，春秋季多雨的气候环境。白天</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>花盆要放置在阳光充足的向阳处给予充足的光照。因为植物需要通过叶绿素经过光合作用提供养分 这样才可以使</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>花叶片宽厚、挺拔，叶色鲜绿，花香扑鼻。反之，则叶片高瘦、疲软，叶色枯黄，甚至不开花。以疏松肥沃、土层深厚的冲积沙壤土为最宜，pH在5-7.5时均宜生长。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>主要价值观赏价值：中国</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>花独具天然丽质，芬芳清新，素洁幽雅，超凡脱俗。因此，人们自古以来就将其与兰花、菊花、菖蒲并列为花中“四雅”；又将其与梅花、茶花、迎春花并列为雪中“四友”。它只要一碟清水、几粒卵石，置于案头窗台，就能在万花凋零的寒冬腊月展翠吐芳，春意盎然，祥瑞温馨。人们用它庆贺新年，作“岁朝清供”的年花。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>以鳞茎入药，春秋采集，洗去泥沙，开水烫后，切片晒干或鲜用，有小毒。具有清热解毒，散结消肿等疗效。用于腮腺炎，痈疖疔毒初起红肿热痛等症。经济价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>花花香清郁，鲜花芳香油含量达0.20%-0.45%,经提炼可调制香精、香料；可配制香水，香皂及高级化妆品。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>香精是香型配调中不可缺少的原料。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>花在过年象征－－思念，表示团圆。中国</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>：万事如意、吉祥、美好、纯洁、高尚、纯洁的爱情、妇女德行。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd style=\'color:yellow;text-decoration: underline\'>水仙</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000550&idx=1&sn=ac228f7ac08ff779a538499759aad2b0&chksm=4ffc1520788b9c365be1cb070600d41b33e03a0fc67813718baf7c6ee5246edfe659c3618055#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566575342, 0, 0, 'GvnDcC2QjGEN4AUqdM7DySt9ZhvNHiknAYLnmqMdLro', '0');
INSERT INTO `zbp_post` VALUES (67, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】41-《葱兰》', '葱莲（学名：Zephyranthes candida （Lindl.）Herb.），又名玉帘、葱兰等，多年生草本植物，鳞茎卵形，直径约2.5厘米，具有明显的颈部，颈长2.5-5厘米。叶狭线形，肥厚，亮绿色，长20-30厘米，宽 2-4毫米', '<div style=\'text-align: center;overflow: auto;\'><img title=葱兰 alt=葱兰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvo4dUZ8gq8bhmfMVSSPQfDOLAwnWSrn1VXN5UT9827qLl07gPs2B2ibq1LvMpciaJuWry0knYUMiaPQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000544&idx=1&sn=40fa091497d4745abbe10fba26528236&chksm=4ffc1526788b9c3066dd82950667b8de4843e6d84484b3de182925308672aa4c2078ae10dda4#rd style=\'color:yellow;text-decoration: underline\'>葱兰</a>（学名：Zephyranthes candida （Lindl.）Herb.），又叫做玉帘、葱莲等，是一种多年生草本植物，鳞茎呈卵形，直径大概约2.5cm，具有非常明显的颈部，颈长2.5到5cm。叶狭且呈线形，叶肥厚，颜色为亮绿色，长20至30cm，宽 2至4mm。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000544&idx=1&sn=40fa091497d4745abbe10fba26528236&chksm=4ffc1526788b9c3066dd82950667b8de4843e6d84484b3de182925308672aa4c2078ae10dda4#rd style=\'color:yellow;text-decoration: underline\'>葱兰</a>形态特征多年生草本。鳞茎卵形，直径约2.5厘米，具有明显的颈部，颈长2.5-5厘米。叶狭线形，肥厚，亮绿色，长20-30厘米，宽 2-4毫米。花茎中空；花单生于花茎顶端，下有带褐红色的佛焰苞状总苞，总苞片顶端2裂；花梗长约1厘米；花白色，外面常带淡红色；几无花被管，花被片6，长3-5厘米，顶端钝或具短尖头，宽约1厘米，近喉部常有很小的鳞片；雄蕊6，长约为花被的1/2；花柱细长，柱头不明显3裂。蒴果近球形，直径约1.2厘米，3瓣开裂；种子黑色，扁平。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000544&idx=1&sn=40fa091497d4745abbe10fba26528236&chksm=4ffc1526788b9c3066dd82950667b8de4843e6d84484b3de182925308672aa4c2078ae10dda4#rd style=\'color:yellow;text-decoration: underline\'>葱兰</a>繁殖方式播种繁殖:把种子一粒一粒地粘放在基质的表面上，覆盖基质1厘米厚。然后把播种的花盆放入水中。水的深度为花盆高度的1/2-2/3，让水慢侵地浸上来，按3厘米×3厘米的同距点播。播后覆盖基质,覆盖厚度为种粒的2-3倍。播后可用喷雾器淋湿。以后当盆土略干时。再淋水，仍要注意浇水的力度不能太大，以免把种子冲超来。  分株繁殖:把母株从花盆内取出，抖掉多余的盆土，把盘结在一起的根系尽可能地分开，用锋利的小刀把它剖开成两株或两株以上，分出来的每一株都要带有相当的根系，并对其叶片进行适当地修剪，以利于成活。  </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000544&idx=1&sn=40fa091497d4745abbe10fba26528236&chksm=4ffc1526788b9c3066dd82950667b8de4843e6d84484b3de182925308672aa4c2078ae10dda4#rd style=\'color:yellow;text-decoration: underline\'>葱兰</a>生长习性葱莲喜肥沃土壤，喜阳光充足，耐半阴与低湿，宜肥沃、带有黏性而排水好的土壤。较耐寒，在长江流域可保持常绿，0°C以下亦可存活较长时间。在-10°C左右的条件下，短时不会受冻，但时间较长则可能冻死。葱莲极易自然分球，分株繁殖容易，栽培需注意冬季适当防寒。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000544&idx=1&sn=40fa091497d4745abbe10fba26528236&chksm=4ffc1526788b9c3066dd82950667b8de4843e6d84484b3de182925308672aa4c2078ae10dda4#rd style=\'color:yellow;text-decoration: underline\'>葱兰</a>主要价值营养价值：葱莲株从低矮、终年常绿、花朵繁多、花期长，繁茂的白色花朵高出叶端，在丛丛绿叶的烘托下，异常美丽，花期给人以清凉舒适的感觉。适用于林下、边缘或半荫处作园林地被植物，也可作花坛、花径的镶边材料，在草坪中成丛散植，可组成缀花草坪，也可盆栽供室内观赏。药用价值：其带鳞茎的全草是一种民间草药，有平肝、宁心、熄风镇静的作用，主治小儿惊风，羊痫疯。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000544&idx=1&sn=40fa091497d4745abbe10fba26528236&chksm=4ffc1526788b9c3066dd82950667b8de4843e6d84484b3de182925308672aa4c2078ae10dda4#rd style=\'color:yellow;text-decoration: underline\'>葱兰</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000544&idx=1&sn=40fa091497d4745abbe10fba26528236&chksm=4ffc1526788b9c3066dd82950667b8de4843e6d84484b3de182925308672aa4c2078ae10dda4#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566487104, 0, 0, 'GvnDcC2QjGEN4AUqdM7DycPOH6fxUjtcRylFkKOVxwc', '0');
INSERT INTO `zbp_post` VALUES (68, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】39-《珊瑚樱》', '珊瑚樱（拉丁学名：Solanum pseudocapsicum L.）是茄科茄属植物。直立分枝小灌木，高达2米，全株光滑无毛。叶互生，狭长圆形至披针形，长1-6厘米，宽0.5-1.5厘米，先端尖或钝。花多单生，很少成蝎尾状花序，无总花梗', '<div style=\'text-align: center;overflow: auto;\'><img title=珊瑚樱 alt=珊瑚樱 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvQGgGhHvYblQ015gftLbVEvl38Zr2Arxr2PH58ia8tgATGjriadh1XI3icoZIB98BmibHhqr1b0A3ckQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>（拉丁学名：Solanum pseudocapsicum L.）是茄科茄属植物。又名冬珊瑚、红珊瑚、四季果。直立分枝小灌木，高达2米，全株光滑无毛。叶互生，狭长圆形至披针形，长1-6厘米，宽0.5-1.5厘米，先端尖或钝。花多单生，很少成蝎尾状花序，无总花梗或近于无总花梗，腋外生或近对叶生。浆果橙红色，直径1-1.5厘米。种子盘状，扁平，直径约2-3毫米。花期初夏，果期秋末。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>形态特征直立分枝小灌木，高达2米，全株光滑无毛。叶互生，狭长圆形至披针形，长1-6厘米，宽0.5-1.5厘米，先端尖或钝，基部狭楔形下延成叶柄，边全缘或波状，两面均光滑无毛，中脉在下面凸出，侧脉6-7对，在下面更明显；叶柄长约2-5毫米，与叶片不能截然分开。     花多单生，很少成蝎尾状花序，无总花梗或近于无总花梗，腋外生或近对叶生，花梗长约3-4毫米；花小，白色，直径约0.8-1厘米；萼绿色，直径约4毫米，5裂，裂片长约1.5毫米；花冠筒隐于萼内，长不及1毫米，冠檐长约5毫米，裂片5，卵形，长约3.5毫米，宽约2毫米；花丝长不及1毫米，花药黄色，矩圆形，长约2毫米；子房近圆形，直径约1毫米，花柱短，长约、2毫米，柱头截形。浆果橙红色，直径1-1.5厘米，萼宿存，果柄长约1厘米，顶端膨大。种子盘状，扁平，直径约2-3毫米。花期初夏，果期秋末。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>一般用种子播种繁殖，常在春季进行，热带地区四季都可进行播种。因为每个果实成熟期可能不一致，因此需随熟随采。在水中把果实洗出种子，晾晒干后贮藏或即播。种子可先育苗再上盆或者直播盆内。种子发芽适温约为20-25℃，10天左右即可发芽。需要移植的，可在幼苗具有3-4片真叶时先移植1次，在具7-8片真叶时再把苗定植在直径约为8厘米以上的花盆中（以后根据花盆的大小及植株生长情况再确定是否需要进行换更大的盆），或者用约30厘米口径的大花盆，每盆种上3株。期间也可不进行移植，从播种起约8个星期后把幼苗直接上盆。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>较为粗生，对土壤要求不很严格，但以富含有机质、排水良好、疏松肥沃的壤土或沙质壤土生长最佳。如果使用一般的土壤作为盆栽基质，应向其混入约1/3量的富含有机质的材料，如泥炭、腐叶土、堆沤过的蘑菇渣等。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>在全日照、半日照下均能适应，但在夏季因为阳光太强烈，宜适当遮荫，特别是在阳光最猛烈的中午。但是，如果栽培的环境光线太暗，则植株易徒长，开花结果不良。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>是传统的室内盆栽观果良品。春季播种的植株在夏秋开花，初秋至春季结果，是元旦和春节花卉淡季难得的观果花卉佳品。每一果实从结果到成熟、再到落果，时间可长达3个月以上，是盆栽观果花卉中观果期最长的品种之一。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>的根可以入药，味辛，微苦，性温，有毒；具有活血止痛；主治腰肌劳损，闪挫扭伤。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd style=\'color:yellow;text-decoration: underline\'>珊瑚樱</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000512&idx=1&sn=56c0af73812f6a4afb38c3584c83bd2e&chksm=4ffc1506788b9c10291a8897b89fbe3ea662c194fccc7df40edf95c4d336121bb44b6e8b535f#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566313490, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyRUsbghQapEw7XF7F2znxi8', '0');
INSERT INTO `zbp_post` VALUES (69, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】38-《一串红》', '一串红，又称爆仗红、象牙红，为唇形科鼠尾草属植物。花序修长，色红鲜艳，花期又长，适应性强，为中国城市和园林中最普遍栽培的草本花卉。亚灌木状草本，高可达90厘米。茎钝四稜形，具浅槽，无毛。叶卵圆形或三角状卵圆形，长2.5-7厘米', '<div style=\'text-align: center;overflow: auto;\'><img title=一串红 alt=一串红 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvhOsWrdRjutbSJyThk7cceGBBBPTCfMBJmFZ331J9jWrrHluYph5E83a1IrOS6lTWoIWOt8ib8Mkw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>，又名爆仗红、象牙红，是唇形科鼠尾草属的植物。花序是修长的，颜色红且鲜艳，花期非常长，有极强的适应性，在中国城市和园林中，是最常见栽种的草本花卉植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>形态特征亚灌木状草本，高可达90厘米。茎钝四稜形，具浅槽，无毛。叶卵圆形或三角状卵圆形，长2.5-7厘米，宽2-4.5厘米，先端渐尖，基部截形或圆形，稀钝，边缘具锯齿，上面绿色，下面较淡，两面无毛，下面具腺点；茎生叶叶柄长3-4.5厘米，无毛。       轮伞花序2-6花，组成顶生总状花序，花序长达20厘米或以上；苞片卵圆形，红色，大，在花开前包裹着花蕾，先端尾状渐尖；花梗长4-7毫米，密被染红的具腺柔毛，花序轴被微柔毛。花萼钟形，红色，开花时长约1.6厘米，花后增大达2厘米，外面沿脉上被染红的具腺柔毛，内面在上半部被微硬伏毛，二唇形，唇裂达花萼长1/3，上唇三角状卵圆形，长5-6毫米，宽10毫米，先端具小尖头，下唇比上唇略长，深2裂，裂片三角形，先端渐尖。花冠红色，长4-4.2厘米，外被微柔毛，内面无毛，冠筒筒状，直伸，在喉部略增大，冠檐二唇形，上唇直伸，略内弯，长圆形，长8-9毫米，宽约4毫米，先端微缺，下唇比上唇短，3裂，中裂片半圆形，侧裂片长卵圆形，比中裂片长。能育雄蕊2，近外伸，花丝长约5毫米，药隔长约1.3厘米，近伸直，上下臂近等长，上臂药室发育，下臂药室不育，下臂粗大，不联合。退化雄蕊短小。花柱与花冠近相等，先端不相等2裂，前裂片较长。花盘等大。小坚果椭圆形，长约3.5毫米，暗褐色，顶端具不规则极少数的绉折突起，边缘或稜具狭翅，光滑。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>采用播种或杆插法繁殖，以播种较多。华北地区播种季节不限，其余地区以春季为宜。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>花期较迟，春播者9~10月开花，如要使花期提前或采收种子，应在3月初将种子播于温室或温床。播种床内施以少量基肥，将床面平整并浇透水，水渗后播种，覆一层薄土，播种后8~10天种子萌发。生长约100天开花，花期约两个月。杆插多用嫩枝，以3~5月或9~10月较为适宜，结合摘顶芽进行。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>生长习性喜阳，也耐半阴，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>要求疏松、肥沃和排水良好的砂质壤土。而对用甲基溴化物处理土壤和碱性土壤反应非常敏感，适宜于pH5.5—6.0的土壤中生长。耐寒性差，生长适温20－25摄氏度。15摄氏度以下停止生长，10摄氏度以下叶片枯黄脱落</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>常用红花品种，秋高气爽之际，花朵繁密，色彩艳丽。常用作花丛花坛的主体材料。也可植于带状花坛或自然式纯植于林缘．常与浅黄色美人蕉、矮万寿菊、浅蓝或水粉色水牡丹、翠菊、矮霍香蓟等配合布置。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>矮生品种更宜用于花坛，白花品种除与红花品种配合观赏效果较好外。一般白花、紫花品种的观赏价值不及红花品种。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>代表恋爱的心，一串白代表精力充沛，一串紫代表智慧。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd style=\'color:yellow;text-decoration: underline\'>一串红</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000500&idx=1&sn=920b957c937f3b98d0b0ea4e9d01a358&chksm=4ffc14f2788b9de4d52e836bc855109280316f9a220415326b309b07f201f55441acc36cca29#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566228264, 0, 0, 'GvnDcC2QjGEN4AUqdM7Dyfv-PXdPyAbghxohvDwbfzg', '0');
INSERT INTO `zbp_post` VALUES (70, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】40-《黄秋英》', '黄秋英（学名：Cosmos sulphureus Cav.）是菊科秋英属植物。一年生草本，高1.5-2米，叶2-3次羽状深裂，裂片披针形至椭圆形，头状花序2.5-5厘米，花序梗长6-25厘米，舌状花橘黄色或金黄色，先端具3齿；管状花黄色。', '<div style=\'text-align: center;overflow: auto;\'><img title=黄秋英 alt=黄秋英 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctiau5HtRtWkMreMGxCj9ZUIatwMoeBv72iaUNa1F77EbeDTLJsJfMK5xyfIvDomDsOaMBEsj5X4BFA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>（学名：Cosmos sulphureus Cav.）是菊科秋英属植物。一年生草本，高1.5-2米，叶2-3次羽状深裂，裂片披针形至椭圆形，头状花序2.5-5厘米，花序梗长6-25厘米，舌状花橘黄色或金黄色，先端具3齿；管状花黄色。瘦果具粗毛，连同喙长达18-25毫米，喙纤弱。花期6-7月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>是一年生草本，高1.5-2米，具柔毛。叶2-3次羽状深裂，裂片披针形至椭圆形。头状花序2.5-5厘米，花序梗长6-25厘米。外层苞片较内层苞片为短，长约4-8毫米，狭椭圆形；内层苞片长椭圆状披针形，长8-10毫米。舌状花橘黄色或金黄色，先端具3齿；管状花黄色。瘦果具粗毛，连同喙长达18-25毫米，喙纤弱。花期6-7月。        </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>繁殖方式播种繁殖:在春、夏、秋三季均可播种，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>种子较大，每克种子约12粒，发芽适温18-22℃，育苗时湿度过高会降但发芽率。播种前用锰酸钾溶液浸泡种子1-2小时，浓度0.5%左右，经消毒后可杀灭种子表面携带的病原菌，利于种子萌发，促使种子迅速发芽，幼苗生长整齐健壮。播种可采用苗珠播种或穴盘播种。生长快速，从播种到开花约50-55天。  扦插繁殖:可选用当年播种健壮小苗的腋芽或剪取当年生健壮小苗的顶芽（结合摘心）进行扦插。也可在花谢后选择健壮的植株，剪去老枝，地上部保留2-3厘米主茎，剪后施以氮为主的复合肥，待发芽后剪取扦插。插穗长度保留3-4个节，尽量在节间下剪取，去掉下部叶片，保留上部1-2片叶。然后用生根剂400倍液和黄泥混合成泥浆，将插穗剪口蘸点泥浆，待泥浆干后插育苗池，育苗池要用80%的遮光网遮盖，插苗池基质可直接用新鲜河沙或新鲜黄泥，扦插时用一根细竹棒预先在基质上打孔，一边打孔，一边插入插穗，并用两个手指轻轻压实插穗基部的培养土，使插穗与基质充分结合。扦插密度约2.5-3厘米左右，以插穗叶片相互碰到而不重叠为标准，扦插深度以一个节入土即可。插后浇透水，以后每天喷1-2次叶面即可。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>扦插使用生根剂扦插育苗效果显著，插后14天左右即可发根，20天可移植。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>生长习性基质：黄泥、谷壳、鸡粪便以6:3:0的比例拌匀，堆沤半年后即可使用。上盆：根据</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>的生长特性，当扦插苗根系长到6-8毫米时即可移植上盆，如果根系长得太长会消耗养分，小苗生长不健壮，影响生长。所以要及时上盆，上盆后需立即浇足水，使小苗与基质充分结合，如阳光太强，要用遮光网适当遮盖3-4天。修剪：当</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>幼苗长至6-8厘米左右时，即可摘心，以缩短植株高度，增加侧枝数量，增加花朵。如要留作扦插苗繁殖用，可在10-12厘米高时进行摘心，第二次摘心仅在原来基础留2-3节为宜，以促使植株矮壮、丰满、花密。浇水：要求适时、适量、合理浇水，浇水要根据天气变化和季节的不同进行，一般阴雨天要少浇或不浇，夏、秋季气温高蒸发量大时要多浇，反之则少浇。浇水要视盆土情况而定，遵循“不干不浇，浇则浇透”的原则，平时盆内不得有积水，如发现盆内积水要及时处理，否则会造成烂根、叶枯黄，引起植株死亡。花序形成前，盆土要保持一定的干燥，以利孕育花序。花蕾形成后，适当浇水保持土壤湿润即可。施肥：要加强肥水管理，施肥应注意氮、磷、钾的配合。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>花序较长，特别是第二代苗，植株较高宜，花序长，易倒伏。所以在栽培管理时一般不施用氮肥，适量增加磷、钾肥即可。在生长期内，结合浇水15-20天施1次腐熟的鸡粪便或豆饼肥，出现花蕾时，用1-2%磷酸二氢钾溶液喷洒更佳，每次施肥前应松土除草.温度：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>生长适温为15-35℃，冬季温度不得低于7℃，否则植株受冻害，不能开花，严重时会冻死。夏季温度在35℃可正常生长，35℃以上高温植株徒长开花少，严重时植株叶片脱落影响观赏。所以</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>在阳光充足条件下，植株生长整齐，高度一致，开花整齐，花色鲜艳。在光照不足或雨天过密条件下，植株生长偏高，花序伸长，易倒伏，花色较淡。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>花大、色艳，适宜多株丛植或片植。也可利用其能自播繁衍的特点，与其他多年生花卉一起用于花境栽植，或在草坪及林缘自然式配植。植株低矮紧凑、花头转密的矮生品种，可用于花坛布置，也可用作切花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd style=\'color:yellow;text-decoration: underline\'>黄秋英</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000527&idx=1&sn=4418227721d0871d8fbebc36b6aa32f2&chksm=4ffc1509788b9c1fa47fb01d6d8d67e353180edaefca0c22f2c9f5667ab9cff243ef39a059a2#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566397113, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyYBplVUh46wkSbrjlD-L2gc', '0');
INSERT INTO `zbp_post` VALUES (71, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】37-《绿绒蒿》', '绿绒蒿是一年或多年生草本，具黄色液汁。一种产西欧，其余48种均分布于中国-喜马拉雅地区，但最原始种在华中。中国有38种，集中分布于西南部，其中仅云南就占17种，多集中分布于滇西北海拔3000--5000米的雪山草甸，高山灌丛、流石滩', '<div style=\'text-align: center;overflow: auto;\'><img title=绿绒蒿 alt=绿绒蒿 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctCrVE76VCIMNHpzwoCWNQY0icmArfyPauI2lMr8I0sahpBwF5l3MtcjLv5iakDiaRuJqYibJn7dlqUpg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>是一年或多年生草本，具黄色液汁。一种产西欧，其余48种均分布于中国-喜马拉雅地区，但最原始种在华中。中国有38种，集中分布于西南部，其中仅云南就占17种，多集中分布于滇西北海拔3000--5000米的雪山草甸，高山灌丛、流石滩，少数种类在滇中、滇东北的亚高山地带。西北祁连山区也有分布。为著名的观赏植物，有些种类可入药治病。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>为一年生或多年生草本，主根明屁肥厚呈萝卜状，茎分枝或不分枝，株高0.3-1m，有的品种可达1.5m二其叶长椭同形、阔卯形或具长柄如汤匙形或分裂为琴形等，叶面具柔长的茸毛，因而得名“</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>”。因种类不同，花型各异，有的自叶丛中抽出花莛，一丛数莛，每莛一朵；有的茎上着花，一茎数花；花有单瓣及重瓣，花有蓝、黄、紫、红等多种颜色，花期6-8月。蒴果近球形、卵形或倒卵形。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>繁殖方式播种繁殖:8月播种，真叶长出2片～3片时移苗。定植时，植株需带土。喜充足的阳光、肥沃和排水良好的 砂质壤土 。较耐寒，在华北地区多有栽培，具直根，不耐移植。定植后每年应及时追肥、除草和松土。播种或根插繁殖：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>于8月下旬至9月都可播种，甚至10至11月也可播种，幼苗为直根系，不耐移栽，播种最好用小花盆或营养钵育苗，待长出3～4片叶时脱盆下地，也可直接播入地中，2周出苗，具3～4片叶时间苗，株行距20-30cm，不再移栽。根插宜于早春萌芽前或秋季进行。若在生长期内移栽，应待植株具6～7片叶后于阴天进行。移栽之前一定要透水，挖掘时尽量多带土，少伤根系，以利成活。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>是野生高山花卉，生长于海拔三千至四千米以上的流石滩和冰川的前沿。人称“高山牡丹”，欧洲人推崇为“世界名花”。因全株被有绒毛或刚毛而得名。罂粟科，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>耐寒，宜冬季干燥、夏季湿润凉爽的气候，喜富含有机质和排 水良好的土壤。不耐移植，可于秋季直播繁殖。栽培地宜选通风良好而较为蔽荫处。要防止夏季强光照射。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>主要价值观赏价值：高海拔地带的气候特点：冰霜凛冽，辐射强烈，风大土薄，昼夜温差悬殊，气候变化甚大。草本植物和小灌木亦常成垫状，匍匐于地。然而，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>竟能在这样艰苦的环境中迎风笑日，顽强生长。在那百草不生，海拔高达5000米的流石滩中开放。医学价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>不仅花色艳丽具有很高的观赏价值，有些种类还可入药治病。如：全缘</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>(丽江又名“黄芙蓉”)、尼泊尔</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a> (禄劝又名“山窝笋”)全草入药，具有清热解毒的功效，清热止咳；花前采叶治胃中反酸；花退热催吐、消炎，还治跌打骨折。总状</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>，兄弟民族又称“红毛洋参”、 “雪参”，根入药，治气虚、浮肿、哮喘等症，具有补中益气的作用。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd style=\'color:yellow;text-decoration: underline\'>绿绒蒿</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000489&idx=1&sn=f889fc575d8b9d735065f92bae63ebd5&chksm=4ffc14ef788b9df9009bb803259d8ba6420652c63c600e33017d2041e6c1f34cb0c21c445dcd#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566141829, 0, 0, 'GvnDcC2QjGEN4AUqdM7DySc6w9L1uDidd3SjfmYa7WI', '0');
INSERT INTO `zbp_post` VALUES (72, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】36-《沙漠玫瑰》', '沙漠玫瑰（拉丁学名：Adenium obesum.），其实它并不是生长在沙漠地区的玫瑰，与玫瑰也没什么近缘关系或相像之处。又名：天宝花，夹竹桃科，这是一种夹竹桃科的植物。天宝花属多肉植物，也称多浆植物。多肉灌木或小乔木，高达4.5m', '<div style=\'text-align: center;overflow: auto;\'><img title=沙漠玫瑰 alt=沙漠玫瑰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibct8bChg0xY0c588icSfJqwD6iapsqxVJ3zV1TDaialWVLg9pahnhGW8fkuXaHicx6rbicWVw4jiaXPlACnw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>（拉丁学名：Adenium obesum.），它实际上不是生长在沙漠中的玫瑰，它与玫瑰没有关系，也与玫瑰没有相似之处。又称：天宝花，夹竹桃科，这是夹竹桃科植物。天宝花是肉质植物，又称肉质植物。高达4.5米的肉质灌木或小树；树干肿胀。单叶互生，丛生枝，倒卵形到椭圆形，长15厘米，全缘，先端钝和短尖，肉质，近无柄。喜欢高温、干燥、阳光明媚的环境，耐热、耐寒，因为沙漠的起源地靠近沙漠，红色的玫瑰被称为</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>；原产于肯尼亚、坦桑尼亚、非洲，花朵盛开美丽，常被种植和观赏。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>形态特征高达4.5米的肉质灌木或小树；树干肿胀。单叶互生，丛生枝，倒卵形到椭圆形，长15厘米，全缘，先端钝和短尖，肉质，近无柄。花冠漏斗状，外面有短柔毛，5裂，径约5cm，外缘红色至粉红色，中部色浅，裂片边缘波状；顶生伞房花序。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>花形似小喇叭，玫瑰红色，非常艳丽。伞形花序三五成丛，灿烂似锦，四季开花不断。因原产地接近沙漠且红如玫瑰而得名</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>单叶互生，倒卵形，顶端急尖，长8厘米～10厘米，宽2厘米～4厘米，革质，有光泽，腹面深绿色，背面灰绿色，全缘。总状花序，顶生，着花10多朵，喇叭状，长6厘米～8厘米；花冠5裂，有玫红、粉红、白色及复色等。5月～12月是</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>的花期，花有红、玫红、粉红、白等色。南方温室栽培较易结实。种子有白色柔毛，可助其飞行散布。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>繁殖方式常用扦插、嫁接和压条繁殖，也可播种。扦插，以夏季最好，选取1-2年生枝条，以顶端枝最好，剪成10厘米长，待切口晾干后插于沙床，插后约3-4周生根。嫁接，用夹竹桃作砧木，在夏季采用劈接法，成活后植株生长健壮，容易开花。压条，常在夏季采用高空压条法，在健壮枝条上切去2/3，先用苔藓填充后再用塑料薄膜包扎，约25天生根，45天后剪下盆栽。夏季播种，发芽温度21℃。    </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>生长习性性喜高温、干旱、阳光充足的气候环境，喜富含钙质的、疏松透气的、排水良好的砂质壤土，不耐荫蔽，忌涝，忌浓肥和生肥，畏寒冷，生长适温25-30℃。夏季可放在室外阳光充足处，不必遮阴，充分浇水，以保持土壤湿润，但不能积水。冬季需节制浇水，越冬温度维持在10℃以上，使其落叶休眠。栽培中每年酌情施有机肥2次至3次。繁殖可在夏季选择10厘米左右的1年至2年生枝条截取，待切口稍干后扦插于沙床中，3周至4周即可生根。也可在夏季用高空压条法繁殖。如果能采集到种子，还可进行播种繁殖。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>主要价值园林价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>植株矮小，树形古朴苍劲，根茎肥大如酒瓶状。每年4-5月和9-10月二度开花，鲜红妍丽，形似喇叭，极为别致，深爱人们喜爱。南方地栽布置小庭院，古朴端庄，自然大方。盆栽观赏，装饰室内阳台别具一格。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>花内服治反胃、痢疾、脱肛、吐血、下血、痄腮、白带过多等，外敷可治疗疮疖肿。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>属于夹竹桃科天宝花属，具有一定的毒性，其乳汁毒性较强，如误食</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd style=\'color:yellow;text-decoration: underline\'>沙漠玫瑰</a>自我经验沙漠与玫瑰的碰撞，变成了</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000475&idx=1&sn=22d96a865184cbe035878482b8bf5666&chksm=4ffc14dd788b9dcbb07adb5d2332db487e94650d35ef084c638b29f235ca5c59ac6368e79394#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566054145, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyaCIDDO6oEfrrwUjIqEUaJ4', '0');
INSERT INTO `zbp_post` VALUES (73, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】35-《花烛》', '花烛（Anthurium andraeanum Linden）：是单子叶植物纲天南星科花烛属多年生常绿草本植物。茎节短；叶自基部生出，绿色，革质，全缘，长圆状心形或卵心形。叶柄细长，佛焰苞平出，革质并有蜡质光泽，橙红色或猩红色；肉穗花序黄色', '<div style=\'text-align: center;overflow: auto;\'><img title=花烛 alt=花烛 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvld82kgJXgEWv6eFPE7ZTicoKmkUBkgPIZGVibCfDz6ZJVx3azEvy4t44aNKCY3SHT5JGT2xfRbVcA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>（Anthurium andraeanum Linden）：是单子叶植物纲天南星科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>属多年生常绿草本植物。茎节短；叶自基部生出，绿色，革质，全缘，长圆状心形或卵心形。叶柄细长，佛焰苞平出，革质并有蜡质光泽，橙红色或猩红色；肉穗花序黄色，可常年开花不断。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>形态特征天南星科多年生常绿草本植物。茎节短；叶自基部生出，绿色，革质，全缘，长圆状心形或卵心形，叶柄细长。佛焰苞平出，卵心形，革质并有蜡质光泽，橙红色或猩红色；肉穗花序长5-7厘米，黄色，可常年开花不断。    </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>繁殖方式扦插繁殖:对直立性有茎的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>品种可采用扦插繁殖，剪取带1-2个茎节有3-4片叶的作插穗，插入水苔中，待萌发新根后定植盆内。  分株繁殖:分株时期主要在凉爽高湿的春季，秋季阴凉天气也可分株。切忌在炎热的夏天或干燥寒冷的季节分株。分株时须注意以不伤母株为原则，太大的侧芽不分，靠太紧的侧芽不分，太弱小的侧芽也不分，主要分出比较容易与母株分离且较为健壮、至少有2条主要根系以上的侧芽。   播种繁殖:播种法一般在育种中才使用。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>自然授粉不良，需经人工授粉才能得到种子，种子成熟后，要随采随播。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>种子较大，采用室内盆播，发芽适温为25℃-28℃，播后20-25天发芽，播种苗需培育3-4年才能开花。常规繁殖方法的优点是简单易行、成本低，一是繁殖速度慢，满足新品种推广的需求。二是繁殖植株易受病原侵染,，在植株体内积累,，导致物种退化甚至死亡。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>为多年生常绿草本花卉，性喜温热多湿而又排水良好的半阴的环境，怕干旱和强光暴晒，适宜生长昼温为26-32℃，夜温为21-32℃。所能忍受的最高温为35℃，可忍受的低温为14℃。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>花叶俱美，花期长、为优质的切花材料。用红色</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>为主材，配上小菊、霞草、艳山姜、变叶木瓶插，有着浓厚的江南乡惰。若以</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>为主花，篮插，配以文心兰、报岁兰、百合、天门冬，是一份精美无比的节日礼物。环保价值：可以吸收人体排出的废气（氨气、丙酮），也可以吸收装修残留的各种有害气体（甲醛等），同时可以保持空气湿润，避免人体的鼻粘膜干燥。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd style=\'color:yellow;text-decoration: underline\'>花烛</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000464&idx=1&sn=6a94b0cf67ea4ded5eefeb71314a8511&chksm=4ffc14d6788b9dc0313c071b5f3cd6f8b7c60034b7f01594189ef5b07b9727717abb683968c6#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565964631, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyTkR-ZqLXlufQd65106aRuc', '0');
INSERT INTO `zbp_post` VALUES (74, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】34-《球兰》', '球兰（学名：Hoya carnosa（L.f.）R. Br），又名：马骝解、狗舌藤、铁脚板等，马利筋亚科球兰属多年生植物 [1]  。攀援灌木，附生于树上或石上，茎节上生气根。分布于云南、广西、广东、台湾；热带及亚热带其他地区也有栽培或野生', '<div style=\'text-align: center;overflow: auto;\'><img title=球兰 alt=球兰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctxrFRgqfJ3zTRibGoZz0pBERB4RYGAiapORZPicZ9URwiaWJ4jKWh7T39rQiay0hz0wicBf1T7faMZ7emA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>（学名：Hoya carnosa（L.f.）R. Br），又名：马骝解、狗舌藤、铁脚板等，马利筋亚科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>属多年生植物。攀援灌木，附生于树上或石上，茎节上生气根。分布于云南、广西、广东、台湾；热带及亚热带其他地区也有栽培或野生。生平原，林中或栽培。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>形态特征攀援灌木，附生于树上或石上；茎节上生气根。叶对生，肉质，卵圆形至卵圆状长圆形，长3.5-12厘米，宽3-4.5厘米，顶端钝，基部圆形；侧脉不明显，约有4对。     聚伞花序伞形状，腋生，着花约30朵；花白色，直径2厘米；花冠辐状，花冠筒短，裂片外面无毛，内面多乳头状突起；副花冠星状，外角急尖，中脊隆起，边缘反折而成1孔隙，内角急尖，直立；花粉块每室1个，伸长，侧边透明。蓇葖线形，光滑，长7.5-10厘米；种子顶端具白色绢质种毛。花期4-6月，果期7-8月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>繁殖方式扦插繁殖:夏末取半成熟枝或花后取顶端枝，长8～10厘米，插穗必须带茎节，清洗剪口乳液，晾干后插入沙床，室温保持20～25℃，插后20～30天生根。    压条繁殖:春末夏初将充实茎蔓在茎节间处稍加刻伤，用水苔在刻伤处包上，外用薄膜包上，扎紧，待生根后剪下盆栽，也可将盆栽</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>放在畦面，把节间刻伤后埋入土下，生根后剪断上盆。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>多附生于树干、石壁上，喜温暖，耐干燥，喜髙温、高湿、半阴环境，夏、秋季需保持较高空气温度，忌烈日曝晒，若日照过强，叶色会泛黄，色彩粗涩而无光泽，其适生温度为20~25\'C，除华南温暖地区外，盆栽需温室越冬，冬季气温不低于1℃，在每日有3~4h充足阳光方能开花，光照不足地区常盆栽观叶；在富含腐殖质且排水良好的土壤中生长旺盛，较适宜多光照和稍干土壤。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>主要价值药用价值：清热化痰，消肿止痛。治肺热咳嗽，痈肿，瘰疬，乳妇奶少，关节疼痛，睾丸炎。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd style=\'color:yellow;text-decoration: underline\'>球兰</a>自我经验</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000447&idx=1&sn=49a0a74ac7894369916846ff257aad46&chksm=4ffc14b9788b9daf992e4c3393c54b8816c424b6c95744e9cb4609833b9a052b2b8ccba232fc#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1565831310, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQqELktwG8Wm4YIBuZ5T5QA', '0');
INSERT INTO `zbp_post` VALUES (75, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】53-《风铃草》', '风铃草（学名：Campanula medium L.）是桔梗科风铃草属植物。二年生宿根草本植物。株高50-120厘米；茎粗壮直立，基生。叶簇生，卵形至倒卵形；小花1-2朵聚生成总状花序，花冠钟形，长约6厘米，有白、蓝或紫等色', '<div style=\'text-align: center;overflow: auto;\'><img title=风铃草 alt=风铃草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuQTMTof9jEaC0Cia0FwBfv9eibECPm2f36q0ibTASFxVXWqV8Hw2CvNiadMj1gJyriay2zms7JUvrV28w/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>（学名：Campanula medium L.）是桔梗科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>属植物。二年生宿根草本植物。株高50-120厘米；茎粗壮直立，基生。叶簇生，卵形至倒卵形；小花1-2朵聚生成总状花序，花冠钟形，长约6厘米，有白、蓝或紫等色；蒴果，带有宿存的花萼裂片；种子多数，椭圆状，平滑。花期5-6月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>是二年生宿根草本植物。株高50-120厘米。多毛。茎粗壮直立，基生。叶簇生，卵形至倒卵形，叶缘具波状钝锯齿，表面粗糙，叶柄具翅，茎生。叶小而无柄。     小花1-2朵聚生成总状花序。花冠钟形，长约6厘米，五裂，基部稍膨大，有白、蓝或紫等色；花萼上生有刚毛状纤毛，花萼与子房贴生，裂片5枚，花冠5裂；雄蕊着生于花筒基部，花丝基部扩大成片状，花药长棒状，柱头3-5裂，子房下位，3-5室。蒴果，带有宿存的花萼裂片。种子多数，椭圆状，平滑。花期5-6月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>繁殖方式播种繁殖:在冬、春季种植较为适宜。因此中国广东地区以初秋播种为好，播种一般在8月下旬至9月初进行，到春节即可开花。从播种到开花需6个月左右，也可根据开花时间确定播种时间。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>夏季喜凉爽、冬季温和的气候，喜光照充足环境，可耐半阴。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>对温度比较敏感，生长适温为13-18℃，发芽适温为20-22℃，冬季温度低于2℃则停止生长，茎叶开始枯黄。夏季28℃以上的高温对植株生长不利，30-35℃以上的高温，植株叶片会变黄脱落，甚至全株枯萎。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>对土壤要求不严，以含丰富腐殖质、疏松透气的沙质土壤为好。基质pH值在5.5-6.2之间为宜。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>喜干耐旱，忌水湿。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd style=\'color:yellow;text-decoration: underline\'>风铃草</a>株形粗壮，花朵钟状似风铃，花色明丽素雅，是园林中常见的冬、春季草花，也适合庭院栽培或作大中型盆栽，布置客厅、阳台等处。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000665&idx=1&sn=9c838b2e761c7cd301b3c5bc38b79f5f&chksm=4ffc159f788b9c893eaf4e59d6e3c278839583ee4c9a2f7da390b4dc432d1df68b6ed43614ca#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567520215, 0, 0, 'GvnDcC2QjGEN4AUqdM7Dyd1gPNWfr_ojbTW7gduTk5o', '0');
INSERT INTO `zbp_post` VALUES (76, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】49-《紫茉莉》', '紫茉莉（学名：Mirabilis jalapa L.）：草本，高可达1米。根肥粗，倒圆锥形，黑色或黑褐色。茎直立，圆柱形，多分枝，无毛或疏生细柔毛，节稍膨大。叶片卵形或卵状三角形，全缘，两面均无毛，脉隆起。花常数朵簇生枝端，总苞钟形', '<div style=\'text-align: center;overflow: auto;\'><img title=紫茉莉 alt=紫茉莉 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuWZ3yIeJ2IAvM7dibHsGCnvrPEyuQ2WbYz9HxqlKEXJMPUawCvzQicv7RNiagNdB5FY61E7DtdwxMJQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd style=\'color:yellow;text-decoration: underline\'>紫茉莉</a>（学名：Mirabilis jalapa L.）：属于草本植物，植株高度可达1m。根部肥粗，呈倒圆锥形，颜色为黑色或黑褐色。茎杆直立，为圆柱形，有多分枝，且无毛，疏生细柔毛，节有点膨大。叶片呈卵形或者是卵状三角形，全缘，两面都没有无毛，脉隆起。花常数朵簇生枝端，总苞钟形，长大概1cm，5裂，裂片三角状卵形；花被为紫红色、黄色、白色或杂色，高脚碟状，筒部长2至6cm，檐部直径2.5-3cm，5浅裂；花午后开放，有香气，次日午前凋萎。瘦果球形，直径5至8mm，革质，黑色，表面具皱纹；种子为胚乳白粉质。花期在6-10月，果期在8-11月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd style=\'color:yellow;text-decoration: underline\'>紫茉莉</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd style=\'color:yellow;text-decoration: underline\'>紫茉莉</a>为一年生草本植物，高可达1m。根肥粗，倒圆锥形，黑色或黑褐色。茎杆直立，圆柱形，多分枝，无毛或疏生细柔毛，节稍膨大。叶片卵形或卵状三角形，长3-15cm，宽2-9cm，顶端渐尖，基部截形或心形，全缘，两面均无毛，脉隆起；叶柄长1-4cm，上部叶几无柄。     花常数朵簇生枝端；花梗长1-2mm；总苞钟形，长约1cm，5裂，裂片三角状卵形，顶端渐尖，无毛，具脉纹，果时宿存；花被紫红色、黄色、白色或杂色，高脚碟状，筒部长2-6cm，檐部直径2.5-3厘米，5浅裂；花午后开放，有香气，次日午前凋萎；雄蕊5，花丝细长，常伸出花外，花药球形；花柱单生，线形，伸出花外，柱头头状。瘦果球形，直径5-8mm，革质，黑色，表面具皱纹；种子胚乳白粉质。花期在6-10月，果期在8-11月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd style=\'color:yellow;text-decoration: underline\'>紫茉莉</a>繁殖方式播种繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd style=\'color:yellow;text-decoration: underline\'>紫茉莉</a>可春播繁衍，也能自播繁衍，通常用种子繁殖。以小坚果为播种繁殖材料，可于4月中下旬直播于露地，发芽适温15-20℃，七八天萌发。因属深根性花卉，不宜在露地苗床上播种后移栽。如有条件可事先播入内径10厘米的筒盆，成苗后脱盆定植。 块根繁殖:北方秋末可将地上部分剪掉挖起宿根，用潮土埋在花盆里放低温室越冬。来年春季露地继续栽培，成株快，开花早。宿根植株的长势虽不如播种苗健旺，但它的块根却逐年膨大，连续3年即可长成直径10厘米左右的褐色块头，苍皮叠皱，质地坚硬。春季，可把块根下部1/3栽入浅盆，块根上部裸露，逐渐茎化适应外界环境。并因形授意，构成自然山石盆景。每年春天发出1~2个新枝开花结实。秋后剪去枝条，把块根原盆入室，保持盆土微潮收藏好。能连续培养数十年，块根越老越显得古朴苍劲，别具神韵。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd style=\'color:yellow;text-decoration: underline\'>紫茉莉</a>生长习性性喜温和而湿润的气候条件，不耐寒，冬季地上部分枯死，在江南地区地下部分可安全越冬而成为宿根草花，来年春季续发长出新的植株。露地栽培要求土层深厚、疏松肥沃的壤土，盆栽可用一般花卉培养土。在略有蔽荫处生长更佳。花朵在傍晚至清晨开放，在强光下闭合，夏季能有树荫则生长开花良好，酷暑烈日下往往有脱叶现象。喜通风良好环境，夏天有驱蚊的效果。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd style=\'color:yellow;text-decoration: underline\'>紫茉莉</a>主要价值观赏价值：中国南北各地常栽培，为观赏花卉，有时逸为野生。   药用价值：根、叶可供药用，有清热解毒、活血调经和滋补的功效。种子白粉可去面部癍痣粉刺。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000634&idx=1&sn=0af3d59d53b5bfe54ec02bd0bfd3ddd2&chksm=4ffc157c788b9c6a165c25683da6134d80890404a6173404263bb31dfcaa29a62d067ce0dd56#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567180506, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQNKiOkoqFTy85HG7Rwm0MM', '0');
INSERT INTO `zbp_post` VALUES (77, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】52-《醉蝶花》', '醉蝶花（学名：Cleome spinosa Jacq.）：一年生草本植物。花茎直立，株高40厘米-60厘米，有的品种可达1米， 其茎上长有粘汁细毛，会散发一股强烈的特殊气味；叶片为掌状复叶，小叶5枚-7枚，为矩圆状披针形；总状花序顶生', '<div style=\'text-align: center;overflow: auto;\'><img title=醉蝶花 alt=醉蝶花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvfozFb10umhNOrbwngAfhniblGarblc5oZPENx71fCoTaECRcMQBWYYzmzy0ZEknATZgl2UzNtiawQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>（学名：Cleome spinosa Jacq.）：一年生草本植物。花茎直立，株高40厘米-60厘米，有的品种可达1米， 其茎上长有粘汁细毛，会散发一股强烈的特殊气味；叶片为掌状复叶，小叶5枚-7枚，为矩圆状披针形；总状花序顶生，花由底部向上次第开放，花瓣披针形向外反卷，花苞红色，花瓣呈玫瑰红色或白色，雄蕊特长；蒴果圆柱形，种子浅褐色。花期在6月-9月，其花茎长而壮实，花朵盛开时，总状花序形成一个丰满的花球，朵朵小花犹如翩翩起舞的蝴蝶，非常美观。中国无野生，各大城市常见栽培。原产热带美洲，全球热带至温带栽培以供观赏，也是一种优良的蜜源植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>形态特征一年生强壮草本，高1-1.5米，全株被粘质腺毛，有特殊臭味，有托叶刺，刺长达4毫米，尖利，外弯。叶为具5-7小叶的掌状复叶，小叶草质，椭圆状披针形或倒披针形，中央小叶盛大，长6-8厘米，宽1.5-2.5厘米，最外侧的最小，长约2厘米，宽约5毫米，基部锲形，狭延成小叶柄，与叶柄相联接处稍呈蹼状，顶端渐狭或急尖，有短尖头，两面被毛，背面中脉有时也在侧脉上常有刺，侧脉10-15对；叶柄长2-8厘米,常有淡黄色皮刺。总状花序长达40厘米，密被粘质腺毛；苞片单1，叶状，卵状长圆形，长5-20毫米，无柄或近无柄，基部多少心形；花蕾圆筒形，长约2.5厘米，直径4毫米，无毛；花梗长2-3厘米，被短腺毛，单生于苞片腋内；萼片4，长的6毫米，长圆状椭圆形，顶端渐尖，外被腺毛；花瓣粉红色，少见白色，在芽中时覆瓦状排列，无毛，爪长5-12毫米，瓣片倒卵伏匙形，长10-15毫米，宽4-6毫米，顶端圆形，基部渐狭；雄蕊6，花丝长3.5-4厘米，花药线形，长7-8毫米；雌雄蕊柄长1-3毫米；雌蕊柄长4厘米，果时略有增长；子房线柱形，长3-4毫米，无毛；几无花柱，柱头头状。     果圆柱形，长5.5-6.5厘米，中部直径约4毫米，两端梢钝，表面近平坦或徽呈念珠状，有细而密且不甚清晰的脉纹。种子直径约2毫米，表面近平滑或有小疣状突起，不具假种皮。花期夏末秋初，果期夏末秋初。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>繁殖方式播种繁殖:对播种用的基质进行消毒，最好的方法就是把它放到锅里炒热，什么病虫都能烫死。用25-30摄氏度温水把种子浸泡3-10个小时，直到种子吸水并膨胀起来。对于用手或其它工具难以夹起来的细小的种子，可以把牙签的一端用水沾湿，把种子一粒一粒地粘放在基质的表面上，覆盖基质1公分厚，然后把播种的花盆放入水中，水的深度为花盆高度的1/2-2/3，让水慢慢地浸上来（这个方法称为“盆浸法”）。 扦插繁殖:插穗生根的最适温度为18℃-25℃，低于18℃，插穗生根困难、缓慢；高于25℃，插穗的剪口容易受到病菌侵染而腐烂，并且温度越高，腐烂的比例越大。扦插后遇到低温时，保温的措施主要是用薄膜把用来扦插的花盆或容器包起来；扦插后温度太高温时，降温的措施主要是给插穗遮荫，要遮去阳光的50-80%，同时，给插穗进行喷雾，每天3-5次，晴天温度较高喷的次数也较多，阴雨天温度较低温度较大，喷的次数则少或不喷。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>生长习性适应性强。性喜高温，较耐暑热，忌寒冷。喜阳光充足地，半遮荫地亦能生长良好。对土壤要求不苛刻，水肥充足的肥沃地，植株高大；一般肥力中等的土壤，也能生长良好；沙壤土或带粘重的土壤或碱性土生长不良。喜湿润土壤，亦较能耐干旱，忌积水。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>的花瓣轻盈飘逸，盛开时似蝴蝶飞舞，颇为有趣，可在夏秋季节布置花坛、花境，也可进行矮化栽培，将其作为盆栽观赏。在园林应用中，可根据其能耐半阴的特性，种在林下或建筑阴面观赏。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>不仅是优质蜜源，全草还可入药。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>全草：辛、涩，平。有小毒。祛风散寒，杀虫止痒。相传，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd style=\'color:yellow;text-decoration: underline\'>醉蝶花</a>开花有一个特性，就是总是在某个入夜的黄昏盛开，然后在第二天白天就凋谢了，生如蜉蝣。所以它的性格是飘渺而神秘，就像是一场短暂而绚烂的爱情，给你虚幻无常，沉沉浮浮，飘零无着之感。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000664&idx=1&sn=af3143873028e792fd4a08ab37d375e2&chksm=4ffc159e788b9c88b053e627326851abc669e9326936eb756c958e408d0edd24e57b89a871ac#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567435794, 0, 0, 'GvnDcC2QjGEN4AUqdM7DybxZXAKwNH6xRVPiUAj3oG0', '0');
INSERT INTO `zbp_post` VALUES (78, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】50-《仙人掌》', '仙人掌（学名：Opuntia stricta (Haw.) Haw. var. dillenii (Ker-Gawl.) Benson ）是仙人掌科缩刺仙人掌的变种。丛生肉质灌木，高1.5-3米。上部分枝宽倒卵形、倒卵状椭圆形或近圆形', '<div style=\'text-align: center;overflow: auto;\'><img title=仙人掌 alt=仙人掌 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcs5r80icuGyfbUvRRmBLFmLqNYNeFB4NxhXtpajibHciaZ9icVOGaMMXticmkywzia1DMLVSLWl4K4bWxsQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>（学名：Opuntia stricta (Haw.) Haw. var. dillenii (Ker-Gawl.) Benson ）是</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>的一种变种。是丛生肉质灌木，高1.5~3m。上部分枝宽呈倒卵形、倒卵状椭圆形或者是近圆形，绿色至蓝绿色，且无毛；刺黄色，有淡褐色横纹，坚硬；倒刺直立。叶钻形，绿色，早落。花辐状；花托倒卵形，基部渐狭，绿色；萼状花被黄色，具绿色中肋；花丝淡黄色；花药黄色；花柱淡黄色；柱头黄白色。浆果倒卵球形，顶端凹陷，表面平滑无毛，紫红色，倒刺刚毛和钻形刺。种子多数，扁圆形，边缘稍不规则，无毛，淡黄褐色。花期6至10（-12)月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>形态特征丛生肉质灌木，高（1-）1.5-3m。上部分枝宽倒卵形、倒卵状椭圆形或近圆形，长10-35（-40）cm，宽7.5-20（-25）cm，厚达1.2-2cm，先端圆形，边缘通常不规则波状，基部楔形或渐狭，绿色至蓝绿色，无毛；小窠疏生，直径0.2-0.9cm，明显突出，成长后刺常增粗并增多，每小窠具（1-）3-10（-20）根刺，密生短绵毛和倒刺刚毛；刺黄色，有淡褐色横纹，粗钻形，多少开展并内弯，基部扁，坚硬，长1.2-4（-6）cm，宽1-1.5mm；倒刺刚毛暗褐色，长2-5mm，直立，多少宿存；短绵毛灰色，短于倒刺刚毛，宿存。叶钻形，长4-6mm，绿色，早落。     花辐状，直径5-6.5cm；花托倒卵形，长3.3-3.5cm，直径1.7-2.2cm，顶端截形并凹陷，基部渐狭，绿色，疏生突出的小窠，小窠具短绵毛、倒刺刚毛和钻形刺；萼状花被片宽倒卵形至狭倒卵形，长10-25mm，宽6-12mm，先端急尖或圆形，具小尖头，黄色，具绿色中肋；瓣状花被片倒卵形或匙状倒卵形，长25-30mm，宽12-23mm，先端圆形、截形或微凹，边缘全缘或浅啮蚀状；花丝淡黄色，长9-11mm；花药长约1.5毫米，黄色；花柱长11-18mm，直径1.5-2mm，淡黄色；柱头5，长4.5-5mm，黄白色。浆果倒卵球形，顶端凹陷，基部多少狭缩成柄状，长4-6cm，直径2.5-4cm，表面平滑无毛，紫红色，每侧具5-10个突起的小窠，小窠具短绵毛、倒刺刚毛和钻形刺。种子多数扁圆形，长4-6mm，宽4-4.5mm，厚约2mm，边缘稍不规则，无毛，淡黄褐色。花期6-10（-12）月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>繁殖方式分株繁殖:多数</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>类花卉极易分生子苗或子球，将其子苗或子球拔下另行栽植，极易成活。对于有些不易分生子球的品种，可用切断法强制它繁殖子球。即将母株茎体上部的部分切除（切除部分可作嫁接或扦插的插穗），使其失去上部的生长点而不能再向上生长。加强水肥管理，就能长出新的子球。 扦插繁殖:家庭栽培</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>，大多采用扦插法繁殖，成活率极高。扦插时间在春、夏、秋皆可。扦插应选不老不嫩的茎块，把茎块从母株上切下后，先放在半阴通风处晾5-7天，等切口干燥，皮层略向内收缩，生成一层薄膜时，再行扦插，插穗长度在10厘米，插深3厘米。插入经消毒的砂土里，几天后浇水，浇水时稍湿润即可，以防止插穗腐烂，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>一般扦插20天后生根。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>生长习性野生品种生长于阴森潮湿地（坟墓或乡间小路边），其着生地为红壤，因此耐寒性强，喜阴，能忍受的高温极限为日平均温度24℃；喜湿润，也耐干旱，习惯于偏酸性土壤，以疏松、肥沃的腐殖质土最好。有夏季休眠习性，极易繁殖，通常一颗石蒜极易发展成一片石蒜。极易栽培，一般在开花后分球栽培为宜，栽培时施以足够基肥，以土盖没球茎，浇透水即可。石蒜的黑色花球极易养活，保持湿润一年都不会死。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>主要价值食用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>果实清香甜美、鲜嫩多汁，一般以鲜食为主；墨西哥等地也用鲜果加工成罐头或酒精饮料，也可加蜂蜜、鲜奶冰块打成果汁，而做冰琪淋风味更佳。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>在中国作为药用植物首载于清代赵学敏所著的《本草纲目拾遗》，该书记载：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>味淡，性寒具有行气活血、清热解毒、消肿止痛、健脾止泻、安神利尿的功效，内服外用治疗多种疾病。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>主要治疗疔疮肿毒，外还可以治疗胃痛、痞块腹痛、急性痢疾、肠痔泻血、哮喘等症。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>是世界上最柔弱的东西，她娇嫩如水，稍一触碰便失去了生命。上帝不忍，在她的心上加上了一套盔甲，坚硬如铁，上面还带有伤人的钢刺。从此，再也没有人能看到</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>之心了，凡是接近她的生物都会鲜血淋漓。很久之后，有一位勇者要铲除这恶物，剑出刀落，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>变成了两半，从中却是绿色的液体。原来，那是被封存的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd style=\'color:yellow;text-decoration: underline\'>仙人掌</a>之心，由于无人了解其中的寂寞，化成了滴滴泪珠。所以</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000635&idx=1&sn=35ec780c4901277dae7c9e4cf7edc3d2&chksm=4ffc157d788b9c6b399878bc8d19d051af4ce83b49c5510489d4a41d28fb80f6bc2e5fb267f6#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567264123, 0, 0, 'GvnDcC2QjGEN4AUqdM7DydIbldubfsTjSYj2BWngt4I', '0');
INSERT INTO `zbp_post` VALUES (79, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】47-《茉莉》', '茉莉花，别名：茉莉，拉丁文名：Jasminum sambac (L.) Ait，木犀科、素馨属直立或攀援灌木，高达3米。小枝圆柱形或稍压扁状，有时中空，疏被柔毛。叶对生，单叶，叶片纸质，圆形、椭圆形、卵状椭圆形或倒卵形', '<div style=\'text-align: center;overflow: auto;\'><img title=茉莉 alt=茉莉 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctK7Ce48Qva7u1icHzVlvUryicSe8mRKs1TXlUwbqMwRTGwibOpIcF1wOkQs6MmfLFDwibbl6jfb4sO5A/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>，拉丁文名：Jasminum sambac (L.) Ait，木犀科、素馨属直立或攀援灌木，高达3米。小枝圆柱形或稍压扁状，有时中空，疏被柔毛。叶对生，单叶，叶片纸质，圆形、椭圆形、卵状椭圆形或倒卵形，两端圆或钝，基部有时微心形，在上面稍凹入或凹起，下面凸起，细脉在两面常明显，微凸起，除下面脉腋间常具簇毛外，其余无毛；裂片长圆形至近圆形，先端圆或钝。果球形，呈紫黑色。花期5-8月，果期7-9月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>形态特征直立或攀援灌木，高达3米。小枝圆柱形或稍压扁状，有时中空，疏被柔毛。叶对生，单叶，叶片纸质，圆形、椭圆形、卵状椭圆形或倒卵形，长4-12.5cm，宽2-7.5cm，两端圆或钝，基部有时微心形，侧脉4-6对，在上面稍凹入或凹起，下面凸起，细脉在两面常明显，微凸起，除下面脉腋间常具簇毛外，其余无毛；叶柄长2-6mm，被短柔毛，具有关节。聚伞花序顶生，通常有花3朵，有时单花或多达5朵；花序梗长1-4.5cm，被短柔毛；苞片微小，锥形，长4-8mm；花梗长0.3-2cm；花极芳香；花萼无毛或疏被短柔毛，裂片线形，长5-7mm；花冠白色，花冠管长0.7-1.5cm，裂片长圆形至近圆形，宽5-9mm，先端圆或钝。果球形，径约1cm，呈紫黑色。花期5-8月，果期7-9月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>繁殖方式压条繁殖:选用较长的枝条，在节下部轻轻刻伤，埋入盛沙泥的小盆，经常保湿，20-30天开始生根，2个月后可与母株割离成苗，另行栽植。 扦插繁殖:于4-10月进行，选取成熟的1年生枝条，剪成带有两个节以上的插穗，去除下部叶片，插在泥沙各半的插床，覆盖塑料薄膜，保持较高空气湿度，约经40-60天生根。    </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>性喜温暖湿润，在通风良好、半阴的环境生长最好。土壤以含有大量腐殖质的微酸性砂质土壤为最适合。大多数品种畏寒、畏旱，不耐霜冻、湿涝和碱土。冬季气温低于3℃时，枝叶易遭受冻害，如持续时间长就会死亡。而落叶藤本类就是很耐寒耐旱的了。盆栽</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>花：盛夏季每天要早、晚浇水，如空气干燥，需补充喷水；冬季休眠期，要控制浇水量，如盆土过湿，会引起烂根或落叶。生长期间需每周施稀薄饼肥一次。春季换盆后，要经常摘心整形，盛花期后，要重剪，以利萌发新枝，使植株整齐健壮，开花旺盛。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>主要价值观赏价值：室内栽培，常绿小灌木类的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>花叶色翠绿，花色洁白，香味浓厚，为常见庭园及盆栽观赏芳香花卉。经济价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>油的身价很高，相当于黄金的价格。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>花还可薰制茶叶，或蒸取汁液，可代替蔷薇露，地处江南的苏州、南京、杭州、金华等地长期以来都作为熏茶香料进行生产。药用价值：根（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>根）：苦，温。有毒。麻醉，止痛。用于跌损筋骨，龋齿，头痛，失眠。叶（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>叶）：辛，凉。清热解表。用于外感发热，腹胀泻泻。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd style=\'color:yellow;text-decoration: underline\'>茉莉</a>花外之语好一朵美丽的</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000610&idx=1&sn=8ca9e970d78dbe99e2172262aacd71f3&chksm=4ffc1564788b9c7281484fc5e01e2a823188a85af573e7543fc2a1bd325955ceffef01963395#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567006487, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyfXiLsgkRsAlt1nyMgz9Fy4', '0');
INSERT INTO `zbp_post` VALUES (80, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】48-《凤仙花》', '喜欢就点击蓝字关注我快点关注我们吧大千世界，植物王国，每日一花，让我们一起来揭开其神奇的面纱！凤仙花凤仙花', '<div style=\'text-align: center;overflow: auto;\'><img title=凤仙花 alt=凤仙花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuVjeZuasCALaeQfr6HnLymJaEdK8FkUNl6JJLeWtSX5zq40VhNy06NpUzQJsZL31IO5mVJUK4R8A/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>，别名：指甲花，急性子，凤仙透骨草，拉丁文名：Impatiens balsamina L.，为双子叶植物纲、牻牛儿苗目、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>属一年生草本花卉，[1]全株分根、茎、叶子、花、果实和种子六个部分。因其花头、翅、尾、足俱翘然如凤状，故又名金凤花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>的身体有根、茎、叶子、花、果实和种子六个部分。花颜色多样，有粉红，大红，紫色，粉紫等多种颜色，花瓣或者叶子捣碎，用树叶包在指甲上，能染上鲜艳的红色，非常漂亮，很受女孩子的喜爱。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>形态特征一年生草本，高60-100厘米。茎粗壮，肉质，直立，不分枝或有分枝，无毛或幼时被疏柔毛，基部直径可达8毫米，具多数纤维状根，下部节常膨大。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>繁殖方式播种繁殖:繁殖培育</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>用种子繁殖。3～9进行播种，以4月播种最为适宜，这样6月上、中旬即可开花，花期可保持两个多月。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>性喜阳光，怕湿，耐热不耐寒。喜向阳的地势和疏松肥沃的土壤，在较贫瘠的土壤中也可生长。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>生存力强，适应性好，一般很少有病虫害。如果气温高、湿度大，出现白粉病，可用50%基硫菌灵可湿性粉800倍液喷洒防治。如发生叶斑病，可用50%多菌灵可湿性粉500倍液防治。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>主要虫害是红天蛾，其幼虫会啃食凤仙叶片。如发现有此虫害，可人工捕捉灭除。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>主要价值观赏价值：我国各地庭园广泛栽培，为常见的观赏花卉。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>如鹤顶、似彩凤，姿态优美，妩媚悦人。香艳的红色凤仙和娇嫩的碧色凤仙都是早晨开放，是欣赏</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>因其花色、品种极为丰富，是美化花坛、花境的常用材料，可丛植、群植和盆栽，也可作切花水养。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>是著名中药。花入药，可活血消胀，治跌打损伤。花外搽可治鹅掌疯，又能除狐臭；种子煎膏外搽，可治麻木酸痛。活血通经，祛风除湿；活血止痛；解毒杀虫。主风湿肢体痿废；腰胁疼痛；妇女闭腹痛；产后瘀血未尽；跌打损伤；骨折；痈疽疮毒；毒蛇咬伤；白带；鹅掌风；灰指甲。活血通经，祛风止痛，外用解毒。用于闭经，跌打损伤，瘀血肿痛，风湿性关节炎，痈疖疔疮，蛇咬伤，手癣。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd style=\'color:yellow;text-decoration: underline\'>凤仙花</a>花 语别碰我，源于它的英文别名Touch me not，美语为Don\'t touch me，因为它的籽荚只要轻轻一碰就会弹射很多出籽儿来。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000624&idx=1&sn=3f2902932bf3159fe7cc224a66d917cd&chksm=4ffc1576788b9c60ac2674ab17f2e804164b510d4ac3d9d2d5ee13d271c481051764da12f864#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567089491, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyXiyRh3bbwsgpZS6mWt--5o', '0');
INSERT INTO `zbp_post` VALUES (81, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】51-《紫花凤梨》', '紫花凤梨（学名：Tillandsia cyanea Linden ex K. Koch）凤梨科铁兰属观赏植物。体型不大，株高不及30厘米。叶窄线形，长20厘米－30厘米，宽1－1.5厘米，簇生，浓绿色，质硬面是凹弧状', '<div style=\'text-align: center;overflow: auto;\'><img title=紫花凤梨 alt=紫花凤梨 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsz2j2dQnLOyQa6oeK3v8JibWic5OEXQutVfCkM4IUpGSOUNe2ticgeOl4OkFwibj3SEdiagd7NI1VNppQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000655&idx=1&sn=a0a565cef3398801b2611ef1913cfc8d&chksm=4ffc1589788b9c9faff1bda9ba8bf677c6fadfe1f92395df891e72b4a66981cb5795463e2dea#rd style=\'color:yellow;text-decoration: underline\'>紫花凤梨</a>（学名：Tillandsia cyanea Linden ex K. Koch）凤梨科铁兰属观赏植物。体型不大，株高不及30厘米。叶窄线形，长20厘米－30厘米，宽1－1.5厘米，簇生，浓绿色，质硬面是凹弧状，中部带有紫褐色斑晕背绿褐色。花序梗自叶丛中抽生，长约20厘米，顶端12－15厘米处扁平，形成穗状花序，宽4－4.5厘米，由粉红色近淡紫色的苞片对生组；青紫色小花由苞片内开出，约20朵之多，花瓣卵形，3片；冠径约3厘米，形似蝴蝶。苞片可观赏达数月之久。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000655&idx=1&sn=a0a565cef3398801b2611ef1913cfc8d&chksm=4ffc1589788b9c9faff1bda9ba8bf677c6fadfe1f92395df891e72b4a66981cb5795463e2dea#rd style=\'color:yellow;text-decoration: underline\'>紫花凤梨</a>形态特征体型不大，株高不及30厘米。叶窄线形，长20厘米－30厘米，宽1－1.5厘米，簇生，浓绿色，质硬面是凹弧状，中部带有紫褐色斑晕背绿褐色。花序梗自叶丛中抽生，长约20厘米，顶端12－15厘米处扁平，形成穗状花序，宽4－4.5厘米，由粉红色近淡紫色的苞片对生组；青紫色小花由苞片内开出，约20朵之多，花瓣卵形，3片；冠径约3厘米，形似蝴蝶。苞片可观赏达数月之久。        </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000655&idx=1&sn=a0a565cef3398801b2611ef1913cfc8d&chksm=4ffc1589788b9c9faff1bda9ba8bf677c6fadfe1f92395df891e72b4a66981cb5795463e2dea#rd style=\'color:yellow;text-decoration: underline\'>紫花凤梨</a>繁殖方式播种繁殖:观赏凤梨在自然状态下很难获得种子，就是通过人工授粉等方法获得种子，其后代的性状也会发生分离，不能保持母株的优良性状，故播种繁殖只在原生种种质保存和培育新品种时采用。大多数观赏凤梨的种子在脱离母体后很快会失去活力，应即采即播。凤梨从开花至种子成熟一般需要3-4个月的时间。凤梨的种子很小，一个果实可产生数十粒种子，1株可产生上千粒种子。凤梨的播种方法可采用室内盆播或育苗盘播种。播种基质可采用河沙、珍珠岩和泥炭土混合，播种前必须高温消毒。将种子散播于基质表面后轻压一下，不需覆土，盖上塑料薄膜或玻璃保湿即可。在发芽适温24℃-26℃的条件下，约7-14天发芽，实生苗具3-4片真叶时可移植于4-5厘米的花盆中。凤梨的播种苗需培养3-4年后中心叶才能转色并开花供观赏。分株繁殖:观赏凤梨谢花后，基部叶腋处会产生多个吸芽。通常以4月-6月为分株的适宜时期。待吸芽长至10厘米左右、有3-5个叶时，先把整株从盆中脱出，除去一些盆土，一手抓住母株，另一只手的拇指与食指紧夹吸芽基部，斜下用力就可把吸芽掰下来。伤口用杀菌剂消毒后稍晾干，扦插于珍珠岩、粗沙中。保持基质和空气湿润，适当遮阳，过1-2个月有新根长出后，可转入正常管理。扦插繁殖:观赏凤梨小规模生产和家庭栽培，一般采用蘖芽扦插。观赏凤梨原株开花前后基部叶腋处产生多个蘖芽，待蘖芽长到10厘米左右，有3-5片叶时，用利刀在贴近母株的部位连短缩茎切下，伤口用杀菌剂消毒后稍晾干，蘸浓度为300-500毫克/千克的萘乙酸，扦插于珍珠岩、粗沙或培养土中，保持基质和空气湿润，并适当遮荫，1-2个月后即有新根长出，可转入正常管理。注意蘖芽太小时扦插不易生根，极易腐烂；太大时分株，消耗营养太多，降低繁殖系数。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000655&idx=1&sn=a0a565cef3398801b2611ef1913cfc8d&chksm=4ffc1589788b9c9faff1bda9ba8bf677c6fadfe1f92395df891e72b4a66981cb5795463e2dea#rd style=\'color:yellow;text-decoration: underline\'>紫花凤梨</a>生长习性观赏凤梨性喜温暖湿润、阳光充足的环境，只有在明亮的光照条件下，才能正常开花并且获得最美的叶片，但在夏季仍需防止正午阳光的直射。适宜的生长温度为夏季20-30 ℃，冬季15-18 ℃，低于10 ℃则难以生长，夜间最低保持5 ℃，土壤以中性或微酸性砂质壤土混合腐叶土或泥炭土。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000655&idx=1&sn=a0a565cef3398801b2611ef1913cfc8d&chksm=4ffc1589788b9c9faff1bda9ba8bf677c6fadfe1f92395df891e72b4a66981cb5795463e2dea#rd style=\'color:yellow;text-decoration: underline\'>紫花凤梨</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000655&idx=1&sn=a0a565cef3398801b2611ef1913cfc8d&chksm=4ffc1589788b9c9faff1bda9ba8bf677c6fadfe1f92395df891e72b4a66981cb5795463e2dea#rd style=\'color:yellow;text-decoration: underline\'>紫花凤梨</a>小巧玲珑，秀丽美观，花期甚长，又能耐阴，是家庭养护、美化居室的极好材料。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000655&idx=1&sn=a0a565cef3398801b2611ef1913cfc8d&chksm=4ffc1589788b9c9faff1bda9ba8bf677c6fadfe1f92395df891e72b4a66981cb5795463e2dea#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567348309, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyRbQtZEoOfd9jrJx_UipJB8', '0');
INSERT INTO `zbp_post` VALUES (82, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】46-《狼毒》', '狼毒(Stellerachamaejasme)，又称断肠草、拔萝卜、燕子花、馒头花等，为瑞香科狼毒属植物，多年生草本，是我国草地重要的有毒植物。瑞香狼毒生长在海拔2600～4200 m的干旱、半干旱退化草地上', '<div style=\'text-align: center;overflow: auto;\'><img title=狼毒 alt=狼毒 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcv7IGfmPpVbPkCVbPVxokHFnFQpeL05tYVFQtibXfDwoveJgbthJ7SMXLJPDyWJ5XluWdX8KwibVhuA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>(Stellerachamaejasme)，又称断肠草、拔萝卜、燕子花、馒头花等，为瑞香科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>属植物，多年生草本，是我国草地重要的有毒植物。瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>生长在海拔2600～4200 m的干旱、半干旱退化草地上，主要分布于我国的甘肃、宁夏、西藏、四川、内蒙古等地区，在俄罗斯、蒙古、尼泊尔也均有分布。重度退化的草原上，瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>形态特征多年生草本，除生殖器官外无毛。根圆柱状，肉质，常分枝，长20-30厘米，直径4-6厘米。茎单一不分枝，高15-45厘米，直径5-7毫米。叶互生，于茎下部鳞片状，呈卵状长圆形，长1-2厘米，宽4-6毫米，向上渐大，逐渐过渡到正常茎生叶；茎生叶长圆形，长4-6.5厘米，宽1-2厘米，先端圆或尖，基部近平截，侧脉羽状不明显；无叶柄；总苞叶同茎生叶，常5枚；伞幅5，长4-6厘米；次级总苞叶常3枚，卵形，长约4厘米，宽约2厘米；苞叶2枚，三角状卵形，长与宽均约2厘米，先端尖，基部近平截。序单生二歧分枝的顶端，无柄；总苞钟状，具白色柔毛，高约4毫米，直径4-5毫米，边缘4裂，裂片圆形，具白色柔毛；腺体4,半圆形，淡褐色。雄花多枚，伸出总苞之外；雌花1枚，子房柄长3-5毫米；子房密被白色长柔毛；花柱3,中部以下合生；柱头不分裂，中部微凹。蒴果卵球状，长约6 毫米，直径6-7毫米，被白色长柔毛；果柄长达5毫米；花柱宿存；成熟时分裂为3个分果爿。种子扁球状，长与直径均约4毫米，灰褐色，腹面条纹不清；种阜无柄。花果期5-7月。         </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>繁殖方式瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>靠种子扩散进行下一代的繁殖拓展。种子发芽成苗后，瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>的茎基部在每年9、10月就会形成膨大凸起的越冬芽，进入休眠状态；翌年越冬芽长出地面后变红，而后变绿，迅速生长。瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>的越冬芽为混合芽，同时含有枝芽和花芽，每个越冬芽在花芽分化过程中形成一个花序。1龄植株当年发育成茎叶，但也有报道其能开花但不结实；2、3龄植株处于生殖生长阶段，能开花但结实量少，而后结实量逐年增多。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>生长习性生于海拔100-600米的草原、干燥丘陵坡地、多石砾干山坡及阳坡稀疏的松林下。在自然状态下，瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>是靠种子进行繁殖的植物，结实率较高，因此研究瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>种子的形态结构、种子休眠、种子发芽及种子储藏等特性，对于揭示群落内瑞香</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd style=\'color:yellow;text-decoration: underline\'>狼毒</a>种群更新机制具有非常重要的意义，也为瑞香</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000604&idx=1&sn=cfc14db410d06161cfa73bccd06144dc&chksm=4ffc155a788b9c4cb6149928ff5c11be01c7694666055d5cca780ccfae4752412e1393b4a0bc#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566918706, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyWlnvq3kK0g-d_gd0NJb56k', '0');
INSERT INTO `zbp_post` VALUES (83, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】45-《彼岸花》', '红色彼岸花（Lycorisradiata）、又称“舍子花”。是多年生草本植物，地下有球形鳞茎，外包暗褐色膜质鳞被。叶带状较窄，色深绿，自基部抽生，发于秋天花谢后。花期为夏末秋初，约从7月至9月。花茎长30－60厘米，通常4－6朵排成伞形', '<div style=\'text-align: center;overflow: auto;\'><img title=彼岸花 alt=彼岸花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctz8eCib7FAWniaWElgI1W9IZiburQE1Hsoou4QyFTXSibZpsJpJJY9T4eE8FXPH822Wft4oI2EFjS7Lw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd style=\'color:yellow;text-decoration: underline\'>彼岸花</a>（Lycorisradiata）、又称“舍子花”。是多年生草本植物，地下有球形鳞茎，外包暗褐色膜质鳞被。叶带状较窄，色深绿，自基部抽生，发于秋天花谢后。花期为夏末秋初，约从7月至9月。花茎长30－60厘米，通常4－6朵排成伞形，着生在花茎顶端，花瓣倒披针形，花被红色（亦有白花品种），向后开展卷曲，边缘呈皱波状，花被管极短；雄蕊和花柱突出，花型较小，周长在6厘米以上。花开不见叶， 叶在不见花， 花叶两不见。这种花经常长在野外的石缝里、坟头上，所以有人说它是“黄泉路上的花”。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd style=\'color:yellow;text-decoration: underline\'>彼岸花</a>形态特征为石蒜科多年生草本花卉。花如龙爪。具鳞茎，形如洋葱头；叶丛生，细长尖端，状似蒜叶，肉质、带形、青绿色具白粉；花萼单生，顶生伞形花序。夏秋之交，花茎破土而出，伞形花序顶生，有花5至7朵，红艳奇特（除红色外还有白色品种），花瓣反卷如龙爪。先开花后长叶，冬天叶子不落，夏天叶落休眠。由于花和叶子不能见面的特性，石蒜又被称为“无情无义”的花。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd style=\'color:yellow;text-decoration: underline\'>彼岸花</a>没有黄色的，黄色的是黄花石蒜，学名Lycoris aurea。花期也不一样：其原本繁茂的丛丛碧叶，每逢初夏时节，会毫无声息地凋零，消失得无影无踪；仲夏，其花茎又在忽然之间拔地而起，绽放出风韵独具、金色灿烂忽地笑的花朵来，给人以愉悦和惊喜，其名也因此而得。它的寓意也好一些，是幸福之意。和</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd style=\'color:yellow;text-decoration: underline\'>彼岸花</a>只是长得非常像，毕竟他们是同科植物。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd style=\'color:yellow;text-decoration: underline\'>彼岸花</a>繁殖方式分球繁殖:极易栽培，一般在开花后分球栽培为宜，栽培时施以足够基肥，以土盖没球茎，浇透水即可。石蒜的黑色花球极易养活，保持湿润一年都不会死。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd style=\'color:yellow;text-decoration: underline\'>彼岸花</a>生长习性野生品种生长于阴森潮湿地（坟墓或乡间小路边），其着生地为红壤，因此耐寒性强，喜阴，能忍受的高温极限为日平均温度24℃；喜湿润，也耐干旱，习惯于偏酸性土壤，以疏松、肥沃的腐殖质土最好。有夏季休眠习性，极易繁殖，通常一颗石蒜极易发展成一片石蒜。极易栽培，一般在开花后分球栽培为宜，栽培时施以足够基肥，以土盖没球茎，浇透水即可。石蒜的黑色花球极易养活，保持湿润一年都不会死。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd style=\'color:yellow;text-decoration: underline\'>彼岸花</a>主要价值观赏价值：红花石蒜是东亚常见的园林观赏植物，冬赏其叶，秋赏其花。此花多用于别墅花园种植，一般成百上千只一起种，秋季会大批开放，景观效果极佳。此外，由于石蒜的根茎与水仙十分相似，都是球状茎。有些黑心花贩会用它冒充水仙来卖钱，所以买花时要仔细甄别，以防上当。药用价值：药用鳞茎：含有多种生物碱，如石蒜碱、石蒜胺碱、加兰他敏等。具有祛风消肿、解毒抗癌作用。用于治疗胃癌、食管癌、肝癌，并试治肺癌、卵巢癌、宫颈癌、淋巴癌。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000590&idx=1&sn=d05beec46ee7070e71b16c1a8227f040&chksm=4ffc1548788b9c5e3ba08dedcacb40a26010eb05f8f11b8ec37074ac2e5c01ac711f746c9caa#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566830100, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyejJAO3sqRTM4sipTHNaKs0', '0');
INSERT INTO `zbp_post` VALUES (84, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】44-《三色堇》', '三色堇（学名：Viola tricolor L.）是堇菜科堇菜属的二年或多年生草本植物。基生叶叶片长卵形或披针形，具长柄，茎生叶叶片卵形、长圆形或长圆披针形，先端圆或钝，边缘具稀疏的圆齿或钝锯齿。三色菫以露天栽种为宜。', '<div style=\'text-align: center;overflow: auto;\'><img title=三色堇 alt=三色堇 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuL2AgfYIsFFg1iavP9Uwl1BnrQ9lMiav7yz1gcd5E5Cw5jXuMlXafStiaryJLWkn5fsZWeEcFI9DMgA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>（学名：Viola tricolor L.）是堇菜科堇菜属的二年或多年生草本植物。基生叶叶片长卵形或披针形，具长柄，茎生叶叶片卵形、长圆形或长圆披针形，先端圆或钝，边缘具稀疏的圆齿或钝锯齿。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>     形态特征茎高10-40cm，全株光滑。地上茎较粗，直立或稍倾斜，有棱，单一或多分枝。基生叶叶片长卵形或披针形，具长柄；茎生叶叶片卵形、长圆状圆形或长圆状披针形，先端圆或钝，基部圆，边缘具稀疏的圆齿或钝锯齿，上部叶叶柄较长，下部者较短；托叶大型，叶状，羽状深裂，长1-4cm。:      花大，直径约3.5-6cm，每个茎上有3-10朵，通常每花有紫、白、黄三色；花梗稍粗，单生叶腋，上部具2枚对生的小苞片；小苞片极小，卵状三角形；萼片绿色，长圆状披针形，长1.2-2.2cm，宽3-5mm，先端尖，边缘狭膜质，基部附属物发达，长3-6mm，边缘不整齐；上方花瓣深紫堇色，侧方及下方花瓣均为三色，有紫色条纹，侧方花瓣里面基部密被须毛，下方花瓣距较细，长5-8mm；子房无毛，花柱短，基部明显膝曲，柱头膨大，呈球状，前方具较大的柱头孔。蒴果椭圆形，长8-12mm。无毛。染色体数目不定，2n=20，26，42，46。:           </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>:繁殖方式 播种繁殖:播种宜采用较为疏松的人工介质可采用床播、箱播，有条件的可穴盘育苗，介质要求pH值为5.5~5.8，经消毒处理，播种后保持介质温度18℃~22℃，避光遮阴，5~7d陆续出苗。5~7d胚根展出，播种后必须始终保持介质湿润，需覆盖粗蛭石或中沙，覆盖以不见种子为度</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>种子发芽经常会很不整齐，前后可相差l周时间出苗，在这段时间内充分保持土壤介质湿润。  扦插繁殖:以5-6月进行，剪取植株基部萌发的枝条，插入泥炭中，保持空气湿润，插后15-20天生根，成活率高。      分株繁殖:常在花后进行，将带不定根的侧枝或根茎处萌发的带根新枝剪下，可矗接盆栽，并放半阴处恢复。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>生长习性[_^strong:906a019b!]较耐寒，喜凉爽，喜阳光，在昼温15-25℃、夜温3-5℃的条件下发育良好。忌高温和积水，耐寒抗霜，昼温若连续在30℃以上，则花芽消失，或不形成花瓣；昼温持续25℃时，只开花不结实，即使结实，种子也发育不良。根系可耐-15℃低温，但低于-5℃叶片受冻边缘变黄。日照长短比光照强度对开花的影响大，日照不良，开花不佳。喜肥沃、排水良好、富含有机质的中性壤土或粘壤土，pH为5.4~7.4。为多年生花卉，常作二年生栽培。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>在庭院布置上常地栽于花坛上，可作毛毡花坛、花丛花坛，成片、成线、成圆镶边栽植都很相宜。还适宜布置花境、草坪边缘；不同的品种与其他花卉配合栽种能形成独特的早春景观；另外也可盆栽或布置阳台、窗台、台阶或点缀居室、书房、客堂颇具新意，饶有雅趣。药用价值：全草：清热解毒，散瘀，止咳，利尿。用于咳嗽，小儿瘰疬，无名肿毒。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>花外之语。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd style=\'color:yellow;text-decoration: underline\'>三色堇</a>，名字已经俘获人心，相貌亦是没让人失望。当你路过公路边时候，留意下，你就会发现她，记住她叫</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000578&idx=1&sn=a1f92f6db37ea048d087eeca051e1b43&chksm=4ffc1544788b9c52030b325dcdf01af17b1750fa96de951f844821048c1ec351fd274fde9ab0#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1566735424, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyWw9JAHwAE9kAEhN7W-SDmI', '0');
INSERT INTO `zbp_post` VALUES (85, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】63-《燕子掌》', '燕子掌（学名：Crassula ovata (Mill.) Druce）是景天科青锁龙属植物，为多年生常绿肉质亚灌木，株高一年生约为31厘米，二年生42厘米，三年生约为49厘米，多年生可达2-4米，主根短粗。茎肥厚，圆柱状，呈绿色或银灰绿', '<div style=\'text-align: center;overflow: auto;\'><img title=燕子掌 alt=燕子掌 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibct1EkXwPzTrXS1VdnVXxg4olSvQY2FtoslicyuYqnpicTdLibNlugm77kZsBdtqCxvXibbDCibmj6UaAaw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>（学名：Crassula ovata (Mill.) Druce）是景天科青锁龙属植物，为多年生常绿肉质亚灌木，株高一年生约为31厘米，二年生42厘米，三年生约为49厘米，多年生可达2-4米，主根短粗。茎肥厚，圆柱状，呈绿色或银灰绿色，茎基部2年后逐步木质化。叶对生，肥厚肉质，多汁，倒卵形、长圆状匙形。花开后多成五瓣，5-10朵成密集圆锥花序，初期为白色，淡粉红色、淡紫红色，通常10年后开花，花期约10-15天。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>为多年生常绿肉质亚灌木，株高一年生约为31厘米，二年生42厘米，三年生约为49厘米，多年生可达2-4米，主根短粗，呈锥形，侧根细长而密生，根系较小，呈倒三角形。茎肥厚，圆柱状，呈绿色或银灰绿色，茎基部2年后逐步木质化，木质化后为淡灰绿色或淡褐绿色，随木质化程度的加大，表面层逐步趋裂。多二歧对称分枝成一整齐美观树形。叶对生，肥厚肉质，多汁，倒卵形、长圆状匙形，长2-5厘米，最长可达9厘米，宽1.5-2.5厘米，最宽可达3.5厘米，先端圆钝，基部渐狭，不成柄状，亦不相连接，全绿、亮绿色、无毛、带蜡状。   花开后多成五瓣，5-10朵成密集圆锥花序，初期为白色，淡粉红色、淡紫红色，后期呈淡蓝色或紫红色，长8-10毫米，花瓣狭，有短尖头。人工栽培的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>，通常10年后开花，花期约10-15天。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>繁殖方式扦插繁殖:常用扦插法繁殖。一般在春、秋季进行，剪取健壮的顶端枝条，长7-10厘米，最好在阴凉处晾1天，待伤口稍微干燥后，再插于湿润的沙土或珍珠岩中，插后约3星期生根，成活率极高。也可用单叶扦插，切叶后稍晾干，再插入沙床，插后约4星期生根，根长2-3厘米时上盆。 分株繁殖:分株繁殖每年春、秋两季株扦插成活率较高。扦插用苗，应在生长2年以上，健壮、无病虫害的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>上分株采选，除顶端嫩枝外，其他的枝节均可分株采选。将采选的枝节保留上端留3-4对叶子片，通风晾12-24小时后扦插。扦插前1天漫灌浇1次透水，扦插后不可浇水，以免引起烂根。用有机肥料作底肥，扦插的株，行距为20厘米左右，一般25-30天可生根。生根后可再浇1次透水，使其快速生长。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>生长习性野生品种生长于阴森潮湿地（坟墓或乡间小路边），其着生地为红壤，因此耐寒性强，喜阴，能忍受的高温极限为日平均温度24℃；喜湿润，也耐干旱，习惯于偏酸性土壤，以疏松、肥沃的腐殖质土最好。有夏季休眠习性，极易繁殖，通常一颗石蒜极易发展成一片石蒜。极易栽培，一般在开花后分球栽培为宜，栽培时施以足够基肥，以土盖没球茎，浇透水即可。石蒜的黑色花球极易养活，保持湿润一年都不会死。彼岸花主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>树冠挺拔秀美，茎叶碧绿油亮，四季常青，盛开美丽的花朵，十分清雅别致。可配盆架、石砾加工成小型盆景，也可单独盆栽，装饰茶几、案头、书桌、窗台等处。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>的提取物中含有多种有效化合物，如β-谷甾醇、D-甘露醇、氯化钾、氯化钠的混合体、3-甲基-2-丁烯基异胍等，其中3-甲基-2-丁烯基异胍为首次发现。通过动物药效学试验表明：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>提取物对逆转动物四氧嘧啶所致的血糖升高，免疫器官抑制，体重下降，血中胆固醇、甘油三酯升高均有显著的治疗作用，对试验动物整体综合保护效果也是非常明显的。经第三军医大学附属新桥医院临床药理基地按WHO确定的2型糖尿病诊断标准，和《中医新药临床研究技术指导原则》进行临床实验，证明</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd style=\'color:yellow;text-decoration: underline\'>燕子掌</a>提取物对2型糖尿病及并发症的治疗，显效率为44%，有效率为46.9%，总有效率90.9%。在治疗糖尿病的过程中，随着血糖的下降，其血中胆固醇、甘油三酯也随之下降，并使血中高密度酯蛋白APOA1较明显地升高，低密度酯蛋白APOB逐渐下降。且不易产生低血糖反应。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000751&idx=1&sn=8bc4012e9eb1aa0b316f9194d3e61a89&chksm=4ffc15e9788b9cff4106e4a56feb5ea1de9e8eed9755bc347308d2813b6cc2c9eabdc55dab7f#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568362232, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyXs3ws03JHg7vOaiH-fUfXA', '0');
INSERT INTO `zbp_post` VALUES (86, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】61-《大丽花》', '大丽花（Dahlia pinnata Cav.），别名大理花、天竺牡丹、东洋菊、大丽菊、地瓜花，菊科、大丽花属植物，多年生草本，有巨大棒状块根。茎直立，多分枝，高1.5-2米，粗壮。原产于墨西哥，墨西哥人把它视为大方、富丽的象征', '<div style=\'text-align: center;overflow: auto;\'><img title=大丽花 alt=大丽花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvh0ibkk48TkG5HYca7B5lwoObzVNKRLzFwg4EpVdS8hMYugRMvIOx2IuRFdSIrEeuPziaibEVPQNTdg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>（Dahlia pinnata Cav.），又叫大理花、天竺牡丹、东洋菊、大丽菊、地瓜花，菊科、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>属植物，是一种多年生草本，有巨大棒状块根。茎直立，多分枝，高1.5~2m，粗壮。原产于墨西哥，墨西哥人把它视为大方、富丽的象征，因此将它尊为国花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>形态特征多年生草本，有巨大棒状块根。茎直立，多分枝，高1.5~2m，粗壮。叶1-3回羽状全裂，上部叶有时不分裂，裂片卵形或长圆状卵形，下面灰绿色，两面无毛。     头状花序大，有长花序梗，常下垂，宽6-12cm。总苞片外层约5个，卵状椭圆形，叶质，内层膜质，椭圆状披针形。舌状花1层，白色，红色，或紫色，常卵形，顶端有不明显的3齿，或全缘；管状花黄色，有时在栽培种全部为舌状花。瘦果长圆形，长9-12mm，宽3-4mm，黑色，扁平，有2个不明显的齿。花期6月至12月，果期9月至10月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>繁殖方式分根繁殖:春季3月～4月间，取出贮藏的块根，将每一块根及附着生于根颈上的芽一齐切割下来（切口处涂草木灰防腐），另行栽植。由于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>仅根颈部才有芽，所以分割的每个块根上，必须有带芽的根颈；无根颈或根颈上无芽点的块根均不出芽，不能栽植。若根颈上发芽点不明显或不易辨认时，可于早春提前催芽，在温床内将根丛以较密的距离排好，然后壅土、浇水，给予一定的温度，待出芽后再分，每个分株至少具有一个芽。分株繁殖简便易行，成活率高，植株健壮，但繁殖系数不如扦插法高。 扦插繁殖:6至8月份，侧芽长至15至20cm，结合除侧芽随剪随播。插穗需3芽，留上部2芽，第3芽剪至叶基部去叶片，插入土中深及第二芽，浇透水，立支架用50%的遮荫网双层遮荫。晴天早、晚各浇水一次，连续浇水半个月，土壤湿度保持在90%，3周后生根，成活率很高，当年秋天即可开花。 种子繁殖:种子繁殖在</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>培育过程中占有绝大比重，适用于易结实的品种。一般秋季采收的种子在翌年2~3月间播种，白天15~20℃，夜间不低于12℃。种子繁殖出的实生苗生命力旺盛，生长发育健康，适应性强，短时间内可获得大量植株，但开花结果慢。花期较晚，不能保持母本的优良特性，有退化现象。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>喜半阴，阳光过强影响开花，光照时间一般10~12小时，培育幼苗时要避免阳光直射。对温度条件的要求</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>喜欢凉爽的气候，9月下旬开花最大、最艳、最盛，但不耐霜，霜后茎叶立刻枯萎。生长期内对温度要求不严，8~35℃均能生长，15~25℃为宜。对水分的要求</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>不耐干旱，不耐涝，一般盆栽见土干则浇透水，做到见湿见干；多雨天可倒盆排水（地栽不必经常浇水）对土壤的要求</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>适宜栽培于土壤疏松、排水良好的肥沃沙质土壤中。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>之所以被称为世界名花之一，主要是因为它的花期长、花径大、花朵多。在北方地区，花期从5月至11月中旬，在温度适宜条件下可周年开花不断，以秋后开花最盛。精品</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>最大花径可达到30~40cm，是目前花卉中独一无二的。花色有红、紫、白、黄、橙、墨、复色七大色系，花朵有单瓣和重瓣，单瓣花朵开放时间短些，重瓣花朵开放时间较长。花朵特征与瓣形变化是品种鉴定的主要依据，有球型、菊花型、牡丹型、装饰型、碟型、盘型、绣球型和芍药型等花型的品种群体，以色彩瑰丽，花朵优美而闻名。经济价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd style=\'color:yellow;text-decoration: underline\'>大丽花</a>种植难度小、片植效果好，一般家庭都可以栽种，且花期长，块根3月下旬下种，6月中旬就可以开花，而且可以一直开到霜降时期，既能美化庭院，还能给农户带来经济收益，所以庭院养殖是一个很好的方向。药用价值：根内含菊糖，在医药上有与葡萄糖同样的功效。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000749&idx=1&sn=ebbddcdabd8029e26c4796dcaef2223a&chksm=4ffc15eb788b9cfdc19e87ca2659281d825509dc1239820e4cc8f99838e437d259e23ceac52c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568212786, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyW1dsOMiYKUW2ln9M4jORmY', '0');
INSERT INTO `zbp_post` VALUES (87, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】62-《君子兰》', '君子兰（学名：Clivia miniata），别名剑叶石蒜、大叶石蒜，是石蒜科君子兰属的观赏花卉。原产于南非南部。是多年生草本植物，花期长达30-50天，以冬春为主，元旦至春节前后也开花，忌强光，为半阴性植物，喜凉爽，忌高温。', '<div style=\'text-align: center;overflow: auto;\'><img title=君子兰 alt=君子兰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcu6QJaY8RK31n5jeVpRCqFVlT7SftD3PaubzZRur6aALEfEIH7oy1CYLPIwr5yHJFvlOcJtHibONwg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>（学名：Clivia miniata），别名剑叶石蒜、大叶石蒜，是石蒜科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>属的观赏花卉。原产于南非南部。是多年生草本植物，花期长达30-50天，以冬春为主，元旦至春节前后也开花，忌强光，为半阴性植物，喜凉爽，忌高温。生长适温为15-25℃，低于5℃则停止生长。喜肥厚、排水性良好的土壤和湿润的土壤，忌干燥环境。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>具有很高的观赏价值，中国常在温室盆栽供观赏。分株或种子繁殖。功效相同的尚有垂笑</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>（C. nobilis），各地温室栽培，花色多样。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>根肉质纤维状，为乳白色，十分粗壮。根系粗大，很有肉质感。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>茎基部宿存的叶基部扩大互抱成假鳞茎状。叶片从根部短缩的茎上呈二列叠出，排列整齐，宽阔呈带形，顶端圆润，质地硬而厚实，并有光泽及脉纹。基生叶质厚，叶形似剑，叶片革质，深绿色，具光泽，带状，长30-50厘米，最长可达85厘米，宽3-5厘米，下部渐狭，互生排列，全缘     花葶自叶腋中抽出，若从种子开始养护，一般要达到15片叶时开花。花茎宽约2厘米；小花有柄，在花顶端呈伞形排列，花漏斗状，直立，黄或橘黄色、橙红色。伞形花序顶生，花直立，有数枚覆瓦状排列的苞片，每个花序有小花7-30朵，多的可达40朵以上。花被裂片6，合生。垂笑</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>则花稍垂，花被狭漏斗状。花梗长2.5-5厘米；花直立向上，花被宽漏斗形，鲜红色，内面略带黄色；花被管长约5毫米，外轮花被裂片顶端有微凸头，内轮顶端微凹，略长于雄蕊；花柱长，稍伸出于花被外。浆果紫红色，宽卵形。盛花期自元旦至春节，以春夏季为主，可全年开花，有时冬季也可开花，也有在夏季6-7月间开花的。果实成熟期10月左右，属浆果，紫红色。花、叶并美。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>繁殖方式播种繁殖:授粉的方法是：当花被开裂后2-3天，花苞成熟、柱头有粘液分泌时，即为授粉时机。授粉时，用新毛笔蘸取雄蕊的花粉，轻轻地振落在雌蕊的柱头上。为提高结子率，可在上午9-10时、下午2-3时之间各授粉1次，约8-9个月后种子可成熟。当果皮由绿色逐渐变为黑紫色时，即可将果穗剪下，过10-20天后把种子剥出。 分株繁殖:分株时，先将</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>母株从盆中同来，去掉宿土，找出可以分株的腑芽。如果子株生在母株外沿，株体较小，可以一手握住鳞茎部分，另一手捏住子株基部，撕掰一下，就能把子株掰离母体；如果子株粗壮，不易掰下，就应该用准备好的锋利小刀把它割下来。千万不可强掰，以免损伤幼株。子株割下后，应立即用干木炭粉涂抹伤口，以吸干流液，防止腐烂。接着，将子株上盆种植 。种植时，种植深度以埋住子株的基部假鳞茎为度，靠苗株的部位要使其略高一些，并盖上经过消毒的沙土。种好后随即浇一次透水，待到2星期后伤口愈合时，再加盖一层培养土。一般须经1-2个月生出新根，1-2年开花。用分株法繁殖的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>，遗传性比较稳定，可以保持原种的各种特征。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>原产于非洲南部的热带地区，生长在树的下面，所以它既怕炎热又不耐寒，喜欢半阴而湿润的环境，畏强烈的直射阳光，生长的最佳温度在18-28℃之间，10℃以下，30℃以上，生长受抑制。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>喜欢通风的环境，喜深厚肥沃疏松的土壤，适宜在疏松肥沃的微酸性有机质土壤内生长。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>是著名的温室花卉，适宜室内培养。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>，株形端庄优美，叶片苍翠挺拔，花大色艳，果实红亮，叶花果并美，有一季观花、三季观果、四季观叶之称。其花期长达四五十天，而且能够早春开花，是重要的节庆花卉。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>是多年生常绿草本植物，叶花果俱佳，赏叶胜观花。经济价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>能够满足人们美化居室、陶冶情操、净化空气、增进健康等多方面的需要，使你的居室尽现雍容华贵的气派，为丰富和调剂人们的生活增添光彩和魅力。美观大方，又耐阴，宜盆栽室内摆设，为观叶赏花，也是布置会场、装饰宾馆环境的理想盆花。还有净化空气的作用和药用价值，是人们的首选品种。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd style=\'color:yellow;text-decoration: underline\'>君子兰</a>为石蒜科，其植株文雅俊秀，有君子风姿，花如兰，而得名。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000750&idx=1&sn=fe97bb4b66481188adace9bbb9fbae1c&chksm=4ffc15e8788b9cfe17ee04a50b2bcae7195c733378107e49b9bbb2e5c1406a4bdb6043cefb8e#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568299506, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyYLsI2CLEfWXfRNM2ePHF2A', '0');
INSERT INTO `zbp_post` VALUES (88, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】58-《素馨花》', '素馨花，拉丁文名：Jasminum grandiflorum L.木犀科、素馨属攀援灌木，高1-4米。小枝圆柱形，具棱或沟。叶对生，花序中间之花的梗明显短于周围之花的梗；花芳香；花萼无毛，裂片锥状线形，果未见。花期8-10月', '<div style=\'text-align: center;overflow: auto;\'><img title=素馨花 alt=素馨花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuzsoc8lSqmibGOcXY7nWoRicWrOHk7G5j2qlu5RcUh2yh0GgHPheCMRwf3ic8g8RJL7CibH8zE7LnbfA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>，拉丁文名：Jasminum grandiflorum L.木犀科、素馨属攀援灌木，高1-4米。小枝圆柱形，具棱或沟。叶对生，花序中间之花的梗明显短于周围之花的梗；花芳香；花萼无毛，裂片锥状线形，果未见。花期8-10月。产于云南、四川、西藏及喜马拉雅地区。生石灰岩山地，海拔约1 800米。世界各地广泛栽培。本种花芳香而美丽，常栽培供观赏。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>形态特征攀援灌木，高1-4米。小枝圆柱形，具棱或沟。叶对生，羽状深裂或具5-9小叶，叶长3-8厘米，宽3-6厘米；叶轴常具窄翼，叶柄长0.5-4厘米；小叶片卵形或长卵形，顶生小叶片常为窄菱形，长0.7-3.8厘米，宽0.5-1.5厘米，先端急尖、渐尖、钝或圆，有时具短尖头、基部楔形、钝或圆。     聚伞花序顶生或腋生，有花2-9朵；花序梗长0-3厘米；苞片线形，长2-3毫米；花梗长0.5-2.5厘米，花序中间之花的梗明显短于周围之花的梗；花芳香；花萼无毛，裂片锥状线形，长（3-）5-10毫米；花冠白色，高脚碟状，花冠管长1.3-2.5厘米，裂片多为5枚，长圆形，长1.3-2.2厘米，宽0.8-1.4厘米。果未见。花期8-10月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>繁殖方式种子繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>结的果实不多，果实近碗豆籽大小，一个果实里只有一粒种子。种子在冬腊月果实充分发黑成熟时采收，采收后连外果肉摆放在通风干燥处晾干后于立春节令前后播种；播种苗床用肥沃疏松沙壤地作畦，以15cm行距开浅沟条播后覆土1cm或采用15cmx15cm浅塘点播；播完种后覆盖1层松针或稻草，以防浇水时种子露土，然后杀菌消毒，浇透水，小拱棚扣膜保湿增温，精心管护半年后可成苗出圃。 扦插繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>扦插育苗可在短期内繁殖大量花苗。扦插时间在气候干湿分明的云南地区保护地育苗，以1月中旬（立春节令前20d左右）为宜；露地扦插育苗以6、7月芒种至大暑节令之间刚进入雨季时期为宜。 分株繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>分株繁殖更为简单易行，适宜于一般家庭少量栽植。分株繁植时，选取母株周围串根萌蘖苗顺根撬出，剪去藤蔓10-50cm以上部分后用塑料薄膜扎紧封顶即成1株新苗，直接用来定植即可。如开春定植，当年冬季可现蕾，次年阳春可大量开花。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>生长习性喜温暖、湿润的自然条件，土壤以富含腐殖质的砂质壤为好。生石灰岩山地，海拔约1800米。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>通体雪白．盛开时花朵密密麻麻缀满枝头，且有清淡香气，馨花令人神清气爽，精力充沛。药用价值：舒肝解郁；行气止痛。主肝郁气滞所致的胁肋脘腹作痛；下痢腹痛。用于胃痛，肝炎，月经不调，痛经，带下，口腔炎，皮肤搔痒，睾丸炎，乳腺炎，淋巴结结核。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd style=\'color:yellow;text-decoration: underline\'>素馨花</a>花 语白素馨：和蔼可亲；黄素馨：优美、文雅</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000717&idx=1&sn=6f33737c8df11392bd9173e1e9a67cf0&chksm=4ffc15cb788b9cdd0537b6796c824b2fd8c9c471190fff352da06e692c32a6c97bdca75e62f5#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567939165, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyWboVWH29bJPDbsX_CZuaJo', '0');
INSERT INTO `zbp_post` VALUES (89, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】59-《蓖麻》', '蓖麻（拉丁学名：Ricinus communis L.），为双子叶植物纲、大戟目、大戟科、蓖麻属一年生或多年生草本植物、热带或南方地区常成多年生灌木或小乔木。单叶互生，叶片盾状圆形。掌状分裂至叶片的一半以下，圆锥花序与叶对生及顶生', '<div style=\'text-align: center;overflow: auto;\'><img title=蓖麻 alt=蓖麻 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuLY95LdTBklLC2Zbbb4taicLnepbObRDaKia4Q32mb6bIEibDZU8NBSZXbCu6oWxTQC6Kia4LSREJMXA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>（拉丁学名：Ricinus communis L.），为双子叶植物纲、大戟目、大戟科、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>属一年生或多年生草本植物、热带或南方地区常成多年生灌木或小乔木。单叶互生，叶片盾状圆形。掌状分裂至叶片的一半以下，圆锥花序与叶对生及顶生，下部生雄花，上部生雌花；花瓣性同株，无花瓣；雄蕊多数，花丝多分枝；花柱，深红色。蒴果球形，有软刺，成熟时开裂。花期5-8月，果期7-10月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>形态特征一年生粗壮草本或草质灌木，高达5米；小枝、叶和花序通常被白霜，茎多液汁。叶轮廓近圆形，长和宽达40厘米或更大，掌状7-11裂，裂缺几达中部，裂片卵状长圆形或披针形，顶端急尖或渐尖，边缘具锯齿。网脉明显；叶柄粗壮，中空，长可达40厘米，顶端具2枚盘状腺体，基部具盘状腺体；托叶长三角形，长2-3厘米，早落。     总状花序或圆锥花序，长15-30厘米或更长；苞片阔三角形，膜质，早落；雄花：花萼裂片卵状三角形，长7-10毫米；雄蕊束众多；雌花：萼片卵状披针形，长5-8毫米，凋落；子房卵状，直径约5毫米，密生软刺或无刺，花柱红色，长约4毫米，顶部2裂，密生乳头状突起。蒴果卵球形或近球形，长1.5-2.5厘米，果皮具软刺或平滑；种子椭圆形，微扁平，长8-18毫米，平滑，斑纹淡褐色或灰白色；种阜大。花期几全年或6-9月（栽培）。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>繁殖方式种子繁殖:在播种前，用40~50℃温水把种子浸泡24小时后捞出，埋在湿润的沙子里，种子一般需要5~7天发芽，发芽后就可立即播种。如要迟播的，可用温水浸泡种子48小时，使硬壳变软，籽粒吸足水分，以加快种子发芽速度，经过浸泡后的种子可及时播种，也可进行催芽，待一部分种子露芽时，即可播种。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>生长习性喜高温，不耐霜，酸碱适应性强，在中国广为栽培。平原地区均可种植。海拔20-500米（云南海拔2300米）村旁疏林或河流两岸冲积地常有逸为野生，呈多年生灌木。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>是一种深根作物。当气温稳定在10℃时即可播种。也可育苗移栽。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>主要价值工业价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd style=\'color:yellow;text-decoration: underline\'>蓖麻</a>种子可榨油，油粘度高，凝固点低，既耐严寒又耐高温，在-8~-10℃不冰冻，在500~600℃不凝固和变性，具有其他油脂所不及的特性。为化工、轻工、冶金、机电、纺织、印刷、染料等工业和医药的重要原料。药用价值：消肿拔毒，止痒。治疮疡肿毒，鲜品捣烂外敷；治湿疹搔痒，煎水外洗；并可灭蛆、杀孑孓。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000718&idx=1&sn=35accdef07a206ee3e1dddfe6f0cabc9&chksm=4ffc15c8788b9cdeb388a86235d42ebf79de5d5d6dedaeabefb477091137bac603c0fb5d0a9c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568038770, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyV71N9Z8Y0PJiU7RXDr_9j0', '0');
INSERT INTO `zbp_post` VALUES (90, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】60-《心叶假面花》', '心叶假面花，玄参科多年生草本常作一年生栽培。原产秘鲁，现做绿化用途！多年生草本常作一年生栽培，高约45-60cm。茎细长，分枝，红色。叶对生，卵形至披针形，有锯齿，深绿色。总状花序松散，猩红色，小花径约2cm，有距，花梗细长。花期5～7月。', '<div style=\'text-align: center;overflow: auto;\'><img title=心叶假面花 alt=心叶假面花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcu6ZhTcO96hLBPKO0uKscP1R1C3UwwuoXicXYF5UQREUjTU6darcz4xFsNIV9PF8vEYeP3iamNV3tiaQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000748&idx=1&sn=91026ec25dedf1773ab61395d63c02ad&chksm=4ffc15ea788b9cfc730e298176dc9182b01a5a39376b90f2d8164eb61a692f503d742b30128d#rd style=\'color:yellow;text-decoration: underline\'>心叶假面花</a>形态特征多年生草本常作一年生栽培，高约45-60cm。茎细长，分枝，红色。叶对生，卵形至披针形，有锯齿，深绿色。总状花序松散，猩红色，小花径约2cm，有距，花梗细长。花期5～7月。        </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000748&idx=1&sn=91026ec25dedf1773ab61395d63c02ad&chksm=4ffc15ea788b9cfc730e298176dc9182b01a5a39376b90f2d8164eb61a692f503d742b30128d#rd style=\'color:yellow;text-decoration: underline\'>心叶假面花</a>繁殖方式播种繁殖:春季播种繁殖。夏季多雨常开花不良，幼株宜摘心促使多分枝。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000748&idx=1&sn=91026ec25dedf1773ab61395d63c02ad&chksm=4ffc15ea788b9cfc730e298176dc9182b01a5a39376b90f2d8164eb61a692f503d742b30128d#rd style=\'color:yellow;text-decoration: underline\'>心叶假面花</a>生长习性喜温暖气候，喜光，较耐寒，以富含腐殖质、排水良好的壤土最好。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000748&idx=1&sn=91026ec25dedf1773ab61395d63c02ad&chksm=4ffc15ea788b9cfc730e298176dc9182b01a5a39376b90f2d8164eb61a692f503d742b30128d#rd style=\'color:yellow;text-decoration: underline\'>心叶假面花</a>主要价值观赏价值：植株飘逸，小巧可爱，花色艳丽；适合布置花坛、花境，或作切花。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000748&idx=1&sn=91026ec25dedf1773ab61395d63c02ad&chksm=4ffc15ea788b9cfc730e298176dc9182b01a5a39376b90f2d8164eb61a692f503d742b30128d#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568123511, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQ2Ki0RcU9sP6VNrDCxYuVc', '0');
INSERT INTO `zbp_post` VALUES (91, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】57-《水团花》', '水团花又名：水杨梅。常绿灌木至小乔木，高达5米；顶芽不明显，由开展的托叶疏松包裹。叶对生，厚纸质，椭圆形至椭圆状披针形，或有时倒卵状长圆形至倒卵状披针形，长4-12厘米，宽1.5-3厘米，顶端短尖至渐尖而钝头', '<div style=\'text-align: center;overflow: auto;\'><img title=水团花 alt=水团花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctia9gGTcj83txzwibtU0AcyC0BuArQGqf5WBcbQia5G1HQ3zxfjYhHkdEOXLawJeaAHUUNoTg3uf1sg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000716&idx=1&sn=91d93235e18f76b62abb9cc4c4fc2215&chksm=4ffc15ca788b9cdcc2c9e2b26ce0eb57de0a6b1cf0212a9c4aa51fc079a7e0923aa5b81ac04a#rd style=\'color:yellow;text-decoration: underline\'>水团花</a>，中药名。水黄凿、青龙珠、穿鱼柳、假杨梅、水加橧、溪棉条、满山香、球花水杨梅、水里斜、水里树。为茜草科植物</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000716&idx=1&sn=91d93235e18f76b62abb9cc4c4fc2215&chksm=4ffc15ca788b9cdcc2c9e2b26ce0eb57de0a6b1cf0212a9c4aa51fc079a7e0923aa5b81ac04a#rd style=\'color:yellow;text-decoration: underline\'>水团花</a>Adina pilulifera (Lam.) Franch. ex Drake的枝叶或花、果。分布于长江以南各地。具有清热祛湿，散瘀止痛，止血敛疮之功效。用于痢疾，肠炎，浮肿，痈肿疮毒，湿疹，溃疡不敛，创伤出血。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000716&idx=1&sn=91d93235e18f76b62abb9cc4c4fc2215&chksm=4ffc15ca788b9cdcc2c9e2b26ce0eb57de0a6b1cf0212a9c4aa51fc079a7e0923aa5b81ac04a#rd style=\'color:yellow;text-decoration: underline\'>水团花</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000716&idx=1&sn=91d93235e18f76b62abb9cc4c4fc2215&chksm=4ffc15ca788b9cdcc2c9e2b26ce0eb57de0a6b1cf0212a9c4aa51fc079a7e0923aa5b81ac04a#rd style=\'color:yellow;text-decoration: underline\'>水团花</a>又名：水杨梅。常绿灌木至小乔木，高达5米；顶芽不明显，由开展的托叶疏松包裹。叶对生，厚纸质，椭圆形至椭圆状披针形，或有时倒卵状长圆形至倒卵状披针形，长4-12厘米，宽1.5-3厘米，顶端短尖至渐尖而钝头，基部钝或楔形，有时渐狭窄，上面无毛，下面无毛或有时被稀疏短柔毛；侧脉6-12对，脉腋窝陷有稀疏的毛；叶柄长2-6毫米，无毛或被短柔毛；托叶2裂，早落。头状花序明显腋生，极稀顶生，直径不计花冠4-6毫米，花序轴单生，不分枝；小苞片线形至线状棒形，无毛；总花梗长3-4.5厘米，中部以下有轮生小苞片5枚；花萼管基部有毛，上部有疏散的毛，萼裂片线状长圆形或匙形；花冠白色，窄漏斗状，花冠管被微柔毛，花冠裂片卵状长圆形；雄蕊5枚，花丝短，着生花冠喉部；子房2室，每室有胚珠多数，花柱伸出，柱头小，球形或卵圆球形。果序直径8-10毫米；小蒴果楔形，长2-5毫米；种子长圆形，两端有狭翅。花期6-7月。        </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000716&idx=1&sn=91d93235e18f76b62abb9cc4c4fc2215&chksm=4ffc15ca788b9cdcc2c9e2b26ce0eb57de0a6b1cf0212a9c4aa51fc079a7e0923aa5b81ac04a#rd style=\'color:yellow;text-decoration: underline\'>水团花</a>生长习性生于海拔200-350m的山谷疏林下或旷野路旁、溪涧水畔。分布于长江以南各地。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000716&idx=1&sn=91d93235e18f76b62abb9cc4c4fc2215&chksm=4ffc15ca788b9cdcc2c9e2b26ce0eb57de0a6b1cf0212a9c4aa51fc079a7e0923aa5b81ac04a#rd style=\'color:yellow;text-decoration: underline\'>水团花</a>主要价值药用价值：用于痢疾，肠炎，浮肿，痈肿疮毒，湿疹，溃疡不敛，创伤出血。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000716&idx=1&sn=91d93235e18f76b62abb9cc4c4fc2215&chksm=4ffc15ca788b9cdcc2c9e2b26ce0eb57de0a6b1cf0212a9c4aa51fc079a7e0923aa5b81ac04a#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567868148, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyVYsQT5NnGxwLR8F6502CfY', '0');
INSERT INTO `zbp_post` VALUES (92, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】54-《老鼠拉冬瓜》', '老鼠拉冬瓜（学名：Melothria indica Lour.[Zehneria indica （Lour.）C. Y. Wu]）属于藤本植物，中国分布广泛，柔弱草质藤本，生于荒地灌木丛、村边、林边潮湿地，常缠绕灌木上。', '<div style=\'text-align: center;overflow: auto;\'><img title=老鼠拉冬瓜 alt=老鼠拉冬瓜 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcttDPKqPJ3icR0UvW0SuuQjuSg1ynqb1ORBMFJicpBl0yFRNQPLqOfYKiaV5lX1gK74OUPEHLliaAicricw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000683&idx=1&sn=809e3bb2f102246ea67227b1e82ddcd9&chksm=4ffc15ad788b9cbbab1168a6ce3a6e336ee9fde29d31fa0a2e01fa95449a6f293b751d74a5d6#rd style=\'color:yellow;text-decoration: underline\'>老鼠拉冬瓜</a>（学名：Melothria indica Lour.[Zehneria indica （Lour.）C. Y. Wu]）属于藤本植物，中国分布广泛，柔弱草质藤本，生于荒地灌木丛、村边、林边潮湿地，常缠绕灌木上。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000683&idx=1&sn=809e3bb2f102246ea67227b1e82ddcd9&chksm=4ffc15ad788b9cbbab1168a6ce3a6e336ee9fde29d31fa0a2e01fa95449a6f293b751d74a5d6#rd style=\'color:yellow;text-decoration: underline\'>老鼠拉冬瓜</a>形态特征多年生草质藤本，长1～2米，有不分枝卷须。根部分膨大成一串纺锤形块根，大小相同，故有</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000683&idx=1&sn=809e3bb2f102246ea67227b1e82ddcd9&chksm=4ffc15ad788b9cbbab1168a6ce3a6e336ee9fde29d31fa0a2e01fa95449a6f293b751d74a5d6#rd style=\'color:yellow;text-decoration: underline\'>老鼠拉冬瓜</a>之名，富含淀粉，所以也作土花粉用。茎纤细，柔弱。单叶互生，有细长柄；叶片卵状三角形，膜质。夏季开白色花。果实卵形或近椭圆形，长1～2厘米，橙黄色，果皮甚薄，内有多数扁平种子。一年生攀援或平卧草本，茎、枝纤细。叶膜质，多型，三角状卵形、卵状心形或戟形，不分裂或3～5分裂，顶端急尖或稀短渐尖，基部弯缺关圆形。雌雄同株，雄花单生或稀2～3朵生于短的总状花序上，花冠淡黄色；雌花与雄花在同一叶腋内单生或稀双生，花冠阔钟形。果实长圆形或狭卵形，种子灰白色。花期4～7月，果期7～10月。      </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000683&idx=1&sn=809e3bb2f102246ea67227b1e82ddcd9&chksm=4ffc15ad788b9cbbab1168a6ce3a6e336ee9fde29d31fa0a2e01fa95449a6f293b751d74a5d6#rd style=\'color:yellow;text-decoration: underline\'>老鼠拉冬瓜</a>主要价值食用价值：各物分别洗净，蜜枣去核，猪瘦肉切块。一起下瓦煲，加清水2000毫升（约8碗量），武火滚沸后改文火煲约1个半小时，下盐便可。药用价值：①痈疮疖肿、皮肤湿疹：用鲜根、叶捣烂外敷；或干根研末调敷患处。②咽喉肿痛：用根5钱~1两，水煎服；或鲜品捣烂外敷。③尿路感染、尿路结石、急性结合膜炎、小儿疳积：用根5钱~1两，水煎服。④老鼠咬伤：用根3~4两混水饮；渣外擦伤处有良效。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000683&idx=1&sn=809e3bb2f102246ea67227b1e82ddcd9&chksm=4ffc15ad788b9cbbab1168a6ce3a6e336ee9fde29d31fa0a2e01fa95449a6f293b751d74a5d6#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567608175, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyfA4KJhhch1Zhmg_0dYL0D4', '0');
INSERT INTO `zbp_post` VALUES (93, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】56-《长寿花》', '长寿花，多年生肉质草本。株高10-30厘米。茎直立。单叶对生，椭圆形，缘具钝齿。聚伞花序；小花橙红至绯红色。蓇葖果。种子多数。花期2-5月。叶片密集翠绿，临近圣诞节日开花，拥簇成团，花色丰富，是惹人喜爱的室内盆栽花卉。', '<div style=\'text-align: center;overflow: auto;\'><img title=长寿花 alt=长寿花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuGGib8GOIgg1NvrjOoMtM1EPaIjlmvkPOX9HOVY79og14MCibWVOjjHH3D4mW4EvoWKCThoENCRyIA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd style=\'color:yellow;text-decoration: underline\'>长寿花</a>，是一种多年生肉质的草本。株高约10-30cm。茎直立。单叶对生，呈椭圆形，缘具钝齿。聚伞花序；小花橙红至绯红色。蓇葖果。种子多数。花期2至5月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd style=\'color:yellow;text-decoration: underline\'>长寿花</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd style=\'color:yellow;text-decoration: underline\'>长寿花</a>是常绿多年生草本多浆的植物。植株小巧，茎直立，株高10-30cm。叶对生，叶片密集翠绿，长圆状匙形或椭圆形，长4-8cm，宽2-6cm，肉质，叶片上部叶缘具波状钝齿，下部全缘，亮绿色，有光泽，叶边略带红色。叶片属厚肉质，密集深绿，有光泽。     圆锥状聚伞花序，挺直，深绿色。花序长7-10cm。每株有花序5-7个，着花60 - 250朵。花小，高脚碟状，花径1.2- 1.6cm，花瓣4片，花朵色彩丰富，花色有绯红、桃红、橙红、黄、橙黄和白等。花冠长管状，基部稍膨大，蓇葖果。种子多数。花期1至4月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd style=\'color:yellow;text-decoration: underline\'>长寿花</a>繁殖方式扦插繁殖:在5-6月或9-10月进行效果最好。选择稍成熟的肉质茎，剪取5-6厘米长，插于沙床中，浇水后用薄膜盖上，室温在15-20℃，插后15-18天生根，30天能盆栽。常用10厘米盆。如种苗不多时，可用叶片扦插。将健壮充实的叶片从叶柄处剪下，待切口稍干燥后斜插或平放沙床上，保持湿度，约10-15天，可从叶片基部生根，并长出新植株。    </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd style=\'color:yellow;text-decoration: underline\'>长寿花</a>生长习性喜温暖稍湿润和阳光充足环境。不耐寒，生长适温为15-25℃，夏季高温超过30℃，则生长受阻，冬季室内温度需12-15℃。低于5℃，叶片发红，花期推迟。冬春开花期如室温超过24℃，会抑制开花，如温度在15℃左右，开花不断。耐干旱，对土壤要求不严，以肥沃的砂壤土为好。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd style=\'color:yellow;text-decoration: underline\'>长寿花</a>为短日照植物，对光周期反应比较敏感。生长发育好的植株，给予短日照（每天光照8-9小时）处理3-4周即可出现花蕾开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd style=\'color:yellow;text-decoration: underline\'>长寿花</a>主要价值观赏价值：花期临近圣诞节，而且花期长，因此成为人们衬托节日气氛的“节日用花”。此花有很高的观赏价值，不开花时还可以赏叶，是非常理想的室内盆栽花卉。植株小巧玲珑，株型紧凑，叶片翠绿，花朵密集。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000697&idx=1&sn=0910d20adf9fa1bd2aa59af30005d53f&chksm=4ffc15bf788b9ca9594073890184d6348f64b4ed554ede4a740c18c54e6bcb814c94aa184ee2#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567781061, 0, 0, 'GvnDcC2QjGEN4AUqdM7DydtjPDQEa8eRFyAEgFR22U8', '0');
INSERT INTO `zbp_post` VALUES (94, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】55-《鸡冠花》', '鸡冠花（学名：Celosia cristata L.），中文别名：鸡髻花、老来红、芦花鸡冠、笔鸡冠、小头鸡冠、凤尾鸡冠、大鸡公花、鸡角根、红鸡冠。一年草本植物，夏秋季开花，花多为红色，呈鸡冠状，故称。原产非洲、美洲热带和印度。喜阳光充足', '<div style=\'text-align: center;overflow: auto;\'><img title=鸡冠花 alt=鸡冠花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctlEaKJcfWpwyJrsW5UicaBtZSXe7UVGW66ebPffI1c5Az47Cpd7qM4gtgOgxXseIUtg5ibZ1YKGP7Q/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>（学名：Celosia cristata L.），中文别名：鸡髻花、老来红、芦花鸡冠、笔鸡冠、小头鸡冠、凤尾鸡冠、大鸡公花、鸡角根、红鸡冠。一年草本植物，夏秋季开花，花多为红色，呈鸡冠状，故称。原产非洲、美洲热带和印度。喜阳光充足、湿热，不耐霜冻。不耐瘠薄，喜疏松肥沃和排水良好的土壤。世界各地广为栽培，普通庭园植物。另外具有很高的药用价值。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>，一年生直立草本，高30-80cm。全株无毛，粗壮。分枝少，近上部扁平，绿色或带红色，有棱纹凸起。单叶互生，具柄；叶片长5-13cm，宽2-6cm，先端渐尖或长尖，基部渐窄成柄，全缘。中部以下多花；苞片、小苞片和花被片干膜质，宿存；胞果卵形，长约3mm，熟时盖裂，包于宿存花被内。种子肾形，黑色，光泽。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>繁殖方式播种繁殖:种子繁殖法，清明时选好地块，施足基肥，耕细耙匀，整平作畦，将咱子均匀地撒于畦面，略盖严种子，踏实浇透水，一般在气温15-20℃时，10-15天可出苗。夏播于芒种后，抢旱灭苍茌，也可与白芍，牡丹或其它作物套种，亩用种一斤。苗高2寸，按行距1尺，株距8寸间苗，间下的苗可移载其它田块，移载后一定要浇水。幼苗期一定要除草松土，不太干旱时，尽量少浇水。苗高尺许，要施追肥一次。封垅后稍适当打去老叶，开花抽穗时，如果天气干旱，要适当浇水，雨季低洼处严防积水。抽穗后可将下部叶腑间的花芽抹除，以利养分集中于顶部主穗生长。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>喜温暖干燥气候，怕干旱，喜阳光，不耐涝，但对土壤要求不严，一般土壤庭院都能种植。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>的品种多，株型有高、中、矮3种；形状有鸡冠状、火炬状、绒球状、羽毛状、扇面状等；花色有鲜红色、橙黄色、暗红色、紫色、白色、红黄相杂色等；叶色有深红色、翠绿色、黄绿色、红绿色等极其好看，成为夏秋季常用的花坛用花。园林价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>因其花序红色、扁平状，形似鸡冠而得名，享有“花中之禽”的美誉。高型品种用于花境、花坛，还是很好的切花材料，切花瓶插能保持10d以上。也可制干花，经久不凋。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd style=\'color:yellow;text-decoration: underline\'>鸡冠花</a>生长于秋天，当夏天的热情被秋风萧瑟所代替，人们心情日渐忧郁时，</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000696&idx=1&sn=5501be4c7efc6414e531a6572f8854fa&chksm=4ffc15be788b9ca821c559adcf54fc8e976ade2fd62f447cc4db2f092376acb3cefe99a8de2c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1567698474, 0, 0, 'GvnDcC2QjGEN4AUqdM7DySi6Y-mp5z3g4j05IXAqcJ8', '0');
INSERT INTO `zbp_post` VALUES (95, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】73-《尾穗苋》', '尾穗苋（学名：Amaranthus caudatus L.）是苋科苋属植物。一年生草本，高达1.5米，茎直立，粗壮，具钝棱角，单一或稍分枝，绿色，或常带粉红色，叶片菱状卵形或菱状披针形，顶端短渐尖或圆钝，具凸尖，基部宽楔形', '<div style=\'text-align: center;overflow: auto;\'><img title=尾穗苋 alt=尾穗苋 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuIibAFU0EDmbkl9Vl0hMhfujQbj1bMTnraficWXQl9bViaZpiaTlbMwEka0K64iceztrUU4YujPcg9icXA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>（学名：Amaranthus caudatus L.）是苋科苋属植物。一年生草本，高达1.5米，茎直立，粗壮，具钝棱角，单一或稍分枝，绿色，或常带粉红色，叶片菱状卵形或菱状披针形，顶端短渐尖或圆钝，具凸尖，基部宽楔形，稍不对称，全缘或波状缘，绿色或红色，圆锥花序顶生，下垂，有多数分枝，中央分枝特长，由多数穗状花序形成，苞片红色，胞果近球形，上半部红色，超出花被片。种子近球形，直径1毫米，淡棕黄色，有厚的环。花期7-8月，果期9-10月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>形态特征一年生草本，高达15米；茎直立，粗壮，具钝棱角，单一或稍分枝，绿色，或常带粉红色，幼时有短柔毛，后渐脱落。叶片菱状卵形或菱状披针形，长4-15厘米，宽2-8厘米，顶端短渐尖或圆钝，具凸尖，基部宽楔形，稍不对称，全缘或波状缘，绿色或红色，除在叶脉上稍有柔毛外，两面无毛；叶柄长1-15厘米，绿色或粉红色，疏生柔毛。     圆锥花序顶生，下垂，有多数分枝，中央分枝特长，由多数穗状花序形成，顶端钝，花密集成雌花和雄花混生的花簇；苞片及小苞片披针形，长3毫米，红色，透明，顶端尾尖，边缘有疏齿，背面有1中脉；花被片长2-2.5毫米，红色，透明，顶端具凸尖，边缘互压，有1中脉，雄花的花被片矩圆形，雌花的花被片矩圆状披针形；雄蕊稍超出；柱头3，长不及1毫米。胞果近球形，直径3毫米，上半部红色，超出花被片。种子近球形，直径1毫米，淡棕黄色，有厚的环。花期7-8月，果期9-10月。最佳赏期为分枝花穗下垂围满植株、花球膨大的盛花期（播种至此时需100天左右）。观赏期可达3个月以上。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>繁殖方式在中国北方通常3-5月播种（中国南方四季均可），播后两个月初现花蕾。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>可垄作穴播，也可畦作条播。采用营养钵育苗，播后覆土不宜超过1厘米，播后温度保持20-25℃，3-5天出苗，随着幼苗增长要及时间苗，使其留下的苗保持茁壮。  </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>原产热带，全世界各地栽培。在中国各地有栽培，有时逸为野生。[1]</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>对土壤要求不严，生长期最佳适温18-28℃，能耐-2℃寒霜。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd style=\'color:yellow;text-decoration: underline\'>尾穗苋</a>可作盆栽观赏，也可作为姿态各异的鲜切花观赏，其干花可作装饰；剪下结有绒状花球的分枝，置于阴凉处，待半干后组拼，中国北方无花时节可作室内装饰。药用价值：根供药用，有滋补强壮作用。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000883&idx=1&sn=79a440e90997a6d377514ee671756505&chksm=4ffc1275788b9b63923f6af5eb935cccf8f70af61a2a91e77e4ada91d22af7bcd52de6709bbd#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569247008, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyS1fUj5htMTIGq9ZRYBIOg4', '0');
INSERT INTO `zbp_post` VALUES (96, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】71-《颠茄》', '颠茄，别名：野山茄、美女草、别拉多娜草，拉丁文名：Atropa belladonna L.是双子叶植物纲、茄科、颠茄属多年生草本，或因栽培为一年生，根粗壮，圆柱形。茎下部单一，带紫色，上部叉状分枝，嫩枝绿色，原产欧洲', '<div style=\'text-align: center;overflow: auto;\'><img title=颠茄 alt=颠茄 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcu1noz5Jb8kNWI3RREkD47lIIcXpSgxvx66EndOPAUsoQrgLibGzOvxpibkELCNUKfgUK9690MupV7Q/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>，别名：野山茄、美女草、别拉多娜草，拉丁文名：Atropa belladonna L.是双子叶植物纲、茄科、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>属多年生草本，或因栽培为一年生，根粗壮，圆柱形。茎下部单一，带紫色，上部叉状分枝，嫩枝绿色，原产欧洲，20世纪30年代引入中国，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>属无限生长型。多年生草本，或因栽培为一年生。根呈直立圆柱形。茎扁中空圆柱形。叶在茎下部互生，有短柄，叶片卵形、卵状椭圆形或椭圆形。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>形态特征多年生草本，或因栽培为一年生，高0.5-2米。根粗壮，圆柱形。茎下部单一，带紫色，上部叉状分枝，嫩枝绿色，多腺毛，老时逐渐脱落。叶互生或在枝上部大小不等两叶双生，叶柄长达4厘米，幼时生腺毛；叶片卵形、卵状椭圆形或椭圆形，长7-25厘米，宽3-12厘米，顶端渐尖或急尖，基部楔形并下延到叶柄，上面暗绿色或绿色，下面淡绿色，两面沿叶脉有柔毛。     花俯垂，花梗长2-3厘米，密生白色腺毛；花萼长约为花冠之半，裂片三角形，长1-1.5厘米，顶端渐尖，生腺毛，花后稍增大，果实成星芒状向外开展；花冠筒状钟形，下部黄绿色，上部淡紫色，长2.5-3厘米，直径约1.5厘米，筒中部稍膨大，5浅裂，裂片顶端钝，花开放时向外反拆，外面纵脉隆起，被腺毛，内面筒基部有毛；花丝下端生柔毛，上端向下弓曲，长约1.7厘米，花药椭圆形，黄色；花盘绕生于子房基部；花柱长约2厘米，柱头带绿色。浆果球状，直径1.5-2厘米，成熟后紫黑色，光滑，汁液紫色。每浆果种子多数，80-180粒，种子细小，千粒重0. 6-0.8g，扁肾形，有网纹，黑褐色，长1.5-2毫米，宽1.2-1.8毫米。花期6-8月，果期7-9月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>繁殖方式播种繁殖:2月中下旬育苗的，先提前扣拱棚提温，然后根据需要做成宽1.0-1.2m、长10-15m、深10cm的畦，将配制好的营养土充分混合后摊平到畦面上，畦内浇一次透水，等水渗下后，再撒一薄层土，将处理好的种子均匀地撒在畦面上，覆土0.5-1cm厚，然后盖严地膜和拱棚膜提温，晚上覆盖草帘，促出苗。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>生长习性喜温暖湿润气候，怕寒冷，忌高温，以20-25℃的气温生长快，超过30℃生长缓慢。雨水多，易罹根病。北京地区5月至6月植株生长快，7月生长慢，冬季不能露地越冬，只作一年生栽培；长江以南产区可作多年生栽培。在阳光充足、适宜土壤湿度环境下生长的植株生物碱含量高。从播种到种子成熟约需140d，花期植株生长最快，从开花至种子成熟是全草干物质积累的高峰时期。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>主要价值药用价值：根和叶含有莨菪碱（Hyoscyamine)，阿托品（Atropine)、东莨菪碱（Scopolamine)、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd style=\'color:yellow;text-decoration: underline\'>颠茄</a>碱（Belladonin)等。叶作镇痉及镇痛药；根治盗汗，并有散瞳的效能。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000882&idx=1&sn=44f153a1f79c15d22dfe8b7b2d73bb6a&chksm=4ffc1274788b9b62e6f9acfcd01b33d6d1472da59ddb5b9ded2b3381652543d4135867227d8c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569075607, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQ_FHZQMpusJ2GvcgbXbNyg', '0');
INSERT INTO `zbp_post` VALUES (97, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】66-《虞美人》', '虞美人（学名：Papaver rhoeas L.）：一年生草本植物，全体被伸展的刚毛，稀无毛。茎直立，高25-90厘米，具分枝。叶片轮廓披针形或狭卵形，羽状分裂，裂片披针形。花单生于茎和分枝顶端，花蕾长圆状倒卵形，下垂', '<div style=\'text-align: center;overflow: auto;\'><img title=虞美人 alt=虞美人 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctiaCiaWP8qAdtkxS6mYXiaLZ7LNgvL2ia6WAH9BpXtxxXTLErwcichCfUEic7wkaTkF6AIYQhIUOnM0ObQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>（学名：Papaver rhoeas L.）：一年生草本植物，全体被伸展的刚毛，稀无毛。茎直立，高25-90厘米，具分枝。叶片轮廓披针形或狭卵形，羽状分裂，裂片披针形。花单生于茎和分枝顶端，花蕾长圆状倒卵形，下垂；萼片2，宽椭圆形；花瓣4，圆形、横向宽椭圆形或宽倒卵形，长2.5-4.5厘米，全缘，稀圆齿状或顶端缺刻状，紫红色，基部通常具深紫色斑点。蒴果宽倒卵形，长1-2.2厘米，无毛，具不明显的肋。种子多数，肾状长圆形，长约1毫米。花果期3-8月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>是一年生草本植物，全体被伸展的刚毛，稀无毛。茎直立，高25-90厘米，具分枝，被淡黄色刚毛。叶互生，叶片轮廓披针形或狭卵形，长3-15厘米，宽1-6厘米，羽状分裂，下部全裂，全裂片披针形和二回羽状浅裂，上部深裂或浅裂、裂片披针形，最上部粗齿状羽状浅裂，顶生裂片通常较大，小裂片先端均渐尖，两面被淡黄色刚毛，叶脉在背面突起，在表面略凹；下部叶具柄，上部叶无柄。     花单生于茎和分枝顶端；花梗长10-15厘米，被淡黄色平展的刚毛。花蕾长圆状倒卵形，下垂；萼片2，宽椭圆形，长1-1.8厘米，绿色，外面被刚毛；花瓣4，圆形、横向宽椭圆形或宽倒卵形，长2.5-4.5厘米，全缘，稀圆齿状或顶端缺刻状，紫红色，基部通常具深紫色斑点；雄蕊多数，花丝丝状，长约8毫米，深紫红色，花药长圆形，长约1毫米，黄色；子房倒卵形，长7-10毫米，无毛，柱头5-18，辐射状，连合成扁平、边缘圆齿状的盘状体。蒴果宽倒卵形，长1-2.2厘米，无毛，具不明显的肋。种子多数，肾状长圆形，长约1毫米。花果期3-8月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>繁殖方式播种繁殖:春、秋季均可播种，一般情况下，春播在3-4月，花期6-7月；秋播在9-11月，花期为次年的5-6月。若为了收集种子，最好采取秋播的方式。土壤整理要细，做畦浇透水，然后撒播或条播。由于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>的种子细小，因此播种时，土壤要整平、打细，撒播后不必覆土，也可薄薄地盖上一层细沙土。覆土厚度已看不见种子为宜（0.2-0.3厘米）。在华北地区，由于冬季严寒，幼苗难以越冬，因此多采用初冬“小雪”时直播，这样可使其在春季尽早萌发生长。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>生长发育适温5-25°C，春夏温度高地区花期缩短，昼夜温差大。夜间低温有利于生长开花，在高海拔山区生长良好，花色更为艳丽。寿命3-5年。耐寒，怕暑热，喜阳光充足的环境，喜排水良好、肥沃的沙壤土。不耐移栽，忌连作与积水。能自播。花期5-8月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>的花多彩丰富、开花时薄薄的花瓣质薄如绫，光洁似绸，轻盈花冠似朵朵红云片片彩绸，虽无风亦似自摇，风动时更是飘然欲飞，颇为美观，花期也长，适宜用于花坛、花境栽植，也可盆栽或作切花用。在公园中成片栽植，景色非常宜人。因为一株上花蕾很多，此谢彼开，可保持相当长的观赏期。用作切花时须在椭半放时剪下，立即浸入温水中，防止乳汁外流过多，否则花枝很快痿缩，花朵也不能全开。药用价值：全草（</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd style=\'color:yellow;text-decoration: underline\'>虞美人</a>）、花、果实：苦、涩，凉。镇痛，镇咳，止泻。用于咳嗽，痢疾，腹痛。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000814&idx=1&sn=a110dcf611246d00cc6c59394b75409a&chksm=4ffc1228788b9b3ea8250101adfaea09eed17578a15b81b1ed2475f3256f75af726fb72c710d#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568644093, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyVMbSHfhLyF2qXSfpGkYWgM', '0');
INSERT INTO `zbp_post` VALUES (98, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】70-《狼尾花》', '狼尾花（拉丁学名：Lysimachia barystachys Bunge）为报春花科珍珠菜属多年生草本，具横走的根茎，全株密被卷曲柔毛。茎直立，高30-100厘米。总状花序顶生，花密集，常转向一侧。蒴果球形，直径2.5-4毫米。', '<div style=\'text-align: center;overflow: auto;\'><img title=狼尾花 alt=狼尾花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctzjBATcuRshVScKfwUR5cb4yjQPNXLhZhopao8Xgp9TdAH9U7esic47R2rqz2ddNM7TZDAaxSoVyA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000816&idx=1&sn=9135d6bcfea59b43ef4954f7bf5978b5&chksm=4ffc1236788b9b206adbe2b53ad860c200140ec957b12289242363c1f725a3d895dcc4e85439#rd style=\'color:yellow;text-decoration: underline\'>狼尾花</a>（虎尾草）（拉丁学名：Lysimachia barystachys Bunge）是一种报春花科珍珠菜属多年生草本，具有横走的根茎，全株密被卷曲柔毛。茎直立，高30至100cm。总状花序顶生，花密集，常转向一侧。蒴果球形，直径2.5-4mm。花期5至8月；果期8至10月。在我国大部分地区均有分布。常见生于草甸、山坡路旁灌丛间，垂直分布上限可达海拔2000m。云南民间用全草治疮疖、刀伤。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000816&idx=1&sn=9135d6bcfea59b43ef4954f7bf5978b5&chksm=4ffc1236788b9b206adbe2b53ad860c200140ec957b12289242363c1f725a3d895dcc4e85439#rd style=\'color:yellow;text-decoration: underline\'>狼尾花</a>形态特征多年生草本，具横走的根茎，全株密被卷曲柔毛。茎直立，高30至100cm。叶互生或近对生，长圆状披针形、倒披针形以至线形，长4至10cm，宽6至22mm，先端钝或锐尖，基部楔形，近于无柄。总状花序顶生，花密集，常转向一侧；花序轴长4至6cm，后渐伸长，果时长可达30cm；苞片线状钻形，花梗长4至6mm，通常稍短于苞片；花萼长3至4mm，分裂近达基部，裂片长圆形，周边膜质，顶端圆形，略呈啮蚀状；花冠白色，长7至10mm，基部合生部分长约2毫米，裂片舌状狭长圆形，宽约2mm，先端钝或微凹，常有暗紫色短腺条；雄蕊内藏，花丝基部约1.5mm连合并贴生于花冠基部，分离部分长约3mm，具腺毛；花药椭圆形，长约1mm；花粉粒具3孔沟，长球形 [(29-31.5) × (20-24)微米]，表面近于平滑；子房无毛，花柱短，长3至3.5mm。蒴果球形，直径2.5至4mm。花期5至8月；果期8至10月。      </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000816&idx=1&sn=9135d6bcfea59b43ef4954f7bf5978b5&chksm=4ffc1236788b9b206adbe2b53ad860c200140ec957b12289242363c1f725a3d895dcc4e85439#rd style=\'color:yellow;text-decoration: underline\'>狼尾花</a>生长习性生于草甸或山坡路旁灌丛间，垂直分布上限可达海拔2000m。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000816&idx=1&sn=9135d6bcfea59b43ef4954f7bf5978b5&chksm=4ffc1236788b9b206adbe2b53ad860c200140ec957b12289242363c1f725a3d895dcc4e85439#rd style=\'color:yellow;text-decoration: underline\'>狼尾花</a>主要价值药用价值：云南民间常用其全草治疮疖、刀伤。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000816&idx=1&sn=9135d6bcfea59b43ef4954f7bf5978b5&chksm=4ffc1236788b9b206adbe2b53ad860c200140ec957b12289242363c1f725a3d895dcc4e85439#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568990189, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyfVihsHOz6bsllPyCK8W9OQ', '0');
INSERT INTO `zbp_post` VALUES (99, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】72-《荷包花》', '荷包花（学名：Calceolaria crenatiflora Cav.）是玄参科、蒲包花属多年生草本植物，植株高可达30厘米，全株 茎、枝、叶上有细小茸毛，叶片卵形对生。花形别致，花冠二唇状，上唇瓣直立较小，下唇瓣膨大似蒲包状', '<div style=\'text-align: center;overflow: auto;\'><img title=荷包花 alt=荷包花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibct6pNY1ibrP3PKIYryuSmwuD86eUTwMu2ZKysejepXUj4qribKl3wYuv37icEqUIVFsCgFdEjIDwFHHA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000881&idx=1&sn=bbf2403409079647fa080e715679d599&chksm=4ffc1277788b9b61ca66e779237e5c26e483eb66df84b6dac5cd77d3c0c7097738e4cdc06ef9#rd style=\'color:yellow;text-decoration: underline\'>荷包花</a>（学名：Calceolaria crenatiflora Cav.）是一种玄参科、蒲包花属多年生草本植物，植株高可达30cm，全株茎、枝、叶上有细小茸毛，叶片卵形对生。花形很别致，花冠呈二唇状，上唇瓣直立较小，下唇瓣膨大类似蒲包状，中间形成空室，柱头着生在两个囊状物之间。花色变化丰富，单色品种有黄、白、红等深浅不同的花色，复色则在各底色上着生橙、粉、褐红等斑点。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000881&idx=1&sn=bbf2403409079647fa080e715679d599&chksm=4ffc1277788b9b61ca66e779237e5c26e483eb66df84b6dac5cd77d3c0c7097738e4cdc06ef9#rd style=\'color:yellow;text-decoration: underline\'>荷包花</a>形态特征多年生草本植物，在园林上多作一年生栽培花卉，株高多30cm，全株茎、枝、叶上有细小茸毛.叶片卵形对生。花形别致，花冠呈二唇状，上唇瓣直立较小，下唇瓣膨大似蒲包状，中间形成空室，柱头着生在两个囊状物之间。     花色变化丰富，单色品种有黄、白、红等深浅不同的花色，复色则在各底色上着生橙、粉、褐红等斑点。萌果，种子细小多粒。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000881&idx=1&sn=bbf2403409079647fa080e715679d599&chksm=4ffc1277788b9b61ca66e779237e5c26e483eb66df84b6dac5cd77d3c0c7097738e4cdc06ef9#rd style=\'color:yellow;text-decoration: underline\'>荷包花</a>繁殖方式播种繁殖:蒲包花的种子颗粒很细小，呈较粗的粉粒状。播种时最好先用厚纸折成一个三角形的小漏斗，将种子放在其中然后右手执小漏斗的下部，使种子均匀地落在播种盆内。不要过密，否则出苗后幼苗生长细弱。盖土只需用细孔筛将细土薄薄地覆上一层即可（如不覆土也无妨）。播后需在盆上盖玻璃，以保持湿度。出苗前置于阴凉处，温度保持在20度左右，7-10天左右可出苗。出苗后取下玻璃片，间苗，并使其逐渐接受阳光，温度降低到1度左右。 分株繁殖:当小苗出现2片真叶时可以移植，待其长出4至6片真叶即可单株上盆。培养土将腐叶土2份、厩肥1份、沙1份、园土6份混合使用，pH值为6.5。11至12月可上大盆定植。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000881&idx=1&sn=bbf2403409079647fa080e715679d599&chksm=4ffc1277788b9b61ca66e779237e5c26e483eb66df84b6dac5cd77d3c0c7097738e4cdc06ef9#rd style=\'color:yellow;text-decoration: underline\'>荷包花</a>好肥喜光，喜凉爽、湿润、通风良好的环境，不耐寒，也畏高温。适生于疏松、肥沃、排水良好的沙质土壤中，生长适温为10-15℃，开花期适温为10℃左右。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000881&idx=1&sn=bbf2403409079647fa080e715679d599&chksm=4ffc1277788b9b61ca66e779237e5c26e483eb66df84b6dac5cd77d3c0c7097738e4cdc06ef9#rd style=\'color:yellow;text-decoration: underline\'>荷包花</a>主要价值观赏价值：蒲包花是初春之季主要观赏花卉之一，能补充冬春季节观赏花卉不足，可作室内装饰点缀，置于阳台或室内观赏。也可用于节日花坛摆设。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000881&idx=1&sn=bbf2403409079647fa080e715679d599&chksm=4ffc1277788b9b61ca66e779237e5c26e483eb66df84b6dac5cd77d3c0c7097738e4cdc06ef9#rd style=\'color:yellow;text-decoration: underline\'>荷包花</a>的观赏范围较窄，很少用于园林景点或公园花坛栽培，大多适应在温室做小巧盆栽，供人们家居摆设。切花价值：蒲包花正值春节应市，奇特的花形，惹人喜爱。也是很好的礼仪花卉，送上一盆鲜红的蒲包花，使节日的气氛更为浓厚。若摆放窗台、阳台或客室，红花翠叶，顿时满室生辉，热闹非凡。在商厦橱窗、宾馆茶室、机场贵宾室点缀数盆蒲包花，绚丽夺目，蔚为奇趣。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000881&idx=1&sn=bbf2403409079647fa080e715679d599&chksm=4ffc1277788b9b61ca66e779237e5c26e483eb66df84b6dac5cd77d3c0c7097738e4cdc06ef9#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569152096, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyQH-9lfDgWLJ3u3bjKqls5M', '0');
INSERT INTO `zbp_post` VALUES (100, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】69-《翠雀》', '翠雀（学名：Delphinium grandiflorum L. ）：为毛茛科、翠雀属多年生草本植物。因其花形别致，酷似一只只燕子，故又名飞燕草、鸽子花。花径4厘米左右。5-10月开花。形态优雅，惹人喜爱。', '<div style=\'text-align: center;overflow: auto;\'><img title=翠雀 alt=翠雀 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsEjutlE6mKVsU8ctUqOlabvtYy8c4ias91iaf7iahWib2uSzVJs5lBtYCb5pDrRiagMvWnNxx5K66EAzw/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>（学名：Delphinium grandiflorum L. ）：为毛茛科、</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>属多年生草本植物。因其花形别致，酷似一只只燕子，故又名飞燕草、鸽子花。花径4厘米左右。5-10月开花。形态优雅，惹人喜爱。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>形态特征多年生草本，无块根。茎高35-65厘米，与叶柄均被反曲而贴伏的短柔毛，上部有时变无毛，等距地生叶，分枝。基生叶和茎下部叶有长柄；叶片圆五角形，长2.2-6厘米，宽4-8.5厘米，三全裂，中央全裂片近菱形，一至二回三裂近中脉，小裂片线状披针形至线形，宽0.6-2.5（-3.5）毫米，边缘干时稍反卷，侧全裂片扇形，不等二深裂近基部，两面疏被短柔毛或近无毛；叶柄长为叶片的3-4倍，基部具短鞘。     总状花序有3-15花；下部苞片叶状，其他苞片线形；花梗长1.5-3.8厘米，与轴密被贴伏的白色短柔毛；小苞片生花梗中部或上部，线形或丝形，长3.5-7毫米；萼片紫蓝色，椭圆形或宽椭圆形，长1.2-1.8厘米，外面有短柔毛，距钻形，长1.7-2（-2.3）厘米，直或末端稍向下弯曲；花瓣蓝色，无毛，顶端圆形；退化雄蕊蓝色，瓣片近圆形或宽倒卵形，顶端全缘或微凹，腹面中央有黄色髯毛；雄蕊无毛；心皮3，子房密被贴伏的短柔毛。蓇葖直，长1.4-1.9厘米；种子倒卵状四面体形，长约2毫米，沿稜有翅。5-10月开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>繁殖方式播种繁殖:可在9-10月份采集饱满健康的野生</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>花种子低温沙藏，于次年春季3—4月播种，发芽适温为15℃左右；秋播在9月下旬-l0月上旬，先播入露地苗床，入冬前进入冷床或冷室越冬，次年春季幼苗发出2-4片真叶时移植，4-7片真叶时定植，间苗保持25-50厘米株距；栽前施足基肥，追肥以氮肥为主。老龄植株长势衰弱，2-3年需移栽一次。为防止植株高大发生倒伏或弯曲，可在苗期控制水分进行蹲苗。此外，在采集野生种子进行人工育种过程中，须注意野生</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>花的萌发特性（需要春化作用），要采用低温沙藏法处理种子才能成功完成种子萌发繁育。对分株繁殖的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>花子株，也要保证生长点的低温刺激才能形成花芽。 分株繁殖:春、秋季均可进行。选取生长旺盛的母株，用工具在基部小心切割成若干子株，直接移栽至花盆或大田中浇透水分，有条件的情况下搭凉棚遮阴一周。 扦插繁殖:春季新芽长至15-18厘米时，选取生长健壮的枝叶（只留2-3个叶片）扦插于苗床（最好是砂质土壤）上，生根后移栽至花盆或大田；也可于花后取基部的新枝进行扦插。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>生长习性喜凉爽通风、日照充足的干燥环境和排水通畅的砂质壤土。耐旱植物，喜光植物。阳性，耐半阴，性强健，耐干旱，耐寒，喜冷凉气候，忌炎热。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>花因其花色大多为蓝紫色或淡紫色，花型似蓝色飞燕落满枝头，因而又名“飞燕草”，是珍贵的蓝色花卉资源，具有很高的观赏价值。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd style=\'color:yellow;text-decoration: underline\'>翠雀</a>花欧洲一些国家于l7世纪就开始园艺化栽培，全球已培育出数千个观赏栽培品种，并广泛用于庭院绿化、盆栽观赏和切花生产。药用价值：根：苦，寒。有毒。泻火止痛，杀虫。含漱用于风热牙痛。全草：外用于疥癣。种子：用于哮喘。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000815&idx=1&sn=7b1beb09356c680eda908a739a5f6b5c&chksm=4ffc1229788b9b3f920a4e2da3a793468ac83f7ebd8ba044813a271377d53e2bf9e86822aa89#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568901742, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyaOR6iE7q5JcKcdyfJ0qeFQ', '0');
INSERT INTO `zbp_post` VALUES (101, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】68-《贝壳花》', '贝壳花（学名：Moluccella laevis Linn.）是唇形科，贝壳花属一、二年草本植物。株高50—60厘米，茎四棱，不分枝。叶对生，心脏状圆形，边缘疏生齿牙；叶柄与叶近等长。花白色，花冠唇形；小坚果光滑。花期6—7月。', '<div style=\'text-align: center;overflow: auto;\'><img title=贝壳花 alt=贝壳花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuXTiaBeSgZzy3SShkQ20T3cjxCpbWZzDZib1360icicK8Jubj1s3tSf6BxPtrrOicyLTLBUmlTuFJReRA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>（学名：Moluccella laevis Linn.）是属于唇形科，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>是一、二年的草本。植株高5至60cm，茎四棱，不分枝。叶对生，心脏状圆形，边缘疏生齿牙；叶柄和叶近等长。花的颜色为白色，花冠呈唇形；小坚果光滑。花期在6至7月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>形态特征植株高度为40至100厘米，心状叶对生，具钝齿。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>的种子是呈三角形状，播下后大概1周左右出苗，幼苗生长缓慢，20天后才开始长真叶，1个多月后还是小苗。大概2个多月后才有一点样子，约3个月左右就可以看到花了。小喇叭样的是它的萼片，小花白色，六朵轮生，芳香花萼似贝壳，花期为6至7月，绿色甚为美观。花冠唇状，花萼绿色似贝壳，故名。茎杆直立，茎四棱，分枝力弱。       </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>繁殖方式播种繁殖:播种法，秋、冬、早春都可播种。用播种方法进行繁殖，春、夏、秋均可，播种适温为15至25℃，约一周发芽，5至6片真叶时即可移栽。栽培用土以肥沃、疏松、排水良好的壤土为佳，土质不宜过粘，否则不发棵，生长缓慢。移栽成活后开始施肥，半月一次，开花后停止施肥。为促其分枝，可进行摘心。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>长势强健，极少有病虫害发生，但要注意潜叶蝇的危害。为观赏需要，可分期分比播种。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>生长习性观叶观花植物，喜阳光及排水良好的土壤。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>花型奇特，素雅美观，是世界流行的插花衬材，也可用作干花及盆栽观赏。现有各种园艺品种，，但以花萼绿色为多，有白色，黄色等类型。每盆</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd style=\'color:yellow;text-decoration: underline\'>贝壳花</a>每年可以吸收150克二氧化碳，释放56克的氧气，是一种非常绿色环保的植物。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000813&idx=1&sn=236e5fb8d7c2e5e850946ba6975f9ced&chksm=4ffc122b788b9b3dfe34ab90e0fe2882986e6c12e821cc35a81a11e78c4472bba255346cfaf9#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568821204, 0, 0, 'GvnDcC2QjGEN4AUqdM7Dyd9UzmUI-XDniOKvcOG-7yI', '0');
INSERT INTO `zbp_post` VALUES (102, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】67-《蓝金花》', '蓝金花，又名蓝鲸花。是属于玄参科植物，分布于巴西。株高50～90公分，叶对生，长椭圆形，先端锐，细锯齿缘。春至秋季开花，腋生，花冠长管状，花瓣蓝紫色，花姿奇特别致，花期极长，适合庭植美化、盆栽或切花。', '<div style=\'text-align: center;overflow: auto;\'><img title=蓝金花 alt=蓝金花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsuOiaRibibEv0FoPfvWSVQ2jXS964IYicowywvlduSEhZZOCNVpeJYgIReFJjM1I7FFOyrgFVdicgerFQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000811&idx=1&sn=f21be9bec74ef397a4e92e02d33200df&chksm=4ffc122d788b9b3b748a1e49ddf7eca11b3d07047b8ffadbe0df49efaeb68085cd3fd770ed9c#rd style=\'color:yellow;text-decoration: underline\'>蓝金花</a>，又名蓝鲸花。是属于玄参科植物，分布于巴西。株高50～90公分，叶对生，长椭圆形，先端锐，细锯齿缘。春至秋季开花，腋生，花冠长管状，花瓣蓝紫色，花姿奇特别致，花期极长，适合庭植美化、盆栽或切花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000811&idx=1&sn=f21be9bec74ef397a4e92e02d33200df&chksm=4ffc122d788b9b3b748a1e49ddf7eca11b3d07047b8ffadbe0df49efaeb68085cd3fd770ed9c#rd style=\'color:yellow;text-decoration: underline\'>蓝金花</a>形态特征株高50～90公分，叶对生，长椭圆形，先端锐，细锯齿缘。春至秋季开花，腋生，花冠长管状，花瓣蓝紫色，花姿奇特别致，花期极长。         </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000811&idx=1&sn=f21be9bec74ef397a4e92e02d33200df&chksm=4ffc122d788b9b3b748a1e49ddf7eca11b3d07047b8ffadbe0df49efaeb68085cd3fd770ed9c#rd style=\'color:yellow;text-decoration: underline\'>蓝金花</a>繁殖方式扦插法，春、秋季为适期。   </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000811&idx=1&sn=f21be9bec74ef397a4e92e02d33200df&chksm=4ffc122d788b9b3b748a1e49ddf7eca11b3d07047b8ffadbe0df49efaeb68085cd3fd770ed9c#rd style=\'color:yellow;text-decoration: underline\'>蓝金花</a>生长习性栽培土质以肥沃之砂质壤土为佳，排水需良好，全日照、半日照均理想。施肥每月一次，花谢后立即剪除花茎，补给肥料，能萌发新枝再开花；每年春季修剪整枝，植株老化施以强剪或更新栽培。性喜温暖，耐高温，生育适温摄氏18～28度</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000811&idx=1&sn=f21be9bec74ef397a4e92e02d33200df&chksm=4ffc122d788b9b3b748a1e49ddf7eca11b3d07047b8ffadbe0df49efaeb68085cd3fd770ed9c#rd style=\'color:yellow;text-decoration: underline\'>蓝金花</a>主要价值观赏价值：适合庭植美化、盆栽或切花。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000811&idx=1&sn=f21be9bec74ef397a4e92e02d33200df&chksm=4ffc122d788b9b3b748a1e49ddf7eca11b3d07047b8ffadbe0df49efaeb68085cd3fd770ed9c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568731122, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyaUC6-BNiD2UPWhNZx5wRT4', '0');
INSERT INTO `zbp_post` VALUES (103, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】64-《随意草》', '随意草（学名：Physostegia virginiana (L.) Benth.）：为唇形科随意草属，多年生宿根草本植物，具匍匐茎。株高60-120厘米，茎四方形。叶对生，披针形，叶缘有细锯齿，叶秀花艳，成株丛生状，盛开的花穗迎风摇曳', '<div style=\'text-align: center;overflow: auto;\'><img title=随意草 alt=随意草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsxu9utJRfgksic6Bjw5CBK2rmjJn144z3ZVFwKiae84p5SwF8ccfY3KNn4uajLtqeibbL8v6HTge3vA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>（学名：Physostegia virginiana (L.) Benth.）：为唇形科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>属，多年生宿根草本植物，具匍匐茎。株高60-120厘米，茎四方形。叶对生，披针形，叶缘有细锯齿，叶秀花艳，成株丛生状，盛开的花穗迎风摇曳，婀娜多姿。生性强健，地下匍匐茎易生幼苗，栽培1株后，常自行繁殖无数幼株。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>是多年生草本植物，株高60-120厘米，地上茎直立丛生，有根茎，地上茎直立呈四棱状。叶对生，长椭圆至披针形，缘有锯齿，呈亮绿色。穗状花序聚成圆锥花序状，顶生，长20-30厘米，单一或分枝。唇形花冠，花序自下端往上逐渐绽开，小花密集。如将小花推向一边，不会复位，因而得名。小花玫瑰紫色。花期夏季、持久。有白、粉色、深桃红、红、玫红、雪青、紫红或斑叶变种。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>繁殖方式分株繁殖:分株宜在春季萌发前进行，一般3年左右分株一次。春、秋季为分株适期， 只要切取成株长出的幼株或地下根茎另植即可。亦可在秋季剪取健壮新芽，扦插于排水良好的砂床，待发根后再移植。 播种繁殖:因</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>结实率不高，种子稀，播种繁殖的方法很少使用。扦插繁殖:扦插只要在生长季节都可以进行。通常在4-5月取当年萌发的新梢10厘米左右，扦于砻糠灰或直接扦于中。在口径20厘米的盆中，每盆扦插2-3株，扦后保湿，约2周生根。在成活并萌发新梢后，要注意施肥，10天左右施一次。新梢长至10厘米左右时，仅需保留2节，将顶梢掐去，以控制株高，并促其分枝。以后视其发育和对花期的要求，株高情况，若株型过高，还需掐去顶梢。生长期间，要给予充足的阳光，但盛夏最好能稍加遮荫。长期光照不足，会导致徒长，节间过长。水分宜充足一些，不能太干。气候干燥时，每天还应喷水1-2次，否则会因干旱、干燥而导致老叶焦边脱落。第一批花后，要及时去掉残花，并追施粪肥2次，促进新梢生长，若肥力不差，可以二次着花。入冬后，地上部分容易枯萎，盆栽的，应移至背风处，稍保持土壤湿润就能越冬。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>生长习性喜温暖，耐寒性也较强。喜阳光充足的环境，但不耐强光暴晒，生长适温18-28℃。荫蔽处植株易徒长，开花不良。宜疏松、肥沃和排水良好的沙质壤土。夏季干燥则生长不良。喜湿润，不耐旱。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd style=\'color:yellow;text-decoration: underline\'>随意草</a>主要价值观赏价值：红花石蒜是东亚常见的园林观赏植物，冬赏其叶，秋赏其花。此花多用于别墅花园种植，一般成百上千只一起种，秋季会大批开放，景观效果极佳。此外，由于石蒜的根茎与水仙十分相似，都是球状茎。有些黑心花贩会用它冒充水仙来卖钱，所以买花时要仔细甄别，以防上当。药用价值：药用鳞茎：含有多种生物碱，如石蒜碱、石蒜胺碱、加兰他敏等。具有祛风消肿、解毒抗癌作用。用于治疗胃癌、食管癌、肝癌，并试治肺癌、卵巢癌、宫颈癌、淋巴癌。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000752&idx=1&sn=d136181654c63da8d5955fe5c33f14e8&chksm=4ffc15f6788b9ce0b3181ccded4de4ea9a934c3395b2b0ab827e2e4e02069bb4bf6ecb5d92b5#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568472301, 0, 0, 'GvnDcC2QjGEN4AUqdM7DycRIcyi_b-Y277Ex5GeOPsA', '0');
INSERT INTO `zbp_post` VALUES (104, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】65-《腊梅》', '腊梅，蜡梅科蜡梅属，落叶灌木，常丛生。叶对生，椭圆状卵形至卵状披针形，花着生于第二年生枝条叶腋内，先花后叶，芳香，直径2~4厘米；花被片圆形、长圆形、倒卵形、椭圆形或匙形，无毛，花丝比花药长或等长，花药内弯，无毛，花柱长达子房3倍，', '<div style=\'text-align: center;overflow: auto;\'><img title=腊梅 alt=腊梅 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcutQ8cX1oM1Zicsj6lAOaKe0A0jsib9b0UBkaA8fn4zL4kTwQFepPRukcbpHzPR70IgjibfZRggYZNJA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000810&idx=1&sn=4c27ce0661a1bfdad7d146bc6c398941&chksm=4ffc122c788b9b3ab526ca6a22a081785f0f87ab38457c36d306990aef7f53fd6e63199441aa#rd style=\'color:yellow;text-decoration: underline\'>腊梅</a>、蜡花、黄梅花。蜡梅科蜡梅属，落叶灌木，常丛生。叶对生，椭圆状卵形至卵状披针形，花着生于第二年生枝条叶腋内，先花后叶，芳香，直径2~4厘米；花被片圆形、长圆形、倒卵形、椭圆形或匙形，无毛，花丝比花药长或等长，花药内弯，无毛，花柱长达子房3倍，基部被毛。果托近木质化，口部收缩，并具有钻状披针形的被毛附生物。冬末先叶开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000810&idx=1&sn=4c27ce0661a1bfdad7d146bc6c398941&chksm=4ffc122c788b9b3ab526ca6a22a081785f0f87ab38457c36d306990aef7f53fd6e63199441aa#rd style=\'color:yellow;text-decoration: underline\'>腊梅</a>形态特征落叶灌木，高达4米；幼枝四方形，老枝近圆柱形，灰褐色，无毛或被疏微毛，有皮孔；鳞芽通常着生于第二年生的枝条叶腋内，芽鳞片近圆形，覆瓦状排列，外面被短柔毛。叶纸质至近革质，卵圆形、椭圆形、宽椭圆形至卵状椭圆形，有时长圆状披针形，长5-25厘米，宽2-8厘米，顶端急尖至渐尖，有时具尾尖，基部急尖至圆形，除叶背脉上被疏微毛外无毛。     花着生于第二年生枝条叶腋内，先花后叶，芳香，直径2-4厘米；花被片圆形、长圆形、倒卵形、椭圆形或匙形，长5-20毫米，宽5-15毫米，无毛，内部花被片比外部花被片短，基部有爪；雄蕊长4毫米，花丝比花药长或等长，花药向内弯，无毛，药隔顶端短尖，退化雄蕊长3毫米；心皮基部被疏硬毛，花柱长达子房3倍，基部被毛。果托近木质化，坛状或倒卵状椭圆形，长2-5厘米，直径1-2.5厘米，口部收缩，并具有钻状披针形的被毛附生物。花期11月至翌年3月，果期4-11月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000810&idx=1&sn=4c27ce0661a1bfdad7d146bc6c398941&chksm=4ffc122c788b9b3ab526ca6a22a081785f0f87ab38457c36d306990aef7f53fd6e63199441aa#rd style=\'color:yellow;text-decoration: underline\'>腊梅</a>繁殖方式蜡梅繁殖一般以嫁接为主，分株、播种、扦插、压条也可。嫁接以切接为主，也可采用靠接和芽接。切接多在3~4月进行，当叶芽萌动有麦粒大小时嫁接最易成活。如芽发得过大，接后很难成活。切接前一个月，就要从壮龄母树上，选粗壮而又较长的一年生枝，截去顶梢，使养分集中到枝的中段，则有利于嫁接成活。接穗长约6~7厘米，砧木可用狗蝇蜡梅或用4~5年生蜡梅实生苗。砧木切口可略长，深达木质部为宜，扎缚后的切口要涂以泥浆，然后壅土封起。接后约一个月，即可扒开封土检查成活。用切接法繁殖的蜡梅，生长旺盛，当年可高达40~60厘米。靠接繁殖多在5月份前后进行，砧木多用数年生蜡梅实生苗。先把砧木苗上盆培养成活，把它们搬至用作接穗的母枝附近，选择母枝上和砧木苗粗细相当的枝条，在适当部位削成梭形切口，长约3~5厘米，深达木质部。削口要平展，砧木和接穗的削口长短和大小要一致，然后把它们靠在一起，使四周的形成层相互对齐，用塑料带自下而上紧密绑扎在一起。嫁接成活后先自接口下面将接穗剪断，再把切口上面的砧木枝梢剪掉即成。  </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000810&idx=1&sn=4c27ce0661a1bfdad7d146bc6c398941&chksm=4ffc122c788b9b3ab526ca6a22a081785f0f87ab38457c36d306990aef7f53fd6e63199441aa#rd style=\'color:yellow;text-decoration: underline\'>腊梅</a>生长习性蜡梅性喜阳光，能耐荫、耐寒、耐旱，忌渍水。蜡梅花在霜雪寒天傲然开放，花黄似蜡，浓香扑鼻，是冬季观赏主要花木。怕风，较耐寒，在不低于-15℃时能安全越冬，北京以南地区可露地栽培，花期遇-10℃低温，花朵受冻害。好生于土层深厚、肥沃、疏松、排水良好的微酸性沙质壤土上，在盐碱地上生长不良。耐旱性较强，怕涝，故不宜在低洼地栽培。树体生长势强，分枝旺盛，根茎部易生萌蘖。耐修剪，易整形。先花后叶，花期11月~翌年3月，7~8月成熟。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000810&idx=1&sn=4c27ce0661a1bfdad7d146bc6c398941&chksm=4ffc122c788b9b3ab526ca6a22a081785f0f87ab38457c36d306990aef7f53fd6e63199441aa#rd style=\'color:yellow;text-decoration: underline\'>腊梅</a>主要价值园林价值：蜡梅是冬季赏花的理想名贵花木。它更广泛地应用于城乡园林建设。药用价值：解暑生津，顺气止咳。用于暑热心烦、口渴、百日咳、肝胃气痛、水火烫伤。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000810&idx=1&sn=4c27ce0661a1bfdad7d146bc6c398941&chksm=4ffc122c788b9b3ab526ca6a22a081785f0f87ab38457c36d306990aef7f53fd6e63199441aa#rd style=\'color:yellow;text-decoration: underline\'>腊梅</a>花 语慈爱之心，高尚的心灵。忠实、独立、坚毅、忠贞、刚强、坚贞、高洁。高风亮节、傲气凌人、澄澈的心、浩然正气、独立创新。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000810&idx=1&sn=4c27ce0661a1bfdad7d146bc6c398941&chksm=4ffc122c788b9b3ab526ca6a22a081785f0f87ab38457c36d306990aef7f53fd6e63199441aa#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1568558626, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyWlvOWG7-U0SOUpKPF4XaTI', '0');
INSERT INTO `zbp_post` VALUES (105, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】83-《南茼蒿》', '南茼蒿，拉丁学名：Chrysanthemum segetum，菊科筒蒿属植物。一年生草本,茎和叶均可食,是我国南方各省区重要的春季蔬菜之一。光滑无毛或几光滑无毛，高20-60厘米。茎直立，富肉质。叶椭圆形、倒卵状披针形或倒卵状椭圆形', '<div style=\'text-align: center;overflow: auto;\'><img title=南茼蒿 alt=南茼蒿 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvibSjsLqCuXyg0ibicXDnaz8nickuxjhE1icqK9Sdjfibqyt7RGJI8dx4ukLzLYt8BCrLDgpG1AlwXEibzA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000985&idx=1&sn=98b35fddcfde22c07a88e8e6fb3ba569&chksm=4ffc12df788b9bc943cda03fd9b0d23c37989939efab1368c8a1e0826a4f17a7479813eb3c63#rd style=\'color:yellow;text-decoration: underline\'>南茼蒿</a>，拉丁学名：Chrysanthemum segetum，菊科筒蒿属植物。一年生草本,茎和叶均可食,是我国南方各省区重要的春季蔬菜之一。光滑无毛或几光滑无毛，高20-60厘米。茎直立，富肉质。叶椭圆形、倒卵状披针形或倒卵状椭圆形，边缘有不规则的大锯齿，少有成羽状浅裂的，长41-6厘米，基部楔形，无柄。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000985&idx=1&sn=98b35fddcfde22c07a88e8e6fb3ba569&chksm=4ffc12df788b9bc943cda03fd9b0d23c37989939efab1368c8a1e0826a4f17a7479813eb3c63#rd style=\'color:yellow;text-decoration: underline\'>南茼蒿</a>形态特征光滑无毛或几光滑无毛，高20-60厘米。茎直立，富肉质。叶椭圆形、倒卵状披针形或倒卵状椭圆形，边缘有不规则的大锯齿，少有成羽状浅裂的，长4-6厘米，基部楔形，无柄。头状花序单生茎端或少数生茎枝顶端，但不形成伞房花序，花梗长5厘米。总苞径1-2厘米。内层总苞片顶端膜质扩大几成附片状。舌片长达1.5厘米。舌状花瘦果有2条具狭翅的侧肋，间肋不明显，每面3-6条，贴近。管状花瘦果的肋约10条，等形等距，椭圆状。花果期3-6月。       </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000985&idx=1&sn=98b35fddcfde22c07a88e8e6fb3ba569&chksm=4ffc12df788b9bc943cda03fd9b0d23c37989939efab1368c8a1e0826a4f17a7479813eb3c63#rd style=\'color:yellow;text-decoration: underline\'>南茼蒿</a>繁殖方式茼蒿种植主要采取撒播或条播，播种后覆土1厘米左右，耙平镇压。春播一般在3~4月间，秋种在8~9月间，冬种在11~12月间。小叶品种适于密植，用种量大，每亩2~2.50千克；大叶种侧枝多，开展度大，用种量小，每亩1千克左右。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000985&idx=1&sn=98b35fddcfde22c07a88e8e6fb3ba569&chksm=4ffc12df788b9bc943cda03fd9b0d23c37989939efab1368c8a1e0826a4f17a7479813eb3c63#rd style=\'color:yellow;text-decoration: underline\'>南茼蒿</a>生长习性半耐寒性蔬菜，在冷凉温和，土壤相对湿度保持在70%--80%的环境下，有利于其生长。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000985&idx=1&sn=98b35fddcfde22c07a88e8e6fb3ba569&chksm=4ffc12df788b9bc943cda03fd9b0d23c37989939efab1368c8a1e0826a4f17a7479813eb3c63#rd style=\'color:yellow;text-decoration: underline\'>南茼蒿</a>主要价值食用价值：我国南方各省区普遍作蔬菜栽培，食其肉茎及叶。药用价值：全草和种子药用，能和脾胃、利小便、消痰饮，治肝气不舒、偏坠气痛、小便不利。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000985&idx=1&sn=98b35fddcfde22c07a88e8e6fb3ba569&chksm=4ffc12df788b9bc943cda03fd9b0d23c37989939efab1368c8a1e0826a4f17a7479813eb3c63#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1570065808, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyaxICXt4paw3a-XxvBrdELo', '0');
INSERT INTO `zbp_post` VALUES (106, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】82-《琉璃晃》', '大戟科大戟属多肉植物，又名琉璃光，原产南非，多年生肉质植物。耐干旱，刚栽时少浇水，生长期每周浇水一次，冬季每月浇水一次。光照：全日照。施肥：较喜肥，生长期每月施肥一次。注意：琉璃晃汁液有毒的，对眼睛，皮肤和粘膜特别危险。', '<div style=\'text-align: center;overflow: auto;\'><img title=琉璃晃 alt=琉璃晃 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcugiaLibmL8J0lMjtsTg3BkvicVKoMwZ6skFfiaLU51GhmLgAIYJLI6NYRicG67ybbyALyqNcicKKyD8ZrQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000984&idx=1&sn=2e30d676ffbda4e1491b1da542d4824d&chksm=4ffc12de788b9bc800fc65cf6c81643740a1d886577e1367cd36e5f3bccb10e86ce3cc8d311d#rd style=\'color:yellow;text-decoration: underline\'>琉璃晃</a>汁液有毒的，对眼睛，皮肤和粘膜特别危险。所以要极端注意不要让任何汁液接触你的眼睛或嘴巴。栽培植物必须小心处理。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000984&idx=1&sn=2e30d676ffbda4e1491b1da542d4824d&chksm=4ffc12de788b9bc800fc65cf6c81643740a1d886577e1367cd36e5f3bccb10e86ce3cc8d311d#rd style=\'color:yellow;text-decoration: underline\'>琉璃晃</a>形态特征多年生肉质植物。株高8~10厘米，株幅15~20厘米。茎球状或短圆筒形，很容易从旁生出新茎是因为有很多的生长点，花开于顶端棱角的软刺之间。易生不定芽，常群生，茎具12~20条纵向排列的锥状疣突，绿色。叶片着生在每个疣突的顶端，细小，早落。聚伞花序，着生在主茎或分枝茎的顶端，花杯状，黄绿色。花期夏季       </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000984&idx=1&sn=2e30d676ffbda4e1491b1da542d4824d&chksm=4ffc12de788b9bc800fc65cf6c81643740a1d886577e1367cd36e5f3bccb10e86ce3cc8d311d#rd style=\'color:yellow;text-decoration: underline\'>琉璃晃</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000984&idx=1&sn=2e30d676ffbda4e1491b1da542d4824d&chksm=4ffc12de788b9bc800fc65cf6c81643740a1d886577e1367cd36e5f3bccb10e86ce3cc8d311d#rd style=\'color:yellow;text-decoration: underline\'>琉璃晃</a>繁殖可用扦插法，也可以用分株法。扦插要用健壮的枝叶，剪下后把它放在阴凉的位置晾晒一下，最后把它扦插到土壤里面。分株法也很容易，只需要将旁生的小株剪下，把它种植到盆土里面就好了。繁殖的时候要选对时间，尽可能的避开植株的休眠期，不然成活率会低一些。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000984&idx=1&sn=2e30d676ffbda4e1491b1da542d4824d&chksm=4ffc12de788b9bc800fc65cf6c81643740a1d886577e1367cd36e5f3bccb10e86ce3cc8d311d#rd style=\'color:yellow;text-decoration: underline\'>琉璃晃</a>是一个小的种群，按少数个体长在砂石土地中，常发现于白色石英砂石表面上。他们大部分生活在裸露的地面上，也部分生长于小灌木丛的阴影中。野生的</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000984&idx=1&sn=2e30d676ffbda4e1491b1da542d4824d&chksm=4ffc12de788b9bc800fc65cf6c81643740a1d886577e1367cd36e5f3bccb10e86ce3cc8d311d#rd style=\'color:yellow;text-decoration: underline\'>琉璃晃</a>大部分植株隐藏生长在地下，完全生长在地面之上的部分，形状像一个半球或一个垫子</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000984&idx=1&sn=2e30d676ffbda4e1491b1da542d4824d&chksm=4ffc12de788b9bc800fc65cf6c81643740a1d886577e1367cd36e5f3bccb10e86ce3cc8d311d#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1570024159, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyRXh4sNd0H0WYCY_HL7u468', '0');
INSERT INTO `zbp_post` VALUES (107, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】78-《磨盘草》', '磨盘草（Abutilon indicum (Linn.) Sweet），又名：金花草、唐挡草。一年生成多年生、直立、分枝，亚灌木状草本，高0.5～2.5米，全部皆被灰色短柔毛。生于砂地、旷野或路旁。果为倒圆形似磨盘，直径约1.5厘米', '<div style=\'text-align: center;overflow: auto;\'><img title=磨盘草 alt=磨盘草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvGIoQoURBp7CDqLgmRibJTkkXoJuHibNoP0szXOZ1TOz6NvR1j3VgV5sM4maRlsqKK1JscOWtzWd7Q/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000887&idx=1&sn=a2e9bdd3d0d02e2bb9031bdd2bfb4f60&chksm=4ffc1271788b9b676e3fab01bb672fb6a7dbe156b1c1c097750ffa47e4e47babadd7c8f138b1#rd style=\'color:yellow;text-decoration: underline\'>磨盘草</a>（Abutilon indicum (Linn.) Sweet），又名：金花草、唐挡草。一年生成多年生、直立、分枝，亚灌木状草本，高0.5～2.5米，全部皆被灰色短柔毛。生于砂地、旷野或路旁。果为倒圆形似磨盘，直径约1.5厘米，黑色，分果爿15-20，先端截形，具短芒，被星状长硬毛；种子肾形，被星状疏柔毛。花期7-10月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000887&idx=1&sn=a2e9bdd3d0d02e2bb9031bdd2bfb4f60&chksm=4ffc1271788b9b676e3fab01bb672fb6a7dbe156b1c1c097750ffa47e4e47babadd7c8f138b1#rd style=\'color:yellow;text-decoration: underline\'>磨盘草</a>形态特征一年生或多年生直立的亚灌木状草本，高达1-2.5米，分枝多，全株均被灰色短柔毛。叶卵圆形或近圆形，长3-9厘米，宽2.5 7厘米，先端短尖或渐尖，基部心形，边缘具不规则锯齿，两面均密被灰色星状柔毛；叶柄长2-4厘米，被灰色短柔毛和疏丝状长毛，毛长约1毫米；托叶钻形，长1 2毫米，外弯。     花单生于叶腋，花梗长达4厘米，近顶端具节，被灰色星状柔毛；花萼盘状，绿色，直径6-10毫米，密被灰色柔毛，裂片5，宽卵形，先端短尖；花黄色，直径2-2.5厘米，花瓣5，长7-8毫米；雄蕊柱被星状硬毛；心皮15-20，成轮状，花柱枝5，柱头头状。果为倒圆形似磨盘，直径约1.5厘米，黑色，分果爿15-20，先端截形，具短芒，被星状长硬毛；种子肾形，被星状疏柔毛。花期7-10月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000887&idx=1&sn=a2e9bdd3d0d02e2bb9031bdd2bfb4f60&chksm=4ffc1271788b9b676e3fab01bb672fb6a7dbe156b1c1c097750ffa47e4e47babadd7c8f138b1#rd style=\'color:yellow;text-decoration: underline\'>磨盘草</a>繁殖方式种子繁殖:用种子繁殖。造成熟饱满种子，翌年3月直播，按行株距35cm×30cm开穴，每穴播种子3-4颗，覆土3cm，播后浇水保湿，7-10d即可发芽出苗。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000887&idx=1&sn=a2e9bdd3d0d02e2bb9031bdd2bfb4f60&chksm=4ffc1271788b9b676e3fab01bb672fb6a7dbe156b1c1c097750ffa47e4e47babadd7c8f138b1#rd style=\'color:yellow;text-decoration: underline\'>磨盘草</a>生长习性常生于海拔800米以下的地带，如平原、海边、砂地、旷野、山坡、河谷及路旁等处。喜温暖湿润和阳光充足的气候，生长适温在25-30℃，不耐寒，一般土壤均能种植，较耐旱，喜肥，在疏松而肥沃的土壤上生长茂盛。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000887&idx=1&sn=a2e9bdd3d0d02e2bb9031bdd2bfb4f60&chksm=4ffc1271788b9b676e3fab01bb672fb6a7dbe156b1c1c097750ffa47e4e47babadd7c8f138b1#rd style=\'color:yellow;text-decoration: underline\'>磨盘草</a>主要价值药用价值：干燥全草主干粗约2厘米，有分枝，外皮有网格状皱纹，淡灰褐色如被粉状，触之有柔滑感。叶皱缩，浅灰绿色，背面色淡，少数呈浅黄棕色，被短柔毛，手捻之较柔韧面不易碎，有时叶腋有花或果。气微。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000887&idx=1&sn=a2e9bdd3d0d02e2bb9031bdd2bfb4f60&chksm=4ffc1271788b9b676e3fab01bb672fb6a7dbe156b1c1c097750ffa47e4e47babadd7c8f138b1#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569680494, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyYWTMgUPZIapGp8E6Q-tI-E', '0');
INSERT INTO `zbp_post` VALUES (108, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】81-《黄桷兰》', '黄桷兰，又名白兰（植物名实图考） 白玉兰。是木兰科、木兰属的落叶乔木植物。黄桷兰枝干挺拔， 高达17米，幼枝、叶互生，薄革质，叶色苍翠，花单生于叶腋，白色，花朵素雅，极香，香味清幽甜润，含苞欲放时香味最浓郁；花期长，温度适宜可花开不绝。', '<div style=\'text-align: center;overflow: auto;\'><img title=黄桷兰 alt=黄桷兰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvspmjMPrWCwxeDWibAZiaGibLxAkiarvJyJjeYIR2WJbbOicl19UqYtmIPoHib7iaVmUSpojlVH5UFcic8IA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd style=\'color:yellow;text-decoration: underline\'>黄桷兰</a>，又名白兰（植物名实图考） 白玉兰。是木兰科、木兰属的落叶乔木植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd style=\'color:yellow;text-decoration: underline\'>黄桷兰</a>枝干挺拔， 高达17米，幼枝、叶互生，薄革质，叶色苍翠，花单生于叶腋，白色，花朵素雅，极香，香味清幽甜润，含苞欲放时香味最浓郁；花期长，温度适宜可花开不绝。有独特的生长习性。原产印度尼西亚爪哇，现广植于东南亚。我国福建、广东、广西、云南等省区栽培极盛，长江流域各省区多盆栽，在温室越冬。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd style=\'color:yellow;text-decoration: underline\'>黄桷兰</a>形态特征常绿乔木，高达17米，枝广展，呈阔伞形树冠；胸径30厘米；树皮灰色；揉枝叶有芳香；嫩枝及芽密被淡黄白色微柔毛，老时毛渐脱落。叶薄革质，长椭圆形或披针状椭圆形，长10-27厘米，宽4-9.5厘米，先端长渐尖或尾状渐尖，基部楔形，上面无毛，下面疏生微柔毛，干时两面网脉均很明显；叶柄长1.5-2厘米，疏被微柔毛；托叶痕几达叶柄中部。     花白色，极香；花被片10片，披针形，长3-4厘米，宽3-5毫米；雄蕊的药隔伸出长尖头；雌蕊群被微柔毛，雌蕊群柄长约4毫米；心皮多数，通常部分不发育，成熟时随着花托的延伸，形成蓇葖疏生的聚合果；蓇葖熟时鲜红色。花期4-9月，夏季盛开，通常不结实。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd style=\'color:yellow;text-decoration: underline\'>黄桷兰</a>繁殖方式少见结实，多用嫁接繁殖，用黄兰、含笑、火力楠等为砧木；也可用空中压条或靠接繁殖。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd style=\'color:yellow;text-decoration: underline\'>黄桷兰</a>生长习性阳性要求阳光充足。喜暖热湿润，喜酸性土，不耐碱土，不耐干旱，忌过于潮湿，尤忌积水。不耐寒，冬季室内最低温度应保持在5℃以上。宜排水良好、疏松肥沃的微酸性土壤。抗烟能力差。易结果，一般2～3年生嫁接苗开花后即可结果。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd style=\'color:yellow;text-decoration: underline\'>黄桷兰</a>主要价值装饰价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd style=\'color:yellow;text-decoration: underline\'>黄桷兰</a>独特的香味，常被人们用线串起来，挂在胸前点缀或者挂在轿车的后视镜。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000983&idx=1&sn=64fc2198b99e1ad5d5a1fb94bcc0dcf2&chksm=4ffc12d1788b9bc7e6e0b2ec8ab0414d25b2040551d5e04961c0dc816be51d4ad5303873ecf4#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569935583, 0, 0, 'GvnDcC2QjGEN4AUqdM7DydresNxPJHAKNHHi6uxOrZg', '0');
INSERT INTO `zbp_post` VALUES (109, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】80-《红球姜》', '红球姜（学名：Zingiber zerumbet (L.) Smith）是姜科姜属多年生草本植物，根茎块状，株高可达2米。叶片披针形至长圆状披针形，无柄或短柄；总花梗长可达30厘米，花序球果状，顶端钝，苞片覆瓦状排列，紧密，近圆形', '<div style=\'text-align: center;overflow: auto;\'><img title=红球姜 alt=红球姜 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctjcmvqKDlib2WSWaSPYeROxorJO4eHiawJjVYUKKz6rYCoCibYFNBR9V4vtibDlgQDGecALiclmYQc0AQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd style=\'color:yellow;text-decoration: underline\'>红球姜</a>（学名：Zingiber zerumbet (L.) Smith）是姜科姜属多年生草本植物，根茎块状，株高可达2米。叶片披针形至长圆状披针形，无柄或短柄；总花梗长可达30厘米，花序球果状，顶端钝，苞片覆瓦状排列，紧密，近圆形，初时淡绿色，后变红色，边缘膜质，花萼膜质，花冠管纤细，裂片披针形，淡黄色，唇瓣淡黄色，侧裂片倒卵形，蒴果椭圆形，种子黑色。7-9月开花，10月结果。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd style=\'color:yellow;text-decoration: underline\'>红球姜</a>形态特征多年生草本，根茎块状，内部淡黄色；株高0.6-2米；具块状根茎，内部淡黄色。叶成二列，叶鞘抱茎。叶片披针形至长圆状披针形，长15-40厘米，宽3-8厘米，无毛或背面被疏长柔毛；无柄或具短柄；叶舌长1.5-2厘米。     穗状花序着生于花茎顶端，近长圆形，松果状。苞片密集，覆瓦状排列，幼时绿色，后转红色。小花具细长花冠筒，檐部3裂，白色。花期夏、秋季。总花梗长10-30厘米，被5-7枚鳞片状鞘；花序球果状，顶端钝，长6-15厘米，宽3.5-5厘米；苞片覆瓦状排列，紧密，近圆形，长2-3.5厘米，初时淡绿色，后变红色，边缘膜质，被小柔毛，内常贮有粘液；花萼长1.2-2厘米，膜质，一侧开裂；花冠管长2-3厘米，纤细，裂片披针形，淡黄色，后方的一枚长，长1.5-2.5厘米；唇瓣淡黄色，中央裂片近圆形或近倒卵形，长1.5-2厘米，宽约1.5厘米，顶端2裂，侧裂片倒卵形，长约1 厘米；雄蕊长1厘米，药隔附属体喙状，长8毫米。蒴果椭圆形，长8-12毫米；种子黑色。花期：7-9月，果期：10月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd style=\'color:yellow;text-decoration: underline\'>红球姜</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd style=\'color:yellow;text-decoration: underline\'>红球姜</a>以无性繁殖为主，一般采用分株法、分地下茎法繁殖，生产上大面积栽培时可用工厂化组培快繁育苗。用分地下茎法繁殖一般在春季进行，将地下之块茎挖起，以1-2个芽连根茎用利刀切开种植于大田即可，切口注意消毒处理。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd style=\'color:yellow;text-decoration: underline\'>红球姜</a>生长习性喜半阴，生于林下阴湿处，适于各种土壤生长，以肥沃、排水良好的壤土或者砂质壤土、腐殖质土种植最好。喜温热气候，植株对光照强弱反应不敏感，既喜阳又耐阴，最适温度22-30℃，11月份以后，温度低于20℃以下植株生长缓慢或停止生长，地上部分开始萎蔫、凋谢。冬季休眠，可剪除地上部枯枝败叶，土壤保持半湿润状态，不宜太干。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd style=\'color:yellow;text-decoration: underline\'>红球姜</a>主要价值园林价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd style=\'color:yellow;text-decoration: underline\'>红球姜</a>花序转红后, 可剪下来插花观赏, 观赏期约巧天, 因而适宜作切花用。药用价值：根状茎：辛，温。祛瘀消肿，解毒止痛。用于脘腹胀痛，消化不良，泄泻，跌打肿痛及解毒。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000982&idx=1&sn=347b89ea2b8d49529ecf119fe7620411&chksm=4ffc12d0788b9bc606c183051a18320c45350ca8e0813af244919efae45ce185c9e187f56345#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569851605, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyZ243LhjxJlbAHlZlCvgkU0', '0');
INSERT INTO `zbp_post` VALUES (110, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】76-《情人草》', '舞草（学名：Codariocalyx motorius (Houtt.) Ohashi）：直立小灌木，高达1.5米。茎单一或分枝，无毛。叶为三出复叶，顶生小叶长椭圆形或披针形，侧生小叶很小，长椭圆形或线形或有时缺。', '<div style=\'text-align: center;overflow: auto;\'><img title=情人草 alt=情人草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsJDarWxmQ74ia0lpFMUfRVss94FhA2ovPWfv0Tr7f4CphVKUxsKe6D5zYXzoUhXU1OQ329CLgsZEg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000935&idx=1&sn=6c282eba14a9bfbd789b3368afd57a8e&chksm=4ffc12a1788b9bb72d41bc1ff7fa43f1f372651988197e0bc3fd32aed2be32b2d8ad5501c7fa#rd style=\'color:yellow;text-decoration: underline\'>情人草</a>又叫舞草（学名：Codariocalyx motorius (Houtt.) Ohashi）：直立小灌木，高达1.5米。茎单一或分枝，无毛。叶为三出复叶，顶生小叶长椭圆形或披针形，侧生小叶很小，长椭圆形或线形或有时缺。圆锥花序或总状花序顶生或腋生；花冠紫红色，雌蕊长10-12毫米，子房被微毛。荚果镰刀形或直，腹缝线直，背缝线稍缢缩，成熟时沿背缝线开裂，疏被钩状短毛，有荚节5-9；种子长4-4.5毫米，宽2.5-3毫米。花期7-9月，果期10-11月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000935&idx=1&sn=6c282eba14a9bfbd789b3368afd57a8e&chksm=4ffc12a1788b9bb72d41bc1ff7fa43f1f372651988197e0bc3fd32aed2be32b2d8ad5501c7fa#rd style=\'color:yellow;text-decoration: underline\'>情人草</a>形态特征舞草是直立小灌木植物，高达1.5米。茎单一或分枝，圆柱形，微具条纹，无毛.叶为三出复叶，侧生小叶很小或缺而仅具单小叶；托叶窄三角形，长10-14毫米，基部宽1.7-2.3毫米，通常偏斜，无毛，边缘疏生小柔毛；叶柄长1.1-2厘米，上面具沟槽，疏生开展柔毛；顶生小叶长椭圆形或披针形，长5.5-10厘米，宽1-2.5厘米，先端圆形或急尖，有细尖，基部钝或圆，上面无毛，下面被贴伏短柔毛，侧脉每边8-14条，不达叶缘，侧生小叶很小，长椭圆形或线形或有时缺；小托叶钻形，长3-5毫米，两面无毛；小叶柄长约2毫米。     圆锥花序或总状花序顶生或腋生，花序轴具弯曲钩状毛；苞片宽卵形，长约6毫米，密生，花时脱落；花梗开花时长1-4毫米，花后延长至3-7毫米，被开展毛；花萼膜质，长2-2.5毫米，外面被毛，萼筒长1-1.5毫米，上部裂片先端2裂，长约1毫米，下部裂片长约1毫米；花冠紫红色，旗瓣长宽各7.5-10毫米，翼瓣长6.5-9.5毫米，宽4-5毫米，龙骨瓣长约10毫米，宽约3毫米，具长瓣柄，雄蕊长8-11毫米；雌蕊长10-12毫米，子房被微毛。荚果镰刀形或直，长2.5-4厘米，宽约5毫米，腹缝线直，背缝线稍缢缩，成熟时沿背缝线开裂，疏被钩状短毛，有荚节5-9；种子长4-4.5毫米，宽2.5-3毫米。花期7-9月，果期10-11月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000935&idx=1&sn=6c282eba14a9bfbd789b3368afd57a8e&chksm=4ffc12a1788b9bb72d41bc1ff7fa43f1f372651988197e0bc3fd32aed2be32b2d8ad5501c7fa#rd style=\'color:yellow;text-decoration: underline\'>情人草</a>繁殖方式可用播种与扦插繁殖，春、秋季为适期。通常采用播种法。播种时因种子表面有一层蜡质，水分难以渗入，且种皮表面又有抑制萌芽的物质，必须进行催芽。催芽用干细沙先擦去蜡质，然后用38-40℃温水中浸泡，将鼓胀冒芽的种子逐渐挑出播种。3-5月播种当年可开花，5月底播种明年上半年可开花。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000935&idx=1&sn=6c282eba14a9bfbd789b3368afd57a8e&chksm=4ffc12a1788b9bb72d41bc1ff7fa43f1f372651988197e0bc3fd32aed2be32b2d8ad5501c7fa#rd style=\'color:yellow;text-decoration: underline\'>情人草</a>生长习性喜阳光和温暖湿润的环境。耐旱，耐瘠薄土壤。是对一定频率和强度的声波极富感应性植物，与温度和日光亦有关。当气温24℃以上，风和日丽的晴天，小叶会自行交叉转动，亲吻和弹跳，两叶转动幅度可达180度，然后又弹回原处。当气温28-34℃或在闷热的晴天或雨过天晴，纵观全珠，数十双叶片时而如情人双双缠绵般拥抱，时而又翩翩起舞，使人眼花缭乱，给人以清新`美妙神秘的感受，当夜幕降临，它又将叶子贴在枝干，紧紧依偎着，真是罕见的花卉。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000935&idx=1&sn=6c282eba14a9bfbd789b3368afd57a8e&chksm=4ffc12a1788b9bb72d41bc1ff7fa43f1f372651988197e0bc3fd32aed2be32b2d8ad5501c7fa#rd style=\'color:yellow;text-decoration: underline\'>情人草</a>主要价值观赏价值：舞草叶片具有自然舞动的特性，已应用于园艺观赏业，是制作盆景的优良材料，观赏价值高。药用价值：舞草还具有药用保健价值，全株均可入药。据《本草纲目》记载，该草具有去瘀生新、舒筋活络之功效，其叶可治骨折；枝茎泡酒服，能强壮筋骨，治疗风湿骨疼。用鲜叶片泡水洗面，可使皮肤光滑白嫩。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000935&idx=1&sn=6c282eba14a9bfbd789b3368afd57a8e&chksm=4ffc12a1788b9bb72d41bc1ff7fa43f1f372651988197e0bc3fd32aed2be32b2d8ad5501c7fa#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569509961, 0, 0, 'GvnDcC2QjGEN4AUqdM7Dyaw2ikZwCrtQfYFOLualCxQ', '0');
INSERT INTO `zbp_post` VALUES (111, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】79-《袋鼠花》', '喜欢就点击蓝字关注我快点关注我们吧大千世界，植物王国，每日一花，让我们一起来揭开其神奇的面纱！袋鼠花袋鼠花', '<div style=\'text-align: center;overflow: auto;\'><img title=袋鼠花 alt=袋鼠花 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuibYSkf8GNUpqjuLCGiaZxqeYAhWVQ7OIKicTJJnKryuMCmJHuiaQhNicEiaphwxNGYuxm1gMhiaIaicBO2Q/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>（学名：Nematanthus gregarius D.L.Denham）是苦苣苔科，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>属多年生草本植物，具有较短的根茎，株高可达120厘米，颜色视品种不同而着生，茎粗可达4厘米左右。叶片扁平且丛生，基生叶色暗绿，总状花序，唇形花冠，因管状外形上附着天鹅绒似的绒毛，酷似袋鼠爪而得名。花色繁多、绒毛状，花期长，春天到夏天都能开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>形态特征多年生草本，具有较短的根茎，株高40-120厘米，须根较细但很发达。茎上附着细绒毛，颜色视品种不同而着生，茎的粗度达0.4厘米左右。叶扁平且丛生，线状披针形，叶剑形，基生叶色暗绿。     花茎自叶间抽出，总状花序，唇形花冠，因管状外形上附着天鹅绒似的绒毛，酷似袋鼠爪而得名。花序硕长，有花达15-20朵之多。花色繁多、绒毛状，分蘖性较强。花期长，春天到夏天都能开花。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>很难通过种子繁殖，商业种苗一般通过组培生产。组培生产出的植株常常表现为生长高度一致，花期花色基本相同，易于商品性生产和管理。由于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>具有分蘖性较强的特点，所以当植株生长过密时可以进行分株。分株时，根部切口需用800倍液的多菌灵或1000倍液的百菌清进行消毒。分株后浇透水，待新芽开始生长后进入常规管理。此繁殖方法可以保持母株当年开花，但繁殖系数低，分株后，植株恢复生长慢，不适于规模栽培。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>是一种寿命较短的植物，生长适温为18℃-28℃，越冬温度不得低于5℃。当温度高于30℃或低于5℃时，植株生长速度减缓甚至停止生长。生长需要阳光充沛，稍耐荫。喜肥。喜排水良好的沙壤土。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd style=\'color:yellow;text-decoration: underline\'>袋鼠花</a>因花形奇特，花期又长，从春末开到秋季，茂密的黄绿色花枝密生绒毛，极像绒状玩具，非常生动可爱。是国际花卉市场十分引人注目的切花和盆花材料，切花装饰居室，呈现出新鲜、新款新奇的意趣盆花点缀窗台 阳台或小庭园，充满活力更具情；在温暖地的公园、植物国中用于布置花坛或多年生花境，更趋自然和谐。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000888&idx=1&sn=7f52682f6a19cd64a8144b39cd72747f&chksm=4ffc127e788b9b68c46a3f93ac261dfe1d3d2d91962f3a01f2946fdb593102dc8f1793de734f#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569765958, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyXCH1ZiwO_gBY1xV0qu-z0Y', '0');
INSERT INTO `zbp_post` VALUES (112, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】77-《欧耧斗菜》', '欧耧斗菜（学名：Aquilegia vulgaris L. ）是毛茛科耧斗菜属植物，株高30-60厘米。基生叶有长柄，基生叶及茎下部叶为二回三出复叶，小叶2-3裂，裂片边缘具圆齿。最上部茎生叶近无柄。聚伞花序，具数朵花，通常蓝色，也有白色、', '<div style=\'text-align: center;overflow: auto;\'><img title=欧耧斗菜 alt=欧耧斗菜 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctc2rb2DLeFB8wF7VHOiaWX1DsI6MSOWNZ5zqWprgWS7ibSdSYYrCSWglYz55GfcZmTeBHESQlbbqGg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000886&idx=1&sn=e9d974fb13d0b5b2c7a1eff3113fe71e&chksm=4ffc1270788b9b66033b15c65e06cc9e486516aa56dcde16b2b9d6f53c099af64a8271fe56ed#rd style=\'color:yellow;text-decoration: underline\'>欧耧斗菜</a>（学名：Aquilegia vulgaris L. ）是毛茛科耧斗菜属植物，株高30-60厘米。基生叶有长柄，基生叶及茎下部叶为二回三出复叶，小叶2-3裂，裂片边缘具圆齿。最上部茎生叶近无柄。聚伞花序，具数朵花，通常蓝色，也有白色、红色、粉色等，下垂。蓇葖果，种子黑色。花期5-7月。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000886&idx=1&sn=e9d974fb13d0b5b2c7a1eff3113fe71e&chksm=4ffc1270788b9b66033b15c65e06cc9e486516aa56dcde16b2b9d6f53c099af64a8271fe56ed#rd style=\'color:yellow;text-decoration: underline\'>欧耧斗菜</a>形态特征株高30-60厘米。基生叶有长柄，基生叶及茎下部叶为二回三出复叶，小叶2-3裂，裂片边缘具圆齿。最上部茎生叶近无柄，狭3裂。聚伞花序，具数朵花，花大，通常蓝色，也有白色、红色、粉色等，下垂。萼片5，花瓣5，距向内弯曲成钩状。蓇葖果，种子黑色。花期5-7月。       </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000886&idx=1&sn=e9d974fb13d0b5b2c7a1eff3113fe71e&chksm=4ffc1270788b9b66033b15c65e06cc9e486516aa56dcde16b2b9d6f53c099af64a8271fe56ed#rd style=\'color:yellow;text-decoration: underline\'>欧耧斗菜</a>繁殖方式市场上出售的均为杂交一代种子，发芽率高，一般于春秋二季进行，但植株一般需低温春化才能开花，因此，春播苗大多要到第二年春季才能开花结实。基质宜选择疏松、透气、排水良好的土壤，可选择颗粒较细的泥炭、草炭土或腐叶土，大量可用播种盘育苗，少量的也可用花盆播种。  </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000886&idx=1&sn=e9d974fb13d0b5b2c7a1eff3113fe71e&chksm=4ffc1270788b9b66033b15c65e06cc9e486516aa56dcde16b2b9d6f53c099af64a8271fe56ed#rd style=\'color:yellow;text-decoration: underline\'>欧耧斗菜</a>生长习性原产于欧洲和俄罗斯（西伯利亚），中国大量引种栽培。其抗性强，适应性好，喜凉爽湿润及阳光充足的环境，且耐寒性极好，但不耐高温及强光。生长适温15-20℃。喜疏松肥沃及排水良好的沙壤土。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000886&idx=1&sn=e9d974fb13d0b5b2c7a1eff3113fe71e&chksm=4ffc1270788b9b66033b15c65e06cc9e486516aa56dcde16b2b9d6f53c099af64a8271fe56ed#rd style=\'color:yellow;text-decoration: underline\'>欧耧斗菜</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000886&idx=1&sn=e9d974fb13d0b5b2c7a1eff3113fe71e&chksm=4ffc1270788b9b66033b15c65e06cc9e486516aa56dcde16b2b9d6f53c099af64a8271fe56ed#rd style=\'color:yellow;text-decoration: underline\'>欧耧斗菜</a>花艳丽，花色繁多，花形独特，叶态优美，为优良观花草本。可用于花境造景，也可植于林缘、疏林下片植或丛植于花坛及点缀岩石园中，也可盆栽用于庭院、居室美化。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000886&idx=1&sn=e9d974fb13d0b5b2c7a1eff3113fe71e&chksm=4ffc1270788b9b66033b15c65e06cc9e486516aa56dcde16b2b9d6f53c099af64a8271fe56ed#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569598359, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyXcRwtvhbFi9z_KeCBryO60', '0');
INSERT INTO `zbp_post` VALUES (113, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】75-《倒提壶》', '倒提壶（学名：Cynoglossum amabile Stapf et Drumm.）：紫草科，琉璃草属多年生草本植物，高可达60厘米。茎密生贴伏短柔毛。基生叶，长圆状披针形或披针形，茎生叶长圆形或披针形，无柄，花序锐角分枝，紧密', '<div style=\'text-align: center;overflow: auto;\'><img title=倒提壶 alt=倒提壶 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcsprelZjkVtHlWWzKQ9pxbgJRCTq95qichibWXYPniaS7xRvLgIVn62RVnwS8S55GBMqAVEdTI1JK3DA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd style=\'color:yellow;text-decoration: underline\'>倒提壶</a>（学名：Cynoglossum amabile Stapf et Drumm.）：紫草科，琉璃草属多年生草本植物，高可达60厘米。茎密生贴伏短柔毛。基生叶，长圆状披针形或披针形，茎生叶长圆形或披针形，无柄，花序锐角分枝，紧密，向上直伸，圆锥状，无苞片；裂片卵形或长圆形，花冠通常蓝色，稀白色，裂片圆形，花柱线状圆柱形，小坚果卵形，5-9月花果期。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd style=\'color:yellow;text-decoration: underline\'>倒提壶</a>形态特征多年生草本，高15-60厘米。茎单一或数条丛生，密生贴伏短柔毛。基生叶具长柄，长圆状披针形或披针形，长5-20厘米（包括叶柄），宽1.5-4厘米，稀5厘米，两面密生短柔毛；茎生叶长圆形或披针形，无柄，长2-7厘米，侧脉极明显。     花序锐角分枝，分枝紧密，向上直伸，集为圆锥状，无苞片；花梗长2-3毫米，果期稍增长；花萼长2.5-3.5毫米，外面密生柔毛，裂片卵形或长圆形，先端尖；花冠通常蓝色，稀白色，长5-6毫米，檐部直径8-10毫米，裂片圆形，长约2.5毫米，有明显的网脉，喉部具5个梯形附属物，附属物长约1毫米；花丝长约0.5毫米，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd style=\'color:yellow;text-decoration: underline\'>倒提壶</a>生花冠筒中部，花药长圆形，长约1毫米；花柱线状圆柱形，与花萼近等长或较短。小坚果卵形，长3-4毫米，背面微凹，密生锚状刺，边缘锚状刺基部连合，成狭或宽的翅状边，腹面中部以上有三角形着生面。花果期5-9月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd style=\'color:yellow;text-decoration: underline\'>倒提壶</a>繁殖方式南方平原地区度夏较难，通常作一年生栽培。繁殖多采用播种的方式，自播能力强。种子较大，抗性好，直播即可。温度保持在10度左右合适，秋播。冬季低于-5℃时应转入温室。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd style=\'color:yellow;text-decoration: underline\'>倒提壶</a>为直根系植物，具有膨大的变态根，不适合生长期移栽。盆栽株型尚可，尽量选择深盆。营养期叶子簇生，进入花期时才有伸长的花茎。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd style=\'color:yellow;text-decoration: underline\'>倒提壶</a>生长习性日照充足开花频繁，该种耐荫。土质以肥沃，排水要求良好的土壤，在粘重的土壤中生长不良。生长适温18-20℃，忌高温。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd style=\'color:yellow;text-decoration: underline\'>倒提壶</a>主要价值观赏价值：花冠具极短的花冠筒，花瓣平展5 裂，喉部突起；花色以蓝色为主；春末至初夏开花，花姿清雅宜人，类似勿忘草。适合庭园丛植或组合盆栽。药用价值：清热利湿，散瘀止血，止咳。用于疟疾，肝炎，痢疾，尿痛，白带，肺结核咳嗽；外用治创伤出血，骨折，关节脱臼。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000885&idx=1&sn=5d03cca42dff817c995381688cf814c5&chksm=4ffc1273788b9b6554fb188483b3f13d75d0b1c3a597e979c07a485b72e4b5887a72870c5caa#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569421750, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyahJoxwInmHKfu8RoXnugpQ', '0');
INSERT INTO `zbp_post` VALUES (114, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】74-《猴面小龙兰》', '猴面小龙兰（学名：Dracula simia (Luer) Luer）：兰科，小龙兰属小型附生兰，高约40厘米，茎粗壮，叶交互对生，倒卵状披针形。花对生在叶腋内，漏斗状，花梗自基部长出，种子细小。4-5月开花。', '<div style=\'text-align: center;overflow: auto;\'><img title=猴面小龙兰 alt=猴面小龙兰 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibctRFnVicbV5mfxyCOUDEPFFqy4Nic3feOZP80neBwvUpo5bpVdLX9M2Rca1fEAYOa7k8D4nTv19NYvg/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>（学名：Dracula simia (Luer) Luer）：兰科，小龙兰属小型附生兰，高约40厘米，茎粗壮，叶交互对生，倒卵状披针形。花对生在叶腋内，漏斗状，花梗自基部长出，种子细小。4-5月开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>形态特征</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>是小型附生兰，有短小的匍匐茎，高30-40厘米，茎粗壮，中空。叶交互对生，宽卵圆形。伏地处节上生根。宽卵圆形，长宽近相等，上部略狭。叶倒卵状披针形。     稀疏总状花序，花对生在叶腋内，漏斗状，黄色，通常有紫红色斑块或斑点，花梗自基部长出，萼瓣与花瓣合成三瓣，中心为唇瓣。种子细小。4-5月开花。常作1-2年生栽培。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>繁殖方式播种繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>能自花授粉，但种子易散落，应适时分批采收。因猴面兰种子细小，温室栽培通常秋播，用平盘或穴盘育苗，种子不须覆土，播后应充分见光，一般25天左右出苗。苗期勿浇水太勤，保持基质湿润即可。同时每周喷洒1次杀菌剂，长出2-3片真叶时需移植一次，苗高10厘米以上时定植于花盆中。播种的实生苗植株1-2年可开花。幼苗抵抗力较差，水分一旦过多，易染病而致茎、根腐烂。 分株繁殖:</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>进行分株繁殖。分株一般在早春新芽萌发前进行，先把母株从花盆内取出，去掉多余的基质，把盘结的根系尽可能地分开，用利刀从茎基部剖开，每个新株应保持两株或两株以上，带有一定的根系，并用毛笔蘸硫黄粉或多菌灵粉药剂涂抹伤口，以防伤口感染病菌。分株后新植株当年就能开花。无菌繁殖:蒴果即将成熟前进行无菌播种效果最佳。播种培养基可采用1/2MS+10%椰子汁或香蕉汁50g/L+乳蛋白2g/L+蔗糖20-30g/L+琼脂8g/L，播种后15天可见淡绿色膨大胚，50-60天形成直径2毫米左右的原球茎时，可转至MS+BA1mg/L的培养基中，50-60天后长成2-3片叶的小苗。再转至生根培养基中，约60天长成完整植株时出瓶栽培。试管苗出瓶时定植在消毒后的水苔或泥炭土和珍珠岩的混合基质中，成活率为85%以上。定植3-5个月可上盆移栽，上盆后1-2年即可开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>要求凉爽的生长环境，怕酷热，度夏困难，也不耐霜寒，10℃-25℃是适宜的栽培温度。南方较温暖地区，可全年置于室外半阴处放置或塑料大棚栽培，长江流域或以北地域，应在中温或高温的温室栽培，晚秋、冬季、早春三季，温度不是很高，给予直射阳光的照射，以利于植株形成花芽，开花、结实。若遇到高温天气或在夏季，应提高湿度和覆盖遮阳网降温，温度低于10℃以下应保温、加温。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd style=\'color:yellow;text-decoration: underline\'>猴面小龙兰</a>分枝性强，花形奇特又大，花色丰富多处，耐湿、耐阴性好。多用于盆栽、吊盆或吊蓝栽培，非常适合居室的窗前、阳台的墙壁、商厦的橱窗、展厅的入口等处的绿饰，富有活力，自然轻松。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000884&idx=1&sn=181c856a33bf3ab63a1407c50c47ba86&chksm=4ffc1272788b9b64d8df952faa466347b2fa95fd75a3a7687fe2c5fe8d56e51bb03a3e0ba20c#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1569330746, 0, 0, 'GvnDcC2QjGEN4AUqdM7DyW3S__68RqUdJau9G96wSSM', '0');
INSERT INTO `zbp_post` VALUES (116, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】90-《微孔草》', '微孔草（Microula sikkimensis Hemsl.）为紫草科微孔草属2年生草本植物，主要分布于我国青海、甘肃以及四川、云南、西藏和陕西的部分地区，是我国特有的珍稀油料植物。微孔草富含γ-亚麻酸，是开发特色营养保健食品、保健食用', '<div style=\'text-align: center;overflow: auto;\'><img title=微孔草 alt=微孔草 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcuS9qtCxSOg78t8uicg7VY1UXtx9CdSicwFpkXricxxrBxTPkoesOq1E1lITuz8YTdlytibEYickwrQ9NA/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>（Microula sikkimensis Hemsl.）为紫草科</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>属2年生草本植物，主要分布于我国青海、甘肃以及四川、云南、西藏和陕西的部分地区，是我国特有的珍稀油料植物。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>富含γ-亚麻酸，是开发特色营养保健食品、保健食用油、新型化妆品和医药产品的理想原料。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>形态特征茎高6-65厘米，直立或渐升，常自基部起有长或短的分枝，或不分枝，被刚毛，有时还混生稀疏糙伏毛。基生叶和茎下部叶具长柄，卵形、狭卵形至宽披针形，长4-12厘米，宽0.7-4.4厘米，顶端急尖、渐尖，稀钝，基部圆形或宽楔形，中部以上叶渐变小，具短柄至无柄，狭卵形或宽披针形，基部渐狭，边缘全缘，两面有短伏毛，下面沿中脉有刚毛，上面还散生带基盘的刚毛。     花序密集，直径0.5-1.5厘米，有时稍伸长，长约达2厘米，生茎顶端及无叶的分枝顶端，基部苞片叶状，其他苞片小，长0.5-2毫米；花梗短，密被短糙伏毛；花萼长约2毫米，果期长达3.5毫米，5裂近基部，裂片线形或狭三角形，外面疏被短柔毛和长糙毛，边缘密被短柔毛，内面有短伏毛；花冠蓝色或蓝紫色，檐部直径5-9（-11）毫米，无毛，裂片近圆形，筒部长2.5-3.8（-4）毫米，无毛，附属物低梯形或半月形，长约0.3毫米，无毛或有短毛。小坚果卵形，长2-2.5毫米，宽约1.8毫米，有小瘤状突起和短毛，背孔位于背面中上部，狭长圆形，长1-1.5毫米，着生面位腹面中央。5-9月开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>繁殖方式</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>可进行有性繁殖和无性繁殖。试验证明，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>种子有12 个月左右的后熟期，自然贮藏后经预冷处理可打破休眠。在相同温度下，贮藏12个月的种子发芽率比收获时高41%。生产实践中还是以块根繁殖方式为主。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>是2年生植物。自然状况下由于生长季节短，当年种子萌发后以块根越冬，第2年才开花结实。应用组织培养技术可实现</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>的快速繁殖，做到每年都可收获种子。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>耐寒，耐旱，喜强光照。生长于高寒草甸、林地、灌丛和次生植被中，常与1年生作物或1-2年生野生植物混合生长形成植物群落。这些群落与环境之间未达到平衡，处于不稳定状态，群落内部物种成分常发生变化。随原有种的消失，新物种的入侵，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>的数量逐步减少，有时从群落中消失。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>在植物群落中通常不是优势种，其优势度处在7%-15%。它作为次生演替初始阶段植被成员而占据有效空间，成为高山地区次生植被中的生态适宜种而生存。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>主要价值观赏价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>是传统藏药，全草可治疗眼疾、痘疹等病。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>种子中含有18种氨基酸，其中人体必需氨基酸的含量达39.74%；矿物质营养元素丰富；饼渣中的粗蛋白含量达23.96%，经药理试验，已证实这种植物油可明显降低血清胆固醇、甘油三脂及血清丙二醛的含量，具有防止血脂沉积、动脉粥样硬化、维持生物膜和血管内膜结构完整和降低高血脂症等功能。药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>综合利用价值高，种子提油后的饼粕蛋白质含量高，可作动物饲料；茎秆可作牲畜饲料；花是优质蜜源，并有观赏价值。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>种子含油率达到43.5%，油中的不饱和脂肪酸含量高达86.39%，γ─亚麻酸（γ-linolenicacid）含量为6.37%，经动物急性毒性试验，证明无毒。因此</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd style=\'color:yellow;text-decoration: underline\'>微孔草</a>种子油已成为有医疗价值的含γ─亚麻酸的新油源。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000992&idx=1&sn=203cc95ebbc8117c2fc2b1f6ea138b6d&chksm=4ffc12e6788b9bf082d74fccdd0a81c0967b35807dc228d59c92ead2d44355cf271d2ae7f2e0#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1570719716, 0, 2, 'GvnDcC2QjGEN4AUqdM7DyQCE6vNA19dI4uzJcFD1FxI', '0');
INSERT INTO `zbp_post` VALUES (117, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】88-《平贝母》', '平贝母（学名：Fritillaria ussuriensis Maxim）是百合科，贝母属多年生草本植物；植株长可达1米。叶轮生或对生，条形至披针形，花紫色而具黄色小方格，顶端的花具叶状苞片，苞片先端强烈卷曲；药近基着，5-6月开花。', '<div style=\'text-align: center;overflow: auto;\'><img title=平贝母 alt=平贝母 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcttJP5UiahNWnGaTYX0MicGuzWHxw23vjibdcA6Xrmu1xXNvedeibicHjmKuWq6WmFvRk3EpqN9wdwJicWQ/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000990&idx=1&sn=221d868ba0e0e084cd36f0d179905c68&chksm=4ffc12d8788b9bce45d34baabb178c00936ac07b24cc5956a65a1d40c0a2bdfbc3bd8078ef69#rd style=\'color:yellow;text-decoration: underline\'>平贝母</a>（学名：Fritillaria ussuriensis Maxim）是百合科，贝母属多年生草本植物；植株长可达1米。叶轮生或对生，条形至披针形，花紫色而具黄色小方格，顶端的花具叶状苞片，苞片先端强烈卷曲；药近基着，5-6月开花。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000990&idx=1&sn=221d868ba0e0e084cd36f0d179905c68&chksm=4ffc12d8788b9bce45d34baabb178c00936ac07b24cc5956a65a1d40c0a2bdfbc3bd8078ef69#rd style=\'color:yellow;text-decoration: underline\'>平贝母</a>形态特征多年生草本；鳞茎深埋土中，外有鳞茎皮，鳞茎由2枚鳞片组成，直径1-1.5厘米，周围还常有少数小鳞茎，容易脱落。植株长可达1米。叶轮生或对生，在中上部常兼有少数散生的，条形至披针形，长7-14厘米，宽3-6.5毫米，先端不卷曲或稍卷曲。     花1-3朵，紫色而具黄色小方格，顶端的花具4-6枚叶状苞片，苞片先端强烈卷曲；外花被片长约3.5厘米，宽约1.5厘米，比内花被片稍长而宽；蜜腺窝在背面明显凸出；雄蕊长约为花被片的3/5，花药近基着，花丝具小乳突，上部更多；花柱也有乳突，柱头裂片长约5毫米。花期5-6月。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000990&idx=1&sn=221d868ba0e0e084cd36f0d179905c68&chksm=4ffc12d8788b9bce45d34baabb178c00936ac07b24cc5956a65a1d40c0a2bdfbc3bd8078ef69#rd style=\'color:yellow;text-decoration: underline\'>平贝母</a>繁殖方式播种繁殖:播种方法一般采用横床条播：大、中鳞茎的行距10-15厘米，株距3-5厘米，覆土3-4厘米。小鳞茎的采用横床宽幅条播，即幅宽10厘米，幅间距8-10厘米，株距1-1.5厘米，覆土2厘米。播种时，先将划好的床面表土起出5-6厘米，放在步道上，使床面呈一平底槽，接着在槽底施入3厘米厚的腐熟有机肥，肥的上面覆表土2-3厘米，然后条播后覆土。搂平床面，防止积水，再在床面上盖一层2-3厘米的腐熟有机肥或粉碎的草炭、落叶，称盖头粪。无论新旧贝母地，每年都要施盖头粪。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000990&idx=1&sn=221d868ba0e0e084cd36f0d179905c68&chksm=4ffc12d8788b9bce45d34baabb178c00936ac07b24cc5956a65a1d40c0a2bdfbc3bd8078ef69#rd style=\'color:yellow;text-decoration: underline\'>平贝母</a>生长习性生长于低海拔地区的林下、草甸或河谷。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000990&idx=1&sn=221d868ba0e0e084cd36f0d179905c68&chksm=4ffc12d8788b9bce45d34baabb178c00936ac07b24cc5956a65a1d40c0a2bdfbc3bd8078ef69#rd style=\'color:yellow;text-decoration: underline\'>平贝母</a>主要价值药用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000990&idx=1&sn=221d868ba0e0e084cd36f0d179905c68&chksm=4ffc12d8788b9bce45d34baabb178c00936ac07b24cc5956a65a1d40c0a2bdfbc3bd8078ef69#rd style=\'color:yellow;text-decoration: underline\'>平贝母</a>其鳞茎入药，性味辛、苦、平，有止咳化痰、润肺、散结的功效。主治支气管炎、肺结核、肺炎、百日咳等咳嗽、痰喘、溃疡病、淋巴结核、乳腺炎、痈疮等症。</p><p style=\'color: #dd6161\'>来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除</p><div style=\'color: #24be48;\'><hr><a style=\'color: #24be48;text-decoration: underline;font-size: large;padding: 15px;display: inline-block;\' target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000990&idx=1&sn=221d868ba0e0e084cd36f0d179905c68&chksm=4ffc12d8788b9bce45d34baabb178c00936ac07b24cc5956a65a1d40c0a2bdfbc3bd8078ef69#rd>【查看原文】为了更好的体验,请点击查看原文</a><hr></div><div style=\'text-align: center;\'><img style=\'display:none;\' src=\'http://upload.chelflan.cn/Fiahr1gGcyN3tXiPDoTup5ZXSlmc\' style=\'width: 300px;\'></div><div style=\'text-align: center;font-size: 13px;\'><img src=\'http://upload.chelflan.cn/Fn64eg8NsS_4FgXdQlt_pWAZKzZH\' style=\'width: 300px;\'><p style=\'color: #24be48;\'>更多精彩请关注【每日一花 Daily Flower】公众号</p></div>', 1570542803, 0, 3, 'GvnDcC2QjGEN4AUqdM7DyYY2oiL9kZDVx1Kwyjye2xI', '0');
INSERT INTO `zbp_post` VALUES (118, 2, 1, 'wechat', 0, 0, 'dailyflower', 0, 0, '【每日一花】91-《鸡蛋果》', '鸡蛋果（学名：Passiflora edulia Sims），又名百香果，是西番莲科西番莲属的草质藤本植物，长约6米；茎具细条纹，无毛；花瓣5枚，与萼片等长；基部淡绿色，中部紫色，顶部白色，浆果卵球形，直径3～4厘米，无毛，熟时紫色', '<div style=\'text-align: center;overflow: auto;\'><img title=鸡蛋果 alt=鸡蛋果 src=http://img01.store.sogou.com/net/a/04/link?appid=100520029&url=http://mmbiz.qpic.cn/mmbiz_jpg/C0z7xMSYibcvMicvHLjyM4HQ88q6fmTllZ074ia6MFbcR5TXaRyGibyynd59xl9EgOuo3uQl97ZZXfUHibBJcw63A7w/0?wx_fmt=jpeg></div><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>（学名：Passiflora edulia Sims），又叫百香果，是一种西番莲科西番莲属的草质藤本得植物，长大概6m；茎具细条纹，没有毛；花瓣有5枚，与萼片等长；基部呈淡绿色，中部呈紫色，顶部呈白色，浆果卵球形，直径3至4cm，无毛，熟时为紫色；种子多数为卵形。花期为6月，果期为11月。生于海拔180～1900m的山谷丛林。原产于安的列斯群岛，广植于热带、亚热带地区。主要有紫果和黄果两大类型。植株寿命大概20年，经济寿命一般8~10年。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>形态特征草质藤本，长大概6米；茎具细条纹，没有毛。叶纸质，长6至13cm，宽8-13cm，基部楔形或心形，掌状3深裂，中间裂片卵形，两侧裂片卵状长圆形，裂片边缘有内弯腺尖细锯齿，近裂片缺弯的基部有1-2个杯状小腺体，无毛。     聚伞花序退化仅存1花，与卷须对生；花芳香，直径约4厘米；花梗长4-4.5cm；苞片绿色，宽卵形或菱形，长1-1.2cm，边缘有不规则细锯齿；萼片5枚，外面绿色，内面绿白色，长2.5-3cm，外面顶端具1角状附属器；花瓣5枚，与萼片等长；外副花冠裂片4-5轮，外2轮裂片丝状，约与花瓣近等长，基部淡绿色，中部紫色，顶部白色，内3轮裂片窄三角形，长约2mm；内副花冠非褶状，顶端全缘或为不规则撕裂状，高约1-1.2mm；花盘膜质，高约4mm；雌雄蕊柄长1-1.2cm；雄蕊5枚，花丝分离，基部合生，长5-6mm，扁平；花药长圆形，长5-6mm，淡黄绿色；子房倒卵球形，长约8毫米，被短柔毛；花柱3枚，扁棒状，柱头肾形。浆果卵球形，直径3-4cm，纵径5~7cm，横径4~6 cm，无毛，熟时紫色，幼果绿色，成熟果紫色，单果重60~120克，果壳坚韧，果浆黄色。果皮革质坚韧，光滑。果肉间充满黄色果汁，似生鸡蛋黄。果汁含量40%左右，加工出汁率28%至33%。     </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>繁殖方式种子繁殖:8-11月边结果，边成熟，应分期分批采收。选个大、饱满的果实留种。去掉果皮和果肉，取出种子，洗净晾干贮藏。翌年春季3月播种。将种子均匀撒播于苗床上，覆盖细土2cm，浇水保湿，育苗1年可定植。 分株繁殖:利用植株根部萌发的分蘖苗挖出来定植。按行株距30cm×30cm开穴，每穴栽种1株。 </p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>最适宜的生长温度为20度～30度，一般在不低于0度的气温下生长良好，到－2度时植株会严重受害甚至死亡，年平均气温18度以上的地区最为适宜种植，易获得丰产。所以，在中国南方温暖湿润地区尤其海南地区分布较广。一般年降雨量在1500～2000mm之间且分布均匀的条件下</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>生长最好，而商品性发展种植地区则不宜少于1 000mm。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>较耐旱，但如遇干旱，仍需灌溉。土壤过于干燥，会影响藤蔓及果实发育，严重时枝条呈现枯萎状，果实不发育并会发生落果现象。雨季时，注意排水。作为一种热带水果，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>喜欢充足阳光，来促进枝蔓生长和营养积累。长日照条件有利于</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>的开花，在年日照时数2300～2800h的地区，</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>营养生长好，养分积累多，枝蔓生长快，早结，丰产。最适宜的生长温度为20度～30度，一般在不低于0度的气温下生长良好，但是到-2度时植株会严重受害，甚至死亡。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>具有适应性强，对土壤要求不高。但大面积生产的士壤土层至少有0.5m，需土壤肥沃、疏松、排水良好，土壤ph值为5.5～6.5为佳。</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>主要价值食用价值：</p><p style=\'line-height: 22px;letter-spacing: 2px;text-indent: 2em;\'><a  target=\'_blank\' href=http://mp.weixin.qq.com/s?__biz=Mzg4ODI5MjIwMQ==&mid=100000993&idx=1&sn=041cc132891ea15906590680fb5fc768&chksm=4ffc12e7788b9bf1a721b7dcebf534c38b7f0aa9d9c2466d6107270f6e1e83748b2585f6d68a#rd style=\'color:yellow;text-decoration: underline\'>鸡蛋果</a>含有17种氨基酸，且有丰富的蛋白质、脂肪、糖、维生素、钙、磷、铁、钾、SOD酶和超纤维等165种对人体有益物质，更被称为水果中的VC（维生素）之王，口感非常独特。药用价值：清肺润燥；安神止痛；和血止痢。主咳嗽；咽干；声嘶；大便秘结；失眠；痛经；关节痛；痢疾</p>，却不是母鸡的蛋。--END--来源：chelflan（侵删）注：植物资料来自百度百科、部分图片来自互联网上，若有侵权，请联系删除。Created with Sketch.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nb